        except (subprocess.TimeoutExpired, _timeout.TimeoutError) as e:
            if module.params["on_timeout"] == "error":
                module.fail_json(msg=str(e))
            elif module.params["on_timeout"] == "warn":
                module.warn(str(e))
            return default
    return wrapper

