def detect_virt_product(self, key):
    virtual_product_facts = {}
    host_tech = set()
    guest_tech = set()

    # We do similar to what we do in linux.py -- We want to allow multiple
    # virt techs to show up, but maintain compatibility, so we have to track
    # when we would have stopped, even though now we go through everything.
    found_virt = False

    self.detect_sysctl()
    if self.sysctl_path:
        rc, out, err = self.module.run_command("%s -n %s" % (self.sysctl_path, key))
        if rc == 0:
            if re.match('(KVM|kvm|Bochs|SmartDC).*', out):
                guest_tech.add('kvm')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'kvm'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if re.match('.*VMware.*', out):
                guest_tech.add('VMware')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'VMware'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if out.rstrip() == 'VirtualBox':
                guest_tech.add('virtualbox')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'virtualbox'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if re.match('(HVM domU|XenPVH|XenPV|XenPVHVM).*', out):
                guest_tech.add('xen')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'xen'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if out.rstrip() == 'Hyper-V':
                guest_tech.add('Hyper-V')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'Hyper-V'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if out.rstrip() == 'Parallels':
                guest_tech.add('parallels')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'parallels'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if out.rstrip() == 'RHEV Hypervisor':
                guest_tech.add('RHEV')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'RHEV'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True
            if (key == 'security.jail.jailed') and (out.rstrip() == '1'):
                guest_tech.add('jails')
                if not found_virt:
                    virtual_product_facts['virtualization_type'] = 'jails'
                    virtual_product_facts['virtualization_role'] = 'guest'
                    found_virt = True

    virtual_product_facts['virtualization_tech_guest'] = guest_tech
    virtual_product_facts['virtualization_tech_host'] = host_tech
    return virtual_product_facts

def detect_virt_vendor(self, key):
    virtual_vendor_facts = {}
    host_tech = set()
    guest_tech = set()
    self.detect_sysctl()
    if self.sysctl_path:
        rc, out, err = self.module.run_command("%s -n %s" % (self.sysctl_path, key))
        if rc == 0:
            if out.rstrip() == 'QEMU':
                guest_tech.add('kvm')
                virtual_vendor_facts['virtualization_type'] = 'kvm'
                virtual_vendor_facts['virtualization_role'] = 'guest'
            if out.rstrip() == 'OpenBSD':
                guest_tech.add('vmm')
                virtual_vendor_facts['virtualization_type'] = 'vmm'
                virtual_vendor_facts['virtualization_role'] = 'guest'

    virtual_vendor_facts['virtualization_tech_guest'] = guest_tech
    virtual_vendor_facts['virtualization_tech_host'] = host_tech
    return virtual_vendor_facts

def boolean(value, strict=True):
    if isinstance(value, bool):
        return value

    normalized_value = value

    if isinstance(value, (str, bytes)):
        normalized_value = to_text(value, errors='surrogate_or_strict').lower().strip()

    if not isinstance(value, c.Hashable):
        normalized_value = None  # prevent unhashable types from bombing, but keep the rest of the existing fallback/error behavior

    if normalized_value in BOOLEANS_TRUE:
        return True
    elif normalized_value in BOOLEANS_FALSE or not strict:
        return False

    raise TypeError("The value '%s' is not a valid boolean. Valid booleans include: %s" % (to_text(value), ', '.join(repr(i) for i in BOOLEANS)))

def remove_move(name):
    """Remove item from six.moves."""
    try:
        delattr(_MovedItems, name)
    except AttributeError:
        try:
            del moves.__dict__[name]
        except KeyError:
            raise AttributeError("no such move, %r" % (name,))

def with_metaclass(meta, *bases):
    """Create a base class with a metaclass."""
    # This requires a bit of explanation: the basic idea is to make a dummy
    # metaclass for one level of class instantiation that replaces itself with
    # the actual metaclass.
    class metaclass(type):

        def __new__(cls, name, this_bases, d):
            if sys.version_info[:2] >= (3, 7):
                # This version introduced PEP 560 that requires a bit
                # of extra care (we mimic what is done by __build_class__).
                resolved_bases = types.resolve_bases(bases)
                if resolved_bases is not bases:
                    d['__orig_bases__'] = bases
            else:
                resolved_bases = bases
            return meta(name, resolved_bases, d)

        @classmethod
        def __prepare__(cls, name, this_bases):
            return meta.__prepare__(name, bases)
    return type.__new__(metaclass, 'temporary_class', (), {})

def add_metaclass(metaclass):
    """Class decorator for creating a class with a metaclass."""
    def wrapper(cls):
        orig_vars = cls.__dict__.copy()
        slots = orig_vars.get('__slots__')
        if slots is not None:
            if isinstance(slots, str):
                slots = [slots]
            for slots_var in slots:
                orig_vars.pop(slots_var)
        orig_vars.pop('__dict__', None)
        orig_vars.pop('__weakref__', None)
        if hasattr(cls, '__qualname__'):
            orig_vars['__qualname__'] = cls.__qualname__
        return metaclass(cls.__name__, cls.__bases__, orig_vars)
    return wrapper

def ensure_str(s, encoding='utf-8', errors='strict'):
    """Coerce *s* to `str`.

    For Python 2:
      - `unicode` -> encoded to `str`
      - `str` -> `str`

    For Python 3:
      - `str` -> `str`
      - `bytes` -> decoded to `str`
    """
    # Optimization: Fast return for the common case.
    if type(s) is str:
        return s
    if PY2 and isinstance(s, text_type):
        return s.encode(encoding, errors)
    elif PY3 and isinstance(s, binary_type):
        return s.decode(encoding, errors)
    elif not isinstance(s, (text_type, binary_type)):
        raise TypeError("not expecting type '%s'" % type(s))
    return s

def ensure_text(s, encoding='utf-8', errors='strict'):
    """Coerce *s* to six.text_type.

    For Python 2:
      - `unicode` -> `unicode`
      - `str` -> `unicode`

    For Python 3:
      - `str` -> `str`
      - `bytes` -> decoded to `str`
    """
    if isinstance(s, binary_type):
        return s.decode(encoding, errors)
    elif isinstance(s, text_type):
        return s
    else:
        raise TypeError("not expecting type '%s'" % type(s))

def load_module(self, fullname):
    try:
        # in case of a reload
        return sys.modules[fullname]
    except KeyError:
        pass
    mod = self.__get_module(fullname)
    if isinstance(mod, MovedModule):
        mod = mod._resolve()
    else:
        mod.__loader__ = self
    sys.modules[fullname] = mod
    return mod

def reraise(tp, value, tb=None):
    try:
        if value is None:
            value = tp()
        if value.__traceback__ is not tb:
            raise value.with_traceback(tb)
        raise value
    finally:
        value = None
        tb = None

def exec_(_code_, _globs_=None, _locs_=None):
    """Execute code in a namespace."""
    if _globs_ is None:
        frame = sys._getframe(1)
        _globs_ = frame.f_globals
        if _locs_ is None:
            _locs_ = frame.f_locals
        del frame
    elif _locs_ is None:
        _locs_ = _globs_
    exec("""exec _code_ in _globs_, _locs_""")

def print_(*args, **kwargs):
    """The new-style print function for Python 2.4 and 2.5."""
    fp = kwargs.pop("file", sys.stdout)
    if fp is None:
        return

    def write(data):
        if not isinstance(data, basestring):
            data = str(data)
        # If the file has an encoding, encode unicode with it.
        if (isinstance(fp, file) and
                isinstance(data, unicode) and
                fp.encoding is not None):
            errors = getattr(fp, "errors", None)
            if errors is None:
                errors = "strict"
            data = data.encode(fp.encoding, errors)
        fp.write(data)
    want_unicode = False
    sep = kwargs.pop("sep", None)
    if sep is not None:
        if isinstance(sep, unicode):
            want_unicode = True
        elif not isinstance(sep, str):
            raise TypeError("sep must be None or a string")
    end = kwargs.pop("end", None)
    if end is not None:
        if isinstance(end, unicode):
            want_unicode = True
        elif not isinstance(end, str):
            raise TypeError("end must be None or a string")
    if kwargs:
        raise TypeError("invalid keyword arguments to print()")
    if not want_unicode:
        for arg in args:
            if isinstance(arg, unicode):
                want_unicode = True
                break
    if want_unicode:
        newline = unicode("\n")
        space = unicode(" ")
    else:
        newline = "\n"
        space = " "
    if sep is None:
        sep = space
    if end is None:
        end = newline
    for i, arg in enumerate(args):
        if i:
            write(sep)
        write(arg)
    write(end)

def print_(*args, **kwargs):
    fp = kwargs.get("file", sys.stdout)
    flush = kwargs.pop("flush", False)
    _print(*args, **kwargs)
    if flush and fp is not None:
        fp.flush()

def wrapper(cls):
    orig_vars = cls.__dict__.copy()
    slots = orig_vars.get('__slots__')
    if slots is not None:
        if isinstance(slots, str):
            slots = [slots]
        for slots_var in slots:
            orig_vars.pop(slots_var)
    orig_vars.pop('__dict__', None)
    orig_vars.pop('__weakref__', None)
    if hasattr(cls, '__qualname__'):
        orig_vars['__qualname__'] = cls.__qualname__
    return metaclass(cls.__name__, cls.__bases__, orig_vars)

def current(cls, optional: bool = False) -> t.Self | None:
    """
    Return the currently active context value for the current thread or coroutine.
    Raises ReferenceError if a context is not active, unless `optional` is `True`.
    """
    try:
        return cls._contextvar.get()
    except LookupError:
        if optional:
            return None

        raise ReferenceError(f"A required {cls.__name__} context is not active.") from None

def load_params() -> tuple[bytes, str]:
    """Load module arguments and profile when debugging an Ansible module."""
    parser = argparse.ArgumentParser(description="Directly invoke an Ansible module for debugging.")
    parser.add_argument('args', nargs='?', help='module args JSON (file path or inline string)')
    parser.add_argument('--profile', default='legacy', help='profile for JSON decoding/encoding of args/response')

    parsed_args = parser.parse_args()

    args: str | None = parsed_args.args
    profile: str = parsed_args.profile

    if args:
        if (args_path := pathlib.Path(args)).is_file():
            buffer = args_path.read_bytes()
        else:
            buffer = args.encode(errors='surrogateescape')
    else:
        if sys.stdin.isatty():
            sys.stderr.write('Waiting for Ansible module JSON on STDIN...\n')
            sys.stderr.flush()

        buffer = sys.stdin.buffer.read()

    return buffer, profile

def deprecator_from_collection_name(collection_name: str | None) -> _messages.PluginInfo | None:
    """Returns an instance with the special `collection` type to refer to a non-plugin or ambiguous caller within a collection."""
    # CAUTION: This function is exposed in public API as ansible.module_utils.datatag.deprecator_from_collection_name.

    if not collection_name:
        return None

    _validation.validate_collection_name(collection_name)

    return _messages.PluginInfo(
        resolved_name=collection_name,
        type=None,
    )

def get_best_deprecator(*, deprecator: _messages.PluginInfo | None = None, collection_name: str | None = None) -> _messages.PluginInfo:
    """Return the best-available `PluginInfo` for the caller of this method."""
    _skip_stackwalk = True

    if deprecator and collection_name:
        raise ValueError('Specify only one of `deprecator` or `collection_name`.')

    return deprecator or deprecator_from_collection_name(collection_name) or get_caller_plugin_info() or INDETERMINATE_DEPRECATOR

def get_caller_plugin_info() -> _messages.PluginInfo | None:
    """Try to get `PluginInfo` for the caller of this method, ignoring marked infrastructure stack frames."""
    _skip_stackwalk = True

    if frame_info := _stack.caller_frame():
        return _path_as_plugininfo(frame_info.filename)

    return None  # pragma: nocover

def deduplicate_message_parts(message_parts: list[str]) -> str:
    """Format the given list of messages into a brief message, while deduplicating repeated elements."""
    message_parts = list(reversed(message_parts))

    message = message_parts.pop(0)

    for message_part in message_parts:
        # avoid duplicate messages where the cause was already concatenated to the exception message
        if message_part.endswith(message):
            message = message_part
        else:
            message = _text_utils.concat_message(message_part, message)

    return message

def format_event_brief_message(event: _messages.Event) -> str:
    """
    Format an event into a brief message.
    Help text, contextual information and sub-events will be omitted.
    """
    message_parts: list[str] = []

    while True:
        message_parts.append(event.msg)

        if not event.chain or not event.chain.follow:
            break

        event = event.chain.event

    return deduplicate_message_parts(message_parts)

def deprecation_as_dict(deprecation: _messages.DeprecationSummary) -> _t.Dict[str, _t.Any]:
    """Returns a dictionary representation of the deprecation object in the format exposed to playbooks."""
    from ansible.module_utils._internal._deprecator import INDETERMINATE_DEPRECATOR  # circular import from messages

    if deprecation.deprecator and deprecation.deprecator != INDETERMINATE_DEPRECATOR:
        collection_name = '.'.join(deprecation.deprecator.resolved_name.split('.')[:2])
    else:
        collection_name = None

    result = dict(
        msg=format_event_brief_message(deprecation.event),
        collection_name=collection_name,
    )

    if deprecation.date:
        result.update(date=deprecation.date)
    else:
        result.update(version=deprecation.version)

    return result

def with_metaclass(meta, *bases):
    """Create a base class with a metaclass."""

    # This requires a bit of explanation: the basic idea is to make a dummy
    # metaclass for one level of class instantiation that replaces itself with
    # the actual metaclass.
    class metaclass(type):

        def __new__(cls, name, this_bases, d):
            if sys.version_info[:2] >= (3, 7):
                # This version introduced PEP 560 that requires a bit
                # of extra care (we mimic what is done by __build_class__).
                resolved_bases = types.resolve_bases(bases)
                if resolved_bases is not bases:
                    d['__orig_bases__'] = bases
            else:
                resolved_bases = bases
            return meta(name, resolved_bases, d)

        @classmethod
        def __prepare__(cls, name, this_bases):
            return meta.__prepare__(name, bases)

    return type.__new__(metaclass, 'temporary_class', (), {})

def add_metaclass(metaclass):
    """Class decorator for creating a class with a metaclass."""

    def wrapper(cls):
        orig_vars = cls.__dict__.copy()
        slots = orig_vars.get('__slots__')
        if slots is not None:
            if isinstance(slots, str):
                slots = [slots]
            for slots_var in slots:
                orig_vars.pop(slots_var)
        orig_vars.pop('__dict__', None)
        orig_vars.pop('__weakref__', None)
        if hasattr(cls, '__qualname__'):
            orig_vars['__qualname__'] = cls.__qualname__
        return metaclass(cls.__name__, cls.__bases__, orig_vars)

    return wrapper

def deprecate(importable_name: str, module_name: str, *deprecated_args) -> object:
    """Inject import-time deprecation warnings."""
    if not (importable_name in deprecated_args and (importable := _mini_six.get(importable_name, ...) is not ...)):
        raise AttributeError(f"module {module_name!r} has no attribute {importable_name!r}")

    # TODO Inspect and remove all calls to this function in 2.24
    warnings.deprecate(
        msg=f"Importing {importable_name!r} from {module_name!r} is deprecated.",
        version="2.24",
    )

    return importable

def wrapper(cls):
    orig_vars = cls.__dict__.copy()
    slots = orig_vars.get('__slots__')
    if slots is not None:
        if isinstance(slots, str):
            slots = [slots]
        for slots_var in slots:
            orig_vars.pop(slots_var)
    orig_vars.pop('__dict__', None)
    orig_vars.pop('__weakref__', None)
    if hasattr(cls, '__qualname__'):
        orig_vars['__qualname__'] = cls.__qualname__
    return metaclass(cls.__name__, cls.__bases__, orig_vars)

def get_plugin_info(value: HasPluginInfo) -> _messages.PluginInfo:
    """Utility method that returns a `PluginInfo` from an object implementing the `HasPluginInfo` protocol."""
    return _messages.PluginInfo(
        resolved_name=value.ansible_name,
        type=normalize_plugin_type(value.plugin_type),
    )

def normalize_plugin_type(value: str) -> _messages.PluginType | None:
    """Normalize value and return it as a PluginType, or None if the value does match any known plugin type."""
    value = value.lower()

    if value == 'modules':
        value = 'module'

    try:
        return _messages.PluginType(value)
    except ValueError:
        return None

def caller_frame() -> _inspect.FrameInfo | None:
    """Return the caller stack frame, skipping any marked with the `_skip_stackwalk` local."""
    _skip_stackwalk = True

    return next(iter_stack(), None)

def iter_stack() -> _t.Generator[_inspect.FrameInfo]:
    """Iterate over stack frames, skipping any marked with the `_skip_stackwalk` local."""
    _skip_stackwalk = True

    for frame_info in _inspect.stack():
        if '_skip_stackwalk' in frame_info.frame.f_locals:
            continue

        yield frame_info

def maybe_extract_traceback(exception: BaseException, event: TracebackEvent) -> str | None:
    """Optionally extract a formatted traceback from the given exception, if the specified traceback event is enabled."""

    if not is_traceback_enabled(event):
        return None

    # deprecated: description='use the single-arg version of format_traceback' python_version='3.9'
    tb_lines = traceback.format_exception(type(exception), exception, exception.__traceback__)

    return ''.join(tb_lines)

def validate_collection_name(collection_name: object, name: str = 'collection_name') -> None:
    """Validate a collection name."""
    if not isinstance(collection_name, str):
        raise TypeError(f"{name} must be {str} instead of {type(collection_name)}")

    parts = collection_name.split('.')

    if len(parts) != 2 or not all(part.isidentifier() and not keyword.iskeyword(part) for part in parts):
        raise ValueError(f"{name} must consist of two non-keyword identifiers separated by '.'")

def create_payload() -> str:
    """Create and return an AnsiballZ payload for respawning a module."""
    main = sys.modules['__main__']
    code = inspect.getsource(_respawn_wrapper)

    args = dict(
        module_fqn=main._module_fqn,
        modlib_path=main._modlib_path,
        profile=basic._ANSIBLE_PROFILE,
        json_params=basic._ANSIBLE_ARGS,
    )

    args_string = '\n'.join(f'{key}={value!r},' for key, value in args.items())

    wrapper = f"""{code}

if __name__ == "__main__":
    _respawn_main(
{args_string}
)
"""

    return wrapper

def untag(value: _T, *tag_types: t.Type[AnsibleDatatagBase]) -> _T:
    """
    If tags matching any of `tag_types` are present on `value`, return a copy with those tags removed.
    If no `tag_types` are specified and the object has tags, return a copy with all tags removed.
    Otherwise, the original `value` is returned.
    """
    tag_set = AnsibleTagHelper.tags(value)

    if not tag_set:
        return value

    if tag_types:
        tags_mapping = _AnsibleTagsMapping((type(tag), tag) for tag in tag_set if type(tag) not in tag_types)  # pylint: disable=unidiomatic-typecheck

        if len(tags_mapping) == len(tag_set):
            return value  # if no tags were removed, return the original instance
    else:
        tags_mapping = None

    if not tags_mapping:
        if t.cast(AnsibleTaggedObject, value)._empty_tags_as_native:
            return t.cast(AnsibleTaggedObject, value)._native_copy()

        tags_mapping = _EMPTY_INTERNAL_TAGS_MAPPING

    tagged_type = AnsibleTaggedObject._get_tagged_type(type(value))

    return t.cast(_T, tagged_type._instance_factory(value, tags_mapping))

def tags(value: t.Any) -> t.FrozenSet[AnsibleDatatagBase]:
    tags = _try_get_internal_tags_mapping(value)

    if tags is _EMPTY_INTERNAL_TAGS_MAPPING:
        return _empty_frozenset

    return frozenset(tags.values())

def tag_types(value: t.Any) -> t.FrozenSet[t.Type[AnsibleDatatagBase]]:
    tags = _try_get_internal_tags_mapping(value)

    if tags is _EMPTY_INTERNAL_TAGS_MAPPING:
        return _empty_frozenset

    return frozenset(tags)

def base_type(type_or_value: t.Any, /) -> type:
    """Return the friendly type of the given type or value. If the type is an AnsibleTaggedObject, the native type will be used."""
    if isinstance(type_or_value, type):
        the_type = type_or_value
    else:
        the_type = type(type_or_value)

    if issubclass(the_type, AnsibleTaggedObject):
        the_type = type_or_value._native_type

    # DTFIX-FUTURE: provide a knob to optionally report the real type for debugging purposes
    return the_type

def tag_copy(src: t.Any, value: _T, *, value_type: t.Optional[type] = None) -> _T:
    """Return a copy of `value`, with tags copied from `src`, overwriting any existing tags of the same types."""
    src_tags = AnsibleTagHelper.tags(src)
    value_tags = [(tag, tag._get_tag_to_propagate(src, value, value_type=value_type)) for tag in src_tags]
    tags = [tag[1] for tag in value_tags if tag[1] is not None]
    tag_types_to_remove = [type(tag[0]) for tag in value_tags if tag[1] is None]

    if tag_types_to_remove:
        value = AnsibleTagHelper.untag(value, *tag_types_to_remove)

    return AnsibleTagHelper.tag(value, tags, value_type=value_type)

def tag(value: _T, tags: t.Union[AnsibleDatatagBase, t.Iterable[AnsibleDatatagBase]], *, value_type: t.Optional[type] = None) -> _T:
    """
    Return a copy of `value`, with `tags` applied, overwriting any existing tags of the same types.
    If `value` is an ignored type, or `tags` is empty, the original `value` will be returned.
    If `value` is not taggable, a `NotTaggableError` exception will be raised.
    If `value_type` was given, that type will be returned instead.
    """
    if value_type is None:
        value_type_specified = False
        value_type = type(value)
    else:
        value_type_specified = True

    # if no tags to apply, just return what we got
    # NB: this only works because the untaggable types are singletons (and thus direct type comparison works)
    if not tags or value_type in _untaggable_types:
        if value_type_specified:
            return value_type(value)

        return value

    tag_list: list[AnsibleDatatagBase]

    # noinspection PyProtectedMember
    if type(tags) in _known_tag_types:
        tag_list = [tags]  # type: ignore[list-item]
    else:
        tag_list = list(tags)  # type: ignore[arg-type]

        for idx, tag in enumerate(tag_list):
            # noinspection PyProtectedMember
            if type(tag) not in _known_tag_types:
                # noinspection PyProtectedMember
                raise TypeError(f'tags[{idx}] of type {type(tag)} is not one of {_known_tag_types}')

    existing_internal_tags_mapping = _try_get_internal_tags_mapping(value)

    if existing_internal_tags_mapping is not _EMPTY_INTERNAL_TAGS_MAPPING:
        # include the existing tags first so new tags of the same type will overwrite
        tag_list = list(chain(existing_internal_tags_mapping.values(), tag_list))

    tags_mapping = _AnsibleTagsMapping((type(tag), tag) for tag in tag_list)
    tagged_type = AnsibleTaggedObject._get_tagged_type(value_type)

    return t.cast(_T, tagged_type._instance_factory(value, tags_mapping))

def try_tag(value: _T, tags: t.Union[AnsibleDatatagBase, t.Iterable[AnsibleDatatagBase]]) -> _T:
    """
    Return a copy of `value`, with `tags` applied, overwriting any existing tags of the same types.
    If `value` is not taggable or `tags` is empty, the original `value` will be returned.
    """
    try:
        return AnsibleTagHelper.tag(value, tags)
    except NotTaggableError:
        return value

def first_tagged_on(cls, *values: t.Any) -> t.Any | None:
    """Return the first value which is tagged with this type, or None if no match is found."""
    for value in values:
        if cls.is_tagged_on(value):
            return value

    return None

def get_required_tag(cls, value: t.Any) -> t.Self:
    if (tag := cls.get_tag(value)) is None:
        # DTFIX-FUTURE: we really should have a way to use AnsibleError with obj in module_utils when it's controller-side
        raise ValueError(f'The type {type(value).__name__!r} is not tagged with {cls.__name__!r}.')

    return tag

def default(self, o: object) -> object:
    if self._decode_bytes:
        if type(o) is _profiles._WrappedValue:  # pylint: disable=unidiomatic-typecheck
            o = o.wrapped

        if isinstance(o, bytes):
            return o.decode(errors='surrogateescape')  # backward compatibility with `ansible.module_utils.basic.jsonify`

    return super().default(o)

def get_module_serialization_profile_name(name: str, controller_to_module: bool) -> str:
    if controller_to_module:
        name = f'module_{name}_c2m'
    else:
        name = f'module_{name}_m2c'

    return name

def get_serialization_module_name(name: str | types.ModuleType) -> str:
    if isinstance(name, str):
        if '.' in name:
            return name  # name is already fully qualified

        target_name = f'{__name__}._profiles._{name}'
    elif isinstance(name, types.ModuleType):
        return name.__name__
    else:
        raise TypeError(f'Name is {type(name)} instead of {str} or {types.ModuleType}.')

    if importlib.util.find_spec(target_name):
        return target_name

    # the value of is_controller can change after import; always pick it up from the module
    if _internal.is_controller:
        controller_name = f'ansible._internal._json._profiles._{name}'

        if importlib.util.find_spec(controller_name):
            return controller_name

    raise ValueError(f'Unknown profile name {name!r}.')

def handle_key(cls, k: _t.Any) -> _t.Any:
    while mapped_callable := cls.serialize_map.get(type(k)):
        k = mapped_callable(k)

    k = cls.default(k)

    if not isinstance(k, str):
        k = _dumps(k, cls=Encoder)

    return k

def last_chance(cls, o: _t.Any) -> _t.Any:
    try:
        return str(o)
    except Exception as ex:
        return str(ex)

def deserialize_serializable(cls, value: dict[str, t.Any]) -> object:
    type_key = value[AnsibleSerializable._TYPE_KEY]

    if type_key not in cls._allowed_type_keys:
        cls.cannot_deserialize_error(type_key)

    return AnsibleSerializable._deserialize(value)

def maybe_wrap(cls, o: t.Any) -> t.Any:
    if type(o) in cls._unwrapped_json_types:
        return o

    return _WrappedValue(o)

def default(cls, o: t.Any) -> t.Any:
    # Preserve the built-in JSON encoder support for subclasses of scalar types.

    if isinstance(o, _json_subclassable_scalar_types):
        return o

    # Preserve the built-in JSON encoder support for subclasses of dict and list.
    # Additionally, add universal support for mappings and sequences/sets by converting them to dict and list, respectively.

    if _internal.is_intermediate_mapping(o):
        return {cls.handle_key(k): cls.maybe_wrap(v) for k, v in o.items()}

    if _internal.is_intermediate_iterable(o):
        return [cls.maybe_wrap(v) for v in o]

    return cls.last_chance(o)

def last_chance(cls, o: t.Any) -> t.Any:
    if isinstance(o, Tripwire):
        o.trip()

    cls.cannot_serialize_error(o)

def default(self, o: t.Any) -> t.Any:
    o_type = type(o)

    if o_type is _WrappedValue:  # pylint: disable=unidiomatic-typecheck
        o = o.wrapped
        o_type = type(o)

    if mapped_callable := self._profile.serialize_map.get(o_type):
        return self._profile.maybe_wrap(mapped_callable(o))

    # This is our last chance to intercept the values in containers, so they must be wrapped here.
    # Only containers natively understood by the built-in JSONEncoder are recognized, since any other container types must be present in serialize_map.

    if o_type is dict:  # pylint: disable=unidiomatic-typecheck
        return {self._profile.handle_key(k): self._profile.maybe_wrap(v) for k, v in o.items()}

    if o_type is list or o_type is tuple:  # pylint: disable=unidiomatic-typecheck
        return [self._profile.maybe_wrap(v) for v in o]  # JSONEncoder converts tuple to a list, so just make it a list now

    # Any value here is a type not explicitly handled by this encoder.
    # The profile default handler is responsible for generating an error or converting the value to a supported type.

    return self._profile.default(o)

def raw_decode(self, s: str, idx: int = 0) -> tuple[t.Any, int]:
    obj, end = super().raw_decode(s, idx)

    if _string_encoding_check_enabled():
        try:
            _recursively_check_string_encoding(obj)
        except UnicodeEncodeError as ex:
            raise _create_encoding_check_error() from ex

    obj = self._profile.post_deserialize(self, obj)

    return obj, end

def object_hook(self, pairs: dict[str, object]) -> object:
    if _string_encoding_check_enabled():
        try:
            for key, value in pairs.items():
                key.encode()
                _recursively_check_string_encoding(value)
        except UnicodeEncodeError as ex:
            raise _create_encoding_check_error() from ex

    for mapped_key, mapped_callable in self._profile.deserialize_map.items():
        if mapped_key in pairs:
            return mapped_callable(pairs)

    return pairs

def is_patch_needed(cls) -> bool:
    @dataclasses.dataclass
    class CheckClassVar:
        # this is the broken case requiring patching: ClassVar dot-referenced from a module that is not `typing` is treated as an instance field
        # DTFIX-FUTURE: file/link CPython bug report, deprecate this patch if/when it's fixed in CPython
        a_classvar: _ts.ClassVar[int]  # type: ignore[name-defined]
        a_field: int

    return len(dataclasses.fields(CheckClassVar)) != 1

def patch(cls) -> None:
    """Idempotently apply this patch (if needed)."""
    if cls.is_patched():
        return

    cls.unpatched_implementation = cls.get_current_implementation()

    if not cls.is_patch_needed():
        return

    # __call__ requires an instance (otherwise it'll be __new__)
    setattr(cls.target_container, cls.target_attribute, cls())

    if not cls.is_patch_needed():
        return

    setattr(cls.target_container, cls.target_attribute, cls.unpatched_implementation)

    raise RuntimeError(f"Validation of '{cls.target_container.__name__}.{cls.target_attribute}' failed after patching.")

def load(
        self,
        data: str,
        file_name: str | None = None,  # DTFIX-FUTURE: consider deprecating this in favor of tagging Origin on data
        show_content: bool = True,  # DTFIX-FUTURE: consider future deprecation, but would need RedactAnnotatedSourceContext public
        json_only: bool = False,
) -> t.Any:
    """Backwards compat for now"""
    with _error_utils.RedactAnnotatedSourceContext.when(not show_content):
        return from_yaml(data=data, file_name=file_name, json_only=json_only)

def get_text_file_contents(self, file_name: str, encoding: str | None = None) -> str:
    """
    Returns an `Origin` tagged string with the content of the specified (DWIM-expanded for relative) file path, decrypting if necessary.
    Callers must only specify `encoding` when the user can configure it, as error messages in that case will imply configurability.
    If `encoding` is not specified, UTF-8 will be used.
    """
    bytes_content, source_was_plaintext = self._get_file_contents(file_name)

    if encoding is None:
        encoding = 'utf-8'
        help_text = 'This file must be UTF-8 encoded.'
    else:
        help_text = 'Ensure the correct encoding was specified.'

    try:
        str_content = bytes_content.decode(encoding=encoding, errors='strict')
    except UnicodeDecodeError:
        str_content = bytes_content.decode(encoding=encoding, errors='surrogateescape')

        display.deprecated(
            msg=f"File {file_name!r} could not be decoded as {encoding!r}. Invalid content has been escaped.",
            version="2.23",
            # obj intentionally omitted since there's no value in showing its contents
            help_text=help_text,
        )

    if not source_was_plaintext:
        str_content = SourceWasEncrypted().tag(str_content)

    return AnsibleTagHelper.tag_copy(bytes_content, str_content)

def cleanup_all_tmp_files(self) -> None:
    """
    Removes all temporary files that DataLoader has created
    NOTE: not thread safe, forks also need special handling see __init__ for details.
    """
    for f in list(self._tempfiles):
        try:
            self.cleanup_tmp_file(f)
        except Exception as e:
            display.warning("Unable to cleanup temp files: %s" % to_text(e))

def read_docstring_from_yaml_file(filename, verbose=True, ignore_errors=True):
    """ Read docs from 'sidecar' yaml file doc for a plugin """

    data = _init_doc_dict()
    file_data = {}

    try:
        with open(filename, 'rb') as yamlfile:
            file_data = yaml.load(yamlfile, Loader=AnsibleLoader)
    except Exception as ex:
        msg = f"Unable to parse yaml file {filename}"
        # DTFIX-FUTURE: find a better pattern for this (can we use the new optional error behavior?)
        if not ignore_errors:
            raise AnsibleParserError(f'{msg}.') from ex
        elif verbose:
            display.error(f'{msg}: {ex}')

    if file_data:
        for key in string_to_vars:
            data[string_to_vars[key]] = file_data.get(key, None)

    return data

def read_docstring_from_python_file(filename, verbose=True, ignore_errors=True):
    """
    Use ast to search for assignment of the DOCUMENTATION and EXAMPLES variables in the given file.
    Parse DOCUMENTATION from YAML and return the YAML doc or None together with EXAMPLES, as plain text.
    """
    data = _init_doc_dict()

    try:
        with open(filename, 'rb') as b_module_data:
            M = ast.parse(b_module_data.read())

        for child in M.body:
            if isinstance(child, ast.Assign):
                for t in child.targets:
                    try:
                        theid = t.id
                    except AttributeError:
                        # skip errors can happen when trying to use the normal code
                        display.warning("Building documentation, failed to assign id for %s on %s, skipping" % (t, filename))
                        continue

                    if theid in string_to_vars:
                        varkey = string_to_vars[theid]
                        if isinstance(child.value, ast.Dict):
                            data[varkey] = ast.literal_eval(child.value)
                        else:
                            if theid == 'EXAMPLES':
                                # examples 'can' be yaml, but even if so, we dont want to parse as such here
                                # as it can create undesired 'objects' that don't display well as docs.
                                data[varkey] = to_text(child.value.value)
                            else:
                                # string should be yaml if already not a dict
                                child_value = _tags.Origin(path=filename, line_num=child.value.lineno).tag(child.value.value)
                                data[varkey] = yaml.load(child_value, Loader=AnsibleLoader)

                        display.debug('Documentation assigned: %s' % varkey)

    except Exception as ex:
        msg = f"Unable to parse documentation in python file {filename!r}"
        # DTFIX-FUTURE: better pattern to conditionally raise/display
        if not ignore_errors:
            raise AnsibleParserError(f'{msg}.') from ex
        elif verbose:
            display.error(f'{msg}: {ex}.')

    return data

def read_docstring(filename, verbose=True, ignore_errors=True):
    """ returns a documentation dictionary from Ansible plugin docstrings """

    # NOTE: adjacency of doc file to code file is responsibility of caller
    if filename.endswith(C.YAML_DOC_EXTENSIONS):
        docstring = read_docstring_from_yaml_file(filename, verbose=verbose, ignore_errors=ignore_errors)
    elif filename.endswith(C.PYTHON_DOC_EXTENSIONS):
        docstring = read_docstring_from_python_file(filename, verbose=verbose, ignore_errors=ignore_errors)
    elif not ignore_errors:
        raise AnsibleError("Unknown documentation format: %s" % to_native(filename))

    if not docstring and not ignore_errors:
        raise AnsibleError("Unable to parse documentation for: %s" % to_native(filename))

    # cause seealso is specially processed from 'doc' later on
    # TODO: stop any other 'overloaded' implementation in main doc
    docstring['seealso'] = None

    return docstring

def unquote(data):
    """ removes first and last quotes from a string, if the string starts and ends with the same quotes """
    if is_quoted(data):
        return data[1:-1]
    return data

def parse_kv(args, check_raw=False):
    """
    Convert a string of key/value items to a dict. If any free-form params
    are found and the check_raw option is set to True, they will be added
    to a new parameter called '_raw_params'. If check_raw is not enabled,
    they will simply be ignored.
    """

    tags = []
    if origin_tag := Origin.get_tag(args):
        # NB: adjusting the column number is left as an exercise for the reader
        tags.append(origin_tag)
    if trusted_tag := TrustedAsTemplate.get_tag(args):
        tags.append(trusted_tag)

    args = to_text(args, nonstring='passthru')

    options = {}
    if args is not None:
        vargs = split_args(args)

        raw_params = []
        for orig_x in vargs:
            x = _decode_escapes(orig_x)
            if "=" in x:
                pos = 0
                try:
                    while True:
                        pos = x.index('=', pos + 1)
                        if pos > 0 and x[pos - 1] != '\\':
                            break
                except ValueError:
                    # ran out of string, but we must have some escaped equals,
                    # so replace those and append this to the list of raw params
                    raw_params.append(x.replace('\\=', '='))
                    continue

                k = x[:pos]
                v = x[pos + 1:]

                # FIXME: make the retrieval of this list of shell/command options a function, so the list is centralized
                if check_raw and k not in ('creates', 'removes', 'chdir', 'executable', 'warn', 'stdin', 'stdin_add_newline', 'strip_empty_ends'):
                    raw_params.append(orig_x)
                else:
                    options[k.strip()] = unquote(v.strip())
            else:
                raw_params.append(orig_x)

        # recombine the free-form params, if any were found, and assign
        # them to a special option for use later by the shell/command module
        if len(raw_params) > 0:
            options[u'_raw_params'] = join_args(raw_params)

    if tags:
        options = {AnsibleTagHelper.tag(k, tags): AnsibleTagHelper.tag(v, tags) for k, v in options.items()}

    if origin_tag:
        options = origin_tag.tag(options)

    return options

def join_args(s):
    """
    Join the original cmd based on manipulations by split_args().
    This retains the original newlines and whitespaces.
    """
    result = ''
    for p in s:
        if len(result) == 0 or result.endswith('\n'):
            result += p
        else:
            result += ' ' + p
    return result

def jsonify(result, format=False):
    """Format JSON output."""

    if result is None:
        return "{}"

    indent = None
    if format:
        indent = 4

    try:
        return json.dumps(result, sort_keys=True, indent=indent, ensure_ascii=False)
    except UnicodeDecodeError:
        return json.dumps(result, sort_keys=True, indent=indent)

def parse_vaulttext(b_vaulttext):
    """Parse the vaulttext

    :arg b_vaulttext: byte str containing the vaulttext (ciphertext, salt, crypted_hmac)
    :returns: A tuple of byte str of the ciphertext suitable for passing to a
        Cipher class's decrypt() function, a byte str of the salt,
        and a byte str of the crypted_hmac
    :raises: AnsibleVaultFormatError: if the vaulttext format is invalid
    """
    # SPLIT SALT, DIGEST, AND DATA
    try:
        return _parse_vaulttext(b_vaulttext)
    except AnsibleVaultFormatError:
        raise
    except Exception as ex:
        raise AnsibleVaultFormatError("Vault vaulttext format error.", obj=b_vaulttext) from ex

def match_secrets(secrets, target_vault_ids):
    """Find all VaultSecret objects that are mapped to any of the target_vault_ids in secrets"""
    if not secrets:
        return []

    matches = [(vault_id, secret) for vault_id, secret in secrets if vault_id in target_vault_ids]
    return matches

def ask_vault_passwords(self):
    b_vault_passwords = []

    for prompt_format in self.prompt_formats:
        prompt = prompt_format % {'vault_id': self.vault_id}
        try:
            vault_pass = display.prompt(prompt, private=True)
        except EOFError:
            raise AnsibleVaultError('EOFError (ctrl-d) on prompt for (%s)' % self.vault_id)

        verify_secret_is_not_empty(vault_pass)

        b_vault_pass = to_bytes(vault_pass, errors='strict', nonstring='simplerepr').strip()
        b_vault_passwords.append(b_vault_pass)

    # Make sure the passwords match by comparing them all to the first password
    for b_vault_password in b_vault_passwords:
        self.confirm(b_vault_passwords[0], b_vault_password)

    if b_vault_passwords:
        return b_vault_passwords[0]

    return None

def confirm(self, b_vault_pass_1, b_vault_pass_2):
    # enforce no newline chars at the end of passwords

    if b_vault_pass_1 != b_vault_pass_2:
        # FIXME: more specific exception
        raise AnsibleError("Passwords do not match")

def bytes(self):
    if self._bytes:
        return self._bytes
    if self._text:
        return self._text.encode(self.encoding)
    return None

def decrypt_file(self, filename, output_file=None):

    # follow the symlink
    filename = self._real_path(filename)

    ciphertext = self.read_data(filename)

    try:
        plaintext = self.vault.decrypt(ciphertext)
    except AnsibleError as e:
        raise AnsibleError("%s for %s" % (to_native(e), to_native(filename)))
    self.write_data(plaintext, output_file or filename, shred=False)

def plaintext(self, filename):

    b_vaulttext = self.read_data(filename)
    vaulttext = to_text(b_vaulttext)

    try:
        plaintext = self.vault.decrypt(vaulttext)
        return plaintext
    except AnsibleError as e:
        raise AnsibleVaultError("%s for %s" % (to_native(e), to_native(filename)))

def encrypt(cls, b_plaintext, secret, salt=None):

    if secret is None:
        raise AnsibleVaultError('The secret passed to encrypt() was None')

    if salt is None:
        b_salt = cls._get_salt()
    elif not salt:
        raise AnsibleVaultError('Empty or invalid salt passed to encrypt()')
    else:
        b_salt = to_bytes(salt)

    b_password = secret.bytes
    b_key1, b_key2, b_iv = cls._gen_key_initctr(b_password, b_salt)

    if HAS_CRYPTOGRAPHY:
        b_hmac, b_ciphertext = cls._encrypt_cryptography(b_plaintext, b_key1, b_key2, b_iv)
    else:
        raise AnsibleError(NEED_CRYPTO_LIBRARY + '(Detected in encrypt)')

    b_vaulttext = b'\n'.join([hexlify(b_salt), b_hmac, b_ciphertext])
    # Unnecessary but getting rid of it is a backwards incompatible vault
    # format change
    b_vaulttext = hexlify(b_vaulttext)
    return b_vaulttext

def current(cls, optional: bool = False) -> t.Self:
    """Access vault secrets, if initialized, ala `AmbientContextBase.current()`."""
    if not cls._current and not optional:
        raise ReferenceError(f"A required {cls.__name__} context is not active.")

    return cls._current

def get_ciphertext(value: t.Any, *, with_tags: bool) -> str | None:
    """
    If the given value is an `EncryptedString`, `VaultExceptionMarker` or tagged with `VaultedValue`, return the ciphertext, otherwise return `None`.
    Tags on the value other than `VaultedValue` will be included on the ciphertext if `with_tags` is `True`, otherwise it will be tagless.
    """
    value_type = type(value)
    ciphertext: str | None
    tags = AnsibleTagHelper.tags(value)

    if value_type is _jinja_common.VaultExceptionMarker:
        ciphertext = value._marker_undecryptable_ciphertext
        tags = AnsibleTagHelper.tags(ciphertext)  # ciphertext has tags but value does not
    elif value_type is EncryptedString:
        ciphertext = value._ciphertext
    elif value_type in _jinja_common.Marker._concrete_subclasses:  # avoid wasteful raise/except of Marker when calling get_tag below
        ciphertext = None
    elif vaulted_value := VaultedValue.get_tag(value):
        ciphertext = vaulted_value.ciphertext
    else:
        ciphertext = None

    if ciphertext:
        if with_tags:
            ciphertext = VaultedValue.untag(AnsibleTagHelper.tag(ciphertext, tags))
        else:
            ciphertext = AnsibleTagHelper.untag(ciphertext)

    return ciphertext

def dump_me(self, depth=0):
    """ this is never called from production code, it is here to be used when debugging as a 'complex print' """
    if depth == 0:
        display.debug("DUMPING OBJECT ------------------------------------------------------")
    display.debug("%s- %s (%s, id=%s)" % (" " * depth, self.__class__.__name__, self, id(self)))
    if hasattr(self, '_parent') and self._parent:
        self._parent.dump_me(depth + 2)
        dep_chain = self._parent.get_dep_chain()
        if dep_chain:
            for dep in dep_chain:
                dep.dump_me(depth + 2)
    if hasattr(self, '_play') and self._play:
        self._play.dump_me(depth + 2)

def get_ds(self):
    try:
        return getattr(self, '_ds')
    except AttributeError:
        return None

def validate(self, all_vars=None):
    """ validation that is done at parse time, not load time """
    if not self._validated:
        # walk all fields in the object
        for (name, attribute) in self.fattributes.items():
            # run validator only if present
            method = getattr(self, '_validate_%s' % name, None)
            if method:
                method(attribute, name, getattr(self, name))
            else:
                # and make sure the attribute is of the type it should be
                value = getattr(self, f'_{name}', Sentinel)
                if value is not None:
                    if attribute.isa == 'string' and isinstance(value, (list, dict)):
                        raise AnsibleParserError(
                            "The field '%s' is supposed to be a string type,"
                            " however the incoming data structure is a %s" % (name, type(value)), obj=self.get_ds()
                        )

    self._validated = True

def play(self):
    if hasattr(self, '_play'):
        play = self._play
    elif hasattr(self, '_parent') and hasattr(self._parent, '_play'):
        play = self._parent._play
    else:
        play = self

    if play.__class__.__name__ != 'Play':
        # Should never happen, but handle gracefully by returning None, just in case
        return None

    return play

def squash(self):
    """
    Evaluates all attributes and sets them to the evaluated version,
    so that all future accesses of attributes do not need to evaluate
    parent attributes.
    """
    if not self._squashed:
        for name in self.fattributes:
            setattr(self, name, getattr(self, name))
        self._squashed = True

def get_validated_value(self, name, attribute, value, templar):
    try:
        return self._get_validated_value(name, attribute, value, templar)
    except (TypeError, ValueError):
        raise AnsibleError(f"The value {value!r} could not be converted to {attribute.isa!r}.", obj=value)

def set_to_context(self, name: str) -> t.Any:
    """ set to parent inherited value or Sentinel as appropriate"""

    attribute = self.fattributes[name]
    if isinstance(attribute, NonInheritableFieldAttribute):
        # setting to sentinel will trigger 'default/default()' on getter
        value = Sentinel
    else:
        try:
            value = self._get_parent_attribute(name, omit=True)
        except AttributeError:
            # mostly playcontext as only tasks/handlers/blocks really resolve parent
            value = Sentinel

    setattr(self, name, value)
    return value

def post_validate_attribute(self, name: str, *, templar: TemplateEngine):
    attribute: FieldAttribute = self.fattributes[name]

    # DTFIX-FUTURE: this can probably be used in many getattr cases below, but the value may be out-of-date in some cases
    original_value = getattr(self, name)  # we save this original (likely Origin-tagged) value to pass as `obj` for errors

    if attribute.static:
        value = getattr(self, name)

        # we don't template 'vars' but allow template as values for later use
        if name not in ('vars',) and templar.is_template(value):
            display.warning('"%s" is not templatable, but we found: %s, '
                            'it will not be templated and will be used "as is".' % (name, value))
        return Sentinel

    if getattr(self, name) is None:
        if not attribute.required:
            return Sentinel

        raise AnsibleFieldAttributeError(f'The field {name!r} is required but was not set.', obj=self.get_ds())

    from .role_include import IncludeRole

    if not attribute.always_post_validate and isinstance(self, IncludeRole) and self.statically_loaded:  # import_role
        # normal field attributes should not go through post validation on import_role/import_tasks
        # only import_role is checked here because import_tasks never reaches this point
        return Sentinel

    # Skip post validation unless always_post_validate is True, or the object requires post validation.
    if not attribute.always_post_validate and not self._post_validate_object:
        # Intermediate objects like Play() won't have their fields validated by
        # default, as their values are often inherited by other objects and validated
        # later, so we don't want them to fail out early
        return Sentinel

    try:
        # Run the post-validator if present. These methods are responsible for
        # using the given templar to template the values, if required.
        method = getattr(self, '_post_validate_%s' % name, None)

        if method:
            value = method(attribute, getattr(self, name), templar)
        elif attribute.isa == 'class':
            value = getattr(self, name)
        else:
            try:
                # if the attribute contains a variable, template it now
                value = templar.template(getattr(self, name))
            except AnsibleValueOmittedError:
                # If this evaluated to the omit value, set the value back to inherited by context
                # or default specified in the FieldAttribute and move on
                value = self.set_to_context(name)

                if value is Sentinel:
                    return value

        # and make sure the attribute is of the type it should be
        if value is not None:
            value = self.get_validated_value(name, attribute, value, templar)

        # returning the value results in assigning the massaged value back to the attribute field
        return value
    except Exception as ex:
        if name == 'args':
            raise  # no useful information to contribute, raise the original exception

        raise AnsibleFieldAttributeError(f'Error processing keyword {name!r}.', obj=original_value) from ex

def dump_attrs(self):
    """
    Dumps all attributes to a dictionary
    """
    attrs = {}
    for (name, attribute) in self.fattributes.items():
        attr = getattr(self, name)
        if attribute.isa == 'class':
            attrs[name] = attr.dump_attrs()
        else:
            attrs[name] = attr
    return attrs

def from_attrs(self, attrs):
    """
    Loads attributes from a dictionary
    """
    for (attr, value) in attrs.items():
        if attr in self.fattributes:
            attribute = self.fattributes[attr]
            if attribute.isa == 'class' and isinstance(value, dict):
                obj = attribute.class_type()
                obj.from_attrs(value)
                setattr(self, attr, obj)
            else:
                setattr(self, attr, value)
        else:
            setattr(self, attr, value)  # overridden dump_attrs in derived types may dump attributes which are not field attributes

def update_result_no_log(self, templar: TemplateEngine, result: dict[str, t.Any]) -> None:
    """Set the post-validated no_log value for the result, falling back to a default on validation/templating failure with a warning."""

    if self.finalized:
        no_log = self.no_log
    else:
        try:
            no_log = self.post_validate_attribute('no_log', templar=templar)
        except Exception as ex:
            display.error_as_warning('Invalid no_log value for task, output will be masked.', exception=ex)
            no_log = True

    result_no_log = result.get('_ansible_no_log', False)

    if not isinstance(result_no_log, bool):
        display.warning(f'Invalid _ansible_no_log value of type {type(result_no_log).__name__!r} in task result, output will be masked.')
        no_log = True

    no_log = no_log or result_no_log

    result.update(_ansible_no_log=no_log)

def get_path(self) -> str:
    """ return the absolute path of the playbook object and its line number """
    origin = self._origin

    if not origin:
        try:
            origin = self._parent._play._origin
        except AttributeError:
            pass

    if origin and origin.path:
        path = f"{origin.path}:{origin.line_num or 1}"
    else:
        path = ""

    return path

def get_dep_chain(self):

    if hasattr(self, '_parent') and self._parent:
        return self._parent.get_dep_chain()
    else:
        return None

def get_vars(self):
    """
    Blocks do not store variables directly, however they may be a member
    of a role or task include which does, so return those if present.
    """

    all_vars = {}

    if self._parent:
        all_vars |= self._parent.get_vars()

    all_vars |= self.vars.copy()

    return all_vars

def is_block(ds):
    is_block = False
    if isinstance(ds, dict):
        for attr in ('block', 'rescue', 'always'):
            if attr in ds:
                is_block = True
                break
    return is_block

def preprocess_data(self, ds):
    """
    If a simple task is given, an implicit block for that single task
    is created, which goes in the main portion of the block
    """

    if not Block.is_block(ds):
        if isinstance(ds, list):
            return super(Block, self).preprocess_data(dict(block=ds))
        else:
            return super(Block, self).preprocess_data(dict(block=[ds]))

    return super(Block, self).preprocess_data(ds)

def get_dep_chain(self):
    if self._dep_chain is None:
        if self._parent:
            return self._parent.get_dep_chain()
        else:
            return None
    else:
        return self._dep_chain[:]

def copy(self, exclude_parent=False, exclude_tasks=False):
    def _dupe_task_list(task_list, new_block):
        new_task_list = []
        for task in task_list:
            new_task = task.copy(exclude_parent=True, exclude_tasks=exclude_tasks)
            if task._parent:
                new_task._parent = task._parent.copy(exclude_tasks=True)
                if task._parent == new_block:
                    # If task._parent is the same as new_block, just replace it
                    new_task._parent = new_block
                else:
                    # task may not be a direct child of new_block, search for the correct place to insert new_block
                    cur_obj = new_task._parent
                    while cur_obj._parent and cur_obj._parent != new_block:
                        cur_obj = cur_obj._parent

                    cur_obj._parent = new_block
            else:
                new_task._parent = new_block
            new_task_list.append(new_task)
        return new_task_list

    new_me = super(Block, self).copy()
    new_me._play = self._play
    new_me._use_handlers = self._use_handlers

    if self._dep_chain is not None:
        new_me._dep_chain = self._dep_chain[:]

    new_me._parent = None
    if self._parent and not exclude_parent:
        new_me._parent = self._parent.copy(exclude_tasks=True)

    if not exclude_tasks:
        new_me.block = _dupe_task_list(self.block or [], new_me)
        new_me.rescue = _dupe_task_list(self.rescue or [], new_me)
        new_me.always = _dupe_task_list(self.always or [], new_me)

    new_me._role = None
    if self._role:
        new_me._role = self._role

    new_me.validate()
    return new_me

def set_loader(self, loader):
    self._loader = loader
    if self._parent:
        self._parent.set_loader(loader)
    elif self._role:
        self._role.set_loader(loader)

    dep_chain = self.get_dep_chain()
    if dep_chain:
        for dep in dep_chain:
            dep.set_loader(loader)

def filter_tagged_tasks(self, all_vars):
    """
    Creates a new block, with task lists filtered based on the tags.
    """

    def evaluate_and_append_task(target):
        tmp_list = []
        for task in target:
            if isinstance(task, Block):
                filtered_block = evaluate_block(task)
                if filtered_block.has_tasks():
                    tmp_list.append(filtered_block)
            elif task.evaluate_tags(self._play.only_tags, self._play.skip_tags, all_vars=all_vars):
                tmp_list.append(task)
        return tmp_list

    def evaluate_block(block):
        new_block = block.copy(exclude_parent=True, exclude_tasks=True)
        new_block._parent = block._parent
        new_block.block = evaluate_and_append_task(block.block)
        new_block.rescue = evaluate_and_append_task(block.rescue)
        new_block.always = evaluate_and_append_task(block.always)
        return new_block

    return evaluate_block(self)

def get_tasks(self):
    def evaluate_and_append_task(target):
        tmp_list = []
        for task in target:
            if isinstance(task, Block):
                tmp_list.extend(evaluate_block(task))
            else:
                tmp_list.append(task)
        return tmp_list

    def evaluate_block(block):
        rv = evaluate_and_append_task(block.block)
        rv.extend(evaluate_and_append_task(block.rescue))
        rv.extend(evaluate_and_append_task(block.always))
        return rv

    return evaluate_block(self)

def get_include_params(self):
    if self._parent:
        return self._parent.get_include_params()
    else:
        return dict()

def all_parents_static(self):
    """
    Determine if all of the parents of this block were statically loaded
    or not. Since Task/TaskInclude objects may be in the chain, they simply
    call their parents all_parents_static() method. Only Block objects in
    the chain check the statically_loaded value of the parent.
    """
    from ansible.playbook.task_include import TaskInclude
    if self._parent:
        if isinstance(self._parent, TaskInclude) and not self._parent.statically_loaded:
            return False
        return self._parent.all_parents_static()

    return True

def get_first_parent_include(self):
    from ansible.playbook.task_include import TaskInclude
    if self._parent:
        if isinstance(self._parent, TaskInclude):
            return self._parent
        return self._parent.get_first_parent_include()
    return None

def evaluate_and_append_task(target):
    tmp_list = []
    for task in target:
        if isinstance(task, Block):
            filtered_block = evaluate_block(task)
            if filtered_block.has_tasks():
                tmp_list.append(filtered_block)
        elif task.evaluate_tags(self._play.only_tags, self._play.skip_tags, all_vars=all_vars):
            tmp_list.append(task)
    return tmp_list

def evaluate_and_append_task(target):
    tmp_list = []
    for task in target:
        if isinstance(task, Block):
            tmp_list.extend(evaluate_block(task))
        else:
            tmp_list.append(task)
    return tmp_list

def notify_host(self, host):
    if not self.is_host_notified(host):
        self.notified_hosts.append(host)
        return True
    return False

def remove_host(self, host):
    try:
        self.notified_hosts.remove(host)
    except ValueError:
        raise AnsibleAssertionError(
            f"Attempting to remove a notification on handler '{self}' for host '{host}' but it has not been notified."
        )

def add_host(self, host: Host) -> None:
    if host not in self._hosts:
        self._hosts.append(host)
        return

    raise ValueError()

def get_name(self):
    """ return the name of the Play """
    if self.name:
        return self.name

    if is_sequence(self.hosts):
        self.name = ','.join(self.hosts)
    else:
        self.name = self.hosts or ''

    return self.name

def load(data, variable_manager=None, loader=None, vars=None):
    p = Play()
    if vars:
        p.vars = vars.copy()
    return p.load_data(data, variable_manager=variable_manager, loader=loader)

def compile_roles_handlers(self):
    """
    Handles the role handler compilation step, returning a flat list of Handlers
    This is done for all roles in the Play.
    """

    block_list = []

    if len(self.roles) > 0:
        for r in self.roles:
            if r.from_include:
                continue
            block_list.extend(r.get_handler_blocks(play=self))

    return block_list

def get_vars_files(self):
    if self.vars_files is None:
        return []
    elif not isinstance(self.vars_files, list):
        return [self.vars_files]
    return self.vars_files

def get_tasks(self):
    tasklist = []
    for task in self.pre_tasks + self.tasks + self.post_tasks:
        if isinstance(task, Block):
            tasklist.append(task.block + task.rescue + task.always)
        else:
            tasklist.append(task)
    return tasklist

def argument_spec(self) -> dict:
    """Retrieve the argument spec if one is configured."""
    if not self.validate_argspec:
        return {}

    return self._loader.load_from_file(self._metadata_path)['argument_specs'][self.validate_argspec]['options']

def preprocess_data(self, ds):
    keys = {action for action in C._ACTION_IMPORT_PLAYBOOK if action in ds}

    if len(keys) != 1:
        raise AnsibleError(f'Found conflicting import_playbook actions: {", ".join(sorted(keys))}')

    key = next(iter(keys))

    ds['import_playbook'] = ds.pop(key)

    return ds

def set_attributes_from_plugin(self, plugin):
    # generic derived from connection plugin, temporary for backwards compat, in the end we should not set play_context properties

    # get options for plugins
    options = C.config.get_configuration_definitions(plugin.plugin_type, plugin._load_name)
    for option in options:
        if option:
            flag = options[option].get('name')
            if flag:
                setattr(self, flag, plugin.get_option(flag))

def update_vars(self, variables):
    """
    Adds 'magic' variables relating to connections to the variable dictionary provided.
    In case users need to access from the play, this is a legacy from runner.
    """

    for prop, var_list in C.MAGIC_VARIABLE_MAPPING.items():
        try:
            if 'become' in prop:
                continue

            var_val = getattr(self, prop)
            for var_opt in var_list:
                if var_opt not in variables and var_val is not None:
                    variables[var_opt] = var_val
        except AttributeError:
            continue

def evaluate_tags(self, only_tags, skip_tags, all_vars):
    """Check if the current item should be executed depending on the specified tags.

    NOTE this method is assumed to be called only on Task objects.
    """
    if self.tags:
        templar = TemplateEngine(loader=self._loader, variables=all_vars)
        for obj in self._get_all_taggable_objects():
            if (_tags := getattr(obj, "_tags", Sentinel)) is not Sentinel:
                obj._tags = _flatten_tags(templar.template(_tags))
        tags = set(self.tags)
    else:
        # this makes isdisjoint work for untagged
        tags = self.untagged

    should_run = True  # default, tasks to run

    if only_tags:
        if 'always' in tags:
            should_run = True
        elif ('all' in only_tags and 'never' not in tags):
            should_run = True
        elif not tags.isdisjoint(only_tags):
            should_run = True
        elif 'tagged' in only_tags and tags != self.untagged and 'never' not in tags:
            should_run = True
        else:
            should_run = False

    if should_run and skip_tags:

        # Check for tags that we need to skip
        if 'all' in skip_tags:
            if 'always' not in tags or 'always' in skip_tags:
                should_run = False
        elif not tags.isdisjoint(skip_tags):
            should_run = False
        elif 'tagged' in skip_tags and tags != self.untagged:
            should_run = False

    return should_run

def resolved_action(self) -> str | None:
    """The templated and resolved FQCN of the task action or None.

    If the action is a template, callback plugins can only use this value in certain methods.
    - v2_runner_on_ok and v2_runner_on_failed if there's no task loop
    - v2_runner_item_on_ok and v2_runner_item_on_failed if there is a task loop
    """
    # Consider deprecating this because it's difficult to use?
    # Moving it to the task result would improve the no-loop limitation on v2_runner_on_ok
    # but then wouldn't be accessible to v2_playbook_on_task_start, *_on_skipped, etc.
    if self._resolved_action is not None:
        return self._resolved_action
    if not is_possibly_template(self.action):
        try:
            return self._resolve_action(self.action)
        except AnsibleParserError:
            display.warning(self._resolved_action_warning, obj=self.action)
    else:
        display.warning(self._resolved_action_warning, obj=self.action)
    return None

def get_name(self, include_role_fqcn=True):
    """ return the name of the task """

    if self._role:
        role_name = self._role.get_name(include_role_fqcn=include_role_fqcn)

    if self._role and self.name:
        return "%s : %s" % (role_name, self.name)
    elif self.name:
        return self.name
    else:
        if self._role:
            return "%s : %s" % (role_name, self.action)
        else:
            return "%s" % (self.action,)

def preprocess_data(self, ds):
    """
    tasks are especially complex arguments so need pre-processing.
    keep it short.
    """

    if not isinstance(ds, dict):
        raise AnsibleAssertionError('ds (%s) should be a dict but was a %s' % (ds, type(ds)))

    # the new, cleaned datastructure, which will have legacy items reduced to a standard structure suitable for the
    # attributes of the task class; copy any tagged data to preserve things like origin
    new_ds = AnsibleTagHelper.tag_copy(ds, {})

    # since this affects the task action parsing, we have to resolve in preprocess instead of in typical validator
    default_collection = AnsibleCollectionConfig.default_collection

    collections_list = ds.get('collections')
    if collections_list is None:
        # use the parent value if our ds doesn't define it
        collections_list = self.collections
    else:
        # Validate this untemplated field early on to guarantee we are dealing with a list.
        # This is also done in CollectionSearch._load_collections() but this runs before that call.
        collections_list = self.get_validated_value('collections', self.fattributes.get('collections'), collections_list, None)

    if default_collection and not self._role:  # FIXME: and not a collections role
        if collections_list:
            if default_collection not in collections_list:
                collections_list.insert(0, default_collection)
        else:
            collections_list = [default_collection]

    if collections_list and 'ansible.builtin' not in collections_list and 'ansible.legacy' not in collections_list:
        collections_list.append('ansible.legacy')

    if collections_list:
        ds['collections'] = collections_list

    # use the args parsing class to determine the action, args,
    # and the delegate_to value from the various possible forms
    # supported as legacy
    args_parser = ModuleArgsParser(task_ds=ds, collection_list=collections_list)
    try:
        (action, args, delegate_to) = args_parser.parse()
    except AnsibleParserError as ex:
        # if the raises exception was created with obj=ds args, then it includes the detail
        # so we dont need to add it so we can just re raise.
        if ex.obj:
            raise
        # But if it wasn't, we can add the yaml object now to get more detail
        raise AnsibleParserError("Error parsing task arguments.", obj=ds) from ex

    if args_parser._resolved_action is not None:
        self._resolved_action = args_parser._resolved_action

    new_ds['action'] = action
    new_ds['args'] = args
    new_ds['delegate_to'] = delegate_to

    # we handle any 'vars' specified in the ds here, as we may
    # be adding things to them below (special handling for includes).
    # When that deprecated feature is removed, this can be too.
    if 'vars' in ds:
        # _load_vars is defined in Base, and is used to load a dictionary
        # or list of dictionaries in a standard way
        new_ds['vars'] = self._load_vars(None, ds.get('vars'))
    else:
        new_ds['vars'] = dict()

    for (k, v) in ds.items():
        if k in ('action', 'local_action', 'args', 'delegate_to') or k == action or k == 'shell':
            # we don't want to re-assign these values, which were determined by the ModuleArgsParser() above
            continue
        elif k.startswith('with_') and k.removeprefix("with_") in lookup_loader:
            # transform into loop property
            self._preprocess_with_loop(ds, new_ds, k, v)
        elif C.INVALID_TASK_ATTRIBUTE_FAILED or k in self.fattributes:
            new_ds[k] = v
        else:
            display.warning("Ignoring invalid attribute: %s" % k)

    return super(Task, self).preprocess_data(new_ds)

def get_vars(self):
    all_vars = dict()
    if self._parent:
        all_vars |= self._parent.get_vars()

    all_vars |= self.vars

    if 'tags' in all_vars:
        del all_vars['tags']
    if 'when' in all_vars:
        del all_vars['when']

    return all_vars

def get_include_params(self):
    all_vars = dict()
    if self._parent:
        all_vars |= self._parent.get_include_params()
    if self.action in C._ACTION_ALL_INCLUDES:
        all_vars |= self.vars
    return all_vars

def get_first_parent_include(self):
    from ansible.playbook.task_include import TaskInclude
    if self._parent:
        if isinstance(self._parent, TaskInclude):
            return self._parent
        return self._parent.get_first_parent_include()
    return None

def get_play(self):
    parent = self._parent
    while not isinstance(parent, Block):
        parent = parent._parent
    return parent._play

def preprocess_data(self, ds):
    ds = super(TaskInclude, self).preprocess_data(ds)

    diff = set(ds.keys()).difference(self.VALID_INCLUDE_KEYWORDS)
    for k in diff:
        # This check doesn't handle ``include`` as we have no idea at this point if it is static or not
        if ds[k] is not Sentinel and ds['action'] in C._ACTION_ALL_INCLUDE_ROLE_TASKS:
            if C.INVALID_TASK_ATTRIBUTE_FAILED:
                raise AnsibleParserError("'%s' is not a valid attribute for a %s" % (k, self.__class__.__name__), obj=ds)
            else:
                display.warning("Ignoring invalid attribute: %s" % k)

    return ds

def load(data, play, current_role_path=None, parent_role=None, variable_manager=None, loader=None, collection_list=None):

    if not (isinstance(data, str) or isinstance(data, dict)):
        raise AnsibleParserError("Invalid role definition.", obj=data)

    if isinstance(data, str) and ',' in data:
        raise AnsibleError("Invalid old style role requirement: %s" % data)

    ri = RoleInclude(play=play, role_basedir=current_role_path, variable_manager=variable_manager, loader=loader, collection_list=collection_list)
    return ri.load_data(data, variable_manager=variable_manager, loader=loader)

def repo_url_to_role_name(repo_url):
    # gets the role name out of a repo like
    # http://git.example.com/repos/repo.git" => "repo"

    if '://' not in repo_url and '@' not in repo_url:
        return repo_url
    trailing_path = repo_url.split('/')[-1]
    if trailing_path.endswith('.git'):
        trailing_path = trailing_path[:-4]
    if trailing_path.endswith('.tar.gz'):
        trailing_path = trailing_path[:-7]
    if ',' in trailing_path:
        trailing_path = trailing_path.split(',')[0]
    return trailing_path

def role_yaml_parse(role):

    if isinstance(role, str):
        name = None
        scm = None
        src = None
        version = None
        if ',' in role:
            if role.count(',') == 1:
                (src, version) = role.strip().split(',', 1)
            elif role.count(',') == 2:
                (src, version, name) = role.strip().split(',', 2)
            else:
                raise AnsibleError("Invalid role line (%s). Proper format is 'role_name[,version[,name]]'" % role)
        else:
            src = role

        if name is None:
            name = RoleRequirement.repo_url_to_role_name(src)
        if '+' in src:
            (scm, src) = src.split('+', 1)

        return dict(name=name, src=src, scm=scm, version=version)

    if 'role' in role:
        name = role['role']
        if ',' in name:
            raise AnsibleError("Invalid old style role requirement: %s" % name)
        else:
            del role['role']
            role['name'] = name
    else:
        role = role.copy()

        if 'src' in role:
            # New style: { src: 'galaxy.role,version,name', other_vars: "here" }
            if 'github.com' in role["src"] and 'http' in role["src"] and '+' not in role["src"] and not role["src"].endswith('.tar.gz'):
                role["src"] = "git+" + role["src"]

            if '+' in role["src"]:
                role["scm"], dummy, role["src"] = role["src"].partition('+')

            if 'name' not in role:
                role["name"] = RoleRequirement.repo_url_to_role_name(role["src"])

        if 'version' not in role:
            role['version'] = ''

        if 'scm' not in role:
            role['scm'] = None

    for key in list(role.keys()):
        if key not in VALID_SPEC_KEYS:
            role.pop(key)

    return role

def load(role_include, play, parent_role=None, from_files=None, from_include=False, validate=True, public=None, static=True):
    if from_files is None:
        from_files = {}
    try:
        # TODO: need to fix cycle detection in role load (maybe use an empty dict
        #  for the in-flight in role cache as a sentinel that we're already trying to load
        #  that role?)
        # see https://github.com/ansible/ansible/issues/61527
        r = Role(play=play, from_files=from_files, from_include=from_include, validate=validate, public=public, static=static)
        r._load_role_data(role_include, parent_role=parent_role)

        role_path = r.get_role_path()
        if role_path not in play.role_cache:
            play.role_cache[role_path] = []

        # Using the role path as a cache key is done to improve performance when a large number of roles
        # are in use in the play
        if r not in play.role_cache[role_path]:
            play.role_cache[role_path].append(r)

        return r

    except RecursionError as ex:
        raise AnsibleError("A recursion loop was detected with the roles specified. Make sure child roles do not have dependencies on parent roles",
                           obj=role_include._ds) from ex

def add_parent(self, parent_role):
    """ adds a role to the list of this roles parents """
    if not isinstance(parent_role, Role):
        raise AnsibleAssertionError()

    if parent_role not in self._parents:
        self._parents.append(parent_role)

def get_dep_chain(self):
    dep_chain = []
    for parent in self._parents:
        dep_chain.extend(parent.get_dep_chain())
        dep_chain.append(parent)
    return dep_chain

def get_default_vars(self, dep_chain=None):
    dep_chain = [] if dep_chain is None else dep_chain

    default_vars = dict()
    for dep in self.get_all_dependencies():
        default_vars = combine_vars(default_vars, dep.get_default_vars())
    if dep_chain:
        for parent in dep_chain:
            default_vars = combine_vars(default_vars, parent._default_vars)
    default_vars = combine_vars(default_vars, self._default_vars)
    return default_vars

def get_inherited_vars(self, dep_chain=None, only_exports=False):
    dep_chain = [] if dep_chain is None else dep_chain

    inherited_vars = dict()

    if dep_chain:
        for parent in dep_chain:
            if not only_exports:
                inherited_vars = combine_vars(inherited_vars, parent.vars)
            inherited_vars = combine_vars(inherited_vars, parent._role_vars)
    return inherited_vars

def get_role_params(self, dep_chain=None):
    dep_chain = [] if dep_chain is None else dep_chain

    params = {}
    if dep_chain:
        for parent in dep_chain:
            params = combine_vars(params, parent._role_params)
    params = combine_vars(params, self._role_params)
    return params

def get_vars(self, dep_chain=None, include_params=True, only_exports=False):
    dep_chain = [] if dep_chain is None else dep_chain

    all_vars = {}

    # get role_vars: from parent objects
    # TODO: is this right precedence for inherited role_vars?
    all_vars = self.get_inherited_vars(dep_chain, only_exports=only_exports)

    # get exported variables from meta/dependencies
    seen = []
    for dep in self.get_all_dependencies():
        # Avoid rerunning dupe deps since they can have vars from previous invocations and they accumulate in deps
        # TODO: re-examine dep loading to see if we are somehow improperly adding the same dep too many times
        if dep not in seen:
            # only take 'exportable' vars from deps
            all_vars = combine_vars(all_vars, dep.get_vars(include_params=False, only_exports=True))
            seen.append(dep)

    # role_vars come from vars/ in a role
    all_vars = combine_vars(all_vars, self._role_vars)

    if not only_exports:
        # include_params are 'inline variables' in role invocation. - {role: x, varname: value}
        if include_params:
            # TODO: add deprecation notice
            all_vars = combine_vars(all_vars, self.get_role_params(dep_chain=dep_chain))

        # these come from vars: keyword in role invocation. - {role: x, vars: {varname: value}}
        all_vars = combine_vars(all_vars, self.vars)

    return all_vars

def get_all_dependencies(self):
    """
    Returns a list of all deps, built recursively from all child dependencies,
    in the proper order in which they should be executed or evaluated.
    """
    if self._all_dependencies is None:

        self._all_dependencies = []
        for dep in self.get_direct_dependencies():
            for child_dep in dep.get_all_dependencies():
                self._all_dependencies.append(child_dep)
            self._all_dependencies.append(dep)

    return self._all_dependencies

def set_loader(self, loader):
    self._loader = loader
    for parent in self._parents:
        parent.set_loader(loader)
    for dep in self.get_direct_dependencies():
        dep.set_loader(loader)

def get_composite_name(collection, name, path, depth):
    resolved_collection = collection
    if '.' not in name:
        resource_name = name
    else:
        if collection == 'ansible.legacy' and name.startswith('ansible.builtin.'):
            resolved_collection = 'ansible.builtin'
        resource_name = '.'.join(name.split(f"{resolved_collection}.")[1:])

    # create FQCN
    composite = [resolved_collection]
    if depth:
        composite.extend(path.split(os.path.sep)[depth * -1:])
    composite.append(to_native(resource_name))
    return '.'.join(composite)

def list_collection_plugins(ptype: str, collections: dict[str, bytes], search_paths: list[str] | None = None) -> dict[str, tuple[bytes, object | None]]:
    # Kept for backwards compatibility.
    return {
        name: (info.path, info.plugin_obj)
        for name, info in _list_collection_plugins_with_info(ptype, collections).items()
    }

def list_plugins(ptype: str, collections: list[str] | None = None, search_paths: list[str] | None = None) -> dict[str, tuple[bytes, object | None]]:
    # Kept for backwards compatibility.
    return {
        name: (info.path, info.plugin_obj)
        for name, info in _list_plugins_with_info(ptype, collections, search_paths).items()
    }

def get_plugin_loader_namespace() -> types.SimpleNamespace:
    ns = types.SimpleNamespace()
    for name, obj in get_all_plugin_loaders():
        setattr(ns, name, obj)
    return ns

def add_dirs_to_loader(which_loader, paths):

    loader = getattr(sys.modules[__name__], '%s_loader' % which_loader)
    for path in paths:
        loader.add_directory(path, with_subdir=True)

def get_fqcr_and_name(resource, collection='ansible.builtin'):
    if '.' not in resource:
        name = resource
        fqcr = collection + '.' + resource
    else:
        name = resource.split('.')[-1]
        fqcr = resource

    return fqcr, name

def resolved_fqcn(self) -> str | None:
    if not self.resolved:
        return None

    if not self._resolved_fqcn:
        final_plugin = self.redirect_list[-1]
        if AnsibleCollectionRef.is_valid_fqcr(final_plugin) and final_plugin.startswith('ansible.legacy.'):
            final_plugin = final_plugin.split('ansible.legacy.')[-1]
        if self.plugin_resolved_collection and not AnsibleCollectionRef.is_valid_fqcr(final_plugin):
            final_plugin = self.plugin_resolved_collection + '.' + final_plugin
        self._resolved_fqcn = final_plugin

    return self._resolved_fqcn

def find_plugin(self, name, mod_type='', ignore_deprecated=False, check_aliases=False, collection_list=None):
    """ Find a plugin named name """
    result = self.find_plugin_with_context(name, mod_type, ignore_deprecated, check_aliases, collection_list)
    if result.resolved and result.plugin_resolved_path:
        return result.plugin_resolved_path

    return None

def has_plugin(self, name: str, collection_list: list[str] | None = None) -> bool:
    """ Checks if a plugin named name exists """

    try:
        return self.find_plugin(name, collection_list=collection_list) is not None
    except Exception as ex:
        if isinstance(ex, AnsibleError):
            raise
        # log and continue, likely an innocuous type/package loading failure in collections import
        display.debug('has_plugin error: {0}'.format(to_text(ex)))

        return False

def get(self, name, *args, **kwargs):
    ctx = self.get_with_context(name, *args, **kwargs)
    is_core_plugin = ctx.plugin_load_context.plugin_resolved_collection == 'ansible.builtin'
    if self.class_name == 'StrategyModule' and not is_core_plugin:
        display.deprecated(  # pylint: disable=ansible-deprecated-no-version
            msg='Use of strategy plugins not included in ansible.builtin are deprecated and do not carry '
                'any backwards compatibility guarantees. No alternative for third party strategy plugins '
                'is currently planned.',
        )

    return ctx.object

def get_with_context(self, name, *args, **kwargs) -> get_with_context_result:
    """ instantiates a plugin of the given name using arguments """

    if not name:
        raise ValueError('A non-empty plugin name is required.')

    found_in_cache = True
    class_only = kwargs.pop('class_only', False)
    collection_list = kwargs.pop('collection_list', None)
    if name in self.aliases:
        name = self.aliases[name]

    if (cached_result := (self._plugin_instance_cache or {}).get(name)) and cached_result[1].resolved:
        # Resolving the FQCN is slow, even if we've passed in the resolved FQCN.
        # Short-circuit here if we've previously resolved this name.
        # This will need to be restricted if non-vars plugins start using the cache, since
        # some non-fqcn plugin need to be resolved again with the collections list.
        return get_with_context_result(*cached_result)

    plugin_load_context = self.find_plugin_with_context(name, collection_list=collection_list)
    if not plugin_load_context.resolved or not plugin_load_context.plugin_resolved_path:
        # FIXME: this is probably an error (eg removed plugin)
        return get_with_context_result(None, plugin_load_context)

    fq_name = plugin_load_context.resolved_fqcn
    resolved_type_name = plugin_load_context.plugin_resolved_name
    path = plugin_load_context.plugin_resolved_path
    if (cached_result := (self._plugin_instance_cache or {}).get(fq_name)) and cached_result[1].resolved:
        # This is unused by vars plugins, but it's here in case the instance cache expands to other plugin types.
        # We get here if we've seen this plugin before, but it wasn't called with the resolved FQCN.
        return get_with_context_result(*cached_result)
    redirected_names = plugin_load_context.redirect_list or []

    if path not in self._module_cache:
        self._module_cache[path] = self._load_module_source(python_module_name=plugin_load_context._python_module_name, path=path)
        found_in_cache = False

    self._load_config_defs(resolved_type_name, self._module_cache[path], path)

    obj = getattr(self._module_cache[path], self.class_name)

    if self.base_class:
        # The import path is hardcoded and should be the right place,
        # so we are not expecting an ImportError.
        module = __import__(self.package, fromlist=[self.base_class])
        # Check whether this obj has the required base class.
        try:
            plugin_class = getattr(module, self.base_class)
        except AttributeError:
            return get_with_context_result(None, plugin_load_context)
        if not issubclass(obj, plugin_class):
            display.warning(f"Ignoring {self.type} plugin {resolved_type_name!r} due to missing base class {self.base_class!r}.")
            return get_with_context_result(None, plugin_load_context)

    # FIXME: update this to use the load context
    self._display_plugin_load(self.class_name, resolved_type_name, self._searched_paths, path, found_in_cache=found_in_cache, class_only=class_only)

    if not class_only:
        try:
            # A plugin may need to use its _load_name in __init__ (for example, to set
            # or get options from config), so update the object before using the constructor
            instance = object.__new__(obj)
            self._update_object(obj=instance, name=resolved_type_name, path=path, redirected_names=redirected_names, resolved=fq_name)
            obj.__init__(instance, *args, **kwargs)  # pylint: disable=unnecessary-dunder-call
            obj = instance
        except TypeError as e:
            if "abstract" in e.args[0]:
                # Abstract Base Class or incomplete plugin, don't load
                display.v('Returning not found on "%s" as it has unimplemented abstract methods; %s' % (resolved_type_name, to_native(e)))
                return get_with_context_result(None, plugin_load_context)
            raise

    self._update_object(obj=obj, name=resolved_type_name, path=path, redirected_names=redirected_names, resolved=fq_name)
    if self._plugin_instance_cache is not None and getattr(obj, 'is_stateless', False):
        self._plugin_instance_cache[fq_name] = (obj, plugin_load_context)
    elif self._plugin_instance_cache is not None:
        # The cache doubles as the load order, so record the FQCN even if the plugin hasn't set is_stateless = True
        self._plugin_instance_cache[fq_name] = (None, PluginLoadContext(self.type, self.package))
    return get_with_context_result(obj, plugin_load_context)

def get(self, name: str, *args, **kwargs) -> BaseCacheModule:
    plugin = super().get(name, *args, **kwargs)

    if not plugin:
        raise AnsibleError(f'Unable to load the cache plugin {name!r}.')

    if plugin._persistent:
        return _cache.PluginInterposer(plugin)

    return plugin

def get_contained_plugins(self, collection, plugin_path, name):

    plugins = []

    full_name = '.'.join(['ansible_collections', collection, 'plugins', self.type, name])
    try:
        # use 'parent' loader class to find files, but cannot return this as it can contain multiple plugins per file
        if plugin_path not in self._module_cache:
            self._module_cache[plugin_path] = self._load_module_source(python_module_name=full_name, path=plugin_path)
        module = self._module_cache[plugin_path]
        obj = getattr(module, self.class_name)
    except Exception as e:
        raise KeyError('Failed to load %s for %s: %s' % (plugin_path, collection, to_native(e)))

    plugin_impl = obj()
    if plugin_impl is None:
        raise KeyError('Could not find %s.%s' % (collection, name))

    try:
        method_map = getattr(plugin_impl, self.method_map_name)
        plugin_map = method_map().items()
    except Exception as e:
        display.warning("Ignoring %s plugins in '%s' as it seems to be invalid: %r" % (self.type, to_text(plugin_path), e))
        return plugins

    for func_name, func in plugin_map:
        fq_name = '.'.join((collection, func_name))
        full = '.'.join((full_name, func_name))
        plugin = self._plugin_wrapper_type(func)
        if plugin in plugins:
            continue
        self._update_object(obj=plugin, name=full, path=plugin_path, resolved=fq_name)
        plugins.append(plugin)

    return plugins

def all(self, *args, **kwargs):
    kwargs.pop('_dedupe', None)
    path_only = kwargs.pop('path_only', False)
    class_only = kwargs.pop('class_only', False)  # basically ignored for test/filters since they are functions

    # Having both path_only and class_only is a coding bug
    if path_only and class_only:
        raise AnsibleError('Do not set both path_only and class_only when calling PluginLoader.all()')

    self._ensure_non_collection_wrappers(*args, **kwargs)

    plugins = [plugin for plugin in self._cached_non_collection_wrappers.values() if not isinstance(plugin, _DeferredPluginLoadFailure)]

    if path_only:
        yield from (w._original_path for w in plugins)
    else:
        yield from (w for w in plugins)

def get_plugin_class(obj):
    if isinstance(obj, str):
        return obj.lower().replace('module', '')
    else:
        return obj.__class__.__name__.lower().replace('module', '')

def matches_name(self, possible_names):
    possible_fqcns = set()
    for name in possible_names:
        if '.' not in name:
            possible_fqcns.add(f"ansible.builtin.{name}")
        elif name.startswith("ansible.legacy."):
            possible_fqcns.add(name.removeprefix("ansible.legacy."))
        possible_fqcns.add(name)
    return bool(possible_fqcns.intersection(set(self.ansible_aliases)))

def get_option_and_origin(self, option, hostvars=None):
    if option not in self._options:
        try:
            # some plugins don't use set_option(s) and cannot use direct settings, so this populates the local copy for them
            self._options[option], self._origins[option] = C.config.get_config_value_and_origin(option, plugin_type=self.plugin_type,
                                                                                                plugin_name=self._load_name, variables=hostvars)
        except AnsibleError as e:
            # callers expect key error on missing
            raise KeyError() from e

    return self._options[option], self._origins[option]

def get_option(self, option, hostvars=None):
    if option not in self._options:
        # let it populate _options
        self.get_option_and_origin(option, hostvars=hostvars)
    return self._options[option]

def get_options(self, hostvars=None):
    options = {}
    for option in self.option_definitions.keys():
        options[option] = self.get_option(option, hostvars=hostvars)
    return options

def run(self, tmp=None, task_vars=None):

    result = super(ActionModule, self).run(tmp, task_vars)
    del tmp  # tmp no longer has any effect

    args = self._task.args
    raw = args.pop('_raw_params', {})
    if isinstance(raw, Mapping):
        # TODO: create 'conflict' detection in base class to deal with repeats and aliases and warn user
        args = combine_vars(raw, args)
    else:
        raise AnsibleActionFail('Invalid raw parameters passed, requires a dictionary/mapping got a  %s' % type(raw))

    # Parse out any hostname:port patterns
    new_name = args.get('name', args.get('hostname', args.get('host', None)))
    if new_name is None:
        raise AnsibleActionFail('name, host or hostname needs to be provided')

    display.vv("creating host via 'add_host': hostname=%s" % new_name)

    try:
        name, port = parse_address(new_name, allow_ranges=False)
    except Exception:
        # not a parsable hostname, but might still be usable
        name = new_name
        port = None

    if port:
        args['ansible_ssh_port'] = port

    groups = args.get('groupname', args.get('groups', args.get('group', '')))
    # add it to the group if that was specified
    new_groups = []
    if groups:
        if isinstance(groups, list):
            group_list = groups
        elif isinstance(groups, str):
            group_list = groups.split(",")
        else:
            raise AnsibleActionFail("Groups must be specified as a list.", obj=groups)

        for group_name in group_list:
            if group_name not in new_groups:
                new_groups.append(group_name.strip())

    # Add any variables to the new_host
    host_vars = dict()
    special_args = frozenset(('name', 'hostname', 'groupname', 'groups'))
    for k in args.keys():
        if k not in special_args:
            host_vars[k] = args[k]

    result['changed'] = False
    result['add_host'] = dict(host_name=name, groups=new_groups, host_vars=host_vars)
    return result

def str_or_list_of_str(value: t.Any) -> str | list[str]:
    if isinstance(value, str):
        return value

    if not isinstance(value, list) or any(not isinstance(item, str) for item in value):
        raise TypeError("a string or list of strings is required")

    return value

def finalize_task_arg(cls, name: str, value: t.Any, templar: TemplateEngine, context: t.Any) -> t.Any:
    if name != 'that':
        # `that` is the only key requiring special handling; delegate to base handling otherwise
        return super().finalize_task_arg(name, value, templar, context)

    if not isinstance(value, str):
        # if `that` is not a string, we don't need to attempt to resolve it as a template before validation (which will also listify it)
        return value

    # if `that` is entirely a string template, we only want to resolve to the container and avoid templating the container contents
    if _jinja_bits.is_possibly_all_template(value):
        try:
            templated_that = templar.resolve_to_container(value)
        except AnsibleTemplateError:
            pass
        else:
            if isinstance(templated_that, list):  # only use `templated_that` if it is a list
                return templated_that

    return value

def run(self, tmp=None, task_vars=None):
    validation_result, new_module_args = self.validate_argument_spec(
        argument_spec=dict(
            msg=dict(type='raw', default='Hello world!'),
            var=dict(type=_check_type_str_no_conversion),
            verbosity=dict(type='int', default=0),
        ),
        mutually_exclusive=(
            ('msg', 'var'),
        ),
    )

    result = super(ActionModule, self).run(tmp, task_vars)
    del tmp  # tmp no longer has any effect

    # get task verbosity
    verbosity = new_module_args['verbosity']

    replacing_behavior = ReplacingMarkerBehavior()

    var_behavior = RoutingMarkerBehavior({
        UndefinedMarker: replacing_behavior,
        TruncationMarker: replacing_behavior,
    })

    if verbosity <= self._display.verbosity:
        if raw_var_arg := new_module_args['var']:
            # If var name is same as result, try to template it
            try:
                results = self._templar._engine.extend(marker_behavior=var_behavior).evaluate_expression(raw_var_arg)
            except AnsibleValueOmittedError as ex:
                results = repr(Omit)
                display.warning("The result of the `var` expression could not be omitted; a placeholder was used instead.", obj=ex.obj)
            except Exception as ex:
                raise AnsibleError('Error while resolving `var` expression.', obj=raw_var_arg) from ex

            result[raw_var_arg] = results
        else:
            result['msg'] = new_module_args['msg']

        # force flag to make debug output module always verbose
        result['_ansible_verbose_always'] = True

        # propagate any warnings in the task result unless we're skipping the task
        replacing_behavior.emit_warnings()

    else:
        result['skipped_reason'] = "Verbosity threshold not met."
        result['skipped'] = True

    result['failed'] = False

    return result

def run(self, tmp=None, task_vars=None):
    self._supports_check_mode = True
    self._supports_async = True

    result = super(ActionModule, self).run(tmp, task_vars)
    del tmp  # tmp no longer has any effect

    # Carry-over concept from the package action plugin
    if 'use' in self._task.args and 'use_backend' in self._task.args:
        raise AnsibleActionFail("parameters are mutually exclusive: ('use', 'use_backend')")

    module = self._task.args.get('use', self._task.args.get('use_backend', 'auto'))

    if module in {'yum', 'auto'}:
        try:
            # if we delegate, we should use delegated host's facts
            expr = "hostvars[delegate_to].ansible_facts.pkg_mgr" if self._task.delegate_to else "ansible_facts.pkg_mgr"
            module = self._templar.resolve_variable_expression(expr, local_variables=dict(delegate_to=self._task.delegate_to))
        except Exception:
            pass  # could not get it from template!

    if module not in VALID_BACKENDS:
        facts = self._execute_module(
            module_name="ansible.legacy.setup", module_args=dict(filter="ansible_pkg_mgr", gather_subset="!all"),
            task_vars=task_vars)

        if facts.get("failed", False):
            raise AnsibleActionFail(
                f"Failed to fetch ansible_pkg_mgr to determine the dnf action backend: {facts.get('msg')}",
                result=facts,
            )

        display.debug("Facts %s" % facts)
        module = facts.get("ansible_facts", {}).get("ansible_pkg_mgr", "auto")
        if (not self._task.delegate_to or self._task.delegate_facts) and module != 'auto':
            result['ansible_facts'] = {'pkg_mgr': module}

    if module not in VALID_BACKENDS:
        result.update(
            {
                'failed': True,
                'msg': ("Could not detect which major revision of dnf is in use, which is required to determine module backend.",
                        "You should manually specify use_backend to tell the module whether to use the dnf4 or dnf5 backend})"),
            }
        )

    else:
        if module in {"yum4", "dnf4"}:
            module = "dnf"

        # eliminate collisions with collections search while still allowing local override
        module = 'ansible.legacy.' + module

        if not self._shared_loader_obj.module_loader.has_plugin(module):
            result.update({'failed': True, 'msg': "Could not find a dnf module backend for %s." % module})
        else:
            new_module_args = self._task.args.copy()
            if 'use_backend' in new_module_args:
                del new_module_args['use_backend']
            if 'use' in new_module_args:
                del new_module_args['use']

            display.vvvv("Running %s as the backend for the dnf action plugin" % module)
            result.update(self._execute_module(
                module_name=module, module_args=new_module_args, task_vars=task_vars, wrap_async=self._task.async_val))

    return result

def run(self, tmp=None, task_vars=None):
    """ Load yml files recursively from a directory.
    """
    del tmp  # tmp no longer has any effect

    if task_vars is None:
        task_vars = dict()

    self.show_content = True
    self.included_files = []

    # Validate arguments
    dirs = 0
    files = 0
    for arg in self._task.args:
        if arg in self.VALID_DIR_ARGUMENTS:
            dirs += 1
        elif arg in self.VALID_FILE_ARGUMENTS:
            files += 1
        elif arg in self.VALID_ALL:
            pass
        else:
            raise AnsibleError(f'{arg} is not a valid option in include_vars', obj=arg)

    if dirs and files:
        raise AnsibleError("You are mixing file only and dir only arguments, these are incompatible", obj=self._task.args)

    # set internal vars from args
    self._set_args()

    results = dict()
    failed = False
    if self.source_dir:
        self._set_dir_defaults()
        self._set_root_dir()
        if not path.exists(self.source_dir):
            failed = True
            err_msg = f"{self.source_dir} directory does not exist"
        elif not path.isdir(self.source_dir):
            failed = True
            err_msg = f"{self.source_dir} is not a directory"
        else:
            for root_dir, filenames in self._traverse_dir_depth():
                failed, err_msg, updated_results = self._load_files_in_dir(root_dir, filenames)
                if failed:
                    break
                results.update(updated_results)
    else:
        try:
            self.source_file = self._find_needle('vars', self.source_file)
            failed, err_msg, updated_results = (
                self._load_files(self.source_file)
            )
            if not failed:
                results.update(updated_results)

        except AnsibleError as e:
            failed = True
            err_msg = to_native(e)

    if self.return_results_as_name:
        scope = dict()
        scope[self.return_results_as_name] = results
        results = scope

    result = super(ActionModule, self).run(task_vars=task_vars)

    if failed:
        result['failed'] = failed
        result['message'] = err_msg
    elif self.hash_behaviour is not None and self.hash_behaviour != C.DEFAULT_HASH_BEHAVIOUR:
        merge_hashes = self.hash_behaviour == 'merge'
        existing_variables = {k: v for k, v in task_vars.items() if k in results}
        results = combine_vars(existing_variables, results, merge=merge_hashes)

    result['ansible_included_var_files'] = self.included_files
    result['ansible_facts'] = results
    result['_ansible_no_log'] = not self.show_content

    return result

def run(self, tmp=None, task_vars=None):
    """ handler for package operations """

    self._supports_check_mode = True
    self._supports_async = True

    super(ActionModule, self).run(tmp, task_vars)

    module = self._task.args.get('use', 'auto')

    try:
        if module == 'auto':

            if self._task.delegate_to:
                hosts_vars = task_vars['hostvars'][self._task.delegate_to]
                tvars = combine_vars(self._task.vars, task_vars.get('delegated_vars', {}))
            else:
                hosts_vars = task_vars
                tvars = task_vars

            # use config
            module = tvars.get('ansible_package_use', None)

            if not module:
                # no use, no config, get from facts
                if hosts_vars.get('ansible_facts', {}).get('pkg_mgr', False):
                    facts = hosts_vars
                    pmgr = 'pkg_mgr'
                else:
                    # we had no facts, so generate them
                    # very expensive step, we actually run fact gathering because we don't have facts for this host.
                    facts = self._execute_module(
                        module_name='ansible.legacy.setup',
                        module_args=dict(filter='ansible_pkg_mgr', gather_subset='!all'),
                        task_vars=task_vars,
                    )
                    if facts.get("failed", False):
                        raise AnsibleActionFail(
                            f"Failed to fetch ansible_pkg_mgr to determine the package action backend: {facts.get('msg')}",
                            result=facts,
                        )
                    pmgr = 'ansible_pkg_mgr'

                try:
                    # actually get from facts
                    module = facts['ansible_facts'][pmgr]
                except KeyError:
                    raise AnsibleActionFail('Could not detect a package manager. Try using the "use" option.')

        if module and module != 'auto':
            if not self._shared_loader_obj.module_loader.has_plugin(module):
                raise AnsibleActionFail('Could not find a matching action for the "%s" package manager.' % module)
            else:
                # run the 'package' module
                new_module_args = self._task.args.copy()
                if 'use' in new_module_args:
                    del new_module_args['use']

                # get defaults for specific module
                context = self._shared_loader_obj.module_loader.find_plugin_with_context(module, collection_list=self._task.collections)
                new_module_args = _apply_action_arg_defaults(context.resolved_fqcn, self._task, new_module_args, self._templar)

                if module in self.BUILTIN_PKG_MGR_MODULES:
                    # prefix with ansible.legacy to eliminate external collisions while still allowing library/ override
                    module = 'ansible.legacy.' + module

                display.vvvv("Running %s" % module)
                return self._execute_module(module_name=module, module_args=new_module_args, task_vars=task_vars, wrap_async=self._task.async_val)
        else:
            raise AnsibleActionFail('Could not detect which package manager to use. Try gathering facts or setting the "use" option.')
    finally:
        pass  # avoid de-dent all on refactor

def get_shutdown_command_args(self, distribution):
    reboot_command = self._task.args.get('reboot_command')
    if reboot_command is not None:
        try:
            reboot_command = check_type_str(reboot_command, allow_conversion=False)
        except TypeError as e:
            raise AnsibleError("Invalid value given for 'reboot_command': %s." % to_native(e))

        # No args were provided
        try:
            return reboot_command.split(' ', 1)[1]
        except IndexError:
            return ''
    else:
        args = self._get_value_from_facts('SHUTDOWN_COMMAND_ARGS', distribution, 'DEFAULT_SHUTDOWN_COMMAND_ARGS')

        # Convert seconds to minutes. If less than 60, set it to 0.
        delay_min = self.pre_reboot_delay // 60
        reboot_message = self._task.args.get('msg', self.DEFAULT_REBOOT_MESSAGE)
        return args.format(delay_sec=self.pre_reboot_delay, delay_min=delay_min, message=reboot_message)

def get_shutdown_command(self, task_vars, distribution):
    reboot_command = self._task.args.get('reboot_command')
    if reboot_command is not None:
        try:
            reboot_command = check_type_str(reboot_command, allow_conversion=False)
        except TypeError as e:
            raise AnsibleError("Invalid value given for 'reboot_command': %s." % to_native(e))
        shutdown_bin = reboot_command.split(' ', 1)[0]
    else:
        shutdown_bin = self._get_value_from_facts('SHUTDOWN_COMMANDS', distribution, 'DEFAULT_SHUTDOWN_COMMAND')

    if shutdown_bin[0] == '/':
        return shutdown_bin
    else:
        default_search_paths = ['/sbin', '/bin', '/usr/sbin', '/usr/bin', '/usr/local/sbin']
        search_paths = self._task.args.get('search_paths', default_search_paths)

        try:
            # Convert bare strings to a list
            search_paths = check_type_list(search_paths)
        except TypeError:
            err_msg = "'search_paths' must be a string or flat list of strings, got {0}"
            raise AnsibleError(err_msg.format(search_paths))

        display.debug('{action}: running find module looking in {paths} to get path for "{command}"'.format(
            action=self._task.action,
            command=shutdown_bin,
            paths=search_paths))

        find_result = self._execute_module(
            task_vars=task_vars,
            # prevent collection search by calling with ansible.legacy (still allows library/ override of find)
            module_name='ansible.legacy.find',
            module_args={
                'paths': search_paths,
                'patterns': [shutdown_bin],
                'file_type': 'any'
            }
        )

        full_path = [x['path'] for x in find_result['files']]
        if not full_path:
            raise AnsibleError('Unable to find command "{0}" in search paths: {1}'.format(shutdown_bin, search_paths))
        return full_path[0]

def deprecated_args(self):
    for arg, version in self.DEPRECATED_ARGS.items():
        if self._task.args.get(arg) is not None:
            display.warning("Since Ansible {version}, {arg} is no longer a valid option for {action}".format(
                version=version,
                arg=arg,
                action=self._task.action))

def get_system_boot_time(self, distribution):
    boot_time_command = self._get_value_from_facts('BOOT_TIME_COMMANDS', distribution, 'DEFAULT_BOOT_TIME_COMMAND')
    if self._task.args.get('boot_time_command'):
        boot_time_command = self._task.args.get('boot_time_command')

        try:
            check_type_str(boot_time_command, allow_conversion=False)
        except TypeError as e:
            raise AnsibleError("Invalid value given for 'boot_time_command': %s." % to_native(e))

    display.debug("{action}: getting boot time with command: '{command}'".format(action=self._task.action, command=boot_time_command))
    command_result = self._low_level_execute_command(boot_time_command, sudoable=self.DEFAULT_SUDOABLE)

    if command_result['rc'] != 0:
        stdout = command_result['stdout']
        stderr = command_result['stderr']
        raise AnsibleError("{action}: failed to get host boot time info, rc: {rc}, stdout: {out}, stderr: {err}".format(
                           action=self._task.action,
                           rc=command_result['rc'],
                           out=to_native(stdout),
                           err=to_native(stderr)))
    display.debug("{action}: last boot time: {boot}".format(action=self._task.action, boot=command_result['stdout'].strip()))
    return command_result['stdout'].strip()

def check_boot_time(self, distribution, previous_boot_time):
    display.vvv("{action}: attempting to get system boot time".format(action=self._task.action))
    connect_timeout = self._task.args.get('connect_timeout', self._task.args.get('connect_timeout_sec', self.DEFAULT_CONNECT_TIMEOUT))

    # override connection timeout from defaults to the custom value
    if connect_timeout:
        try:
            display.debug("{action}: setting connect_timeout to {value}".format(action=self._task.action, value=connect_timeout))
            self._connection.set_option("connection_timeout", connect_timeout)
        except AnsibleError:
            try:
                self._connection.set_option("timeout", connect_timeout)
            except (AnsibleError, AttributeError):
                display.warning("Connection plugin does not allow the connection timeout to be overridden")

        self._connection.reset()

    # try and get boot time
    try:
        current_boot_time = self.get_system_boot_time(distribution)
    except Exception as e:
        raise e

    # FreeBSD returns an empty string immediately before reboot so adding a length
    # check to prevent prematurely assuming system has rebooted
    if len(current_boot_time) == 0 or current_boot_time == previous_boot_time:
        raise ValueError("boot time has not changed")

def run_test_command(self, distribution, **kwargs):
    test_command = self._task.args.get('test_command', self._get_value_from_facts('TEST_COMMANDS', distribution, 'DEFAULT_TEST_COMMAND'))
    display.vvv("{action}: attempting post-reboot test command".format(action=self._task.action))
    display.debug("{action}: attempting post-reboot test command '{command}'".format(action=self._task.action, command=test_command))
    try:
        command_result = self._low_level_execute_command(test_command, sudoable=self.DEFAULT_SUDOABLE)
    except Exception:
        # may need to reset the connection in case another reboot occurred
        # which has invalidated our connection
        try:
            self._connection.reset()
        except AttributeError:
            pass
        raise

    if command_result['rc'] != 0:
        msg = 'Test command failed: {err} {out}'.format(
            err=to_native(command_result['stderr']),
            out=to_native(command_result['stdout']))
        raise RuntimeError(msg)

    display.vvv("{action}: system successfully rebooted".format(action=self._task.action))

def do_until_success_or_timeout(self, action, reboot_timeout, action_desc, distribution, action_kwargs=None):
    max_end_time = datetime.now(timezone.utc) + timedelta(seconds=reboot_timeout)
    if action_kwargs is None:
        action_kwargs = {}

    fail_count = 0
    max_fail_sleep = 12
    last_error_msg = ''

    while datetime.now(timezone.utc) < max_end_time:
        try:
            action(distribution=distribution, **action_kwargs)
            if action_desc:
                display.debug('{action}: {desc} success'.format(action=self._task.action, desc=action_desc))
            return
        except Exception as e:
            if isinstance(e, AnsibleConnectionFailure):
                try:
                    self._connection.reset()
                except AnsibleConnectionFailure:
                    pass
            # Use exponential backoff with a max timeout, plus a little bit of randomness
            random_int = secrets.randbelow(1000) / 1000
            fail_sleep = 2 ** fail_count + random_int
            if fail_sleep > max_fail_sleep:

                fail_sleep = max_fail_sleep + random_int
            if action_desc:
                try:
                    error = to_text(e).splitlines()[-1]
                except IndexError as e:
                    error = to_text(e)
                last_error_msg = f"{self._task.action}: {action_desc} fail '{error}'"
                msg = f"{last_error_msg}, retrying in {fail_sleep:.4f} seconds..."

                display.debug(msg)
                display.vvv(msg)
            fail_count += 1
            time.sleep(fail_sleep)

    if last_error_msg:
        msg = f"Last error message before the timeout exception - {last_error_msg}"
        display.debug(msg)
        display.vvv(msg)
    raise TimedOutException('Timed out waiting for {desc} (timeout={timeout})'.format(desc=action_desc, timeout=reboot_timeout))

def validate_reboot(self, distribution, original_connection_timeout=None, action_kwargs=None):
    display.vvv('{action}: validating reboot'.format(action=self._task.action))
    result = {}

    try:
        # keep on checking system boot_time with short connection responses
        reboot_timeout = int(self._task.args.get('reboot_timeout', self._task.args.get('reboot_timeout_sec', self.DEFAULT_REBOOT_TIMEOUT)))

        self.do_until_success_or_timeout(
            action=self.check_boot_time,
            action_desc="last boot time check",
            reboot_timeout=reboot_timeout,
            distribution=distribution,
            action_kwargs=action_kwargs)

        # Get the connect_timeout set on the connection to compare to the original
        try:
            connect_timeout = self._connection.get_option('connection_timeout')
        except KeyError:
            try:
                connect_timeout = self._connection.get_option('timeout')
            except KeyError:
                pass
        else:
            if original_connection_timeout != connect_timeout:
                try:
                    display.debug("{action}: setting connect_timeout/timeout back to original value of {value}".format(action=self._task.action,
                                                                                                                       value=original_connection_timeout))
                    try:
                        self._connection.set_option("connection_timeout", original_connection_timeout)
                    except AnsibleError:
                        try:
                            self._connection.set_option("timeout", original_connection_timeout)
                        except AnsibleError:
                            raise
                    # reset the connection to clear the custom connection timeout
                    self._connection.reset()
                except (AnsibleError, AttributeError) as e:
                    display.debug("{action}: failed to reset connection_timeout back to default: {error}".format(action=self._task.action,
                                                                                                                 error=to_text(e)))

        # finally run test command to ensure everything is working
        # FUTURE: add a stability check (system must remain up for N seconds) to deal with self-multi-reboot updates
        self.do_until_success_or_timeout(
            action=self.run_test_command,
            action_desc="post-reboot test command",
            reboot_timeout=reboot_timeout,
            distribution=distribution,
            action_kwargs=action_kwargs)

        result['rebooted'] = True
        result['changed'] = True

    except TimedOutException as toex:
        result['failed'] = True
        result['rebooted'] = True
        result['msg'] = to_text(toex)
        return result

    return result

def run(self, tmp=None, task_vars=None):
    self._supports_check_mode = True

    # If running with local connection, fail so we don't reboot ourselves
    if self._connection.transport == 'local':
        msg = 'Running {0} with local connection would reboot the control node.'.format(self._task.action)
        return {'changed': False, 'elapsed': 0, 'rebooted': False, 'failed': True, 'msg': msg}

    if self._task.check_mode:
        return {'changed': True, 'elapsed': 0, 'rebooted': True}

    if task_vars is None:
        task_vars = {}

    self.deprecated_args()

    result = super(ActionModule, self).run(tmp, task_vars)

    if result.get('skipped', False) or result.get('failed', False):
        return result

    distribution = self.get_distribution(task_vars)

    # Get current boot time
    try:
        previous_boot_time = self.get_system_boot_time(distribution)
    except Exception as e:
        result['failed'] = True
        result['reboot'] = False
        result['msg'] = to_text(e)
        return result

    # Get the original connection_timeout option var so it can be reset after
    original_connection_timeout = None

    display.debug("{action}: saving original connect_timeout of {timeout}".format(action=self._task.action, timeout=original_connection_timeout))
    try:
        original_connection_timeout = self._connection.get_option('connection_timeout')
    except KeyError:
        try:
            original_connection_timeout = self._connection.get_option('timeout')
        except KeyError:
            display.debug("{action}: connect_timeout connection option has not been set".format(action=self._task.action))

    # Initiate reboot
    reboot_result = self.perform_reboot(task_vars, distribution)

    if reboot_result['failed']:
        result = reboot_result
        elapsed = datetime.now(timezone.utc) - reboot_result['start']
        result['elapsed'] = elapsed.seconds
        return result

    if self.post_reboot_delay != 0:
        display.debug("{action}: waiting an additional {delay} seconds".format(action=self._task.action, delay=self.post_reboot_delay))
        display.vvv("{action}: waiting an additional {delay} seconds".format(action=self._task.action, delay=self.post_reboot_delay))
        time.sleep(self.post_reboot_delay)

    # Make sure reboot was successful
    result = self.validate_reboot(distribution, original_connection_timeout, action_kwargs={'previous_boot_time': previous_boot_time})

    elapsed = datetime.now(timezone.utc) - reboot_result['start']
    result['elapsed'] = elapsed.seconds

    return result

def run(self, tmp=None, task_vars=None):
    """ handler for package operations """

    self._supports_check_mode = True
    self._supports_async = True

    super(ActionModule, self).run(tmp, task_vars)
    del tmp  # tmp no longer has any effect

    module = self._task.args.get('use', 'auto').lower()

    if module == 'auto':
        try:
            # if we delegate, we should use delegated host's facts
            expr = "hostvars[delegate_to].ansible_facts.service_mgr" if self._task.delegate_to else "ansible_facts.service_mgr"
            module = self._templar.resolve_variable_expression(expr, local_variables=dict(delegate_to=self._task.delegate_to))
        except Exception:
            pass  # could not get it from template!

    try:
        if module == 'auto':
            facts = self._execute_module(
                module_name='ansible.legacy.setup',
                module_args=dict(gather_subset='!all', filter='ansible_service_mgr'), task_vars=task_vars)
            self._display.debug("Facts %s" % facts)
            module = facts.get('ansible_facts', {}).get('ansible_service_mgr', 'auto')

        if not module or module == 'auto' or not self._shared_loader_obj.module_loader.has_plugin(module):
            module = 'ansible.legacy.service'

        if module != 'auto':
            # run the 'service' module
            new_module_args = self._task.args.copy()
            if 'use' in new_module_args:
                del new_module_args['use']

            if module in self.UNUSED_PARAMS:
                for unused in self.UNUSED_PARAMS[module]:
                    if unused in new_module_args:
                        del new_module_args[unused]
                        self._display.warning('Ignoring "%s" as it is not used in "%s"' % (unused, module))

            # get defaults for specific module
            context = self._shared_loader_obj.module_loader.find_plugin_with_context(module, collection_list=self._task.collections)
            new_module_args = _apply_action_arg_defaults(context.resolved_fqcn, self._task, new_module_args, self._templar)

            # collection prefix known internal modules to avoid collisions from collections search, while still allowing library/ overrides
            if module in self.BUILTIN_SVC_MGR_MODULES:
                module = 'ansible.legacy.' + module

            self._display.vvvv("Running %s" % module)
            return self._execute_module(module_name=module, module_args=new_module_args, task_vars=task_vars, wrap_async=self._task.async_val)
        else:
            raise AnsibleActionFail('Could not detect which service manager to use. Try gathering facts or setting the "use" option.')

    finally:
        if not self._task.async_val:
            self._remove_tmp_path(self._connection._shell.tmpdir)

def run(self, tmp=None, task_vars=None):
    if task_vars is None:
        task_vars = dict()

    result = super(ActionModule, self).run(tmp, task_vars)
    del tmp  # tmp no longer has any effect

    facts = {}
    cacheable = boolean(self._task.args.pop('cacheable', False))

    if self._task.args:
        for (k, v) in self._task.args.items():
            k = self._templar.template(k)  # a rare case where key templating is allowed; backward-compatibility for dynamic storage

            validate_variable_name(k)

            facts[k] = v
    else:
        raise AnsibleActionFail('No key/value pairs provided, at least one is required for this action to succeed')

    if facts:
        # just as _facts actions, we don't set changed=true as we are not modifying the actual host
        result['ansible_facts'] = facts
        result['_ansible_facts_cacheable'] = cacheable
    else:
        # this should not happen, but JIC we get here
        raise AnsibleActionFail('Unable to create any variables with provided arguments')

    return result

def run(self, tmp=None, task_vars=None):
    if task_vars is None:
        task_vars = dict()

    result = super(ActionModule, self).run(tmp, task_vars)
    del tmp  # tmp no longer has any effect

    stats = {'data': {}, 'per_host': False, 'aggregate': True}

    if self._task.args:
        data = self._task.args.get('data', {})

        if not isinstance(data, dict):
            data = self._templar.template(data)

        if not isinstance(data, dict):
            result['failed'] = True
            result['msg'] = "The 'data' option needs to be a dictionary/hash"
            return result

        # set boolean options, defaults are set above in stats init
        for opt in ['per_host', 'aggregate']:
            val = self._task.args.get(opt, None)
            if val is not None:
                if not isinstance(val, bool):
                    stats[opt] = boolean(self._templar.template(val), strict=False)
                else:
                    stats[opt] = val

        for (k, v) in data.items():
            k = self._templar.template(k)

            validate_variable_name(k)

            stats['data'][k] = self._templar.template(v)

    result['changed'] = False
    result['ansible_stats'] = stats

    return result

def do_until_success_or_timeout(self, what, timeout, connect_timeout, what_desc, sleep=1):
    max_end_time = datetime.now(timezone.utc) + timedelta(seconds=timeout)

    e = None
    while datetime.now(timezone.utc) < max_end_time:
        try:
            what(connect_timeout)
            if what_desc:
                display.debug("wait_for_connection: %s success" % what_desc)
            return
        except Exception as e:
            error = e  # PY3 compatibility to store exception for use outside of this block
            if what_desc:
                display.debug("wait_for_connection: %s fail (expected), retrying in %d seconds..." % (what_desc, sleep))
            time.sleep(sleep)

    raise TimedOutException("timed out waiting for %s: %s" % (what_desc, error))

def ping_module_test(connect_timeout):
    """ Test ping module, if available """
    display.vvv("wait_for_connection: attempting ping module test")
    # re-run interpreter discovery if we ran it in the first iteration
    if self._discovered_interpreter_key:
        task_vars['ansible_facts'].pop(self._discovered_interpreter_key, None)
    # call connection reset between runs if it's there
    try:
        self._connection.reset()
    except AttributeError:
        pass

    ping_result = self._execute_module(module_name='ansible.legacy.ping', module_args=dict(), task_vars=task_vars)

    # Test module output
    if ping_result['ping'] != 'pong':
        raise Exception('ping test failed')

def get_plugin_option(self, plugin, option, default=None):
    """Helper to get an option from a plugin without having to use
    the try/except dance everywhere to set a default
    """
    try:
        return plugin.get_option(option)
    except (AttributeError, KeyError):
        return default

def check_password_prompt(self, b_output: bytes) -> bool:
    """ checks if the expected password prompt exists in b_output """
    prompts = self.get_option('prompt_l10n') or self.SU_PROMPT_LOCALIZATIONS
    password_prompt_strings = "|".join(re.escape(p) for p in prompts)
    # Colon or unicode fullwidth colon
    prompt_pattern = rf"(?:{password_prompt_strings})\s*[:]"
    match = re.search(prompt_pattern, to_text(b_output), flags=re.IGNORECASE)

    if match:
        self.prompt = match.group(0)  # preserve the actual matched string so we can scrub the output

    return bool(match)

def build_become_command(self, cmd, shell):
    super(BecomeModule, self).build_become_command(cmd, shell)

    if not cmd:
        return cmd

    becomecmd = self.get_option('become_exe') or self.name

    flags = self.get_option('become_flags') or ''
    prompt = ''
    if self.get_option('become_pass'):
        self.prompt = '[sudo via ansible, key=%s] password:' % self._id
        if flags:  # this could be simplified, but kept as is for now for backwards string matching
            reflag = []
            for flag in shlex.split(flags):
                if flag in ('-n', '--non-interactive'):
                    continue
                elif not flag.startswith('--'):
                    # handle -XnxxX flags only
                    flag = re.sub(r'^(-\w*)n(\w*.*)', r'\1\2', flag)
                reflag.append(flag)
            flags = shlex.join(reflag)

        prompt = '-p "%s"' % (self.prompt)

    user = self.get_option('become_user') or ''
    if user:
        user = '-u %s' % (user)

    if chdir := self.get_option('sudo_chdir'):
        try:
            becomecmd = f'{shell.CD} {shlex.quote(chdir)} {shell._SHELL_AND} {becomecmd}'
        except AttributeError as ex:
            raise AnsibleError(f'The {shell._load_name!r} shell plugin does not support sudo chdir. It is missing the {ex.name!r} attribute.')

    return ' '.join([becomecmd, flags, prompt, user, self._build_success_command(cmd, shell)])

def get_option(self, option, hostvars=None, playcontext=None):
    """ Overrides the base get_option to provide a fallback to playcontext vars in case a 3rd party plugin did not
    implement the base become options required in Ansible. """
    # TODO: add deprecation warning for ValueError in devel that removes the playcontext fallback
    try:
        return super(BecomeBase, self).get_option(option, hostvars=hostvars)
    except KeyError:
        pc_fallback = ['become_user', 'become_pass', 'become_flags', 'become_exe']
        if option not in pc_fallback:
            raise

        return getattr(playcontext, option, None)

def expect_prompt(self) -> bool:
    """This function assists connection plugins in determining if they need to wait for
    a prompt. Both a prompt and a password are required.
    """
    return bool(self.prompt and self.get_option('become_pass'))

def strip_become_prompt(self, data: bytes) -> bytes:
    """
    Strips the first found configured become prompt from `data`, trailing whitespace and anything that precedes the prompt, then returns the result.
    If no prompt is expected, or the prompt is not `str` or `bytes`, `data` will be returned as-is.
    """
    if not self.prompt or not isinstance(self.prompt, (str, bytes)) or not self.expect_prompt():
        return data

    return self._strip_through_prefix(self.prompt, data)

def check_password_prompt(self, b_output):
    """ checks if the expected password prompt exists in b_output """
    if self.prompt:
        b_prompt = to_bytes(self.prompt).strip()
        return any(l.strip().startswith(b_prompt) for l in b_output.splitlines())
    return False

def check_incorrect_password(self, b_output):
    for errstring in self.fail:
        if self._check_password_error(b_output, errstring):
            return True
    return False

def check_missing_password(self, b_output):
    for errstring in self.missing:
        if self._check_password_error(b_output, errstring):
            return True
    return False

def get(self, key):
    """ This checks the in memory cache first as the fact was not expired at 'gather time'
    and it would be problematic if the key did expire after some long running tasks and
    user gets 'undefined' error in the same play """

    if key not in self._cache:

        if self.has_expired(key) or key == "":
            raise KeyError

        cachefile = self._get_cache_file_name(key)
        try:
            value = self._load(cachefile)
            self._cache[key] = value
        except ValueError as e:
            display.warning("error in '%s' cache plugin while trying to read %s : %s. "
                            "Most likely a corrupt file, so erasing and failing." % (self.plugin_name, cachefile, to_bytes(e)))
            self.delete(key)
            raise AnsibleError("The cache file %s was corrupt, or did not otherwise contain valid data. "
                               "It has been removed, so you can re-run your command now." % cachefile)
        except FileNotFoundError:
            raise KeyError
        except Exception as ex:
            raise AnsibleError(f"Error while accessing the cache file {cachefile!r}.") from ex

    return self._cache.get(key)

def has_expired(self, key):

    if self._timeout == 0:
        return False

    cachefile = self._get_cache_file_name(key)
    try:
        st = os.stat(cachefile)
    except FileNotFoundError:
        return False
    except OSError as ex:
        display.error_as_warning(f"Error in {self.plugin_name!r} cache plugin while trying to stat {cachefile!r}.", exception=ex)

        return False

    if time.time() - st.st_mtime <= self._timeout:
        return False

    if key in self._cache:
        del self._cache[key]
    return True

def contains(self, key):
    cachefile = self._get_cache_file_name(key)

    if key in self._cache:
        return True

    if self.has_expired(key):
        return False
    try:
        os.stat(cachefile)
        return True
    except FileNotFoundError:
        return False
    except OSError as ex:
        display.error_as_warning(f"Error in {self.plugin_name!r} cache plugin while trying to stat {cachefile!r}.", exception=ex)

def set_cache(self):
    for top_level_cache_key in self._cache.keys():
        self._plugin.set(top_level_cache_key, self._cache[top_level_cache_key])

    self._retrieved = copy.deepcopy(self._cache)

def get(self, key, default=None):
    if self._do_load_key(key):
        try:
            self._cache[key] = self._plugin.get(key)
        except KeyError:
            pass
        else:
            self._retrieved[key] = self._cache[key]

    return self._cache.get(key, default)

def pop(self, key, *args):
    if args:
        return self._cache.pop(key, args[0])

    return self._cache.pop(key)

def v2_runner_on_failed(self, result: CallbackTaskResult, ignore_errors: bool = False) -> None:
    host_label = self.host_label(result)

    if self._last_task_banner != result.task._uuid:
        self._print_task_banner(result.task)

    self._handle_warnings_and_exception(result)

    # FIXME: this method should not exist, delegate "suggested keys to display" to the plugin or something... As-is, the placement of this
    #          call obliterates `results`, which causes a task summary to be printed on loop failures, which we don't do anywhere else.
    self._clean_results(result.result, result.task.action)

    if result.task.loop and 'results' in result.result:
        self._process_items(result)
    else:
        if self._display.verbosity < 2 and self.get_option('show_task_path_on_failure'):
            self._print_task_path(result.task)
        msg = "fatal: [%s]: FAILED! => %s" % (host_label, self._dump_results(result.result))
        self._display.display(msg, color=C.COLOR_ERROR, stderr=self.get_option('display_failed_stderr'))

    if ignore_errors:
        self._display.display("...ignoring", color=C.COLOR_SKIP)

def v2_runner_on_ok(self, result: CallbackTaskResult) -> None:
    host_label = self.host_label(result)

    if isinstance(result.task, TaskInclude):
        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)
        return
    elif result.result.get('changed', False):
        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)

        msg = "changed: [%s]" % (host_label,)
        color = C.COLOR_CHANGED
    else:
        if not self.get_option('display_ok_hosts'):
            return

        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)

        msg = "ok: [%s]" % (host_label,)
        color = C.COLOR_OK

    self._handle_warnings_and_exception(result)

    if result.task.loop and 'results' in result.result:
        self._process_items(result)
    else:
        self._clean_results(result.result, result.task.action)

        if self._run_is_verbose(result):
            msg += " => %s" % (self._dump_results(result.result),)
        self._display.display(msg, color=color)

def v2_runner_on_skipped(self, result: CallbackTaskResult) -> None:
    if self.get_option('display_skipped_hosts'):

        self._clean_results(result.result, result.task.action)

        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)

        self._handle_warnings_and_exception(result)

        if result.task.loop is not None and 'results' in result.result:
            self._process_items(result)

        msg = "skipping: [%s]" % result.host.get_name()
        if self._run_is_verbose(result):
            msg += " => %s" % self._dump_results(result.result)
        self._display.display(msg, color=C.COLOR_SKIP)

def v2_runner_on_unreachable(self, result: CallbackTaskResult) -> None:
    if self._last_task_banner != result.task._uuid:
        self._print_task_banner(result.task)

    self._handle_warnings_and_exception(result)

    host_label = self.host_label(result)
    msg = "fatal: [%s]: UNREACHABLE! => %s" % (host_label, self._dump_results(result.result))
    self._display.display(msg, color=C.COLOR_UNREACHABLE, stderr=self.get_option('display_failed_stderr'))

    if result.task.ignore_unreachable:
        self._display.display("...ignoring", color=C.COLOR_SKIP)

def v2_playbook_on_play_start(self, play):
    name = play.get_name().strip()
    if play.check_mode and self.get_option('check_mode_markers'):
        checkmsg = " [CHECK MODE]"
    else:
        checkmsg = ""
    if not name:
        msg = u"PLAY%s" % checkmsg
    else:
        msg = u"PLAY [%s]%s" % (name, checkmsg)

    self._play = play

    self._display.banner(msg)

def v2_on_file_diff(self, result: CallbackTaskResult) -> None:
    if result.task.loop and 'results' in result.result:
        for res in result.result['results']:
            if 'diff' in res and res['diff'] and res.get('changed', False):
                diff = self._get_diff(res['diff'])
                if diff:
                    if self._last_task_banner != result.task._uuid:
                        self._print_task_banner(result.task)
                    self._display.display(diff)
    elif 'diff' in result.result and result.result['diff'] and result.result.get('changed', False):
        diff = self._get_diff(result.result['diff'])
        if diff:
            if self._last_task_banner != result.task._uuid:
                self._print_task_banner(result.task)
            self._display.display(diff)

def v2_runner_item_on_ok(self, result: CallbackTaskResult) -> None:
    host_label = self.host_label(result)
    if isinstance(result.task, TaskInclude):
        return
    elif result.result.get('changed', False):
        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)

        msg = 'changed'
        color = C.COLOR_CHANGED
    else:
        if not self.get_option('display_ok_hosts'):
            return

        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)

        msg = 'ok'
        color = C.COLOR_OK

    self._handle_warnings_and_exception(result)

    msg = "%s: [%s] => (item=%s)" % (msg, host_label, self._get_item_label(result.result))
    self._clean_results(result.result, result.task.action)
    if self._run_is_verbose(result):
        msg += " => %s" % self._dump_results(result.result)
    self._display.display(msg, color=color)

def v2_runner_item_on_skipped(self, result: CallbackTaskResult) -> None:
    if self.get_option('display_skipped_hosts'):
        if self._last_task_banner != result.task._uuid:
            self._print_task_banner(result.task)

        self._handle_warnings_and_exception(result)

        self._clean_results(result.result, result.task.action)
        msg = "skipping: [%s] => (item=%s) " % (result.host.get_name(), self._get_item_label(result.result))
        if self._run_is_verbose(result):
            msg += " => %s" % self._dump_results(result.result)
        self._display.display(msg, color=C.COLOR_SKIP)

def v2_playbook_on_include(self, included_file):
    msg = 'included: %s for %s' % (included_file._filename, ", ".join([h.name for h in included_file._hosts]))
    label = self._get_item_label(included_file._vars)
    if label:
        msg += " => (item=%s)" % label
    self._display.display(msg, color=C.COLOR_INCLUDED)

def v2_playbook_on_start(self, playbook):
    if self._display.verbosity > 1:
        from os.path import basename
        self._display.banner("PLAYBOOK: %s" % basename(playbook._file_name))

    # show CLI arguments
    if self._display.verbosity > 3:
        if context.CLIARGS.get('args'):
            self._display.display('Positional arguments: %s' % ' '.join(context.CLIARGS['args']),
                                  color=C.COLOR_VERBOSE, screen_only=True)

        for argument in (a for a in context.CLIARGS if a != 'args'):
            val = context.CLIARGS[argument]
            if val:
                self._display.display('%s: %s' % (argument, val), color=C.COLOR_VERBOSE, screen_only=True)

    if context.CLIARGS['check'] and self.get_option('check_mode_markers'):
        self._display.banner("DRY RUN")

def v2_runner_on_failed(self, result: CallbackTaskResult, ignore_errors=False) -> None:
    if ignore_errors and self._fail_on_ignore != 'true':
        self._finish_task('ok', result)
    else:
        self._finish_task('failed', result)

def add_host(self, host: HostData) -> None:
    if host.uuid in self.host_data:
        if host.status == 'included':
            # concatenate task include output from multiple items
            host.result = f'{self.host_data[host.uuid].result}\n{host.result}'
        else:
            raise Exception('%s: %s: %s: duplicate host callback: %s' % (self.path, self.play, self.name, host.name))

    self.host_data[host.uuid] = host

def v2_runner_on_failed(self, result: CallbackTaskResult, ignore_errors: bool = False) -> None:
    self._handle_warnings_and_exception(result)

    if result.task.action in C.MODULE_NO_JSON and 'module_stderr' not in result.result:
        self._display.display(self._command_generic_msg(result.host.get_name(), result.result, "FAILED"), color=C.COLOR_ERROR)
    else:
        self._display.display("%s | FAILED! => %s" % (result.host.get_name(), self._dump_results(result.result, indent=4)), color=C.COLOR_ERROR)

def v2_runner_on_ok(self, result: CallbackTaskResult) -> None:
    self._handle_warnings_and_exception(result)

    self._clean_results(result.result, result.task.action)

    if result.result.get('changed', False):
        color = C.COLOR_CHANGED
        state = 'CHANGED'
    else:
        color = C.COLOR_OK
        state = 'SUCCESS'

    if result.task.action in C.MODULE_NO_JSON and 'ansible_job_id' not in result.result:
        self._display.display(self._command_generic_msg(result.host.get_name(), result.result, state), color=color)
    else:
        self._display.display("%s | %s => %s" % (result.host.get_name(), state, self._dump_results(result.result, indent=4)), color=color)

def v2_runner_on_failed(self, result: CallbackTaskResult, ignore_errors: bool = False) -> None:
    if 'exception' in result.result:
        error_text = Templar().template(result.result['exception'])  # transform to a string
        if self._display.verbosity < 3:
            # extract just the actual error message from the exception text
            error = error_text.strip().split('\n')[-1]
            msg = "An exception occurred during task execution. To see the full traceback, use -vvv. The error was: %s" % error
        else:
            msg = "An exception occurred during task execution. The full traceback is:\n" + error_text.replace('\n', '')

        if result.task.action in C.MODULE_NO_JSON and 'module_stderr' not in result.result:
            self._display.display(self._command_generic_msg(result.host.get_name(), result.result, 'FAILED'), color=C.COLOR_ERROR)
        else:
            self._display.display(msg, color=C.COLOR_ERROR)

    self._display.display("%s | FAILED! => %s" % (result.host.get_name(), self._dump_results(result.result, indent=0).replace('\n', '')),
                          color=C.COLOR_ERROR)

def v2_runner_on_ok(self, result: CallbackTaskResult) -> None:

    if result.result.get('changed', False):
        color = C.COLOR_CHANGED
        state = 'CHANGED'
    else:
        color = C.COLOR_OK
        state = 'SUCCESS'

    if result.task.action in C.MODULE_NO_JSON and 'ansible_job_id' not in result.result:
        self._display.display(self._command_generic_msg(result.host.get_name(), result.result, state), color=color)
    else:
        self._display.display("%s | %s => %s" % (result.host.get_name(), state, self._dump_results(result.result, indent=0).replace('\n', '')),
                              color=color)

