    return a

def comment(text, style='plain', **kw):
    # Predefined comment types
    comment_styles = {
        'plain': {
            'decoration': '# '
        },
        'erlang': {
            'decoration': '% '
        },
        'c': {
            'decoration': '// '
        },
        'cblock': {
            'beginning': '/*',
            'decoration': ' * ',
            'end': ' */'
        },
        'xml': {
            'beginning': '<!--',
            'decoration': ' - ',
            'end': '-->'
        }
    }

    if style not in comment_styles:
        raise AnsibleTemplatePluginError(
            message=f"Invalid style {style!r}.",
            help_text=f"Available styles: {', '.join(comment_styles)}",
            obj=style,
        )

    # Pointer to the right comment type
    style_params = comment_styles[style]

    if 'decoration' in kw:
        prepostfix = kw['decoration']
    else:
        prepostfix = style_params['decoration']

    # Default params
    p = {
        'newline': '\n',
        'beginning': '',
        'prefix': (prepostfix).rstrip(),
        'prefix_count': 1,
        'decoration': '',
        'postfix': (prepostfix).rstrip(),
        'postfix_count': 1,
        'end': ''
    }

    # Update default params
    p.update(style_params)
    p.update(kw)

    # Compose substrings for the final string
    str_beginning = ''
    if p['beginning']:
        str_beginning = "%s%s" % (p['beginning'], p['newline'])
    str_prefix = ''
    if p['prefix']:
        if p['prefix'] != p['newline']:
            str_prefix = str(
                "%s%s" % (p['prefix'], p['newline'])) * int(p['prefix_count'])
        else:
            str_prefix = str(
                "%s" % (p['newline'])) * int(p['prefix_count'])
    str_text = ("%s%s" % (
        p['decoration'],
        # Prepend each line of the text with the decorator
        text.replace(
            p['newline'], "%s%s" % (p['newline'], p['decoration'])))).replace(
                # Remove trailing spaces when only decorator is on the line
                "%s%s" % (p['decoration'], p['newline']),
                "%s%s" % (p['decoration'].rstrip(), p['newline']))
    str_postfix = p['newline'].join(
        [''] + [p['postfix'] for x in range(p['postfix_count'])])
    str_end = ''
    if p['end']:
        str_end = "%s%s" % (p['newline'], p['end'])

    # Return the final string
    return "%s%s%s%s%s" % (
        str_beginning,
        str_prefix,
        str_text,
        str_postfix,
        str_end)

def extract(environment: Environment, item, container, morekeys=None):
    if morekeys is None:
        keys = [item]
    elif isinstance(morekeys, list):
        keys = [item] + morekeys
    else:
        keys = [item, morekeys]

    value = container

    for key in keys:
        try:
            value = environment.getitem(value, key)
        except MarkerError as ex:
            value = ex.source

    return value

def b64encode(string, encoding='utf-8', urlsafe=False):
    func = base64.b64encode
    if urlsafe:
        func = base64.urlsafe_b64encode
    return to_text(func(to_bytes(string, encoding=encoding, errors='surrogate_or_strict')))

def b64decode(string, encoding='utf-8', urlsafe=False):
    func = base64.b64decode
    if urlsafe:
        func = base64.urlsafe_b64decode
    return to_text(func(to_bytes(string, errors='surrogate_or_strict')), encoding=encoding)

def flatten(mylist, levels=None, skip_nulls=True):

    ret = []
    for element in mylist:
        if skip_nulls and element in (None, 'None', 'null'):
            # ignore null items
            continue
        elif is_sequence(element):
            if levels is None:
                ret.extend(flatten(element, skip_nulls=skip_nulls))
            elif levels >= 1:
                # decrement as we go down the stack
                ret.extend(flatten(element, levels=(int(levels) - 1), skip_nulls=skip_nulls))
            else:
                ret.append(element)
        else:
            ret.append(element)

    return ret

def subelements(obj, subelements, skip_missing=False):
    """Accepts a dict or list of dicts, and a dotted accessor and produces a product
    of the element and the results of the dotted accessor

    >>> obj = [{"name": "alice", "groups": ["wheel"], "authorized": ["/tmp/alice/onekey.pub"]}]
    >>> subelements(obj, 'groups')
    [({'name': 'alice', 'groups': ['wheel'], 'authorized': ['/tmp/alice/onekey.pub']}, 'wheel')]

    """
    if isinstance(obj, dict):
        element_list = list(obj.values())
    elif isinstance(obj, list):
        element_list = obj[:]
    else:
        raise AnsibleFilterError('obj must be a list of dicts or a nested dict')

    if isinstance(subelements, list):
        subelement_list = subelements[:]
    elif isinstance(subelements, str):
        subelement_list = subelements.split('.')
    else:
        raise AnsibleTypeError('subelements must be a list or a string')

    results = []

    for element in element_list:
        values = element
        for subelement in subelement_list:
            try:
                values = values[subelement]
            except KeyError:
                if skip_missing:
                    values = []
                    break
                raise AnsibleFilterError("could not find %r key in iterated item %r" % (subelement, values))
            except TypeError as ex:
                raise AnsibleTypeError("the key %s should point to a dictionary, got '%s'" % (subelement, values)) from ex
        if not isinstance(values, list):
            raise AnsibleTypeError("the key %r should point to a list, got %r" % (subelement, values))

        for value in values:
            results.append((element, value))

    return results

def dict_to_list_of_dict_key_value_elements(mydict, key_name='key', value_name='value'):
    """ takes a dictionary and transforms it into a list of dictionaries,
        with each having a 'key' and 'value' keys that correspond to the keys and values of the original """

    if not isinstance(mydict, Mapping):
        raise AnsibleTypeError("dict2items requires a dictionary, got %s instead." % type(mydict))

    ret = []
    for key in mydict:
        ret.append({key_name: key, value_name: mydict[key]})
    return ret

def list_of_dict_key_value_elements_to_dict(mylist, key_name='key', value_name='value'):
    """ takes a list of dicts with each having a 'key' and 'value' keys, and transforms the list into a dictionary,
        effectively as the reverse of dict2items """

    if not is_sequence(mylist):
        raise AnsibleTypeError("items2dict requires a list, got %s instead." % type(mylist))

    try:
        return dict((item[key_name], item[value_name]) for item in mylist)
    except KeyError:
        raise AnsibleTypeError(
            "items2dict requires each dictionary in the list to contain the keys '%s' and '%s', got %s instead."
            % (key_name, value_name, mylist)
        )
    except TypeError:
        raise AnsibleTypeError("items2dict requires a list of dictionaries, got %s instead." % mylist)

def ansible_default(
    value: t.Any,
    default_value: t.Any = '',
    boolean: bool = False,
) -> t.Any:
    """Updated `default` filter that only coalesces classic undefined objects; other Undefined-derived types (eg, ErrorMarker) pass through."""
    validate_arg_type('boolean', boolean, bool)

    if isinstance(value, UndefinedMarker):
        return default_value

    if boolean and not value:
        return default_value

    return value

def do_vault(data, secret, salt=None, vault_id='filter_default', wrap_object=False):
    if not isinstance(secret, (str, bytes)):
        raise TypeError(f"Secret passed is required to be a string, instead we got {type(secret)}.")

    if not isinstance(data, (str, bytes)):
        raise TypeError(f"Can only vault strings, instead we got {type(data)}.")

    vs = VaultSecret(to_bytes(secret))
    vl = VaultLib()
    try:
        vault = vl.encrypt(to_bytes(data), vs, vault_id, salt)
    except Exception as ex:
        raise AnsibleError("Unable to encrypt.") from ex

    if wrap_object:
        vault = VaultedValue(ciphertext=str(vault)).tag(secret)
    else:
        vault = to_native(vault)

    return vault

def do_unvault(vault, secret, vault_id='filter_default'):
    if isinstance(vault, VaultExceptionMarker):
        vault = vault._disarm()

    if (first_marker := _template.get_first_marker_arg((vault, secret, vault_id), {})) is not None:
        return first_marker

    if not isinstance(secret, (str, bytes)):
        raise TypeError(f"Secret passed is required to be as string, instead we got {type(secret)}.")

    if not isinstance(vault, (str, bytes)):
        raise TypeError(f"Vault should be in the form of a string, instead we got {type(vault)}.")

    vs = VaultSecret(to_bytes(secret))
    vl = VaultLib([(vault_id, vs)])

    if ciphertext := VaultHelper.get_ciphertext(vault, with_tags=True):
        vault = ciphertext

    if is_encrypted(vault):
        try:
            data = vl.decrypt(vault)
        except Exception as ex:
            raise AnsibleError("Unable to decrypt.") from ex
    else:
        data = vault

    return to_native(data)

def unique(environment, a, case_sensitive=None, attribute=None):

    def _do_fail(ex):
        if case_sensitive is False or attribute:
            raise AnsibleError(
                "Jinja2's unique filter failed and we cannot fall back to Ansible's version as it does not support the parameters supplied."
            ) from ex

    error = e = None
    try:
        if HAS_UNIQUE:
            c = list(do_unique(environment, a, case_sensitive=bool(case_sensitive), attribute=attribute))
    except TypeError as e:
        error = e
        _do_fail(e)
    except Exception as e:
        error = e
        _do_fail(e)
        display.error_as_warning('Falling back to Ansible unique filter as Jinja2 one failed.', e)

    if not HAS_UNIQUE or error:

        # handle Jinja2 specific attributes when using Ansible's version
        if case_sensitive is False or attribute:
            raise AnsibleError("Ansible's unique filter does not support case_sensitive=False nor attribute parameters, "
                               "you need a newer version of Jinja2 that provides their version of the filter.")

        c = []
        for x in a:
            if x not in c:
                c.append(x)

    return c

def intersect(environment, a, b):
    try:
        c = list(set(a) & set(b))
    except TypeError:
        c = unique(environment, [x for x in a if x in b], True)
    return c

def difference(environment, a, b):
    try:
        c = list(set(a) - set(b))
    except TypeError:
        c = unique(environment, [x for x in a if x not in b], True)
    return c

def symmetric_difference(environment, a, b):
    try:
        c = list(set(a) ^ set(b))
    except TypeError:
        isect = intersect(environment, a, b)
        c = [x for x in union(environment, a, b) if x not in isect]
    return c

def union(environment, a, b):
    try:
        c = list(set(a) | set(b))
    except TypeError:
        c = unique(environment, a + b, True)
    return c

def logarithm(x, base=math.e):
    try:
        if base == 10:
            return math.log10(x)
        else:
            return math.log(x, base)
    except TypeError as ex:
        raise AnsibleError('log() can only be used on numbers') from ex

def power(x, y):
    try:
        return math.pow(x, y)
    except TypeError as ex:
        raise AnsibleError('pow() can only be used on numbers') from ex

def inversepower(x, base=2):
    try:
        if base == 2:
            return math.sqrt(x)
        else:
            return math.pow(x, 1.0 / float(base))
    except (ValueError, TypeError) as ex:
        raise AnsibleError('root() can only be used on numbers') from ex

def human_readable(size, isbits=False, unit=None):
    """ Return a human-readable string """
    try:
        return formatters.bytes_to_human(size, isbits, unit)
    except TypeError as ex:
        raise AnsibleError("human_readable() failed on bad input") from ex
    except Exception as ex:
        raise AnsibleError("human_readable() can't interpret the input") from ex

def human_to_bytes(size, default_unit=None, isbits=False):
    """ Return bytes count from a human-readable string """
    try:
        return formatters.human_to_bytes(size, default_unit, isbits)
    except TypeError as ex:
        raise AnsibleError("human_to_bytes() failed on bad input") from ex
    except Exception as ex:
        raise AnsibleError("human_to_bytes() can't interpret the input") from ex

def rekey_on_member(data, key, duplicates='error'):
    """
    Rekey a dict of dicts on another member

    May also create a dict from a list of dicts.

    duplicates can be one of ``error`` or ``overwrite`` to specify whether to error out if the key
    value would be duplicated or to overwrite previous entries if that's the case.
    """
    if duplicates not in ('error', 'overwrite'):
        raise AnsibleError(f"duplicates parameter to rekey_on_member has unknown value {duplicates!r}")

    new_obj = {}

    if isinstance(data, Mapping):
        iterate_over = data.values()
    elif isinstance(data, Iterable) and not isinstance(data, (str, bytes)):
        iterate_over = data
    else:
        raise AnsibleError("Type is not a valid list, set, or dict")

    for item in iterate_over:
        if not isinstance(item, Mapping):
            raise AnsibleError("List item is not a valid dict")

        try:
            key_elem = item[key]
        except KeyError:
            raise AnsibleError(f"Key {key!r} was not found.", obj=item) from None

        # Note: if new_obj[key_elem] exists it will always be a non-empty dict (it will at
        # minimum contain {key: key_elem}
        if new_obj.get(key_elem, None):
            if duplicates == 'error':
                raise AnsibleError(f"Key {key_elem!r} is not unique, cannot convert to dict.")
            elif duplicates == 'overwrite':
                new_obj[key_elem] = item
        else:
            new_obj[key_elem] = item

    return new_obj

def split_url(value, query='', alias='urlsplit'):

    results = helpers.object_to_dict(urlsplit(value), exclude=['count', 'index', 'geturl', 'encode'])

    # If a query is supplied, make sure it's valid then return the results.
    # If no option is supplied, return the entire dictionary.
    if query:
        if query not in results:
            raise ValueError(alias + ': unknown URL component: %s' % query)
        return results[query]
    else:
        return results

def update_auth(self, response, response_text):
    """Return per-request auth token.

    The response should be a dictionary that can be plugged into the
    headers of a request. The default implementation uses cookie data.
    If no authentication data is found, return None
    """
    cookie = response.info().get('Set-Cookie')
    if cookie:
        return {'Cookie': cookie}

    return None

def parse(self, inventory, loader, host_list, cache=True):
    """ parses the inventory file """

    super(InventoryModule, self).parse(inventory, loader, host_list)

    try:
        for h in host_list.split(','):
            h = h.strip()
            if h:
                try:
                    (hostnames, port) = self._expand_hostpattern(h)
                except AnsibleError as e:
                    self.display.vvv("Unable to parse address from hostname, leaving unchanged: %s" % to_text(e))
                    hostnames = [h]
                    port = None

                for host in hostnames:
                    if host not in self.inventory.hosts:
                        self.inventory.add_host(host, group='ungrouped', port=port)
    except Exception as e:
        raise AnsibleParserError("Invalid data from string, could not parse: %s" % to_native(e))

def parse(self, inventory, loader, path, cache=True):
    config_data = loader.load_from_file(path, cache='none')

    try:
        plugin_name = config_data.get('plugin', None)
    except AttributeError:
        plugin_name = None

    if not plugin_name:
        raise AnsibleParserError("no root 'plugin' key found, '{0}' is not a valid YAML inventory plugin config file".format(path))

    plugin = inventory_loader.get(plugin_name)

    if not plugin:
        raise AnsibleParserError("inventory config '{0}' specifies unknown plugin '{1}'".format(path, plugin_name))

    if not plugin.verify_file(path):
        raise AnsibleParserError("inventory source '{0}' could not be verified by inventory plugin '{1}'".format(path, plugin_name))

    self.display.v("Using inventory plugin '{0}' to process inventory source '{1}'".format(plugin._load_name, path))

    # unfortunate magic to swap the real plugin type we're proxying here into the inventory data API wrapper, so the wrapper can make the right compat
    # decisions based on the metadata the real plugin provides instead of our metadata
    inventory._target_plugin = plugin

    plugin.parse(inventory, loader, path, cache=cache)
    try:
        plugin.update_cache_if_changed()
    except AttributeError:
        pass

def host_groupvars(self, host, loader, sources):
    """ requires host object """
    gvars = get_group_vars(host.get_groups())

    if self.get_option('use_vars_plugins'):
        gvars = combine_vars(gvars, get_vars_from_inventory_sources(loader, sources, host.get_groups(), 'all'))

    return gvars

def host_vars(self, host, loader, sources):
    """ requires host object """
    hvars = host.get_vars()

    if self.get_option('use_vars_plugins'):
        hvars = combine_vars(hvars, get_vars_from_inventory_sources(loader, sources, [host], 'all'))

    return hvars

def template(self, pattern, variables):
    # Allow pass-through of data structures for templating later (if applicable).
    # This limitation was part of the original plugin implementation and was updated to maintain feature parity with the new templating API.
    if not isinstance(pattern, str):
        return pattern

    return self.templar.copy_with_new_env(available_variables=variables).template(pattern)

def add_parents(self, inventory, child, parents, template_vars):
    for parent in parents:
        groupname = self.template(parent.get('name'), template_vars)
        if not groupname:
            raise AnsibleParserError(f"Element {child} has a parent with no name.")
        if groupname not in inventory.groups:
            inventory.add_group(groupname)
        group = inventory.groups[groupname]
        for (k, v) in parent.get('vars', {}).items():
            group.set_variable(k, self.template(v, template_vars))
        inventory.add_child(groupname, child)
        self.add_parents(inventory, groupname, parent.get('parents', []), template_vars)

def parse(self, inventory, loader, host_list, cache=True):
    """ parses the inventory file """

    super(InventoryModule, self).parse(inventory, loader, host_list)

    try:
        for h in host_list.split(','):
            h = h.strip()
            if h:
                try:
                    (host, port) = parse_address(h, allow_ranges=False)
                except AnsibleError as e:
                    self.display.vvv("Unable to parse address from hostname, leaving unchanged: %s" % to_text(e))
                    host = h
                    port = None

                if host not in self.inventory.hosts:
                    self.inventory.add_host(host, group='ungrouped', port=port)
    except Exception as e:
        raise AnsibleParserError("Invalid data from string, could not parse: %s" % to_native(e))

def detect_profile_name(value: str) -> str:
    """
    Detect (optional) JSON profile name from an inventory JSON document.
    Defaults to `inventory_legacy`.
    """
    try:
        data = json.loads(value)
    except Exception as ex:
        raise ValueError('Value could not be parsed as JSON.') from ex

    if not isinstance(data, dict):
        raise TypeError(f'Value is {native_type_name(data)!r} instead of {native_type_name(dict)!r}.')

    if (meta := data.get('_meta', ...)) is ...:
        return _inventory_legacy.Decoder.profile_name

    if not isinstance(meta, dict):
        raise TypeError(f"Value contains '_meta' which is {native_type_name(meta)!r} instead of {native_type_name(dict)!r}.")

    if (profile := meta.get('profile', ...)) is ...:
        return _inventory_legacy.Decoder.profile_name

    if not isinstance(profile, str):
        raise TypeError(f"Value contains '_meta.profile' which is {native_type_name(profile)!r} instead of {native_type_name(str)!r}.")

    if not profile.startswith('inventory_'):
        raise ValueError(f"Non-inventory profile {profile!r} is not allowed.")

    return profile

def parse(self, inventory: InventoryData, loader: DataLoader, path: str, cache: bool = False) -> None:
    super(InventoryModule, self).parse(inventory, loader, path)

    self.set_options()

    origin = Origin(description=f'<inventory script output from {path!r}>')

    data, stderr, stderr_help_text = run_command(path, ['--list'], origin)

    try:
        profile_name = detect_profile_name(data)
        decoder = get_decoder(profile_name)
    except Exception as ex:
        raise AnsibleError(
            message="Unable to get JSON decoder for inventory script result.",
            help_text=stderr_help_text,
            # obj will be added by inventory manager
        ) from ex

    try:
        try:
            processed = json.loads(data, cls=decoder)
        except Exception as json_ex:
            AnsibleJSONParserError.handle_exception(json_ex, origin)
    except Exception as ex:
        raise AnsibleError(
            message="Inventory script result could not be parsed as JSON.",
            help_text=stderr_help_text,
            # obj will be added by inventory manager
        ) from ex

    # if no other errors happened, and you want to force displaying stderr, do so now
    if stderr and self.get_option('always_show_stderr'):
        self.display.error(msg=stderr)

    data_from_meta: dict | None = None

    # A "_meta" subelement may contain a variable "hostvars" which contains a hash for each host
    # if this "hostvars" exists at all then do not call --host for each # host.
    # This is for efficiency and scripts should still return data
    # if called with --host for backwards compat with 1.2 and earlier.
    for (group, gdata) in processed.items():
        if group == '_meta':
            data_from_meta = gdata.get('hostvars')

            if not isinstance(data_from_meta, dict):
                raise TypeError(f"Value contains '_meta.hostvars' which is {native_type_name(data_from_meta)!r} instead of {native_type_name(dict)!r}.")
        else:
            self._parse_group(group, gdata, origin)

    if data_from_meta is None:
        display.deprecated(
            msg="Inventory scripts should always provide 'meta.hostvars'. "
                "Host variables will be collected by running the inventory script with the '--host' option for each host.",
            version='2.23',
            obj=origin,
        )

    for host in self._hosts:
        if data_from_meta is None:
            got = self.get_host_variables(path, host, origin)
        else:
            got = data_from_meta.get(host, {})

        self._populate_host_vars([host], got)

def get_host_variables(path: str, host: str, origin: Origin) -> dict:
    """Runs <script> --host <hostname>, to determine additional host variables."""
    origin = origin.replace(description=f'{origin.description} for host {host!r}')

    data, stderr, stderr_help_text = run_command(path, ['--host', host], origin)

    if not data.strip():
        return {}

    try:
        try:
            # Use standard legacy trust inversion here.
            # Unlike the normal inventory output, everything here is considered a variable and thus supports trust (and trust inversion).
            processed = json.loads(data, cls=_legacy.Decoder)
        except Exception as json_ex:
            AnsibleJSONParserError.handle_exception(json_ex, origin)
    except Exception as ex:
        raise AnsibleError(
            message=f"Inventory script result for host {host!r} could not be parsed as JSON.",
            help_text=stderr_help_text,
            # obj will be added by inventory manager
        ) from ex

    return processed

def parse(self, inventory, loader, path, cache=True):
    """ parses the inventory file """
    super(InventoryModule, self).parse(inventory, loader, path)
    self.set_options()

    try:
        data = self._load_file(path)
    except Exception as e:
        raise AnsibleParserError(e)

    if not data:
        raise AnsibleParserError('Parsed empty TOML file')
    elif data.get('plugin'):
        raise AnsibleParserError('Plugin configuration TOML file, not TOML inventory')

    for group_name in data:
        self._parse_group(group_name, data[group_name])

def parse(self, inventory, loader, path, cache=True):
    """ parses the inventory file """

    super(InventoryModule, self).parse(inventory, loader, path)
    self.set_options()

    try:
        data = self.loader.load_from_file(path, cache='none', trusted_as_template=True)
    except Exception as e:
        raise AnsibleParserError(e)

    if not data:
        raise AnsibleParserError('Parsed empty YAML file')
    elif not isinstance(data, MutableMapping):
        raise AnsibleParserError('YAML inventory has invalid structure, it should be a dictionary, got: %s' % type(data))
    elif data.get('plugin'):
        raise AnsibleParserError('Plugin configuration YAML file, not YAML inventory')

    # We expect top level keys to correspond to groups, iterate over them
    # to get host, vars and subgroups (which we iterate over recursively)
    if isinstance(data, MutableMapping):
        for group_name in data:
            self._parse_group(group_name, data[group_name])
    else:
        raise AnsibleParserError("Invalid data from file, expected dictionary and got:\n\n%s" % to_native(data))

def get_cache_plugin(plugin_name, **kwargs):
    if not plugin_name:
        raise AnsibleError("A cache plugin must be configured to use inventory caching.")

    try:
        cache = CachePluginAdjudicator(plugin_name, **kwargs)
    except AnsibleError as e:
        if 'fact_caching_connection' in to_native(e):
            raise AnsibleError("error, '%s' inventory cache plugin requires the one of the following to be set "
                               "to a writeable directory path:\nansible.cfg:\n[default]: fact_caching_connection,\n"
                               "[inventory]: cache_connection;\nEnvironment:\nANSIBLE_INVENTORY_CACHE_CONNECTION,\n"
                               "ANSIBLE_CACHE_PLUGIN_CONNECTION." % plugin_name)
        else:
            raise

    if cache._plugin.ansible_name != 'ansible.builtin.memory' and kwargs and not getattr(cache._plugin, '_options', None):
        raise AnsibleError('Unable to use cache plugin {0} for inventory. Cache options were provided but may not reconcile '
                           'correctly unless set via set_options. Refer to the porting guide if the plugin derives user settings '
                           'from ansible.constants.'.format(plugin_name))
    return cache

def run(self, terms, variables=None, **kwargs):

    self.set_options(var_options=variables, direct=kwargs)

    missing = self.get_option('on_missing')
    ptype = self.get_option('plugin_type')
    pname = self.get_option('plugin_name')
    show_origin = self.get_option('show_origin')

    if (ptype or pname) and not (ptype and pname):
        raise AnsibleError('Both plugin_type and plugin_name are required, cannot use one without the other.')

    ret = []

    for term in terms:
        if not isinstance(term, str):
            raise AnsibleError(f'Invalid setting identifier, {term!r} is not a {str}, its a {type(term)}.')

        result = Sentinel
        origin = None
        try:
            if pname:
                result, origin = _get_plugin_config(pname, ptype, term, variables)
            else:
                result = _get_global_config(term)
        except AnsibleUndefinedConfigEntry:
            if missing == 'error':
                raise
            elif missing == 'warn':
                self._display.warning(f"Skipping, did not find setting {term!r}.")
            elif missing == 'skip':
                pass  # this is not needed, but added to have all 3 options stated

        if result is not Sentinel:
            if show_origin:
                ret.append([result, origin])
            else:
                ret.append(result)
    return ret

def run(self, terms, variables=None, **kwargs):
    ret = []

    self.set_options(var_options=variables, direct=kwargs)

    # populate options
    paramvals = self.get_options()

    if not terms:
        raise AnsibleError('Search key is required but was not found')

    for term in terms:
        kv = parse_kv(term)

        if '_raw_params' not in kv:
            raise AnsibleError('Search key is required but was not found')

        key = kv['_raw_params']

        # parameters override per term using k/v
        reset_params = False
        for name, value in kv.items():
            if name == '_raw_params':
                continue
            if name not in paramvals:
                raise ValueError(f'{name!r} is not a valid option')

            self._deprecate_inline_kv()
            self.set_option(name, value)
            reset_params = True

        if reset_params:
            paramvals = self.get_options()

        # default is just placeholder for real tab
        if paramvals['delimiter'] == 'TAB':
            paramvals['delimiter'] = "\t"

        lookupfile = self.find_file_in_search_path(variables, 'files', paramvals['file'])
        var = self.read_csv(lookupfile, key, paramvals['delimiter'], paramvals['encoding'], paramvals['default'], paramvals['col'], paramvals['keycol'])
        if var is not None:
            if isinstance(var, MutableSequence):
                ret.extend(var)
            else:
                ret.append(var)

    return ret

def run(self, terms, variables=None, **kwargs):
    results = []
    for term in terms:
        # Expect any type of Mapping, notably hostvars
        if not isinstance(term, Mapping):
            raise AnsibleError(f"the 'dict' lookup plugin expects a dictionary, got '{term}' of type {type(term)})")

        results.extend(self._flatten_hash_to_list(term))
    return results

def run(self, terms, variables=None, **kwargs):
    self.set_options(var_options=variables, direct=kwargs)

    ret = []
    d = self.get_option('default')

    for term in terms:
        var = term.split()[0]
        val = os.environ.get(var, d)

        if val is _DEFAULT_UNDEF:
            val = _undef(f'The environment variable {var!r} is not set.')
        else:
            val = Origin(description=f"<environment variable {var!r}>").try_tag(val)

        ret.append(val)

    return ret

def run(self, terms, variables=None, **kwargs):

    ret = []
    self.set_options(var_options=variables, direct=kwargs)

    for term in terms:
        display.debug("File lookup term: %s" % term)
        # Find the file in the expected search path
        try:
            lookupfile = self.find_file_in_search_path(variables, 'files', term, ignore_missing=True)
            display.vvvv(u"File lookup using %s as file" % lookupfile)
            if lookupfile:
                contents = self._loader.get_text_file_contents(lookupfile)
                if self.get_option('lstrip'):
                    contents = contents.lstrip()
                if self.get_option('rstrip'):
                    contents = contents.rstrip()
                ret.append(contents)
            else:
                # TODO: only add search info if abs path?
                raise AnsibleError("File not found. Use -vvvvv to see paths searched.")
        except AnsibleError as ex:
            raise AnsibleError(f"Unable to access the file {term!r}.") from ex

    return ret

def get_value(self, key, section, dflt, is_regexp):
    # Retrieve all values from a section using a regexp
    if is_regexp:
        return [v for k, v in self.cp.items(section) if re.match(key, k)]
    value = None
    # Retrieve a single value
    try:
        value = self.cp.get(section, key)
    except configparser.NoOptionError:
        return dflt
    return value

def run(self, terms, variables=None, **kwargs):

    self.set_options(var_options=variables, direct=kwargs)
    paramvals = self.get_options()

    self.cp = configparser.ConfigParser(
        allow_no_value=paramvals.get('allow_no_value', paramvals.get('allow_none')),
        interpolation=configparser.BasicInterpolation() if paramvals.get('interpolation') else None,
    )
    if paramvals['case_sensitive']:
        self.cp.optionxform = to_native

    ret = []
    for term in terms:

        key = term
        # parameters specified?
        if '=' in term or ' ' in term.strip():
            self._deprecate_inline_kv()
            params = _parse_params(term, paramvals)
            param = None
            try:
                updated_key = False
                updated_options = False
                for param in params:
                    if '=' in param:
                        name, value = param.split('=')
                        if name not in paramvals:
                            raise AnsibleError(f"{name!r} is not a valid option.")
                        self.set_option(name, value)
                        updated_options = True
                    elif key == term:
                        # only take first, this format never supported multiple keys inline
                        key = param
                        updated_key = True
                if updated_options:
                    paramvals = self.get_options()
            except ValueError as ex:
                # bad params passed
                raise ValueError(f"Could not use {param!r} from {params!r}.") from ex
            if not updated_key:
                raise ValueError(f"No key to look up was provided as first term within string inline options: {term}")
                # only passed options in inline string

        # TODO: look to use cache to avoid redoing this for every term if they use same file
        # Retrieve file path
        path = self.find_file_in_search_path(variables, 'files', paramvals['file'])

        # Create StringIO later used to parse ini
        config = StringIO()
        # Special case for java properties
        if paramvals['type'] == "properties":
            config.write(u'[java_properties]\n')
            paramvals['section'] = 'java_properties'

        contents = self._loader.get_text_file_contents(path, encoding=paramvals['encoding'])
        config.write(contents)
        config.seek(0, os.SEEK_SET)

        try:
            self.cp.read_file(config)
        except configparser.DuplicateOptionError as ex:
            raise ValueError(f"Duplicate option in {paramvals['file']!r}.") from ex

        try:
            var = self.get_value(key, paramvals['section'], paramvals['default'], paramvals['re'])
        except configparser.NoSectionError:
            raise ValueError(f"No section {paramvals['section']!r} in {paramvals['file']!r}.") from None

        if var is not None:
            if isinstance(var, MutableSequence):
                for v in var:
                    ret.append(v)
            else:
                ret.append(var)

    return ret

def run(self, terms, variables=None, **kwargs):
    manager = InventoryManager(self._loader, parse=False)
    for group, hosts in variables['groups'].items():
        manager.add_group(group)
        for host in hosts:
            manager.add_host(host, group=group)

    try:
        return [h.name for h in manager.get_hosts(pattern=terms)]
    except AnsibleError:
        return []

def run(self, terms, variables=None, **kwargs):
    my_list = terms[:]
    my_list.reverse()

    if len(my_list) == 0:
        raise AnsibleError("with_nested requires at least one element in the nested list")
    result = my_list.pop()
    while len(my_list) > 0:
        result2 = self._combine(result, my_list.pop())
        result = result2
    new_result = []
    for x in result:
        new_result.append(self._flatten(x))
    return new_result

def run(self, terms, variables=None, **kwargs):
    ret = []

    for term in terms:

        self.set_options(var_options=variables, direct=kwargs)

        changed = None
        relpath, params = self._parse_parameters(term)
        path = self._loader.path_dwim(relpath)
        b_path = to_bytes(path, errors='surrogate_or_strict')
        chars = _gen_candidate_chars(params['chars'])
        ident = None
        first_process = None
        lockfile = None

        try:
            # make sure only one process finishes all the job first
            first_process, lockfile = _get_lock(b_path)

            content = _read_password_file(b_path)

            if content is None or b_path == to_bytes('/dev/null'):
                plaintext_password = random_password(params['length'], chars, params['seed'])
                salt = None
                changed = True
            else:
                plaintext_password, salt, ident = _parse_content(content)

            encrypt = params['encrypt']
            if encrypt and not salt:
                changed = True
                try:
                    salt = random_salt(BaseHash.algorithms[encrypt].salt_size)
                except KeyError:
                    salt = random_salt()

            if not ident:
                ident = params['ident']
            elif params['ident'] and ident != params['ident']:
                raise AnsibleError('The ident parameter provided (%s) does not match the stored one (%s).' % (ident, params['ident']))

            if encrypt and not ident:
                try:
                    ident = BaseHash.algorithms[encrypt].implicit_ident
                except KeyError:
                    ident = None
                if ident:
                    changed = True

            if changed and b_path != to_bytes('/dev/null'):
                content = _format_content(plaintext_password, salt, encrypt=encrypt, ident=ident)
                _write_password_file(b_path, content)

        finally:
            if first_process:
                # let other processes continue
                _release_lock(lockfile)

        if encrypt:
            password = do_encrypt(plaintext_password, encrypt, salt=salt, ident=ident)
            ret.append(password)
        else:
            ret.append(plaintext_password)

    return ret

def run(self, terms, variables=None, **kwargs):

    ret = terms
    if terms:
        try:
            ret = [secrets.choice(terms)]
        except Exception as e:
            raise AnsibleError("Unable to choose random term: %s" % to_native(e))

    return ret

def parse_kv_args(self, args):
    """parse key-value style arguments"""
    for arg in FIELDS:
        value = args.pop(arg, None)
        if value is not None:
            self.set_option(arg, value)
    if args:
        raise AnsibleError(
            "unrecognized arguments to with_sequence: %s"
            % list(args.keys())
        )

def parse_simple_args(self, term):
    """parse the shortcut forms, return True/False"""
    match = SHORTCUT.match(term)
    if not match:
        return False

    dummy, start, end, dummy, stride, dummy, format = match.groups()

    for key in FIELDS:
        value = locals().get(key, None)
        if value is not None:
            self.set_option(key, value)

    return True

def sanity_check(self):
    """
    Returns True if options comprise a valid sequence expression
    Raises AnsibleError if options are an invalid expression
    Returns false if options are valid but result in an empty sequence - these cases do not raise exceptions
    in order to maintain historic behavior
    """
    if self.count is None and self.end is None:
        raise AnsibleError("must specify count or end in with_sequence")
    elif self.count is not None and self.end is not None:
        raise AnsibleError("can't specify both count and end in with_sequence")
    elif self.count is not None:
        # convert count to end
        if self.count != 0:
            self.end = self.start + self.count * self.stride - 1
        else:
            return False
    if self.stride > 0 and self.end < self.start:
        raise AnsibleError("to count backwards make stride negative")
    if self.stride < 0 and self.end > self.start:
        raise AnsibleError("to count forward don't make stride negative")
    if self.stride == 0:
        return False
    if self.format.count('%') != 1:
        raise AnsibleError("bad formatting string: %s" % self.format)

    return True

def generate_sequence(self):
    if self.stride >= 0:
        adjust = 1
    else:
        adjust = -1
    numbers = range(self.start, self.end + adjust, self.stride)

    for i in numbers:
        try:
            formatted = self.format % i
            yield formatted
        except (ValueError, TypeError):
            raise AnsibleError(
                "problem formatting %r with %r" % (i, self.format)
            )

def run(self, terms, variables=None, **kwargs):
    results = []

    if kwargs and not terms:
        # All of the necessary arguments can be provided as keywords, but we still need something to loop over
        terms = ['']

    for term in terms:
        try:
            # set defaults/global
            self.set_options(direct=kwargs)
            try:
                if not self.parse_simple_args(term):
                    self.parse_kv_args(parse_kv(term))
            except AnsibleError:
                raise
            except Exception as e:
                raise AnsibleError("unknown error parsing with_sequence arguments: %r. Error was: %s" % (term, e))

            self.set_fields()
            if self.sanity_check():
                results.extend(self.generate_sequence())

        except AnsibleError:
            raise
        except Exception as e:
            raise AnsibleError(
                "unknown error generating sequence: %s" % e
            )

    return results

def run(self, terms, variables=None, **kwargs):

    def _raise_terms_error(msg=""):
        raise AnsibleError(
            "subelements lookup expects a list of two or three items, " + msg)

    # check lookup terms - check number of terms
    if not isinstance(terms, list) or not 2 <= len(terms) <= 3:
        _raise_terms_error()

    # first term should be a list (or dict), second a string holding the subkey
    if not isinstance(terms[0], (list, dict)) or not isinstance(terms[1], str):
        _raise_terms_error("first a dict or a list, second a string pointing to the subkey")
    subelements = terms[1].split(".")

    if isinstance(terms[0], dict):  # convert to list:
        if terms[0].get('skipped', False) is not False:
            # the registered result was completely skipped
            return []
        elementlist = []
        for key in terms[0]:
            elementlist.append(terms[0][key])
    else:
        elementlist = terms[0]

    # check for optional flags in third term
    flags = {}
    if len(terms) == 3:
        flags = terms[2]
    if not isinstance(flags, dict) and not all(isinstance(key, str) and key in FLAGS for key in flags):
        _raise_terms_error("the optional third item must be a dict with flags %s" % FLAGS)

    # build_items
    ret = []
    for item0 in elementlist:
        if not isinstance(item0, dict):
            raise AnsibleError("subelements lookup expects a dictionary, got '%s'" % item0)
        if item0.get('skipped', False) is not False:
            # this particular item is to be skipped
            continue

        skip_missing = boolean(flags.get('skip_missing', False), strict=False)
        subvalue = item0
        lastsubkey = False
        sublist = []
        for subkey in subelements:
            if subkey == subelements[-1]:
                lastsubkey = True
            if subkey not in subvalue:
                if skip_missing:
                    continue
                else:
                    raise AnsibleError("could not find '%s' key in iterated item '%s'" % (subkey, subvalue))
            if not lastsubkey:
                if not isinstance(subvalue[subkey], dict):
                    if skip_missing:
                        continue
                    else:
                        raise AnsibleError("the key %s should point to a dictionary, got '%s'" % (subkey, subvalue[subkey]))
                else:
                    subvalue = subvalue[subkey]
            else:  # lastsubkey
                if not isinstance(subvalue[subkey], list):
                    raise AnsibleError("the key %s should point to a list, got '%s'" % (subkey, subvalue[subkey]))
                else:
                    sublist = subvalue.pop(subkey, [])
        for item1 in sublist:
            ret.append((item0, item1))

    return ret

def run(self, terms, variables=None, **kwargs):
    my_list = terms[:]
    if len(my_list) == 0:
        raise AnsibleError("with_together requires at least one element in each list")

    return [self._flatten(x) for x in itertools.zip_longest(*my_list, fillvalue=None)]

def run(self, terms, variables=None, **kwargs):

    ret = []

    self.set_options(var_options=variables, direct=kwargs)

    for term in terms:
        display.debug("Unvault lookup term: %s" % term)

        # Find the file in the expected search path
        lookupfile = self.find_file_in_search_path(variables, 'files', term)
        display.vvvv(u"Unvault lookup found %s" % lookupfile)
        if lookupfile:
            ret.append(self._loader.get_text_file_contents(lookupfile))
        else:
            raise AnsibleParserError('Unable to find file matching "%s" ' % term)

    return ret

def run(self, terms, variables=None, **kwargs):

    if variables is None:
        raise AnsibleError('No variables available to search')

    self.set_options(var_options=variables, direct=kwargs)

    ret = []
    variable_names = list(variables.keys())
    for term in terms:

        if not isinstance(term, str):
            raise AnsibleError('Invalid setting identifier, "%s" is not a string, it is a %s' % (term, type(term)))

        try:
            name = re.compile(term)
        except Exception as e:
            raise AnsibleError('Unable to use "%s" as a search parameter: %s' % (term, to_native(e)))

        for varname in variable_names:
            if name.search(varname):
                ret.append(varname)

    return ret

def run(self, terms, variables, **kwargs):
    self.set_options(var_options=variables, direct=kwargs)

    default = self.get_option('default')

    ret = []

    for term in terms:
        if not isinstance(term, str):
            raise AnsibleTypeError(f'Variable name must be {native_type_name(str)!r} not {native_type_name(term)!r}.', obj=term)

        try:
            value = variables[term]
        except KeyError:
            if default is None:
                value = _jinja_bits._undef(f'No variable named {term!r} was found.')
            else:
                value = default

        ret.append(value)

    return self._templar._engine.template(ret)

def get_basedir(self, variables):
    if 'role_path' in variables:
        return variables['role_path']
    else:
        return self._loader.get_basedir()

def find_file_in_search_path(self, myvars, subdir, needle, ignore_missing=False):
    """
    Return a file (needle) in the task's expected search path.
    """

    if 'ansible_search_path' in myvars:
        paths = myvars['ansible_search_path']
    else:
        paths = [self.get_basedir(myvars)]

    result = None
    try:
        result = self._loader.path_dwim_relative_stack(paths, subdir, needle, is_role=bool('role_path' in myvars))
    except AnsibleFileNotFound:
        if not ignore_missing:
            self._display.warning("Unable to find '%s' in expected paths (use -vvvvv to see paths)" % needle)

    return result

def ensure_ncclient(func):
    @wraps(func)
    def wrapped(self, *args, **kwargs):
        if not HAS_NCCLIENT:
            raise AnsibleError("%s: %s" % (missing_required_lib('ncclient'), to_native(NCCLIENT_IMP_ERR)))
        return func(self, *args, **kwargs)
    return wrapped

def remove(self, path, recurse=False):
    quoted_path = self._escape(path)
    if recurse:
        return self._encode_script("""Remove-Item '%s' -Force -Recurse;""" % quoted_path)
    else:
        return self._encode_script("""Remove-Item '%s' -Force;""" % quoted_path)

def expand_user(
    self,
    user_home_path: str,
    username: str = '',
) -> str:
    # This is not called in Ansible anymore but it is kept for backwards
    # compatibility in case other actions plugins outside Ansible called this.
    if user_home_path == '~':
        script = 'Write-Output (Get-Location).Path'
    elif user_home_path.startswith('~\\'):
        script = "Write-Output ((Get-Location).Path + '%s')" % self._escape(user_home_path[1:])
    else:
        script = "Write-Output '%s'" % self._escape(user_home_path)
    return self._encode_script(f"{self._CONSOLE_ENCODING}; {script}")

def exists(self, path):
    path = self._escape(path)
    script = """
        If (Test-Path '%s')
        {
            $res = 0;
        }
        Else
        {
            $res = 1;
        }
        Write-Output '$res';
        Exit $res;
     """ % path
    return self._encode_script(script)

def checksum(self, path, *args, **kwargs):
    display.deprecated(
        msg="The `ShellModule.checksum` method is deprecated.",
        version="2.23",
        help_text="Use `ActionBase._execute_remote_stat()` instead.",
    )
    path = self._escape(path)
    script = """
        If (Test-Path -PathType Leaf '%(path)s')
        {
            $sp = new-object -TypeName System.Security.Cryptography.SHA1CryptoServiceProvider;
            $fp = [System.IO.File]::Open('%(path)s', [System.IO.Filemode]::Open, [System.IO.FileAccess]::Read);
            [System.BitConverter]::ToString($sp.ComputeHash($fp)).Replace("-", "").ToLower();
            $fp.Dispose();
        }
        ElseIf (Test-Path -PathType Container '%(path)s')
        {
            Write-Output "3";
        }
        Else
        {
            Write-Output "1";
        }
    """ % dict(path=path)
    return self._encode_script(script)

def build_module_command(self, env_string, shebang, cmd, arg_path=None):
    bootstrap_wrapper = _get_powershell_script("bootstrap_wrapper.ps1")

    # pipelining bypass
    if cmd == '':
        return self._encode_script(script=bootstrap_wrapper, strict_mode=False, preserve_rc=False)

    # non-pipelining

    cmd_parts = shlex.split(cmd, posix=False)
    cmd_parts = list(map(to_text, cmd_parts))
    if shebang and shebang.lower() == '#!powershell':
        if arg_path:
            # Running a module without the exec_wrapper and with an argument
            # file.
            script_path = cmd_parts[0]
            if not script_path.lower().endswith('.ps1'):
                script_path += '.ps1'

            cmd_parts.insert(0, '-File')
            cmd_parts[1] = f'"{script_path}"'
            if arg_path:
                cmd_parts.append(f'"{arg_path}"')

            wrapper_cmd = " ".join(_common_args + cmd_parts)
            return wrapper_cmd

        else:
            # Running a module with ANSIBLE_KEEP_REMOTE_FILES=true, the script
            # arg is actually the input manifest JSON to provide to the bootstrap
            # wrapper.
            wrapper_cmd = "type " + cmd_parts[0] + " | " + self._encode_script(script=bootstrap_wrapper, strict_mode=False, preserve_rc=False)
            return wrapper_cmd

    elif shebang and shebang.startswith('#!'):
        cmd_parts.insert(0, shebang[2:])
    elif not shebang:
        # The module is assumed to be a binary
        cmd_parts.append(arg_path)
    script = """
        Try
        {
            %s
            %s
        }
        Catch
        {
            $_obj = @{ failed = $true }
            If ($_.Exception.GetType)
            {
                $_obj.Add('msg', $_.Exception.Message)
            }
            Else
            {
                $_obj.Add('msg', $_.ToString())
            }
            If ($_.InvocationInfo.PositionMessage)
            {
                $_obj.Add('exception', $_.InvocationInfo.PositionMessage)
            }
            ElseIf ($_.ScriptStackTrace)
            {
                $_obj.Add('exception', $_.ScriptStackTrace)
            }
            Try
            {
                $_obj.Add('error_record', ($_ | ConvertTo-Json | ConvertFrom-Json))
            }
            Catch
            {
            }
            Echo $_obj | ConvertTo-Json -Compress -Depth 99
            Exit 1
        }
    """ % (env_string, ' '.join(cmd_parts))
    return self._encode_script(script, preserve_rc=False)

def set_options(self, task_keys=None, var_options=None, direct=None):

    super(ShellBase, self).set_options(task_keys=task_keys, var_options=var_options, direct=direct)

    # We can remove the try: except in the future when we make ShellBase a proper subset of
    # *all* shells.  Right now powershell and third party shells which do not use the
    # shell_common documentation fragment (and so do not have system_tmpdirs) will fail
    try:
        self._normalize_system_tmpdirs()
    except KeyError:
        pass

def set_user_facl(self, paths, user, mode):
    """Only sets acls for users as that's really all we need"""
    cmd = ['setfacl', '-m', 'u:%s:%s' % (user, mode)]
    cmd.extend(paths)
    return self.join(cmd)

def remove(self, path, recurse=False):
    path = self.quote(path)
    cmd = 'rm -f '
    if recurse:
        cmd += '-r '
    return cmd + "%s %s" % (path, self._SHELL_REDIRECT_ALLNULL)

def build_module_command(self, env_string, shebang, cmd, arg_path=None):
    env_string = env_string.strip()
    if env_string:
        env_string += ' '

    if shebang is None:
        shebang = ''

    cmd_parts = [
        shebang.removeprefix('#!').strip(),
        cmd.strip(),
        arg_path,
    ]

    cleaned_up_cmd = self.join(
        stripped_cmd_part for raw_cmd_part in cmd_parts
        if raw_cmd_part and (stripped_cmd_part := raw_cmd_part.strip())
    )
    return ''.join((env_string, cleaned_up_cmd))

def append_command(self, cmd, cmd_to_append):
    """Append an additional command if supported by the shell"""

    if self._SHELL_AND:
        cmd += ' %s %s' % (self._SHELL_AND, cmd_to_append)

    return cmd

def results_thread_main(strategy: StrategyBase) -> None:
    value: object

    while True:
        try:
            result = strategy._final_q.get()
            if isinstance(result, StrategySentinel):
                break
            elif isinstance(result, DisplaySend):
                dmethod = getattr(display, result.method)
                dmethod(*result.args, **result.kwargs)
            elif isinstance(result, CallbackSend):
                task_result = strategy._convert_wire_task_result_to_raw(result.wire_task_result)
                strategy._tqm.send_callback(result.method_name, task_result)
            elif isinstance(result, _WireTaskResult):
                result = strategy._convert_wire_task_result_to_raw(result)
                with strategy._results_lock:
                    strategy._results.append(result)
            elif isinstance(result, PromptSend):
                try:
                    value = display.prompt_until(
                        result.prompt,
                        private=result.private,
                        seconds=result.seconds,
                        complete_input=result.complete_input,
                        interrupt_input=result.interrupt_input,
                    )
                except AnsibleError as e:
                    value = e
                except BaseException as e:
                    # relay unexpected errors so bugs in display are reported and don't cause workers to hang
                    try:
                        raise AnsibleError(f"{e}") from e
                    except AnsibleError as e:
                        value = e
                strategy._workers[result.worker_id].worker_queue.put(value)
            else:
                display.warning('Received an invalid object (%s) in the result queue: %r' % (type(result), result))
        except (OSError, EOFError):
            break
        except queue.Empty:
            pass

def cleanup(self):
    # close active persistent connections
    for sock in self._active_connections.values():
        try:
            conn = Connection(sock)
            conn.reset()
        except ConnectionError as e:
            # most likely socket is already closed
            display.debug("got an error while closing persistent connection: %s" % e)
    self._final_q.put(_sentinel)
    self._results_thread.join()

def run(self, iterator, play_context, result=0):
    # execute one more pass through the iterator without peeking, to
    # make sure that all of the hosts are advanced to their final task.
    # This should be safe, as everything should be IteratingStates.COMPLETE by
    # this point, though the strategy may not advance the hosts itself.

    for host in self._hosts_cache:
        if host not in self._tqm._unreachable_hosts:
            try:
                iterator.get_next_task_for_host(self._inventory.hosts[host])
            except KeyError:
                iterator.get_next_task_for_host(self._inventory.get_host(host))

    # return the appropriate code, depending on the status hosts after the run
    if not isinstance(result, bool) and result != self._tqm.RUN_OK:
        return result
    elif len(self._tqm._unreachable_hosts.keys()) > 0:
        return self._tqm.RUN_UNREACHABLE_HOSTS
    elif len(iterator.get_failed_hosts()) > 0:
        return self._tqm.RUN_FAILED_HOSTS
    else:
        return self._tqm.RUN_OK

def get_task_hosts(self, iterator, task_host, task):
    if task.run_once:
        host_list = [host for host in self._hosts_cache if host not in self._tqm._unreachable_hosts]
    else:
        host_list = [task_host.name]
    return host_list

def search_handlers_by_notification(self, notification: str, iterator: PlayIterator) -> t.Generator[Handler, None, None]:
    handlers = [h for b in reversed(iterator._play.handlers) for h in b.block]
    # iterate in reversed order since last handler loaded with the same name wins
    for handler in handlers:
        if not handler.name:
            continue

        if not handler.cached_name:
            def variables_factory() -> dict[str, t.Any]:
                return self._variable_manager.get_vars(
                    play=iterator._play,
                    task=handler,
                    _hosts=self._hosts_cache,
                    _hosts_all=self._hosts_cache_all
                )

            templar = TemplateEngine(variables_factory=variables_factory)

            try:
                handler.name = templar.template(handler.name)
            except AnsibleTemplateError as e:
                # We skip this handler due to the fact that it may be using
                # a variable in the name that was conditionally included via
                # set_fact or some other method, and we don't want to error
                # out unnecessarily
                if not handler.listen:
                    display.warning(
                        "Handler '%s' is unusable because it has no listen topics and "
                        "the name could not be templated (host-specific variables are "
                        "not supported in handler names). The error: %s" % (handler.name, to_text(e))
                    )
                continue

            handler.cached_name = True

        # first we check with the full result of get_name(), which may
        # include the role name (if the handler is from a role). If that
        # is not found, we resort to the simple name field, which doesn't
        # have anything extra added to it.
        if notification in {
            handler.name,
            handler.get_name(include_role_fqcn=False),
            handler.get_name(include_role_fqcn=True),
        }:
            yield handler
            break

    seen = set()
    for handler in handlers:
        if notification in handler.listen:
            if handler.name and handler.name in seen:
                continue
            seen.add(handler.name)
            yield handler

def get_hosts_left(self, iterator):
    """ returns list of available hosts for this iterator by filtering out unreachables """

    hosts_left = []
    for host in self._hosts_cache:
        if host not in self._tqm._unreachable_hosts:
            try:
                hosts_left.append(self._inventory.hosts[host])
            except KeyError:
                hosts_left.append(self._inventory.get_host(host))
    return hosts_left

def update_active_connections(self, results: _c.Iterable[_RawTaskResult]) -> None:
    """ updates the current active persistent connections """
    for r in results:
        if 'args' in r.task_fields:
            socket_path = r.task_fields['args'].get('_ansible_socket')
            if socket_path:
                if r.host not in self._active_connections:
                    self._active_connections[r.host] = socket_path

def cmdloop(self):
    try:
        cmd.Cmd.cmdloop(self)
    except KeyboardInterrupt:
        pass

def evaluate(self, args):
    try:
        return eval(args, globals(), self.scope)
    except Exception:
        t, v = sys.exc_info()[:2]
        if isinstance(t, str):
            exc_type_name = t
        else:
            exc_type_name = t.__name__
        display.display('***%s:%s' % (exc_type_name, repr(v)))
        raise

def do_pprint(self, args):
    """Pretty Print"""
    try:
        result = self.evaluate(args)
        display.display(pprint.pformat(result))
    except Exception:
        pass

def execute(self, args):
    try:
        code = compile(args + '\n', '<stdin>', 'single')
        exec(code, globals(), self.scope)
    except Exception:
        t, v = sys.exc_info()[:2]
        if isinstance(t, str):
            exc_type_name = t
        else:
            exc_type_name = t.__name__
        display.display('***%s:%s' % (exc_type_name, repr(v)))
        raise

def default(self, line):
    try:
        self.execute(line)
    except Exception:
        pass

def load_found_files(self, loader, data, found_files):
    for found in found_files:
        new_data = loader.load_from_file(found, cache='all', unsafe=True, trusted_as_template=True)
        if new_data:  # ignore empty files
            data = combine_vars(data, new_data)
    return data

def parsecolor(color):
    """SGR parameter string for the specified color name."""
    matches = re.match(r"color(?P<color>[0-9]+)"
                       r"|(?P<rgb>rgb(?P<red>[0-5])(?P<green>[0-5])(?P<blue>[0-5]))"
                       r"|gray(?P<gray>[0-9]+)", color)
    if not matches:
        return C.COLOR_CODES[color]
    if matches.group('color'):
        return u'38;5;%d' % int(matches.group('color'))
    if matches.group('rgb'):
        return u'38;5;%d' % (16 + 36 * int(matches.group('red')) +
                             6 * int(matches.group('green')) +
                             int(matches.group('blue')))
    if matches.group('gray'):
        return u'38;5;%d' % (232 + int(matches.group('gray')))

def colorize(lead, num, color):
    """ Print 'lead' = 'num' in 'color' """
    s = u"%s=%-4s" % (lead, str(num))
    if num != 0 and ANSIBLE_COLOR and color is not None:
        s = stringc(s, color)
    return s

def hostcolor(host, stats, color=True):
    if ANSIBLE_COLOR and color:
        if stats['failures'] != 0 or stats['unreachable'] != 0:
            return u"%-37s" % stringc(host, C.COLOR_ERROR)
        elif stats['changed'] != 0:
            return u"%-37s" % stringc(host, C.COLOR_CHANGED)
        else:
            return u"%-37s" % stringc(host, C.COLOR_OK)
    return u"%-26s" % host

def random_password(length=DEFAULT_PASSWORD_LENGTH, chars=C.DEFAULT_PASSWORD_CHARS, seed=None):
    """Return a random password string of length containing only chars

    :kwarg length: The number of characters in the new password.  Defaults to 20.
    :kwarg chars: The characters to choose from.  The default is all ascii
        letters, ascii digits, and these symbols ``.,:-_``
    """
    if not isinstance(chars, str):
        raise AnsibleAssertionError(f'{chars=!r} ({type(chars)}) is not a {type(str)}.')

    if seed is None:
        random_generator = random.SystemRandom()
    else:
        random_generator = random.Random(seed)
    return u''.join(random_generator.choice(chars) for dummy in range(length))

def do_encrypt(result, encrypt, salt_size=None, salt=None, ident=None, rounds=None):
    if PASSLIB_AVAILABLE:
        return PasslibHash(encrypt).hash(result, salt=salt, salt_size=salt_size, rounds=rounds, ident=ident)

    raise AnsibleError("Unable to encrypt nor hash, passlib must be installed.") from PASSLIB_E

def hash(self, secret, salt=None, salt_size=None, rounds=None, ident=None):
    salt = self._clean_salt(salt)
    rounds = self._clean_rounds(rounds)
    ident = self._clean_ident(ident)
    if salt_size is not None and not isinstance(salt_size, int):
        raise TypeError("salt_size must be an integer")
    return self._hash(secret, salt=salt, salt_size=salt_size, rounds=rounds, ident=ident)

def secure_hash_s(data, hash_func=sha1):
    """ Return a secure hash hex digest of data. """

    digest = hash_func()
    data = to_bytes(data, errors='surrogate_or_strict')
    digest.update(data)
    return digest.hexdigest()

def pct_to_int(value, num_items, min_value=1):
    """
    Converts a given value to a percentage if specified as "x%",
    otherwise converts the given value to an integer.
    """
    if isinstance(value, str) and value.endswith('%'):
        value_pct = int(value.replace("%", ""))
        return int((value_pct / 100.0) * num_items) or min_value
    else:
        return int(value)

def object_to_dict(obj, exclude=None):
    """
    Converts an object into a dict making the properties into keys, allows excluding certain keys
    """
    if exclude is None or not isinstance(exclude, list):
        exclude = []
    return dict((key, getattr(obj, key)) for key in dir(obj) if not (key.startswith('_') or key in exclude))

def handle_request(self, request):
    request = json.loads(to_text(request, errors='surrogate_then_replace'))

    method = request.get('method')

    if method.startswith('rpc.') or method.startswith('_'):
        error = self.invalid_request()
        return json.dumps(error)

    args, kwargs = request.get('params')
    setattr(self, '_identifier', request.get('id'))

    rpc_method = None
    for obj in self._objects:
        rpc_method = getattr(obj, method, None)
        if rpc_method:
            break

    if not rpc_method:
        error = self.method_not_found()
        response = json.dumps(error)
    else:
        try:
            result = rpc_method(*args, **kwargs)
        except ConnectionError as exc:
            display.vvv(traceback.format_exc())
            try:
                error = self.error(code=exc.code, message=to_text(exc))
            except AttributeError:
                error = self.internal_error(data=to_text(exc))
            response = json.dumps(error)
        except Exception as exc:
            display.vvv(traceback.format_exc())
            error = self.internal_error(data=to_text(exc, errors='surrogate_then_replace'))
            response = json.dumps(error)
        else:
            if isinstance(result, dict) and 'jsonrpc' in result:
                response = result
            else:
                response = self.response(result)

            try:
                response = json.dumps(response)
            except Exception as exc:
                display.vvv(traceback.format_exc())
                error = self.internal_error(data=to_text(exc, errors='surrogate_then_replace'))
                response = json.dumps(error)

    delattr(self, '_identifier')

    return response

def response(self, result=None):
    response = self.header()
    if isinstance(result, bytes):
        result = to_text(result)
    if not isinstance(result, str):
        response["result_type"] = "pickle"
        result = to_text(pickle.dumps(result), errors='surrogateescape')
    response['result'] = result
    return response

def error(self, code, message, data=None):
    response = self.header()
    error = {'code': code, 'message': message}
    if data:
        error['data'] = data
    response['error'] = error
    return response

def lock_decorator(attr='missing_lock_attr', lock=None):
    """This decorator is a generic implementation that allows you
    to either use a pre-defined instance attribute as the location
    of the lock, or to explicitly pass a lock object.

    This code was implemented with ``threading.Lock`` in mind, but
    may work with other locks, assuming that they function as
    context managers.

    When using ``attr``, the assumption is the first argument to
    the wrapped method, is ``self`` or ``cls``.

    Examples:

        @lock_decorator(attr='_callback_lock')
        def send_callback(...):

        @lock_decorator(lock=threading.Lock())
        def some_method(...):
    """
    def outer(func):
        @wraps(func)
        def inner(*args, **kwargs):
            # Python2 doesn't have ``nonlocal``
            # assign the actual lock to ``_lock``
            if lock is None:
                _lock = getattr(args[0], attr)
            else:
                _lock = lock
            with _lock:
                return func(*args, **kwargs)
        return inner
    return outer

def outer(func):
    @wraps(func)
    def inner(*args, **kwargs):
        # Python2 doesn't have ``nonlocal``
        # assign the actual lock to ``_lock``
        if lock is None:
            _lock = getattr(args[0], attr)
        else:
            _lock = lock
        with _lock:
            return func(*args, **kwargs)
    return inner

def inner(*args, **kwargs):
    # Python2 doesn't have ``nonlocal``
    # assign the actual lock to ``_lock``
    if lock is None:
        _lock = getattr(args[0], attr)
    else:
        _lock = lock
    with _lock:
        return func(*args, **kwargs)

def merge_fragment(target, source):

    for key, value in source.items():
        if key in target:
            # assumes both structures have same type
            if isinstance(target[key], MutableMapping):
                value.update(target[key])
            elif isinstance(target[key], MutableSet):
                value.add(target[key])
            elif isinstance(target[key], MutableSequence):
                value = sorted(frozenset(value + target[key]))
            else:
                raise Exception("Attempt to extend a documentation fragment, invalid type for %s" % key)
        target[key] = value

def add_collection_to_versions_and_dates(fragment, collection_name, is_module, return_docs=False):
    def add(options, option, collection_name_field):
        if collection_name_field not in options:
            options[collection_name_field] = collection_name

    _process_versions_and_dates(fragment, is_module, return_docs, add)

def remove_current_collection_from_versions_and_dates(fragment, collection_name, is_module, return_docs=False):
    def remove(options, option, collection_name_field):
        if options.get(collection_name_field) == collection_name:
            del options[collection_name_field]

    _process_versions_and_dates(fragment, is_module, return_docs, remove)

def add_fragments(doc, filename, fragment_loader, is_module=False, section='DOCUMENTATION'):

    if section not in _FRAGMENTABLE:
        raise AnsibleError(f"Invalid fragment section ({section}) passed to render {filename}, it can only be one of {_FRAGMENTABLE!r}")

    fragments = doc.pop('extends_documentation_fragment', [])

    if isinstance(fragments, str):
        fragments = fragments.split(',')

    unknown_fragments = []

    # doc_fragments are allowed to specify a fragment var other than DOCUMENTATION or RETURN
    # with a . separator; this is complicated by collections-hosted doc_fragments that
    # use the same separator. Assume it's collection-hosted normally first, try to load
    # as-specified. If failure, assume the right-most component is a var, split it off,
    # and retry the load.
    for fragment_slug in fragments:
        fragment_name = fragment_slug.strip()
        fragment_var = section

        fragment_class = fragment_loader.get(fragment_name)
        if fragment_class is None and '.' in fragment_slug:
            splitname = fragment_slug.rsplit('.', 1)
            fragment_name = splitname[0]
            fragment_var = splitname[1].upper()
            fragment_class = fragment_loader.get(fragment_name)

        if fragment_class is None:
            unknown_fragments.append(fragment_slug)
            continue

        # trust-tagged source propagates to loaded values; expressions and templates in config require trust
        fragment_yaml = _tags.TrustedAsTemplate().tag(getattr(fragment_class, fragment_var, None))
        if fragment_yaml is None:
            if fragment_var not in _FRAGMENTABLE:
                # if it's asking for something specific that's missing, that's an error
                unknown_fragments.append(fragment_slug)
                continue
            else:
                fragment_yaml = '{}'  # TODO: this is still an error later since we require 'options' below...

        fragment = yaml.load(_tags.Origin(path=filename).tag(fragment_yaml), Loader=AnsibleLoader)

        real_fragment_name = getattr(fragment_class, 'ansible_name')
        real_collection_name = '.'.join(real_fragment_name.split('.')[0:2]) if '.' in real_fragment_name else ''
        add_collection_to_versions_and_dates(fragment, real_collection_name, is_module=is_module, return_docs=(section == 'RETURN'))

        if section == 'DOCUMENTATION':
            # notes, seealso, options and attributes entries are specificly merged, but only occur in documentation section
            for doc_key in ['notes', 'seealso']:
                if doc_key in fragment:
                    entries = fragment.pop(doc_key)
                    if entries:
                        if doc_key not in doc:
                            doc[doc_key] = []
                        doc[doc_key].extend(entries)

            if 'options' not in fragment and 'attributes' not in fragment:
                raise AnsibleFragmentError("missing options or attributes in fragment (%s), possibly misformatted?: %s" % (fragment_name, filename))

            # ensure options themselves are directly merged
            for doc_key in ['options', 'attributes']:
                if doc_key in fragment:
                    if doc_key in doc:
                        try:
                            merge_fragment(doc[doc_key], fragment.pop(doc_key))
                        except Exception as e:
                            raise AnsibleFragmentError("%s %s (%s) of unknown type: %s" % (to_native(e), doc_key, fragment_name, filename))
                    else:
                        doc[doc_key] = fragment.pop(doc_key)

        # merge rest of the sections
        try:
            merge_fragment(doc, fragment)
        except Exception as e:
            raise AnsibleFragmentError("%s (%s) of unknown type: %s" % (to_native(e), fragment_name, filename))

    if unknown_fragments:
        raise AnsibleFragmentError('unknown doc_fragment(s) in file {0}: {1}'.format(filename, to_native(', '.join(unknown_fragments))))

def get_docstring(filename, fragment_loader, verbose=False, ignore_errors=False, collection_name=None, is_module=None, plugin_type=None):
    """
    DOCUMENTATION can be extended using documentation fragments loaded by the PluginLoader from the doc_fragments plugins.
    """

    if is_module is None:
        if plugin_type is None:
            is_module = False
        else:
            is_module = (plugin_type == 'module')
    else:
        # TODO deprecate is_module argument, now that we have 'type'
        pass

    data = read_docstring(filename, verbose=verbose, ignore_errors=ignore_errors)

    if data.get('doc', False):
        # add collection name to versions and dates
        if collection_name is not None:
            add_collection_to_versions_and_dates(data['doc'], collection_name, is_module=is_module)

        # add fragments to documentation
        add_fragments(data['doc'], filename, fragment_loader=fragment_loader, is_module=is_module, section='DOCUMENTATION')

    if data.get('returndocs', False):
        # add collection name to versions and dates
        if collection_name is not None:
            add_collection_to_versions_and_dates(data['returndocs'], collection_name, is_module=is_module, return_docs=True)

        # add fragments to return
        add_fragments(data['returndocs'], filename, fragment_loader=fragment_loader, is_module=is_module, section='RETURN')

    return data['doc'], data['plainexamples'], data['returndocs'], data['metadata']

def get_versioned_doclink(path):
    """
    returns a versioned documentation link for the current Ansible major.minor version; used to generate
    in-product warning/error links to the configured DOCSITE_ROOT_URL
    (eg, https://docs.ansible.com/ansible/2.8/somepath/doc.html)

    :param path: relative path to a document under docs/docsite/rst;
    :return: absolute URL to the specified doc for the current version of Ansible
    """
    path = to_native(path)
    try:
        base_url = C.config.get_config_value('DOCSITE_ROOT_URL')
        if not base_url.endswith('/'):
            base_url += '/'
        if path.startswith('/'):
            path = path[1:]
        split_ver = ansible_version.split('.')
        if len(split_ver) < 3:
            raise RuntimeError('invalid version ({0})'.format(ansible_version))

        doc_version = '{0}.{1}'.format(split_ver[0], split_ver[1])

        # check to see if it's a X.Y.0 non-rc prerelease or dev release, if so, assume devel (since the X.Y doctree
        # isn't published until beta-ish)
        if split_ver[2].startswith('0'):
            # exclude rc; we should have the X.Y doctree live by rc1
            if any((pre in split_ver[2]) for pre in ['a', 'b']) or len(split_ver) > 3 and 'dev' in split_ver[3]:
                doc_version = 'devel'

        return '{0}{1}/{2}'.format(base_url, doc_version, path)
    except Exception as ex:
        return '(unable to create versioned doc link for path {0}: {1})'.format(path, to_native(ex))

def find_plugin_docfile(plugin, plugin_type, loader):
    """  if the plugin lives in a non-python file (eg, win_X.ps1), require the corresponding 'sidecar' file for docs """

    context = loader.find_plugin_with_context(plugin, ignore_deprecated=False, check_aliases=True)
    if (not context or not context.resolved) and plugin_type in ('filter', 'test'):
        # should only happen for filters/test
        plugin_obj, context = loader.get_with_context(plugin)

    if not context or not context.resolved:
        raise AnsiblePluginNotFound('%s was not found' % (plugin), plugin_load_context=context)

    docfile = Path(context.plugin_resolved_path)
    if docfile.suffix not in C.DOC_EXTENSIONS:
        # only look for adjacent if plugin file does not support documents
        filenames = _find_adjacent(docfile, plugin, C.DOC_EXTENSIONS)
        filename = filenames[0] if filenames else None
    else:
        filename = to_native(docfile)

    if filename is None:
        raise AnsibleError('%s cannot contain DOCUMENTATION nor does it have a companion documentation file' % (plugin))

    return filename, context

def get_plugin_docs(plugin, plugin_type, loader, fragment_loader, verbose):

    # find plugin doc file, if it doesn't exist this will throw error, we let it through
    # can raise exception and short circuit when 'not found'
    filename, context = find_plugin_docfile(plugin, plugin_type, loader)
    collection_name = context.plugin_resolved_collection

    try:
        docs = get_docstring(filename, fragment_loader, verbose=verbose, collection_name=collection_name, plugin_type=plugin_type)
    except Exception as ex:
        raise AnsibleParserError(f'{plugin_type} plugin {plugin!r} did not contain a DOCUMENTATION attribute in {filename!r}.') from ex

    # no good? try adjacent
    if not docs[0]:
        for newfile in _find_adjacent(filename, plugin, C.DOC_EXTENSIONS):
            try:
                docs = get_docstring(newfile, fragment_loader, verbose=verbose, collection_name=collection_name, plugin_type=plugin_type)
                filename = newfile
                if docs[0] is not None:
                    break
            except Exception as ex:
                raise AnsibleParserError(f'{plugin_type} plugin {plugin!r} adjacent file did not contain a DOCUMENTATION attribute in {filename!r}.') from ex

    # add extra data to docs[0] (aka 'DOCUMENTATION')
    if docs[0] is None:
        raise AnsibleParserError(f'No documentation available for {plugin_type} plugin {plugin!r} in {filename!r}.')

    docs[0]['filename'] = filename
    docs[0]['collection'] = collection_name
    docs[0]['plugin_name'] = context.resolved_fqcn

    return docs

def process_deprecation(deprecation, top_level=False):
    collection_name = 'removed_from_collection' if top_level else 'collection_name'
    if not isinstance(deprecation, MutableMapping):
        return
    if (is_module or top_level) and 'removed_in' in deprecation:  # used in module deprecations
        callback(deprecation, 'removed_in', collection_name)
    if 'removed_at_date' in deprecation:
        callback(deprecation, 'removed_at_date', collection_name)
    if not (is_module or top_level) and 'version' in deprecation:  # used in plugin option deprecations
        callback(deprecation, 'version', collection_name)

def process_option_specifiers(specifiers):
    for specifier in specifiers:
        if not isinstance(specifier, MutableMapping):
            continue
        if 'version_added' in specifier:
            callback(specifier, 'version_added', 'version_added_collection')
        if isinstance(specifier.get('deprecated'), MutableMapping):
            process_deprecation(specifier['deprecated'])

def process_options(options):
    for option in options.values():
        if not isinstance(option, MutableMapping):
            continue
        if 'version_added' in option:
            callback(option, 'version_added', 'version_added_collection')
        if not is_module:
            if isinstance(option.get('env'), list):
                process_option_specifiers(option['env'])
            if isinstance(option.get('ini'), list):
                process_option_specifiers(option['ini'])
            if isinstance(option.get('vars'), list):
                process_option_specifiers(option['vars'])
            if isinstance(option.get('deprecated'), MutableMapping):
                process_deprecation(option['deprecated'])
        if isinstance(option.get('suboptions'), MutableMapping):
            process_options(option['suboptions'])

def process_return_values(return_values):
    for return_value in return_values.values():
        if not isinstance(return_value, MutableMapping):
            continue
        if 'version_added' in return_value:
            callback(return_value, 'version_added', 'version_added_collection')
        if isinstance(return_value.get('contains'), MutableMapping):
            process_return_values(return_value['contains'])

def process_attributes(attributes):
    for attribute in attributes.values():
        if not isinstance(attribute, MutableMapping):
            continue
        if 'version_added' in attribute:
            callback(attribute, 'version_added', 'version_added_collection')

def unicode_wrap(func, *args, **kwargs):
    """If a function returns a string, force it to be a text string.

    Use with partial to ensure that filter plugins will return text values.
    """
    return to_text(func(*args, **kwargs), nonstring='passthru')

def wrap_var(v):
    # maintain backward compat by recursively *un* marking TrustedAsTemplate
    if v is None or isinstance(v, AnsibleUnsafe):
        return v

    if isinstance(v, Mapping):
        v = _wrap_dict(v)
    elif isinstance(v, Set):
        v = _wrap_set(v)
    elif is_sequence(v):
        v = _wrap_sequence(v)
    elif isinstance(v, bytes):
        v = AnsibleUnsafeBytes(v)
    elif isinstance(v, str):
        v = AnsibleUnsafeText(v)

    return v

def combine_vars(a, b, merge=None):
    """
    Return a copy of dictionaries of variables based on configured hash behavior
    """

    if merge or merge is None and C.DEFAULT_HASH_BEHAVIOUR == "merge":
        return merge_hash(a, b)

    # HASH_BEHAVIOUR == 'replace'
    _validate_mutable_mappings(a, b)
    result = a | b
    return result

def load_extra_vars(loader: DataLoader) -> dict[str, t.Any]:

    if not getattr(load_extra_vars, 'extra_vars', None):
        extra_vars: dict[str, t.Any] = {}
        for extra_vars_opt in context.CLIARGS.get('extra_vars', tuple()):
            extra_vars_opt = to_text(extra_vars_opt, errors='surrogate_or_strict')
            if extra_vars_opt is None or not extra_vars_opt:
                continue

            if extra_vars_opt.startswith(u"@"):
                # Argument is a YAML file (JSON is a subset of YAML)
                data = loader.load_from_file(extra_vars_opt[1:], trusted_as_template=True)
            elif extra_vars_opt[0] in [u'/', u'.']:
                raise AnsibleOptionsError("Please prepend extra_vars filename '%s' with '@'" % extra_vars_opt)
            elif extra_vars_opt[0] in [u'[', u'{']:
                # Arguments as YAML
                data = loader.load(extra_vars_opt)
            else:
                # Arguments as Key-value
                data = parse_kv(extra_vars_opt)

            if isinstance(data, MutableMapping):
                extra_vars = combine_vars(extra_vars, data)
            else:
                raise AnsibleOptionsError("Invalid extra vars data supplied. '%s' could not be made into a dictionary" % extra_vars_opt)

        load_extra_vars.extra_vars = extra_vars

    return load_extra_vars.extra_vars

def load_options_vars(version):

    if not getattr(load_options_vars, 'options_vars', None):
        if version is None:
            version = 'Unknown'
        options_vars = {'ansible_version': version}
        attrs = {'check': 'check_mode',
                 'diff': 'diff_mode',
                 'forks': 'forks',
                 'inventory': 'inventory_sources',
                 'skip_tags': 'skip_tags',
                 'subset': 'limit',
                 'tags': 'run_tags',
                 'verbosity': 'verbosity'}

        for attr, alias in attrs.items():
            opt = context.CLIARGS.get(attr)
            if opt is not None:
                options_vars['ansible_%s' % alias] = opt

        setattr(load_options_vars, 'options_vars', options_vars)

    return load_options_vars.options_vars

def validate_variable_name(name: object) -> None:
    """Validate the given variable name is valid, raising an AnsibleError if it is not."""
    if isinstance(name, str) and name.isidentifier() and name.isascii() and name not in _jinja_bits.JINJA_KEYWORDS:
        return

    if isinstance(name, (str, int, float, bool, type(None))):
        key_description = f'name {str(name)!r}'  # show common scalar key names as strings
    else:
        key_description = 'name'

    if not isinstance(name, str):
        key_description += f' of type {native_type_name(name)!r}'  # show the type name of all non-string keys

    raise AnsibleError(
        message=f'Invalid variable {key_description}.',
        help_text='Variable names must be strings starting with a letter or underscore character, and contain only letters, numbers and underscores.',
        obj=name,
    )

def from_loose_version(loose_version):
    """This method is designed to take a ``LooseVersion``
    and attempt to construct a ``SemanticVersion`` from it

    This is useful where you want to do simple version math
    without requiring users to provide a compliant semver.
    """
    if not isinstance(loose_version, LooseVersion):
        raise ValueError("%r is not a LooseVersion" % loose_version)

    try:
        version = loose_version.version[:]
    except AttributeError:
        raise ValueError("%r is not a LooseVersion" % loose_version)

    extra_idx = 3
    for marker in ('-', '+'):
        try:
            idx = version.index(marker)
        except ValueError:
            continue
        else:
            if idx < extra_idx:
                extra_idx = idx
    version = version[:extra_idx]

    if version and set(type(v) for v in version) != set((int,)):
        raise ValueError("Non integer values in %r" % loose_version)

    # Extra is everything to the right of the core version
    extra = re.search('[+-].+$', loose_version.vstring)

    version = version + [0] * (3 - len(version))
    return SemanticVersion(
        '%s%s' % (
            '.'.join(str(v) for v in version),
            extra.group(0) if extra else ''
        )
    )

def parse(self, vstring):
    match = SEMVER_RE.match(vstring)
    if not match:
        raise ValueError("invalid semantic version '%s'" % vstring)

    (major, minor, patch, prerelease, buildmetadata) = match.group(1, 2, 3, 4, 5)
    self.vstring = vstring
    self.major = int(major)
    self.minor = int(minor)
    self.patch = int(patch)

    if prerelease:
        self.prerelease = tuple(_Numeric(x) if x.isdigit() else _Alpha(x) for x in prerelease.split('.'))
    if buildmetadata:
        self.buildmetadata = tuple(_Numeric(x) if x.isdigit() else _Alpha(x) for x in buildmetadata.split('.'))

def get_xml_element(self) -> ET.Element:
    """Return an XML element representing this instance."""
    element = ET.Element('testcase', self.get_attributes())

    if self.skipped:
        ET.SubElement(element, 'skipped').text = self.skipped

    element.extend([error.get_xml_element() for error in self.errors])
    element.extend([failure.get_xml_element() for failure in self.failures])

    if self.system_out:
        ET.SubElement(element, 'system-out').text = self.system_out

    if self.system_err:
        ET.SubElement(element, 'system-err').text = self.system_err

    return element

def get_xml_element(self) -> ET.Element:
    """Return an XML element representing this instance."""
    element = ET.Element('testsuite', self.get_attributes())

    if self.properties:
        ET.SubElement(element, 'properties').extend([ET.Element('property', dict(name=name, value=value)) for name, value in self.properties.items()])

    element.extend([test_case.get_xml_element() for test_case in self.cases])

    if self.system_out:
        ET.SubElement(element, 'system-out').text = self.system_out

    if self.system_err:
        ET.SubElement(element, 'system-err').text = self.system_err

    return element

def fire(self, *args, **kwargs):
    for h in self._handlers:
        try:
            h(*args, **kwargs)
        except Exception as ex:
            if self._on_exception(h, ex, *args, **kwargs):
                raise

def collection_finder(cls, value):
    if cls._collection_finder:
        raise ValueError('an AnsibleCollectionFinder has already been configured')

    cls._collection_finder = value

def files(self):
    package = self._package
    parts = package.split('.')
    is_ns = parts[0] == 'ansible_collections' and len(parts) < 3

    if isinstance(package, str):
        if is_ns:
            # Don't use ``spec_from_loader`` here, because that will point
            # to exactly 1 location for a namespace. Use ``find_spec``
            # to get a list of all locations for the namespace
            package = find_spec(package)
        else:
            package = spec_from_loader(package, self._loader)
    elif not isinstance(package, ModuleType):
        raise TypeError('Expected string or module, got %r' % package.__class__.__name__)

    self._ensure_package(package)
    if is_ns:
        return _AnsibleNSTraversable(*package.submodule_search_locations)
    return pathlib.Path(self._get_path(package)).parent

def find_spec(self, fullname, path, target=None):
    loader = self._get_loader(fullname, path)

    if loader is None:
        return None

    spec = spec_from_loader(fullname, loader)
    if spec is not None and hasattr(loader, '_subpackage_search_paths'):
        spec.submodule_search_locations = loader._subpackage_search_paths
    return spec

def find_module(self, fullname, path=None):
    # we ignore the passed in path here- use what we got from the path hook init
    finder = self._get_finder(fullname)

    if finder is None:
        return None
    elif isinstance(finder, FileFinder):
        # this codepath is erroneously used under some cases in py3,
        # and the find_module method on FileFinder does not accept the path arg
        # see https://github.com/pypa/setuptools/pull/2918
        return finder.find_module(fullname)
    else:
        return finder.find_module(fullname, path=[self._pathctx])

def find_spec(self, fullname, target=None):
    split_name = fullname.split('.')
    toplevel_pkg = split_name[0]

    finder = self._get_finder(fullname)

    if finder is None:
        return None
    elif toplevel_pkg == 'ansible_collections':
        return finder.find_spec(fullname, path=[self._pathctx])
    else:
        return finder.find_spec(fullname)

def create_module(self, spec):
    # short-circuit redirect; we've already imported the redirected module, so just alias it and return it
    if self._redirect_module:
        return self._redirect_module
    else:
        return None

def get_source(self, fullname):
    if self._decoded_source:
        return self._decoded_source
    if fullname != self._fullname:
        raise ValueError('this loader cannot load source for {0}, only {1}'.format(fullname, self._fullname))
    if not self._source_code_path:
        return None
    # FIXME: what do we want encoding/newline requirements to be?
    self._decoded_source = self.get_data(self._source_code_path)
    return self._decoded_source

def exec_module(self, module):
    # should never see this
    if not self._redirect:
        raise ValueError('no redirect found for {0}'.format(module.__spec__.name))

    # Replace the module with the redirect
    sys.modules[module.__spec__.name] = import_module(self._redirect)

def try_parse_fqcr(ref, ref_type):
    """
    Attempt to parse a string as a fully-qualified collection reference, returning None on failure (instead of raising an error)
    :param ref: collection reference to parse (a valid ref is of the form 'ns.coll.resource' or 'ns.coll.subdir1.subdir2.resource')
    :param ref_type: the type of the reference, eg 'module', 'role', 'doc_fragment'
    :return: a populated AnsibleCollectionRef object on successful parsing, else None
    """
    try:
        return AnsibleCollectionRef.from_fqcr(ref, ref_type)
    except ValueError:
        pass

def legacy_plugin_dir_to_plugin_type(legacy_plugin_dir_name):
    """
    Utility method to convert from a PluginLoader dir name to a plugin ref_type
    :param legacy_plugin_dir_name: PluginLoader dir name (eg, 'action_plugins', 'library')
    :return: the corresponding plugin ref_type (eg, 'action', 'role')
    """
    if legacy_plugin_dir_name is None:
        plugin_type = None
    else:
        legacy_plugin_dir_name = _to_text(legacy_plugin_dir_name)

        plugin_type = legacy_plugin_dir_name.removesuffix(u'_plugins')

    if plugin_type == u'library':
        plugin_type = u'modules'

    if plugin_type not in AnsibleCollectionRef.VALID_REF_TYPES:
        raise ValueError(f'{legacy_plugin_dir_name!r} cannot be mapped to a valid collection ref type')

    return plugin_type

def is_valid_fqcr(ref, ref_type=None):
    """
    Validates if is string is a well-formed fully-qualified collection reference (does not look up the collection itself)
    :param ref: candidate collection reference to validate (a valid ref is of the form 'ns.coll.resource' or 'ns.coll.subdir1.subdir2.resource')
    :param ref_type: optional reference type to enable deeper validation, eg 'module', 'role', 'doc_fragment'
    :return: True if the collection ref passed is well-formed, False otherwise
    """

    ref = _to_text(ref)

    if not ref_type:
        return bool(re.match(AnsibleCollectionRef.VALID_FQCR_RE, ref))

    return bool(AnsibleCollectionRef.try_parse_fqcr(ref, ref_type))

def is_valid_collection_name(collection_name):
    """
    Validates if the given string is a well-formed collection name (does not look up the collection itself)
    :param collection_name: candidate collection name to validate (a valid name is of the form 'ns.collname')
    :return: True if the collection name passed is well-formed, False otherwise
    """

    collection_name = _to_text(collection_name)

    if collection_name.count(u'.') != 1:
        return False

    return all(
        # NOTE: keywords and identifiers are different in different Pythons
        not iskeyword(ns_or_name) and ns_or_name.isidentifier()
        for ns_or_name in collection_name.split(u'.')
    )

def strip_internal_keys(dirty, exceptions=None):
    # All keys starting with _ansible_ are internal, so change the 'dirty' mapping and remove them.

    if exceptions is None:
        exceptions = tuple()

    if isinstance(dirty, MutableSequence):

        for element in dirty:
            if isinstance(element, (MutableMapping, MutableSequence)):
                strip_internal_keys(element, exceptions=exceptions)

    elif isinstance(dirty, MutableMapping):

        # listify to avoid updating dict while iterating over it
        for k in list(dirty.keys()):
            if isinstance(k, str):
                if k.startswith('_ansible_') and k not in exceptions:
                    del dirty[k]
                    continue

            if isinstance(dirty[k], (MutableMapping, MutableSequence)):
                strip_internal_keys(dirty[k], exceptions=exceptions)
    else:
        raise AnsibleError("Cannot strip invalid keys from %s" % type(dirty))

    return dirty

def remove_internal_keys(data):
    """
    More nuanced version of strip_internal_keys
    """
    for key in list(data.keys()):
        if (key.startswith('_ansible_') and key != '_ansible_parsed') or key in C.INTERNAL_RESULT_KEYS:
            display.warning("Removed unexpected internal key in module return: %s = %s" % (key, data[key]))
            del data[key]

    # remove bad/empty internal keys
    for key in ['warnings', 'deprecations']:
        if key in data and not data[key]:
            del data[key]

    # cleanse fact values that are allowed from actions but not modules
    for key in list(data.get('ansible_facts', {}).keys()):
        if key.startswith('discovered_interpreter_') or key.startswith('ansible_discovered_interpreter_'):
            del data['ansible_facts'][key]

def namespace_facts(facts):
    """ return all facts inside 'ansible_facts' w/o an ansible_ prefix """
    deprefixed = {}
    for k in facts:
        if k.startswith('ansible_') and k not in ('ansible_local',):
            deprefixed[k[8:]] = module_response_deepcopy(facts[k])
        else:
            deprefixed[k] = module_response_deepcopy(facts[k])

    return {'ansible_facts': deprefixed}

def raw_get(self, host_name: str) -> dict[str, t.Any] | Marker:
    """
    Similar to __getitem__, however the returned data is not run through
    the templating engine to expand variables in the hostvars.
    """
    # does not use inventory.hosts, so it can create localhost on demand
    host = self._inventory.get_host(host_name)

    if host is None:
        from ansible._internal._templating import _jinja_bits

        return _jinja_bits._undef(f"hostvars[{host_name!r}]")

    return self._variable_manager.get_vars(host=host, include_hostvars=False)

def preprocess_vars(a):
    """
    Ensures that vars contained in the parameter passed in are
    returned as a list of dictionaries, to ensure for instance
    that vars loaded from a file conform to an expected state.
    """

    # FIXME: this does not properly handle omit, undefined, or dynamic structure from templated `vars` ; templating should be done earlier
    if a is None:
        return None
    elif not isinstance(a, list):
        data = [a]
    else:
        data = a

    for item in data:
        if not isinstance(item, MutableMapping):
            raise AnsibleParserError(
                message="Invalid variable file contents.",
                obj=item,
                help_text="Variable files must contain either a dictionary of variables, or a list of dictionaries.",
            )

    return data

def get_delegated_vars_and_hostname(self, templar, task, variables):
    """Get the delegated_vars for an individual task invocation, which may be in the context
    of an individual loop iteration.

    Not used directly be VariableManager, but used primarily within TaskExecutor
    """
    delegated_vars = {}
    delegated_host_name = ...  # sentinel value distinct from empty/None, which are errors

    if task.delegate_to:
        try:
            delegated_host_name = templar.template(task.delegate_to)
        except AnsibleValueOmittedError:
            pass

    # bypass for unspecified value/omit
    if delegated_host_name is ...:
        return delegated_vars, None

    if not delegated_host_name:
        raise AnsibleError('Empty hostname produced from delegate_to: "%s"' % task.delegate_to)

    delegated_host = self._inventory.get_host(delegated_host_name)
    if delegated_host is None:
        for h in self._inventory.get_hosts(ignore_limits=True, ignore_restrictions=True):
            # check if the address matches, or if both the delegated_to host
            # and the current host are in the list of localhost aliases
            if h.address == delegated_host_name:
                delegated_host = h
                break
        else:
            delegated_host = Host(name=delegated_host_name)

    delegated_vars['ansible_delegated_vars'] = {
        delegated_host_name: self.get_vars(
            play=task.get_play(),
            host=delegated_host,
            task=task,
            include_hostvars=True,
        )
    }
    delegated_vars['ansible_delegated_vars'][delegated_host_name]['inventory_hostname'] = variables.get('inventory_hostname')

    return delegated_vars, delegated_host_name

def clear_facts(self, hostname):
    """
    Clears the facts for a host
    """
    try:
        self._fact_cache.delete(hostname)
    except KeyError:
        pass

def set_host_facts(self, host, facts):
    """
    Sets or updates the given facts for a host in the fact cache.
    """

    if not isinstance(facts, Mapping):
        raise AnsibleAssertionError("the type of 'facts' to set for host_facts should be a Mapping but is a %s" % type(facts))

    warn_if_reserved(facts)

    try:
        host_cache = self._fact_cache.get(host)
    except KeyError:
        # We get to set this as new
        host_cache = facts
    else:
        if not isinstance(host_cache, MutableMapping):
            raise TypeError('The object retrieved for {0} must be a MutableMapping but was'
                            ' a {1}'.format(host, type(host_cache)))
        # Update the existing facts
        host_cache |= facts

    # Save the facts back to the backing store
    self._fact_cache.set(host, host_cache)

def set_nonpersistent_facts(self, host, facts):
    """
    Sets or updates the given facts for a host in the fact cache.
    """

    if not isinstance(facts, Mapping):
        raise AnsibleAssertionError("the type of 'facts' to set for nonpersistent_facts should be a Mapping but is a %s" % type(facts))

    warn_if_reserved(facts)

    try:
        self._nonpersistent_fact_cache[host] |= facts
    except KeyError:
        self._nonpersistent_fact_cache[host] = facts

def set_host_variable(self, host, varname, value):
    """
    Sets a value in the vars_cache for a host.
    """

    warn_if_reserved([varname])

    if host not in self._vars_cache:
        self._vars_cache[host] = dict()

    if varname in self._vars_cache[host] and isinstance(self._vars_cache[host][varname], MutableMapping) and isinstance(value, MutableMapping):
        self._vars_cache[host] = combine_vars(self._vars_cache[host], {varname: value})
    else:
        self._vars_cache[host][varname] = value

def plugins_by_groups():
    """
        merges all plugin sources by group,
        This should be used instead, NOT in combination with the other groups_plugins* functions
    """
    data = {}
    for group in host_groups:
        data[group] = _combine_and_track(data[group], _plugins_inventory(group), "inventory group_vars for '%s'" % group)
        data[group] = _combine_and_track(data[group], _plugins_play(group), "playbook group_vars for '%s'" % group)
    return data

def get_vars_from_path(loader, path, entities, stage):
    data = {}
    if vars_loader._paths is None:
        # cache has been reset, reload all()
        _prime_vars_loader()

    for plugin_name in vars_loader._plugin_instance_cache:
        if (plugin := vars_loader.get(plugin_name)) is None:
            continue

        collection = '.' in plugin.ansible_name and not plugin.ansible_name.startswith('ansible.builtin.')
        # Warn if a collection plugin has REQUIRES_ENABLED because it has no effect.
        if collection and hasattr(plugin, 'REQUIRES_ENABLED'):
            display.warning(
                "Vars plugins in collections must be enabled to be loaded, REQUIRES_ENABLED is not supported. "
                "This should be removed from the plugin %s." % plugin.ansible_name
            )

        if not _plugin_should_run(plugin, stage):
            continue

        if (new_vars := plugin.get_vars(loader, path, entities)) != {}:
            data = combine_vars(data, new_vars)

    return data

def get_reserved_names(include_private: bool = True) -> set[str]:
    """ this function returns the list of reserved names associated with play objects"""

    public = set(TemplateEngine().environment.globals.keys())
    private = set()

    # FIXME: find a way to 'not hardcode', possibly need role deps/includes
    class_list = [Play, Role, Block, Task]

    for aclass in class_list:
        # build ordered list to loop over and dict with attributes
        for name, attr in aclass.fattributes.items():
            if attr.private:
                private.add(name)
            else:
                public.add(name)

    # local_action is implicit with action
    if 'action' in public:
        public.add('local_action')

    # loop implies with_
    # FIXME: remove after with_ is not only deprecated but removed
    if 'loop' in private or 'loop' in public:
        public.add('with_')

    if include_private:
        result = public.union(private)
    else:
        result = public

    result.discard('gather_subset')

    return result

def warn_if_reserved(myvars: c.Iterable[str], additional: c.Iterable[str] | None = None) -> None:
    """Issue a warning for any variable which conflicts with an internally reserved name."""
    if additional is None:
        reserved = _RESERVED_NAMES
    else:
        reserved = _RESERVED_NAMES.union(additional)

    varnames = set(myvars)
    varnames.discard('vars')  # we add this one internally, so safe to ignore

    if conflicts := varnames.intersection(reserved):
        # Ensure the varname used for obj is the tagged one from myvars and not the untagged one from reserved.
        # This can occur because tags do not affect value equality, and intersection can return values from either the left or right side.
        for varname in (name for name in myvars if name in conflicts):
            display.warning(f'Found variable using reserved name {varname!r}.', obj=varname)

def format_message(summary: _messages.SummaryBase, include_traceback: bool) -> str:
    if isinstance(summary, _messages.DeprecationSummary):
        deprecation_message = get_deprecation_message_with_plugin_info(
            msg=summary.event.msg,
            version=summary.version,
            date=summary.date,
            deprecator=summary.deprecator,
        )

        event = dataclasses.replace(summary.event, msg=deprecation_message)
    else:
        event = summary.event

    return _event_formatting.format_event(event, include_traceback)

def get_deprecation_message_with_plugin_info(
    *,
    msg: str,
    version: str | None,
    removed: bool = False,
    date: str | None,
    deprecator: _messages.PluginInfo | None,
) -> str:
    """Internal use only. Return a deprecation message and help text for display."""
    # DTFIX-FUTURE: the logic for omitting date/version doesn't apply to the payload, so it shows up in vars in some cases when it should not

    if removed:
        removal_fragment = 'This feature was removed'
    else:
        removal_fragment = 'This feature will be removed'

    if not deprecator or not deprecator.type:
        # indeterminate has no resolved_name or type
        # collections have a resolved_name but no type
        collection = deprecator.resolved_name if deprecator else None
        plugin_fragment = ''
    elif deprecator.resolved_name == 'ansible.builtin':
        # core deprecations from base classes (the API) have no plugin name, only 'ansible.builtin'
        plugin_type_name = str(deprecator.type) if deprecator.type is _messages.PluginType.MODULE else f'{deprecator.type} plugin'

        collection = deprecator.resolved_name
        plugin_fragment = f'the {plugin_type_name} API'
    else:
        parts = deprecator.resolved_name.split('.')
        plugin_name = parts[-1]
        plugin_type_name = str(deprecator.type) if deprecator.type is _messages.PluginType.MODULE else f'{deprecator.type} plugin'

        collection = '.'.join(parts[:2]) if len(parts) > 2 else None
        plugin_fragment = f'{plugin_type_name} {plugin_name!r}'

    if collection and plugin_fragment:
        plugin_fragment += ' in'

    if collection == 'ansible.builtin':
        collection_fragment = 'ansible-core'
    elif collection:
        collection_fragment = f'collection {collection!r}'
    else:
        collection_fragment = ''

    if not collection:
        when_fragment = 'in the future' if not removed else ''
    elif date:
        when_fragment = f'in a release after {date}'
    elif version:
        when_fragment = f'version {version}'
    else:
        when_fragment = 'in a future release' if not removed else ''

    if plugin_fragment or collection_fragment:
        from_fragment = 'from'
    else:
        from_fragment = ''

    deprecation_msg = ' '.join(f for f in [removal_fragment, from_fragment, plugin_fragment, collection_fragment, when_fragment] if f) + '.'

    return join_sentences(msg, deprecation_msg)

def join_sentences(first: str | None, second: str | None) -> str:
    """Join two sentences together."""
    first = (first or '').strip()
    second = (second or '').strip()

    if first and first[-1] not in ('!', '?', '.'):
        first += '.'

    if second and second[-1] not in ('!', '?', '.'):
        second += '.'

    if first and not second:
        return first

    if not first and second:
        return second

    return ' '.join((first, second))

def capture(self, warning: _messages.WarningSummary) -> None:
    """Add the warning/deprecation to the context if it has not already been seen by this context."""
    if warning in self._seen:
        return

    self._seen.add(warning)

    if isinstance(warning, _messages.DeprecationSummary):
        self._deprecation_warnings.append(warning)
    else:
        self._warnings.append(warning)

def format_event(event: _messages.Event, include_traceback: bool) -> str:
    """Format an event into a verbose message and traceback."""
    msg = format_event_verbose_message(event)

    if include_traceback:
        msg += '\n' + format_event_traceback(event)

    msg = msg.strip()

    if '\n' in msg:
        msg += '\n\n'
    else:
        msg += '\n'

    return msg

def format_event_traceback(event: _messages.Event) -> str:
    """Format an event into a traceback."""
    segments: list[str] = []

    while event:
        segment = event.formatted_traceback or '(traceback missing)\n'

        if event.events:
            child_tracebacks = [format_event_traceback(child) for child in event.events]
            segment += _format_event_children("Sub-Traceback", child_tracebacks)

        segments.append(segment)

        if event.chain:
            segments.append(f'\n{event.chain.traceback_reason}\n\n')

            event = event.chain.event
        else:
            event = None

    return ''.join(reversed(segments))

def format_event_verbose_message(event: _messages.Event) -> str:
    """
    Format an event into a verbose message.
    Help text, contextual information and sub-events will be included.
    """
    segments: list[str] = []
    original_event = event

    while event:
        messages = [event.msg]
        chain: _messages.EventChain = event.chain

        while chain and chain.follow:
            if chain.event.events:
                break  # do not collapse a chained event with sub-events, since they would be lost

            if chain.event.formatted_source_context or chain.event.help_text:
                if chain.event.formatted_source_context != event.formatted_source_context or chain.event.help_text != event.help_text:
                    break  # do not collapse a chained event with different details, since they would be lost

            if chain.event.chain and chain.msg_reason != chain.event.chain.msg_reason:
                break  # do not collapse a chained event which has a chain with a different msg_reason

            messages.append(chain.event.msg)

            chain = chain.event.chain

        msg = _event_utils.deduplicate_message_parts(messages)
        segment = '\n'.join(_get_message_lines(msg, event.help_text, event.formatted_source_context)) + '\n'

        if event.events:
            child_msgs = [format_event_verbose_message(child) for child in event.events]
            segment += _format_event_children("Sub-Event", child_msgs)

        segments.append(segment)

        if chain and chain.follow:
            segments.append(f'\n{chain.msg_reason}\n\n')

            event = chain.event
        else:
            event = None

    if len(segments) > 1:
        segments.insert(0, _event_utils.format_event_brief_message(original_event) + '\n\n')

    return ''.join(segments)

def finalize(self, callback: TaskArgsFinalizerCallback, context: t.Any) -> dict[str, t.Any]:
    resolved_layers: list[c.Mapping[str, t.Any]] = []

    for layer in self._args_layers:
        if isinstance(layer, (str, _vault.EncryptedString)):  # EncryptedString can hide a template
            if constants.config.get_config_value('INJECT_FACTS_AS_VARS'):
                Display().warning(
                    "Using a template for task args is unsafe in some situations "
                    "(see https://docs.ansible.com/ansible/devel/reference_appendices/faq.html#argsplat-unsafe).",
                    obj=layer,
                )

            resolved_layer = self._templar.resolve_to_container(layer, options=_engine.TemplateOptions(value_for_omit={}))
        else:
            resolved_layer = layer

        if not isinstance(resolved_layer, dict):
            raise AnsibleError(f'Task args must resolve to a {native_type_name(dict)!r} not {native_type_name(resolved_layer)!r}.', obj=layer)

        resolved_layers.append(resolved_layer)

    ct = TaskArgsChainTemplar(*reversed(resolved_layers), templar=self._templar, callback=callback, context=context)

    return ct.as_dict()

def finalize_task_arg(cls, name: str, value: t.Any, templar: TemplateEngine, context: t.Any) -> t.Any:
    if name == 'expression':
        return value

    return super().finalize_task_arg(name, value, templar, context)

def run(self, tmp=None, task_vars=None):
    # accepts a list of literal expressions (no templating), evaluates with no failure on undefined, returns all results
    _vr, args = self.validate_argument_spec(
        argument_spec=dict(
            expression=dict(type=_check_type_list_strict, elements=_check_type_str_no_conversion, required=True),
        ),
    )

    with ReplacingMarkerBehavior.warning_context() as replacing_behavior:
        templar = self._templar._engine.extend(marker_behavior=replacing_behavior)

        return dict(
            _ansible_verbose_always=True,
            expression_result=[templar.evaluate_expression(expression) for expression in args['expression']],
        )

def dump_object(value: t.Any) -> object:
    """Internal filter to convert objects not supported by JSON to types which are."""
    if dataclasses.is_dataclass(value):
        return dataclasses.asdict(value)  # type: ignore[arg-type]

    if isinstance(value, ExceptionMarker):
        return dict(
            exception=value._as_exception(),
        )

    return value

def python_literal_eval(value: object, ignore_errors=False) -> object:
    try:
        if isinstance(value, str):
            return ast.literal_eval(value)

        raise AnsibleTypeError("The `value` to eval must be a string.", obj=value)
    except Exception:
        if ignore_errors:
            return value

        raise

def unmask(value: object, type_names: str | list[str]) -> object:
    """
    Internal filter to suppress automatic type transformation in Jinja (e.g., WarningSummary, DeprecationSummary, ErrorSummary).
    Lazy collection caching is in play - the first attempt to access a value in a given lazy container must be with unmasking in place, or the transformed value
    will already be cached.
    """
    validate_arg_type("type_names", type_names, (str, list))

    if isinstance(type_names, str):
        check_type_names = [type_names]
    else:
        check_type_names = type_names

    valid_type_names = {key.__name__ for key in _type_transform_mapping}
    invalid_type_names = [type_name for type_name in check_type_names if type_name not in valid_type_names]

    if invalid_type_names:
        raise AnsibleError(f'Unknown type name(s): {", ".join(invalid_type_names)}', obj=type_names)

    result: object

    if isinstance(value, _AnsibleLazyTemplateMixin):
        result = copy.copy(value)
        result._lazy_options = dataclasses.replace(
            result._lazy_options,
            unmask_type_names=result._lazy_options.unmask_type_names | frozenset(check_type_names),
        )
    else:
        result = value

    return result

def get_or_create_tag(cls, value: t.Any, path: str | os.PathLike | None) -> Origin:
    """Return the tag from the given value, creating a tag from the provided path if no tag was found."""
    if not (origin := cls.get_tag(value)):
        if path:
            origin = Origin(path=str(path))  # convert tagged strings and path-like values to a native str
        else:
            origin = Origin.UNKNOWN

    return origin

def replace(
    self,
    path: str | types.EllipsisType = ...,
    description: str | types.EllipsisType = ...,
    line_num: int | None | types.EllipsisType = ...,
    col_num: int | None | types.EllipsisType = ...,
) -> t.Self:
    """Return a new origin based on an existing one, with the given fields replaced."""
    return dataclasses.replace(
        self,
        **{
            key: value
            for key, value in dict(
                path=path,
                description=description,
                line_num=line_num,
                col_num=col_num,
            ).items()
            if value is not ...
        },  # type: ignore[arg-type]
    )

def alarm_timeout(cls, timeout: int | None) -> _t.Iterator[None]:
    """
    Context for running code under an optional timeout.
    Raises an instance of this class if the timeout occurs.

    New usages of this timeout mechanism are discouraged.
    """
    if timeout is not None:
        if not isinstance(timeout, int):
            raise TypeError(f"Timeout requires 'int' argument, not {datatag.native_type_name(timeout)!r}.")

        if timeout < 0 or timeout > cls._MAX_TIMEOUT:
            # On BSD based systems, alarm is implemented using setitimer.
            # If out-of-bounds values are passed to alarm, they will return -1, which would be interpreted as an existing timer being set.
            # To avoid that, bounds checking is performed in advance.
            raise ValueError(f'Timeout {timeout} is invalid, it must be between 0 and {cls._MAX_TIMEOUT}.')

    if not timeout:
        yield  # execute the context manager's body
        return  # no timeout to deal with, exit immediately

    def on_alarm(_signal: int, _frame: types.FrameType) -> None:
        raise cls(timeout)

    if signal.signal(signal.SIGALRM, on_alarm):
        raise RuntimeError("An existing alarm handler was present.")

    try:
        try:
            if signal.alarm(timeout):
                raise RuntimeError("An existing alarm was set.")

            yield  # execute the context manager's body
        finally:
            # Disable the alarm.
            # If the alarm fires inside this finally block, the alarm is still disabled.
            # This guarantees the cleanup code in the outer finally block runs without risk of encountering the `TaskTimeoutError` from the alarm.
            signal.alarm(0)
    finally:
        signal.signal(signal.SIGALRM, signal.SIG_DFL)

def result_dict_from_exception(exception: BaseException, accept_result_contribution: bool = False) -> dict[str, object]:
    """Return a failed task result dict from the given exception."""
    event = _error_factory.ControllerEventFactory.from_exception(exception, _traceback.is_traceback_enabled(_traceback.TracebackEvent.ERROR))

    result: dict[str, object] = {}
    omit_failed_key = False
    omit_exception_key = False

    if accept_result_contribution:
        while exception:
            if isinstance(exception, ContributesToTaskResult):
                result = dict(exception.result_contribution)
                omit_failed_key = exception.omit_failed_key
                omit_exception_key = exception.omit_exception_key
                break

            exception = exception.__cause__

    if omit_failed_key:
        result.pop('failed', None)
    else:
        result.update(failed=True)

    if omit_exception_key:
        result.pop('exception', None)
    else:
        result.update(exception=_messages.ErrorSummary(event=event))

    if 'msg' not in result:
        # if nothing contributed `msg`, generate one from the exception messages
        result.update(msg=_event_utils.format_event_brief_message(event))

    return result

def from_value(cls, value: t.Any) -> SourceContext | None:
    """Attempt to retrieve source and render a contextual indicator from the value's origin (if any)."""
    if value is None:
        return None

    if isinstance(value, Origin):
        origin = value
        value = None
    else:
        origin = Origin.get_tag(value)

    if RedactAnnotatedSourceContext.current(optional=True):
        return cls.error('content redacted')

    if origin and origin.path:
        return cls.from_origin(origin)

    if value is None:
        truncated_value = None
        annotated_source_lines = []
    else:
        # DTFIX-FUTURE: cleanup/share width
        try:
            value = str(value)
        except Exception as ex:
            value = f'<< context unavailable: {ex} >>'

        truncated_value = textwrap.shorten(value, width=120)
        annotated_source_lines = [truncated_value]

    return SourceContext(
        origin=origin or Origin.UNKNOWN,
        annotated_source_lines=annotated_source_lines,
        target_line=truncated_value,
    )

def result_contribution(self) -> _c.Mapping[str, object]:
    help_text = "Configure `DISPLAY_TRACEBACK` to see a traceback on timeout errors."

    frame = deprecate_value(
        value=help_text,
        msg="The `timedout.frame` task result key is deprecated.",
        help_text=help_text,
        version="2.23",
    )

    return dict(timedout=dict(frame=frame, period=self.timeout))

def default(self, o: _t.Any) -> _t.Any:
    """Hooked default that can conditionally bypass base encoder behavior based on this instance's config."""
    if type(o) is _profiles._WrappedValue:  # pylint: disable=unidiomatic-typecheck
        o = o.wrapped

    if not self._preprocess_unsafe and type(o) is _legacy._Untrusted:  # pylint: disable=unidiomatic-typecheck
        return o.value  # if not emitting unsafe markers, bypass custom unsafe serialization and just return the raw value

    if self._vault_to_text and type(o) is _vault.EncryptedString:  # pylint: disable=unidiomatic-typecheck
        return str(o)  # decrypt and return the plaintext (or fail trying)

    if self._decode_bytes and isinstance(o, bytes):
        return o.decode(errors='surrogateescape')  # backward compatibility with `ansible.module_utils.basic.jsonify`

    return super().default(o)

def serialize_tagged_str(cls, value: _datatag.AnsibleTaggedObject) -> _t.Any:
    if ciphertext := _vault.VaultHelper.get_ciphertext(value, with_tags=False):
        return dict(
            __ansible_vault=ciphertext,
        )

    return _datatag.AnsibleTagHelper.untag(value)

def deserialize_unsafe(cls, value: dict[str, _t.Any]) -> _Untrusted:
    ansible_unsafe = value['__ansible_unsafe']

    if type(ansible_unsafe) is not str:  # pylint: disable=unidiomatic-typecheck
        raise TypeError(f"__ansible_unsafe is {type(ansible_unsafe)} not {str}")

    return _Untrusted(ansible_unsafe)

def pre_serialize(cls, encoder: Encoder, o: _t.Any) -> _t.Any:
    # DTFIX7: these conversion args probably aren't needed
    avv = cls.visitor_type(invert_trust=True, convert_mapping_to_dict=True, convert_sequence_to_list=True, convert_custom_scalars=True)

    return avv.visit(o)

def raw_decode(self, s: str, idx: int = 0) -> tuple[_t.Any, int]:
    self._origin = _tags.Origin.get_tag(s)
    self._trusted_as_template = _tags.TrustedAsTemplate.is_tagged_on(s)

    return super().raw_decode(s, idx)

def launch_ssh_agent() -> None:
    """If configured via `SSH_AGENT`, launch an ssh-agent for Ansible's use and/or verify access to an existing one."""
    try:
        _launch_ssh_agent()
    except Exception as ex:
        raise AnsibleError("Failed to launch ssh agent.") from ex

def to_blob(self) -> bytes:
    if self < 0:
        raise ValueError("negative mpint not allowed")
    if not self:
        return b""
    nbytes = (self.bit_length() + 8) // 8
    ret = bytearray(self.to_bytes(length=nbytes, byteorder='big'))
    ret[:0] = uint32(len(ret)).to_blob()
    return ret

def to_blob(self) -> bytes:
    if length := len(self):
        return uint32(length).to_blob() + self
    else:
        return b""

def to_blob(self) -> bytes:
    val = self.encode('utf-8')
    if length := len(val):
        return uint32(length).to_blob() + val
    else:
        return b""

def to_blob(self) -> bytes:
    rv = bytearray()
    for field in dataclasses.fields(self):
        fv = getattr(self, field.name)
        if isinstance(fv, SupportsToBlob):
            rv.extend(fv.to_blob())
        else:
            raise NotImplementedError(field.type)
    return rv

def from_blob(cls, blob: memoryview | bytes) -> t.Self:
    args: list[t.Any] = []
    for _field_name, field_type in t.get_type_hints(cls).items():
        if isinstance(field_type, SupportsFromBlob):
            fv, blob = field_type.consume_from_blob(blob)
            args.append(fv)
        else:
            raise NotImplementedError(str(field_type))
    return cls(*args)

def public_key(self) -> CryptoPublicKey:
    type: KeyAlgo = self.type
    match type:
        case KeyAlgo.RSA:
            return RSAPublicNumbers(self.e, self.n).public_key()
        case KeyAlgo.ECDSA256 | KeyAlgo.ECDSA384 | KeyAlgo.ECDSA521:
            curve = _ECDSA_KEY_TYPE[KeyAlgo(type)]
            return EllipticCurvePublicKey.from_encoded_point(curve(), self.Q)
        case KeyAlgo.ED25519:
            return Ed25519PublicKey.from_public_bytes(self.enc_a)
        case KeyAlgo.DSA:
            return DSAPublicNumbers(self.y, DSAParameterNumbers(self.p, self.q, self.g)).public_key()
        case _:
            raise NotImplementedError(type)

def from_public_key(public_key: CryptoPublicKey) -> PublicKeyMsg:
    match public_key:
        case DSAPublicKey():
            dsa_pn: DSAPublicNumbers = public_key.public_numbers()
            return DSAPublicKeyMsg(
                KeyAlgo.DSA,
                mpint(dsa_pn.parameter_numbers.p),
                mpint(dsa_pn.parameter_numbers.q),
                mpint(dsa_pn.parameter_numbers.g),
                mpint(dsa_pn.y),
            )
        case EllipticCurvePublicKey():
            return EcdsaPublicKeyMsg(
                getattr(KeyAlgo, f'ECDSA{public_key.curve.key_size}'),
                unicode_string(f'nistp{public_key.curve.key_size}'),
                binary_string(
                    public_key.public_bytes(
                        encoding=serialization.Encoding.X962,
                        format=serialization.PublicFormat.UncompressedPoint,
                    )
                ),
            )
        case Ed25519PublicKey():
            return Ed25519PublicKeyMsg(
                KeyAlgo.ED25519,
                binary_string(
                    public_key.public_bytes(
                        encoding=serialization.Encoding.Raw,
                        format=serialization.PublicFormat.Raw,
                    )
                ),
            )
        case RSAPublicKey():
            rsa_pn: RSAPublicNumbers = public_key.public_numbers()
            return RSAPublicKeyMsg(KeyAlgo.RSA, mpint(rsa_pn.e), mpint(rsa_pn.n))
        case _:
            raise NotImplementedError(public_key)

def add(
    self,
    private_key: CryptoPrivateKey,
    comments: str | None = None,
    lifetime: int | None = None,
    confirm: bool | None = None,
) -> None:
    key_msg = PrivateKeyMsg.from_private_key(private_key)
    key_msg.comments = unicode_string(comments or '')
    if lifetime:
        key_msg.constraints += constraints([ProtocolMsgNumbers.SSH_AGENT_CONSTRAIN_LIFETIME]).to_blob() + uint32(lifetime).to_blob()
    if confirm:
        key_msg.constraints += constraints([ProtocolMsgNumbers.SSH_AGENT_CONSTRAIN_CONFIRM]).to_blob()

    if key_msg.constraints:
        msg = ProtocolMsgNumbers.SSH_AGENTC_ADD_ID_CONSTRAINED.to_blob()
    else:
        msg = ProtocolMsgNumbers.SSH_AGENTC_ADD_IDENTITY.to_blob()
    msg += key_msg.to_blob()
    self.send(msg)

def list(self) -> KeyList:
    req = ProtocolMsgNumbers.SSH_AGENTC_REQUEST_IDENTITIES.to_blob()
    r = memoryview(bytearray(self.send(req)))
    if r[0] != ProtocolMsgNumbers.SSH_AGENT_IDENTITIES_ANSWER:
        raise SshAgentFailure('agent: non-identities answer received for identities list')
    return KeyList.from_blob(r[1:])

def current() -> AnsibleAccessContext:
    """Creates or retrieves an `AnsibleAccessContext` for the current logical callstack."""
    try:
        ctx: AnsibleAccessContext = AnsibleAccessContext._contextvar.get()
    except LookupError:
        # didn't exist; create it
        ctx = AnsibleAccessContext()
        AnsibleAccessContext._contextvar.set(ctx)  # we ignore the token, since this should live for the life of the thread/async ctx

    return ctx

def access(self, value: t.Any) -> None:
    """Notify all contexts which have registered interest in the given value that it is being accessed."""
    if not self._notify_contexts:
        return

    value_types = AnsibleTagHelper.tag_types(value) | frozenset((type(value),))
    masked: set[type] = set()

    for ctx in reversed(self._notify_contexts):
        if ctx._mask:
            if (ctx_type := type(ctx)) in masked:
                continue

            masked.add(ctx_type)

        # noinspection PyProtectedMember
        if ctx._type_interest.intersection(value_types):
            ctx._notify(value)

def get(self, key: t.Any) -> t.Any:
    """Get the value for the given key, templating the result before returning it."""
    for source in self.sources:
        if key not in source:
            continue

        value = source[key]

        try:
            return self.template(key, value)
        except AnsibleValueOmittedError:
            break  # omit == obliterate - matches historical behavior where dict layers were squashed before templating was applied
        except Exception as ex:
            raise AnsibleError(f'Error while resolving value for {key!r}.', obj=value) from ex

    raise KeyError(key)

def keys(self) -> t.Iterable[t.Any]:
    """
    Returns a sorted iterable of all keys present in all source layers, without templating associated values.
    Values that resolve to `omit` are thus included.
    """
    return sorted(set(itertools.chain.from_iterable(self.sources)))

def items(self) -> t.Iterable[t.Tuple[t.Any, t.Any]]:
    """
    Returns a sorted iterable of (key, templated value) tuples.
    Any tuple where the templated value resolves to `omit` will not be included in the result.
    """
    for key in self.keys():
        try:
            yield key, self.get(key)
        except KeyError:
            pass

def when(cls, condition: bool, /) -> t.Self | _contextlib.nullcontext:
    """Returns a new instance if `condition` is True (usually `TemplateContext.is_top_level`), otherwise a `nullcontext` instance."""
    if condition:
        return cls()

    return _contextlib.nullcontext()

def extend(self, marker_behavior: MarkerBehavior | None = None) -> t.Self:
    new_templar = type(self)(
        loader=self._loader,
        variables=self._variables,
        variables_factory=self._variables_factory,
        marker_behavior=marker_behavior or self._marker_behavior,
    )

    if self._environment:
        new_templar._environment = self._environment

    return new_templar

def environment(self) -> AnsibleEnvironment:
    if not self._environment:
        self._environment = AnsibleEnvironment(ansible_basedir=self.basedir)

    return self._environment

def available_variables(self) -> dict[str, t.Any] | ChainMap[str, t.Any]:
    """Available variables this instance will use when templating."""
    # DTFIX3: ensure that we're always accessing this as a shallow container-level snapshot, and eliminate uses of anything
    #  that directly mutates this value. _new_context may resolve this for us?
    if self._variables is None:
        self._variables = self._variables_factory() if self._variables_factory else {}

    return self._variables

def resolve_variable_expression(
    self,
    expression: str,
    *,
    local_variables: dict[str, t.Any] | None = None,
) -> t.Any:
    """
    Resolve a potentially untrusted string variable expression consisting only of valid identifiers, integers, dots, and indexing containing these.
    Optional local variables may be provided, which can only be referenced directly by the given expression.
    Valid: x, x.y, x[y].z, x[1], 1, x[y.z]
    Error: 'x', x['y'], q('env')
    """
    components = re.split(r'[.\[\]]', expression)

    try:
        for component in components:
            if re.fullmatch('[0-9]*', component):
                continue  # allow empty strings and integers

            validate_variable_name(component)
    except Exception as ex:
        raise AnsibleError(f'Invalid variable expression: {expression}', obj=expression) from ex

    return self.evaluate_expression(TrustedAsTemplate().tag(expression), local_variables=local_variables)

def template(
    self,
    variable: t.Any,  # DTFIX-FUTURE: once we settle the new/old API boundaries, rename this (here and in other methods)
    *,
    options: TemplateOptions = TemplateOptions.DEFAULT,
    mode: TemplateMode = TemplateMode.DEFAULT,
    lazy_options: LazyOptions = LazyOptions.DEFAULT,
) -> t.Any:
    """Templates (possibly recursively) any given data as input."""
    original_variable = variable

    for _attempt in range(TRANSFORM_CHAIN_LIMIT):
        if variable is None or (value_type := type(variable)) in IGNORE_SCALAR_VAR_TYPES:
            return variable  # quickly ignore supported scalar types which are not be templated

        value_is_str = isinstance(variable, str)

        if template_ctx := TemplateContext.current(optional=True):
            stop_on_template = template_ctx.stop_on_template
        else:
            stop_on_template = False

        if mode is TemplateMode.STOP_ON_TEMPLATE:
            stop_on_template = True

        with (
            TemplateContext(template_value=variable, templar=self, options=options, stop_on_template=stop_on_template) as ctx,
            DeprecatedAccessAuditContext.when(ctx.is_top_level),
            JinjaCallContext(accept_lazy_markers=True),  # let default Jinja marker behavior apply, since we're descending into a new template
        ):
            try:
                if not value_is_str:
                    # transforms are currently limited to non-str types as an optimization
                    if (transform := _type_transform_mapping.get(value_type)) and value_type.__name__ not in lazy_options.unmask_type_names:
                        variable = transform(variable)
                        continue

                    template_result = _AnsibleLazyTemplateMixin._try_create(variable, lazy_options)
                elif not lazy_options.template:
                    template_result = variable
                elif not is_possibly_template(variable, options.overrides):
                    template_result = variable
                elif not self._trust_check(variable, skip_handler=stop_on_template):
                    template_result = variable
                elif stop_on_template:
                    raise TemplateEncountered()
                else:
                    compiled_template = self._compile_template(variable, options)

                    template_result = compiled_template(self.available_variables)
                    template_result = self._post_render_mutation(variable, template_result, options)
            except TemplateEncountered:
                raise
            except Exception as ex:
                template_result = defer_template_error(ex, variable, is_expression=False)

            if ctx.is_top_level or mode is TemplateMode.ALWAYS_FINALIZE:
                template_result = self._finalize_top_level_template_result(
                    variable, options, template_result, stop_on_container=mode is TemplateMode.STOP_ON_CONTAINER
                )

        return template_result

    raise AnsibleTemplateTransformLimitError(obj=original_variable)

def is_template(self, data: t.Any, overrides: TemplateOverrides = TemplateOverrides.DEFAULT) -> bool:
    """
    Evaluate the input data to determine if it contains a template, even if that template is invalid. Containers will be recursively searched.
    Objects subject to template-time transforms that do not yield a template are not considered templates by this method.
    Gating a conditional call to `template` with this method is redundant and inefficient -- request templating unconditionally instead.
    """
    options = TemplateOptions(overrides=overrides) if overrides is not TemplateOverrides.DEFAULT else TemplateOptions.DEFAULT

    try:
        self.template(data, options=options, mode=TemplateMode.STOP_ON_TEMPLATE)
    except TemplateEncountered:
        return True
    else:
        return False

def defer_template_error(ex: Exception, variable: t.Any, *, is_expression: bool) -> Marker:
    if not ex.__traceback__:
        raise AssertionError('ex must be a previously raised exception')

    if isinstance(ex, MarkerError):
        return ex.source

    exception_to_raise = create_template_error(ex, variable, is_expression)

    if exception_to_raise is ex:
        return CapturedExceptionMarker(ex)  # capture the previously raised exception

    try:
        raise exception_to_raise from ex  # raise the newly synthesized exception before capturing it
    except Exception as captured_ex:
        return CapturedExceptionMarker(captured_ex)

def create_template_error(ex: Exception, variable: t.Any, is_expression: bool) -> AnsibleTemplateError:
    if isinstance(ex, AnsibleTemplateError):
        exception_to_raise = ex
    else:
        kind = "expression" if is_expression else "template"
        ex_type = AnsibleTemplateError  # always raise an AnsibleTemplateError/subclass

        if isinstance(ex, RecursionError):
            msg = f"Recursive loop detected in {kind}."
        elif isinstance(ex, TemplateSyntaxError):
            msg = f"Syntax error in {kind}."

            if is_expression and is_possibly_template(variable):
                msg += " Template delimiters are not supported in expressions."

            ex_type = AnsibleTemplateSyntaxError
        else:
            msg = f"Error rendering {kind}."

        exception_to_raise = ex_type(msg, obj=variable)

    if exception_to_raise.obj is None:
        exception_to_raise.obj = TemplateContext.current().template_value

    # DTFIX-FUTURE: Look through the TemplateContext hierarchy to find the most recent non-template
    #   caller and use that for origin when no origin is available on obj. This could be useful for situations where the template
    #   was embedded in a plugin, or a plugin is otherwise responsible for losing the origin and/or trust. We can't just use the first
    #   non-template caller as that will lead to false positives for re-entrant calls (e.g. template plugins that call into templar).

    return exception_to_raise

def is_possibly_template(value: str, overrides: TemplateOverrides = TemplateOverrides.DEFAULT):
    """
    A lightweight check to determine if the given string looks like it contains a template, even if that template is invalid.
    Returns `True` if the given string starts with a Jinja overrides header or if it contains template start strings.
    """
    return value.startswith(JINJA2_OVERRIDE) or overrides._contains_start_string(value)

def is_possibly_all_template(value: str, overrides: TemplateOverrides = TemplateOverrides.DEFAULT):
    """
    A lightweight check to determine if the given string looks like it contains *only* a template, even if that template is invalid.
    Returns `True` if the given string starts with a Jinja overrides header or if it starts and ends with Jinja template delimiters.
    """
    return value.startswith(JINJA2_OVERRIDE) or overrides._starts_and_ends_with_jinja_delimiters(value)

def merge(self, kwargs: dict[str, t.Any] | None, /) -> TemplateOverrides:
    """Return a new instance based on the current instance with the given kwargs overridden."""
    if kwargs:
        return self.from_kwargs(dataclasses.asdict(self) | kwargs)

    return self

def from_kwargs(cls, kwargs: dict[str, t.Any] | None, /) -> TemplateOverrides:
    """TemplateOverrides instance factory; instances resolving to all default values will instead return the DEFAULT singleton for optimization."""
    if kwargs:
        value = cls(**kwargs)

        if value.overlay_kwargs():
            return value

    return cls.DEFAULT

def get_all(self):
    """
    Override Jinja's default get_all to return all vars in the context as a ChainMap with a mutable layer at the bottom.
    This provides some isolation against accidental changes to inherited variable contexts without requiring copies.
    """
    layers = []

    if self.vars:
        layers.append(self.vars)
    if self.parent:
        layers.append(self.parent)

    # HACK: always include a sacrificial plain-dict on the bottom layer, since Jinja's debug and stacktrace rewrite code invokes
    # `__setitem__` outside a call context; this will ensure that it always occurs on a plain dict instead of a lazy one.
    return ChainMap({}, *layers)

def tokeniter(self, *args, **kwargs) -> t.Iterator[t.Tuple[int, str, str]]:
    """Pre-escape backslashes in expression ({{ }}) raw string constants before Jinja's Lexer.wrap() can interpret them as ASCII escape sequences."""
    token_stream = super().tokeniter(*args, **kwargs)

    # if we have no context, Jinja's doing a nested compile at runtime (eg, import/include); historically, no backslash escaping is performed
    if not (tcc := _TemplateCompileContext.current(optional=True)) or not tcc.escape_backslashes:
        yield from token_stream
        return

    in_variable = False

    for token in token_stream:
        token_type = token[1]

        if token_type == TOKEN_VARIABLE_BEGIN:
            in_variable = True
        elif token_type == TOKEN_VARIABLE_END:
            in_variable = False
        elif in_variable and token_type == TOKEN_STRING:
            token = token[0], token_type, token[2].replace('\\', '\\\\')

        yield token

def is_safe_attribute(self, obj: t.Any, attr: str, value: t.Any) -> bool:
    # deprecated: description="remove relaxed template sandbox mode support" core_version="2.23"
    if _TemplateConfig.sandbox_mode == _SandboxMode.ALLOW_UNSAFE_ATTRIBUTES:
        return True

    if (type_or_tuple := self._allowed_unsafe_attributes.get(attr)) and isinstance(obj, type_or_tuple):
        return True

    return super().is_safe_attribute(obj, attr, value)

def compile_expression(self, source: str, *args, **kwargs) -> TemplateExpression:
    # compile_expression parses and passes the tree to from_string; for debug support, activate the context here to capture the intermediate results
    with _CompileStateSmugglingCtx.when(self._debuggable_template_source) as ctx:
        if isinstance(ctx, _CompileStateSmugglingCtx):  # only present if debugging is enabled
            ctx.template_source = source

        return super().compile_expression(source, *args, **kwargs)

def concat(nodes: t.Iterable[t.Any]) -> t.Any:  # type: ignore[override]
    node_list = list(_flatten_nodes(nodes))

    if not node_list:
        return None

    # this code is complemented by our tweaked CodeGenerator _output_const_repr that ensures that literal constants
    # in templates aren't double-repr'd in the generated code
    if len(node_list) == 1:
        return node_list[0]

    # In order to ensure that all markers are tripped, do a recursive finalize before we repr (otherwise we can end up
    # repr'ing a Marker). This requires two passes, but avoids the need for a parallel reimplementation of all repr methods.
    try:
        node_list = _finalize_template_result(node_list, FinalizeMode.CONCAT)
    except MarkerError as ex:
        return ex.source  # return the first Marker encountered

    return ''.join([to_text(v) for v in node_list])

def getattr(self, obj: t.Any, attribute: str) -> t.Any:
    """
    Get `attribute` from the attributes of `obj`, falling back to items in `obj`.
    If no item was found, return a sandbox-specific `UndefinedMarker` if `attribute` is protected by the sandbox,
    otherwise return a normal `UndefinedMarker` instance.
    This differs from the built-in Jinja behavior which will not fall back to items if `attribute` is protected by the sandbox.
    """
    # example template that uses this: "{{ some.thing }}" -- obj is the "some" dict, attribute is "thing"

    is_safe = True

    try:
        value = getattr(obj, attribute)
    except AttributeError:
        value = _sentinel
    else:
        if not (is_safe := self.is_safe_attribute(obj, attribute, value)):
            value = _sentinel

    if value is _sentinel:
        try:
            value = obj[attribute]
        except (TypeError, LookupError):
            value = self.undefined(obj=obj, name=attribute) if is_safe else self.unsafe_undefined(obj, attribute)

    AnsibleAccessContext.current().access(value)

    return value

def call(
    self,
    __context: Context,
    __obj: t.Any,
    *args: t.Any,
    **kwargs: t.Any,
) -> t.Any:
    try:
        if _DirectCall.is_marked(__obj):
            # Both `_lookup` and `_query` handle arg proxying and `Marker` args internally.
            # Performing either before calling them will interfere with that processing.
            return super().call(__context, __obj, *args, **kwargs)

        # Jinja's generated macro code handles Markers, so preemptive raise on Marker args and lazy retrieval should be disabled for the macro invocation.
        is_macro = isinstance(__obj, Macro)

        if not is_macro and (first_marker := get_first_marker_arg(args, kwargs)) is not None:
            return first_marker

        with JinjaCallContext(accept_lazy_markers=is_macro):
            call_res = super().call(__context, __obj, *lazify_container_args(args), **lazify_container_kwargs(kwargs))

            if __obj is range:
                # Preserve the ability to do `range(1000000000) | random` by not converting range objects to lists.
                # Historically, range objects were only converted on Jinja finalize and filter outputs, so they've always been floating around in templating
                # code and visible to user plugins.
                return call_res

            return _wrap_plugin_output(call_res)

    except MarkerError as ex:
        return ex.source
    except Exception as ex:
        return CapturedExceptionMarker(ex)

def iter_marker_args(args: c.Sequence, kwargs: dict[str, t.Any]) -> t.Generator[Marker]:
    """Utility method to iterate plugin args and yield any `Marker` encountered."""
    for arg in itertools.chain(args, kwargs.values()):
        if isinstance(arg, Marker):
            yield arg

def validate_arg_type(name: str, value: t.Any, allowed_type_or_types: type | tuple[type, ...], /) -> None:
    """Validate the type of the given argument while preserving context for Marker values."""
    # DTFIX-FUTURE: find a home for this as a general-purpose utliity method and expose it after some API review
    if isinstance(value, allowed_type_or_types):
        return

    if isinstance(allowed_type_or_types, type):
        arg_type_description = repr(native_type_name(allowed_type_or_types))
    else:
        arg_type_description = ' or '.join(repr(native_type_name(item)) for item in allowed_type_or_types)

    if isinstance(value, Marker):
        try:
            value.trip()
        except Exception as ex:
            raise AnsibleTypeError(f"The {name!r} argument must be of type {arg_type_description}.", obj=value) from ex

    raise AnsibleTypeError(f"The {name!r} argument must be of type {arg_type_description}, not {native_type_name(value)!r}.", obj=value)

def get(self, key: t.Any, default: t.Any = None) -> t.Any:
    if (value := super().get(key, _NoKeySentinel)) is _NoKeySentinel:
        return default

    return self._proxy_or_render_lazy_value(key, value)

def setdefault(self, key, default=None, /) -> t.Any:
    if (value := self.get(key, _NoKeySentinel)) is not _NoKeySentinel:
        return value

    super().__setitem__(key, default)

    return default

def pop(self, key, default=_NoKeySentinel, /) -> t.Any:
    if (value := super().get(key, _NoKeySentinel)) is _NoKeySentinel:
        if default is _NoKeySentinel:
            raise KeyError(key)

        return default

    value = self._proxy_or_render_lazy_value(_NoKeySentinel, value)

    del self[key]

    return value

def popitem(self) -> t.Any:
    try:
        key = next(reversed(self))
    except StopIteration:
        raise KeyError("popitem(): dictionary is empty")

    value = self._proxy_or_render_lazy_value(_NoKeySentinel, self[key])

    del self[key]

    return key, value

def pop(self, idx: t.SupportsIndex = -1, /) -> t.Any:
    if not self:
        raise IndexError('pop from empty list')

    try:
        value = self[idx]
    except IndexError:
        raise IndexError('pop index out of range')

    value = self._proxy_or_render_lazy_value(_NoKeySentinel, value)

    del self[idx]

    return value

def emit_warnings(self) -> None:
    """Emit warning messages caused by Marker values, aggregated by unique template."""

    display = Display()
    grouped_templates = itertools.groupby(self._trackers, key=lambda tracker: tracker.value._marker_template_source)

    for template, items in grouped_templates:
        item_list = list(items)

        msg = f'Encountered {len(item_list)} template error{"s" if len(item_list) > 1 else ""}.'

        for item in item_list:
            msg += f'\nerror {item.number} - {item.value._as_message()}'

        display.warning(msg=msg, obj=template)

def error_summary(value: _messages.ErrorSummary) -> str:
    """Render ErrorSummary as a formatted traceback for backward-compatibility with pre-2.19 TaskResult.exception."""
    if _traceback._is_traceback_enabled(_traceback.TracebackEvent.ERROR):
        return _event_formatting.format_event_traceback(value.event)

    return '(traceback unavailable)'

def encrypted_string(value: EncryptedString) -> str | VaultExceptionMarker:
    """Decrypt an encrypted string and return its value, or a VaultExceptionMarker if decryption fails."""
    try:
        return value._decrypt()
    except Exception as ex:
        return VaultExceptionMarker(
            ciphertext=VaultHelper.get_ciphertext(value, with_tags=True),
            event=_error_factory.ControllerEventFactory.from_exception(ex, _traceback.is_traceback_enabled(_traceback.TracebackEvent.ERROR)),
        )

def construct_mapping(self, node, deep=False):
    # Delegate to built-in implementation to construct the mapping.
    # This is done before checking for duplicates to leverage existing error checking on the input node.
    mapping = super().construct_mapping(node, deep)
    keys = set()

    # Now that the node is known to be a valid mapping, handle any duplicate keys.
    for key_node, _value_node in node.value:
        if (key := self.construct_object(key_node, deep=deep)) in keys:
            msg = f'Found duplicate mapping key {key!r}.'

            if self._duplicate_key_mode == 'error':
                raise AnsibleConstructorError(problem=msg, problem_mark=key_node.start_mark)

            if self._duplicate_key_mode == 'warn':
                display.warning(msg=msg, obj=key, help_text='Using last defined value only.')

        keys.add(key)

    return mapping

def construct_yaml_unsafe(self, node):
    self._unsafe_depth += 1

    try:
        return self._resolve_and_construct_object(node)
    finally:
        self._unsafe_depth -= 1

def construct_yaml_vault(self, node: Node) -> EncryptedString:
    ciphertext = self._resolve_and_construct_object(node)

    if not isinstance(ciphertext, str):
        raise AnsibleConstructorError(problem=f"the {node.tag!r} tag requires a string value", problem_mark=node.start_mark)

    encrypted_string = AnsibleTagHelper.tag_copy(ciphertext, EncryptedString(ciphertext=AnsibleTagHelper.untag(ciphertext)))

    return encrypted_string

def construct_yaml_vault_encrypted(self, node: Node) -> EncryptedString:
    origin = self._node_position_info(node)
    display.deprecated(
        msg='Use of the YAML `!vault-encrypted` tag is deprecated.',
        version='2.23',
        obj=origin,
        help_text='Use the `!vault` tag instead.',
    )

    return self.construct_yaml_vault(node)

def get_node_from_ciphertext(self, data: object) -> ScalarNode | None:
    if ciphertext := VaultHelper.get_ciphertext(data, with_tags=False):
        return self.represent_scalar('!vault', ciphertext, style='|')

    return None

def represent_vault_exception_marker(self, data: _jinja_common.VaultExceptionMarker) -> ScalarNode:
    if node := self.get_node_from_ciphertext(data):
        return node

    data.trip()

def represent_ansible_tagged_object(self, data: AnsibleTaggedObject) -> Node:
    if _internal.is_intermediate_mapping(data):
        return self.represent_dict(data)

    if _internal.is_intermediate_iterable(data):
        return self.represent_list(data)

    if node := self.get_node_from_ciphertext(data):
        return node

    return self.represent_data(AnsibleTagHelper.as_native_type(data))  # automatically decrypts encrypted strings

def build_rst(output_dir: pathlib.Path, template_file: pathlib.Path) -> None:
    """Build RST documentation for ansible-core CLI programs."""
    if not template_file.resolve().is_relative_to(SCRIPT_DIR):
        warnings.warn("Custom templates are intended for debugging purposes only. The data model may change in future releases without notice.")

    output_dir.mkdir(exist_ok=True, parents=True)

    for cli_name, source in generate_rst(template_file).items():
        (output_dir / f'{cli_name}.rst').write_text(source)

def collect_programs() -> dict[str, dict[str, t.Any]]:
    """Return information about CLI programs."""
    programs: list[tuple[str, dict[str, t.Any]]] = []
    cli_bin_name_list: list[str] = []

    for source_file in (SOURCE_DIR / 'lib/ansible/cli').glob('*.py'):
        if not source_file.name.startswith('_'):
            programs.append(generate_options_docs(source_file, cli_bin_name_list))

    return dict(programs)

def populate_subparser_actions(parser: argparse.ArgumentParser, shared_option_names: tuple[str, ...], actions: dict[str, dict[str, t.Any]]) -> int:
    """Generate doc structure from CLI module subparser options."""
    try:
        # noinspection PyProtectedMember
        subparsers: dict[str, argparse.ArgumentParser] = parser._subparsers._group_actions[0].choices  # type: ignore
    except AttributeError:
        subparsers = {}

    depth = 0

    for subparser_action, subparser in subparsers.items():
        subparser_option_names: set[str] = set()
        subparser_action_docs: set[ActionDoc] = set()
        subparser_actions: dict[str, dict[str, t.Any]] = {}

        for action_doc in get_action_docs(subparser):
            for option_alias in action_doc.options:
                if option_alias in shared_option_names:
                    continue

                subparser_option_names.add(option_alias)
                subparser_action_docs.add(action_doc)

        depth = populate_subparser_actions(subparser, shared_option_names, subparser_actions)

        actions[subparser_action] = dict(
            option_names=list(subparser_option_names),
            options=[item.__dict__ for item in subparser_action_docs],
            actions=subparser_actions,
            name=subparser_action,
            desc=trim_docstring(subparser.get_default("func").__doc__),
        )

    return depth + 1

def get_action_docs(parser: argparse.ArgumentParser) -> list[ActionDoc]:
    """Get action documentation from the given argument parser."""
    action_docs = []

    # noinspection PyProtectedMember
    for action in parser._actions:
        if action.help == argparse.SUPPRESS:
            continue

        # noinspection PyProtectedMember, PyUnresolvedReferences
        args = action.dest.upper() if isinstance(action, argparse._StoreAction) else None

        if args or action.option_strings:
            action_docs.append(
                ActionDoc(
                    desc=action.help,
                    options=tuple(action.option_strings),
                    arg=args,
                )
            )

    return action_docs

def trim_docstring(docstring: str | None) -> str:
    """Trim and return the given docstring using the implementation from https://peps.python.org/pep-0257/#handling-docstring-indentation."""
    if not docstring:
        return ''  # pragma: nocover

    # Convert tabs to spaces (following the normal Python rules) and split into a list of lines
    lines = docstring.expandtabs().splitlines()

    # Determine minimum indentation (first line doesn't count)
    indent = sys.maxsize

    for line in lines[1:]:
        stripped = line.lstrip()

        if stripped:
            indent = min(indent, len(line) - len(stripped))

    # Remove indentation (first line is special)
    trimmed = [lines[0].strip()]

    if indent < sys.maxsize:
        for line in lines[1:]:
            trimmed.append(line[indent:].rstrip())

    # Strip off trailing and leading blank lines
    while trimmed and not trimmed[-1]:
        trimmed.pop()

    while trimmed and not trimmed[0]:
        trimmed.pop(0)

    # Return a single string
    return '\n'.join(trimmed)

def validate_extra_context(
    _ctx: Context, _param: Parameter, value: Iterable[str]
) -> OrderedDict[str, str] | None:
    """Validate extra context."""
    for string in value:
        if '=' not in string:
            msg = (
                f"EXTRA_CONTEXT should contain items of the form key=value; "
                f"'{string}' doesn't match that form"
            )
            raise click.BadParameter(msg)

    # Convert tuple -- e.g.: ('program_name=foobar', 'startsecs=66')
    # to dict -- e.g.: {'program_name': 'foobar', 'startsecs': '66'}
    return OrderedDict(s.split('=', 1) for s in value) or None

def parse(self, parser: Parser) -> nodes.Output:
    """Parse datetime template and add datetime value."""
    lineno = next(parser.stream).lineno

    node = parser.parse_expression()

    if parser.stream.skip_if('comma'):
        datetime_format = parser.parse_expression()
    else:
        datetime_format = nodes.Const(None)

    if isinstance(node, nodes.Add):
        call_method = self.call_method(
            '_datetime',
            [node.left, nodes.Const('+'), node.right, datetime_format],
            lineno=lineno,
        )
    elif isinstance(node, nodes.Sub):
        call_method = self.call_method(
            '_datetime',
            [node.left, nodes.Const('-'), node.right, datetime_format],
            lineno=lineno,
        )
    else:
        call_method = self.call_method(
            '_now',
            [node, datetime_format],
            lineno=lineno,
        )
    return nodes.Output([call_method], lineno=lineno)

def random_ascii_string(length: int, punctuation: bool = False) -> str:
    if punctuation:
        corpus = f'{string.ascii_letters}{string.punctuation}'
    else:
        corpus = string.ascii_letters
    return "".join(choice(corpus) for _ in range(length))

def is_copy_only_path(path: str, context: dict[str, Any]) -> bool:
    """Check whether the given `path` should only be copied and not rendered.

    Returns True if `path` matches a pattern in the given `context` dict,
    otherwise False.

    :param path: A file-system path referring to a file or dir that
        should be rendered or just copied.
    :param context: cookiecutter context.
    """
    try:
        for dont_render in context['cookiecutter']['_copy_without_render']:
            if fnmatch.fnmatch(path, dont_render):
                return True
    except KeyError:
        return False

    return False

def apply_overwrites_to_context(
    context: dict[str, Any],
    overwrite_context: dict[str, Any],
    *,
    in_dictionary_variable: bool = False,
) -> None:
    """Modify the given context in place based on the overwrite_context."""
    for variable, overwrite in overwrite_context.items():
        if variable not in context:
            if not in_dictionary_variable:
                # We are dealing with a new variable on first level, ignore
                continue
            # We are dealing with a new dictionary variable in a deeper level
            context[variable] = overwrite

        context_value = context[variable]
        if isinstance(context_value, list):
            if in_dictionary_variable:
                context[variable] = overwrite
                continue
            if isinstance(overwrite, list):
                # We are dealing with a multichoice variable
                # Let's confirm all choices are valid for the given context
                if set(overwrite).issubset(set(context_value)):
                    context[variable] = overwrite
                else:
                    msg = (
                        f"{overwrite} provided for multi-choice variable "
                        f"{variable}, but valid choices are {context_value}"
                    )
                    raise ValueError(msg)
            else:
                # We are dealing with a choice variable
                if overwrite in context_value:
                    # This overwrite is actually valid for the given context
                    # Let's set it as default (by definition first item in list)
                    # see ``cookiecutter.prompt.prompt_choice_for_config``
                    context_value.remove(overwrite)
                    context_value.insert(0, overwrite)
                else:
                    msg = (
                        f"{overwrite} provided for choice variable "
                        f"{variable}, but the choices are {context_value}."
                    )
                    raise ValueError(msg)
        elif isinstance(context_value, dict) and isinstance(overwrite, dict):
            # Partially overwrite some keys in original dict
            apply_overwrites_to_context(
                context_value, overwrite, in_dictionary_variable=True
            )
            context[variable] = context_value
        elif isinstance(context_value, bool) and isinstance(overwrite, str):
            # We are dealing with a boolean variable
            # Convert overwrite to its boolean counterpart
            try:
                context[variable] = YesNoPrompt().process_response(overwrite)
            except InvalidResponse as err:
                msg = (
                    f"{overwrite} provided for variable "
                    f"{variable} could not be converted to a boolean."
                )
                raise ValueError(msg) from err
        else:
            # Simply overwrite the value for this variable
            context[variable] = overwrite

def render_and_create_dir(
    dirname: str,
    context: dict[str, Any],
    output_dir: Path | str,
    environment: Environment,
    overwrite_if_exists: bool = False,
) -> tuple[Path, bool]:
    """Render name of a directory, create the directory, return its path."""
    if not dirname or dirname == "":
        msg = 'Error: directory name is empty'
        raise EmptyDirNameException(msg)

    name_tmpl = environment.from_string(dirname)
    rendered_dirname = name_tmpl.render(**context)

    dir_to_create = Path(output_dir, rendered_dirname)

    logger.debug(
        'Rendered dir %s must exist in output_dir %s', dir_to_create, output_dir
    )

    output_dir_exists = dir_to_create.exists()

    if output_dir_exists:
        if overwrite_if_exists:
            logger.debug(
                'Output directory %s already exists, overwriting it', dir_to_create
            )
        else:
            msg = f'Error: "{dir_to_create}" directory already exists'
            raise OutputDirExistsException(msg)
    else:
        make_sure_path_exists(dir_to_create)

    return dir_to_create, not output_dir_exists

def render_variable(
    env: Environment,
    raw: _Raw,
    cookiecutter_dict: dict[str, Any],
) -> str:
    """Render the next variable to be displayed in the user prompt.

    Inside the prompting taken from the cookiecutter.json file, this renders
    the next variable. For example, if a project_name is "Peanut Butter
    Cookie", the repo_name could be be rendered with:

        `{{ cookiecutter.project_name.replace(" ", "_") }}`.

    This is then presented to the user as the default.

    :param Environment env: A Jinja2 Environment object.
    :param raw: The next value to be prompted for by the user.
    :param dict cookiecutter_dict: The current context as it's gradually
        being populated with variables.
    :return: The rendered value for the default variable.
    """
    if raw is None or isinstance(raw, bool):
        return raw
    if isinstance(raw, dict):
        return {
            render_variable(env, k, cookiecutter_dict): render_variable(
                env, v, cookiecutter_dict
            )
            for k, v in raw.items()
        }
    if isinstance(raw, list):
        return [render_variable(env, v, cookiecutter_dict) for v in raw]
    if not isinstance(raw, str):
        raw = str(raw)

    template = env.from_string(raw)

    return template.render(cookiecutter=cookiecutter_dict)

def prompt_choice_for_template(
    key: str, options: dict, no_input: bool
) -> OrderedDict[str, Any]:
    """Prompt user with a set of options to choose from.

    :param no_input: Do not prompt for user input and return the first available option.
    """
    opts = list(options.keys())
    prompts = {"templates": _prompts_from_options(options)}
    return opts[0] if no_input else read_user_choice(key, opts, prompts, "")

def prompt_for_config(
    context: dict[str, Any], no_input: bool = False
) -> OrderedDict[str, Any]:
    """Prompt user to enter a new config.

    :param dict context: Source for field names and sample values.
    :param no_input: Do not prompt for user input and use only values from context.
    """
    cookiecutter_dict = OrderedDict([])
    env = create_env_with_context(context)
    prompts = context['cookiecutter'].pop('__prompts__', {})

    # First pass: Handle simple and raw variables, plus choices.
    # These must be done first because the dictionaries keys and
    # values might refer to them.
    count = 0
    all_prompts = context['cookiecutter'].items()
    visible_prompts = [k for k, _ in all_prompts if not k.startswith("_")]
    size = len(visible_prompts)
    for key, raw in all_prompts:
        if key.startswith('_') and not key.startswith('__'):
            cookiecutter_dict[key] = raw
            continue
        if key.startswith('__'):
            cookiecutter_dict[key] = render_variable(env, raw, cookiecutter_dict)
            continue

        if not isinstance(raw, dict):
            count += 1
            prefix = f"  [dim][{count}/{size}][/] "

        try:
            if isinstance(raw, list):
                # We are dealing with a choice variable
                val = prompt_choice_for_config(
                    cookiecutter_dict, env, key, raw, no_input, prompts, prefix
                )
                cookiecutter_dict[key] = val
            elif isinstance(raw, bool):
                # We are dealing with a boolean variable
                if no_input:
                    cookiecutter_dict[key] = render_variable(
                        env, raw, cookiecutter_dict
                    )
                else:
                    cookiecutter_dict[key] = read_user_yes_no(key, raw, prompts, prefix)
            elif not isinstance(raw, dict):
                # We are dealing with a regular variable
                val = render_variable(env, raw, cookiecutter_dict)

                if not no_input:
                    val = read_user_variable(key, val, prompts, prefix)

                cookiecutter_dict[key] = val
        except UndefinedError as err:
            msg = f"Unable to render variable '{key}'"
            raise UndefinedVariableInTemplate(msg, err, context) from err

    # Second pass; handle the dictionaries.
    for key, raw in context['cookiecutter'].items():
        # Skip private type dicts not to be rendered.
        if key.startswith('_') and not key.startswith('__'):
            continue

        try:
            if isinstance(raw, dict):
                # We are dealing with a dict variable
                count += 1
                prefix = f"  [dim][{count}/{size}][/] "
                val = render_variable(env, raw, cookiecutter_dict)

                if not no_input and not key.startswith('__'):
                    val = read_user_dict(key, val, prompts, prefix)

                cookiecutter_dict[key] = val
        except UndefinedError as err:
            msg = f"Unable to render variable '{key}'"
            raise UndefinedVariableInTemplate(msg, err, context) from err

    return cookiecutter_dict

def process_response(self, value: str) -> bool:
    """Convert choices to a bool."""
    value = value.strip().lower()
    if value in self.yes_choices:
        return True
    if value in self.no_choices:
        return False
    raise InvalidResponse(self.validate_error_message)

def expand_abbreviations(template: str, abbreviations: dict[str, str]) -> str:
    """Expand abbreviations in a template name.

    :param template: The project template name.
    :param abbreviations: Abbreviation definitions.
    """
    if template in abbreviations:
        return abbreviations[template]

    # Split on colon. If there is no colon, rest will be empty
    # and prefix will be the whole template
    prefix, _sep, rest = template.partition(':')
    if prefix in abbreviations:
        return abbreviations[prefix].format(rest)

    return template

def make_sure_path_exists(path: Path | str) -> None:
    """Ensure that a directory exists.

    :param path: A directory tree path for creation.
    """
    logger.debug('Making sure path exists (creates tree if not exist): %s', path)
    try:
        Path(path).mkdir(parents=True, exist_ok=True)
    except OSError as error:
        msg = f'Unable to create directory at {path}'
        raise OSError(msg) from error

def create_env_with_context(context: dict[str, Any]) -> StrictEnvironment:
    """Create a jinja environment using the provided context."""
    envvars = context.get('cookiecutter', {}).get('_jinja2_env_vars', {})

    return StrictEnvironment(context=context, keep_trailing_newline=True, **envvars)

def is_vcs_installed(repo_type: str) -> bool:
    """
    Check if the version control system for a repo type is installed.

    :param repo_type:
    """
    return bool(which(repo_type))

def drop_recurrences(iterable):
    s = set()
    for item in iterable:
        if item not in s:
            s.add(item)
            yield item

def iterate_authors_by_chronological_order(branch):
    log_call = subprocess.run(
        (
            'git', 'log', branch, '--encoding=utf-8', '--full-history',
            '--reverse', '--format=format:%at;%an;%ae'
        ),
        stdout=subprocess.PIPE, stderr=subprocess.PIPE,
    )
    log_lines = log_call.stdout.decode('utf-8').split('\n')

    authors = tuple(line.strip().split(";")[1] for line in log_lines)
    authors = (author for author in authors if author not in deny_list)
    return drop_recurrences(authors)

def timedelta_parse(s):
    hours, minutes, seconds, microseconds = map(
        int,
        s.replace('.', ':').split(':')
    )
    return datetime_module.timedelta(hours=hours, minutes=minutes,
                                     seconds=seconds,
                                     microseconds=microseconds)

def time_isoformat(time, timespec='microseconds'):
    assert isinstance(time, datetime_module.time)
    if timespec != 'microseconds':
        raise NotImplementedError
    result = '{:02d}:{:02d}:{:02d}.{:06d}'.format(
        time.hour, time.minute, time.second, time.microsecond
    )
    assert len(result) == 15
    return result

def get_write_function(output, overwrite):
    is_path = isinstance(output, (pycompat.PathLike, str))
    if overwrite and not is_path:
        raise Exception('`overwrite=True` can only be used when writing '
                        'content to file.')
    if output is None:
        def write(s):
            stderr = sys.stderr
            try:
                stderr.write(s)
            except UnicodeEncodeError:
                # God damn Python 2
                stderr.write(utils.shitcode(s))
    elif is_path:
        return FileWriter(output, overwrite).write
    elif callable(output):
        write = output
    else:
        assert isinstance(output, utils.WritableStream)

        def write(s):
            output.write(s)
    return write

def set_thread_info_padding(self, thread_info):
    current_thread_len = len(thread_info)
    self.thread_info_padding = max(self.thread_info_padding,
                                   current_thread_len)
    return thread_info.ljust(self.thread_info_padding)

def write(s):
    stderr = sys.stderr
    try:
        stderr.write(s)
    except UnicodeEncodeError:
        # God damn Python 2
        stderr.write(utils.shitcode(s))

def generator_wrapper(*args, **kwargs):
    gen = function(*args, **kwargs)
    method, incoming = gen.send, None
    while True:
        with self:
            try:
                outgoing = method(incoming)
            except StopIteration:
                return
        try:
            method, incoming = gen.send, (yield outgoing)
        except Exception as e:
            method, incoming = gen.throw, e

def get_repr_function(item, custom_repr):
    for condition, action in custom_repr:
        if isinstance(condition, type):
            condition = lambda x, y=condition: isinstance(x, y)
        if condition(item):
            return action
    return repr

def get_shortish_repr(item, custom_repr=(), max_length=None, normalize=False):
    repr_function = get_repr_function(item, custom_repr)
    try:
        r = repr_function(item)
    except Exception:
        r = 'REPR FAILED'
    r = r.replace('\r', '').replace('\n', '')
    if normalize:
        r = normalize_repr(r)
    if max_length:
        r = truncate(r, max_length)
    return r

def truncate(string, max_length):
    if (max_length is None) or (len(string) <= max_length):
        return string
    else:
        left = (max_length - 3) // 2
        right = max_length - 3 - left
        return u'{}...{}'.format(string[:left], string[-right:])

def ensure_tuple(x):
    if isinstance(x, collections_abc.Iterable) and \
                                               not isinstance(x, string_types):
        return tuple(x)
    else:
        return (x,)

def needs_parentheses(source):
    def code(s):
        return compile(s, '<variable>', 'eval').co_code

    return code('{}.x'.format(source)) != code('({}).x'.format(source))

def items(self, frame, normalize=False):
    try:
        main_value = eval(self.code, frame.f_globals or {}, frame.f_locals)
    except Exception:
        return ()
    return self._items(main_value, normalize)

def get_args(cls, dist, header=None):
    """
    Yield write_script() argument tuples for a distribution's
    console_scripts and gui_scripts entry points.
    """
    if header is None:
        header = cls.get_header()
    spec = str(dist.as_requirement())
    for type_ in 'console', 'gui':
        group = type_ + '_scripts'
        for name, ep in dist.get_entry_map(group).items():
            # ensure_safe_name
            if re.search(r'[\\/]', name):
                raise ValueError("Path separators not allowed in script names")
            script_text = TEMPLATE.format(
                          ep.module_name, ep.attrs[0], '.'.join(ep.attrs),
                          spec, group, name)
            args = cls._get_script_args(type_, name, header, script_text)
            for res in args:
                yield res

def init(self, args=None):
    """Fills `settings` with values from `settings.py` and env."""
    from .logs import exception

    self._setup_user_dir()
    self._init_settings_file()

    try:
        self.update(self._settings_from_file())
    except Exception:
        exception("Can't load settings from file", sys.exc_info())

    try:
        self.update(self._settings_from_env())
    except Exception:
        exception("Can't load settings from env", sys.exc_info())

    self.update(self._settings_from_args(args))

def get_loaded_rules(rules_paths):
    """Yields all available rules.

