        except:
            logging.error("Error sending message", exc_info=True)

def on_message(self, message):
    logging.info("got message %r", message)
    parsed = tornado.escape.json_decode(message)
    chat = {"id": str(uuid.uuid4()), "body": parsed["body"]}
    chat["html"] = tornado.escape.to_basestring(
        self.render_string("message.html", message=chat)
    )

    ChatSocketHandler.update_cache(chat)
    ChatSocketHandler.send_updates(chat)

def get_links(html):
    class URLSeeker(HTMLParser):
        def __init__(self):
            HTMLParser.__init__(self)
            self.urls = []

        def handle_starttag(self, tag, attrs):
            href = dict(attrs).get("href")
            if href and tag == "a":
                self.urls.append(href)

    url_seeker = URLSeeker()
    url_seeker.feed(html)
    return url_seeker.urls

def get(self):
    for i in xrange(options.num_chunks):
        self.write('A' * options.chunk_size)
        self.flush()
    self.finish()

def headers_parse_re(headers: str) -> HTTPHeaders:
    h = HTTPHeaders()
    for line in _CRLF_RE.split(headers):
        if line:
            h.parse_line(line)
    return h

def headers_parse_simple(headers: str) -> HTTPHeaders:
    h = HTTPHeaders()
    for line in headers.split("\n"):
        if line.endswith("\r"):
            line = line[:-1]
        if line:
            h.parse_line(line)
    return h

def main():
    parse_command_line()

    try:
        func = Benchmark(options.benchmark).func
    except ValueError:
        known_benchmarks = [benchmark.value for benchmark in Benchmark]
        print(
            "Unknown benchmark: '{}', supported values are: {}"
            .format(options.benchmark, ", ".join(known_benchmarks))
        )
        return

    for _ in range(options.num_runs):
        func()

def get_ax_arg(uri: str) -> str:
    if not ax_ns:
        return ""
    prefix = "openid." + ax_ns + ".type."
    ax_name = None
    for name in handler.request.arguments.keys():
        if handler.get_argument(name) == uri and name.startswith(prefix):
            part = name[len(prefix) :]
            ax_name = "openid." + ax_ns + ".value." + part
            break
    if not ax_name:
        return ""
    return handler.get_argument(ax_name, "")

def chain_future(
    a: Union["Future[_T]", "futures.Future[_T]"],
    b: Union["Future[_T]", "futures.Future[_T]"],
) -> None:
    """Chain two futures together so that when one completes, so does the other.

    The result (success or failure) of ``a`` will be copied to ``b``, unless
    ``b`` has already been completed or cancelled by the time ``a`` finishes.

    .. versionchanged:: 5.0

       Now accepts both Tornado/asyncio `Future` objects and
       `concurrent.futures.Future`.

    """

    def copy(a: "Future[_T]") -> None:
        if b.done():
            return
        if hasattr(a, "exc_info") and a.exc_info() is not None:  # type: ignore
            future_set_exc_info(b, a.exc_info())  # type: ignore
        else:
            a_exc = a.exception()
            if a_exc is not None:
                b.set_exception(a_exc)
            else:
                b.set_result(a.result())

    if isinstance(a, Future):
        future_add_done_callback(a, copy)
    else:
        # concurrent.futures.Future
        from tornado.ioloop import IOLoop

        IOLoop.current().add_future(a, copy)

def future_set_result_unless_cancelled(
    future: "Union[futures.Future[_T], Future[_T]]", value: _T
) -> None:
    """Set the given ``value`` as the `Future`'s result, if not cancelled.

    Avoids ``asyncio.InvalidStateError`` when calling ``set_result()`` on
    a cancelled `asyncio.Future`.

    .. versionadded:: 5.0
    """
    if not future.cancelled():
        future.set_result(value)

def submit(  # type: ignore[override]
    self, fn: Callable[..., _T], *args: Any, **kwargs: Any
) -> "futures.Future[_T]":
    future = futures.Future()  # type: futures.Future[_T]
    try:
        future_set_result_unless_cancelled(future, fn(*args, **kwargs))
    except Exception:
        future_set_exc_info(future, sys.exc_info())
    return future

def run_on_executor_decorator(fn: Callable) -> Callable[..., Future]:
    executor = kwargs.get("executor", "executor")

    @functools.wraps(fn)
    def wrapper(self: Any, *args: Any, **kwargs: Any) -> Future:
        async_future = Future()  # type: Future
        conc_future = getattr(self, executor).submit(fn, self, *args, **kwargs)
        chain_future(conc_future, async_future)
        return async_future

    return wrapper

def copy(a: "Future[_T]") -> None:
    if b.done():
        return
    if hasattr(a, "exc_info") and a.exc_info() is not None:  # type: ignore
        future_set_exc_info(b, a.exc_info())  # type: ignore
    else:
        a_exc = a.exception()
        if a_exc is not None:
            b.set_exception(a_exc)
        else:
            b.set_result(a.result())

def utf8(value: Union[None, str, bytes]) -> Optional[bytes]:
    """Converts a string argument to a byte string.

    If the argument is already a byte string or None, it is returned unchanged.
    Otherwise it must be a unicode string and is encoded as utf8.
    """
    if isinstance(value, _UTF8_TYPES):
        return value
    if not isinstance(value, unicode_type):
        raise TypeError("Expected bytes, unicode, or None; got %r" % type(value))
    return value.encode("utf-8")

def to_unicode(value: Union[None, str, bytes]) -> Optional[str]:
    """Converts a string argument to a unicode string.

    If the argument is already a unicode string or None, it is returned
    unchanged.  Otherwise it must be a byte string and is decoded as utf8.
    """
    if isinstance(value, _TO_UNICODE_TYPES):
        return value
    if not isinstance(value, bytes):
        raise TypeError("Expected bytes, unicode, or None; got %r" % type(value))
    return value.decode("utf-8")

def recursive_unicode(obj: Any) -> Any:
    """Walks a simple data structure, converting byte strings to unicode.

    Supports lists, tuples, and dictionaries.
    """
    if isinstance(obj, dict):
        return {recursive_unicode(k): recursive_unicode(v) for (k, v) in obj.items()}
    elif isinstance(obj, list):
        return list(recursive_unicode(i) for i in obj)
    elif isinstance(obj, tuple):
        return tuple(recursive_unicode(i) for i in obj)
    elif isinstance(obj, bytes):
        return to_unicode(obj)
    else:
        return obj

def make_link(m: typing.Match) -> str:
    url = m.group(1)
    proto = m.group(2)
    if require_protocol and not proto:
        return url  # not protocol, no linkify

    if proto and proto not in permitted_protocols:
        return url  # bad protocol, no linkify

    href = m.group(1)
    if not proto:
        href = "http://" + href  # no proto specified, use http

    if callable(extra_params):
        params = " " + extra_params(href).strip()
    else:
        params = extra_params

    # clip long urls. max_len is just an approximation
    max_len = 30
    if shorten and len(url) > max_len:
        before_clip = url
        if proto:
            proto_len = len(proto) + 1 + len(m.group(3) or "")  # +1 for :
        else:
            proto_len = 0

        parts = url[proto_len:].split("/")
        if len(parts) > 1:
            # Grab the whole host part plus the first bit of the path
            # The path is usually not that interesting once shortened
            # (no more slug, etc), so it really just provides a little
            # extra indication of shortening.
            url = (
                url[:proto_len]
                + parts[0]
                + "/"
                + parts[1][:8].split("?")[0].split(".")[0]
            )

        if len(url) > max_len * 1.5:  # still too long
            url = url[:max_len]

        if url != before_clip:
            amp = url.rfind("&")
            # avoid splitting html char entities
            if amp > max_len - 5:
                url = url[:amp]
            url += "..."

            if len(url) >= len(before_clip):
                url = before_clip
            else:
                # full url is visible on mouse-over (for those who don't
                # have a status bar, such as Safari by default)
                params += ' title="%s"' % href

    return f'<a href="{href}"{params}>{url}</a>'

def multi_future(
    children: Union[Sequence[_Yieldable], Mapping[Any, _Yieldable]],
    quiet_exceptions: "Union[Type[Exception], Tuple[Type[Exception], ...]]" = (),
) -> "Union[Future[List], Future[Dict]]":
    """Wait for multiple asynchronous futures in parallel.

    Since Tornado 6.0, this function is exactly the same as `multi`.

    .. versionadded:: 4.0

    .. versionchanged:: 4.2
       If multiple ``Futures`` fail, any exceptions after the first (which is
       raised) will be logged. Added the ``quiet_exceptions``
       argument to suppress this logging for selected exception types.

    .. deprecated:: 4.3
       Use `multi` instead.
    """
    if isinstance(children, dict):
        keys = list(children.keys())  # type: Optional[List]
        children_seq = children.values()  # type: Iterable
    else:
        keys = None
        children_seq = children
    children_futs = list(map(convert_yielded, children_seq))
    assert all(is_future(i) or isinstance(i, _NullFuture) for i in children_futs)
    unfinished_children = set(children_futs)

    future = _create_future()
    if not children_futs:
        future_set_result_unless_cancelled(future, {} if keys is not None else [])

    def callback(fut: Future) -> None:
        unfinished_children.remove(fut)
        if not unfinished_children:
            result_list = []
            for f in children_futs:
                try:
                    result_list.append(f.result())
                except Exception as e:
                    if future.done():
                        if not isinstance(e, quiet_exceptions):
                            app_log.error(
                                "Multiple exceptions in yield list", exc_info=True
                            )
                    else:
                        future_set_exc_info(future, sys.exc_info())
            if not future.done():
                if keys is not None:
                    future_set_result_unless_cancelled(
                        future, dict(zip(keys, result_list))
                    )
                else:
                    future_set_result_unless_cancelled(future, result_list)

    listening = set()  # type: Set[Future]
    for f in children_futs:
        if f not in listening:
            listening.add(f)
            future_add_done_callback(f, callback)
    return future

def convert_yielded(yielded: _Yieldable) -> Future:
    """Convert a yielded object into a `.Future`.

    The default implementation accepts lists, dictionaries, and
    Futures. This has the side effect of starting any coroutines that
    did not start themselves, similar to `asyncio.ensure_future`.

    If the `~functools.singledispatch` library is available, this function
    may be extended to support additional types. For example::

        @convert_yielded.register(asyncio.Future)
        def _(asyncio_future):
            return tornado.platform.asyncio.to_tornado_future(asyncio_future)

    .. versionadded:: 4.1

    """
    if yielded is None or yielded is moment:
        return moment
    elif yielded is _null_future:
        return _null_future
    elif isinstance(yielded, (list, dict)):
        return multi(yielded)  # type: ignore
    elif is_future(yielded):
        return typing.cast(Future, yielded)
    elif isawaitable(yielded):
        return _wrap_awaitable(yielded)  # type: ignore
    else:
        raise BadYieldError(f"yielded unknown object {yielded!r}")

def next(self) -> Future:
    """Returns a `.Future` that will yield the next available result.

    Note that this `.Future` will not be the same object as any of
    the inputs.
    """
    self._running_future = Future()

    if self._finished:
        return self._return_result(self._finished.popleft())

    return self._running_future

def callback(fut: Future) -> None:
    unfinished_children.remove(fut)
    if not unfinished_children:
        result_list = []
        for f in children_futs:
            try:
                result_list.append(f.result())
            except Exception as e:
                if future.done():
                    if not isinstance(e, quiet_exceptions):
                        app_log.error(
                            "Multiple exceptions in yield list", exc_info=True
                        )
                else:
                    future_set_exc_info(future, sys.exc_info())
        if not future.done():
            if keys is not None:
                future_set_result_unless_cancelled(
                    future, dict(zip(keys, result_list))
                )
            else:
                future_set_result_unless_cancelled(future, result_list)

def error_callback(future: Future) -> None:
    try:
        future.result()
    except asyncio.CancelledError:
        pass
    except Exception as e:
        if not isinstance(e, quiet_exceptions):
            app_log.error(
                "Exception in Future %r after timeout", future, exc_info=True
            )

def timeout_callback() -> None:
    if not result.done():
        result.set_exception(TimeoutError("Timeout"))
    # In case the wrapped future goes on to fail, log it.
    future_add_done_callback(future_converted, error_callback)

def run(self) -> None:
    """Starts or resumes the generator, running until it reaches a
    yield point that is not ready.
    """
    if self.running or self.finished:
        return
    try:
        self.running = True
        while True:
            future = self.future
            if future is None:
                raise Exception("No pending future")
            if not future.done():
                return
            self.future = None
            try:
                try:
                    value = future.result()
                except Exception as e:
                    # Save the exception for later. It's important that
                    # gen.throw() not be called inside this try/except block
                    # because that makes sys.exc_info behave unexpectedly.
                    exc: Optional[Exception] = e
                else:
                    exc = None
                finally:
                    future = None

                if exc is not None:
                    try:
                        yielded = self.gen.throw(exc)
                    finally:
                        # Break up a circular reference for faster GC on
                        # CPython.
                        del exc
                else:
                    yielded = self.gen.send(value)

            except (StopIteration, Return) as e:
                self.finished = True
                self.future = _null_future
                future_set_result_unless_cancelled(
                    self.result_future, _value_from_stopiteration(e)
                )
                self.result_future = None  # type: ignore
                return
            except Exception:
                self.finished = True
                self.future = _null_future
                future_set_exc_info(self.result_future, sys.exc_info())
                self.result_future = None  # type: ignore
                return
            if not self.handle_yield(yielded):
                return
            yielded = None
    finally:
        self.running = False

def handle_yield(self, yielded: _Yieldable) -> bool:
    try:
        self.future = convert_yielded(yielded)
    except BadYieldError:
        self.future = Future()
        future_set_exc_info(self.future, sys.exc_info())

    if self.future is moment:
        self.io_loop.add_callback(self.ctx_run, self.run)
        return False
    elif self.future is None:
        raise Exception("no pending future")
    elif not self.future.done():

        def inner(f: Any) -> None:
            # Break a reference cycle to speed GC.
            f = None  # noqa: F841
            self.ctx_run(self.run)

        self.io_loop.add_future(self.future, inner)
        return False
    return True

def handle_exception(
    self, typ: Type[Exception], value: Exception, tb: types.TracebackType
) -> bool:
    if not self.running and not self.finished:
        self.future = Future()
        future_set_exc_info(self.future, (typ, value, tb))
        self.ctx_run(self.run)
        return True
    else:
        return False

def parse_int(s: str) -> int:
    """Parse a non-negative integer from a string."""
    if DIGITS.fullmatch(s) is None:
        raise ValueError("not an integer: %r" % s)
    return int(s)

def parse_hex_int(s: str) -> int:
    """Parse a non-negative hexadecimal integer from a string."""
    if HEXDIGITS.fullmatch(s) is None:
        raise ValueError("not a hexadecimal integer: %r" % s)
    return int(s, 16)

def close(self) -> None:
    if self.stream is not None:
        self.stream.close()
    self._clear_callbacks()
    if not self._finish_future.done():
        future_set_result_unless_cancelled(self._finish_future, None)

def headers_received(
    self,
    start_line: Union[httputil.RequestStartLine, httputil.ResponseStartLine],
    headers: httputil.HTTPHeaders,
) -> Optional[Awaitable[None]]:
    if headers.get("Content-Encoding", "").lower() == "gzip":
        self._decompressor = GzipDecompressor()
        # Downstream delegates will only see uncompressed data,
        # so rename the content-encoding header.
        # (but note that curl_httpclient doesn't do this).
        headers.add("X-Consumed-Content-Encoding", headers["Content-Encoding"])
        del headers["Content-Encoding"]
    return self._delegate.headers_received(start_line, headers)

def finish(self) -> None:
    if self._decompressor is not None:
        tail = self._decompressor.flush()
        if tail:
            # The tail should always be empty: decompress returned
            # all that it can in data_received and the only
            # purpose of the flush call is to detect errors such
            # as truncated input. If we did legitimately get a new
            # chunk at this point we'd need to change the
            # interface to make finish() a coroutine.
            raise ValueError(
                "decompressor.flush returned data; possible truncated input"
            )
    return self._delegate.finish()

def main() -> None:
    from tornado.options import define, options, parse_command_line

    define("print_headers", type=bool, default=False)
    define("print_body", type=bool, default=True)
    define("follow_redirects", type=bool, default=True)
    define("validate_cert", type=bool, default=True)
    define("proxy_host", type=str)
    define("proxy_port", type=int)
    args = parse_command_line()
    client = HTTPClient()
    for arg in args:
        try:
            response = client.fetch(
                arg,
                follow_redirects=options.follow_redirects,
                validate_cert=options.validate_cert,
                proxy_host=options.proxy_host,
                proxy_port=options.proxy_port,
            )
        except HTTPError as e:
            if e.response is not None:
                response = e.response
            else:
                raise
        if options.print_headers:
            print(response.headers)
        if options.print_body:
            print(native_str(response.body))
    client.close()

def close(self) -> None:
    """Closes the HTTPClient, freeing any resources used."""
    if not self._closed:
        self._async_client.close()
        self._io_loop.close()
        self._closed = True

def initialize(self, defaults: Optional[Dict[str, Any]] = None) -> None:
    self.io_loop = IOLoop.current()
    self.defaults = dict(HTTPRequest._DEFAULTS)
    if defaults is not None:
        self.defaults.update(defaults)
    self._closed = False

def body(self) -> bytes:
    if self.buffer is None:
        return b""
    elif self._body is None:
        self._body = self.buffer.getvalue()

    return self._body

def handle_response(response: "HTTPResponse") -> None:
    if response.error:
        if raise_error or not response._error_is_response_code:
            future_set_exception_unless_cancelled(future, response.error)
            return
    future_set_result_unless_cancelled(future, response)

def start_request(
    self, server_conn: object, request_conn: httputil.HTTPConnection
) -> httputil.HTTPMessageDelegate:
    if isinstance(self.request_callback, httputil.HTTPServerConnectionDelegate):
        delegate = self.request_callback.start_request(server_conn, request_conn)
    else:
        delegate = _CallableAdapter(self.request_callback, request_conn)

    if self.xheaders:
        delegate = _ProxyAdapter(delegate, request_conn)

    return delegate

def headers_received(
    self,
    start_line: Union[httputil.RequestStartLine, httputil.ResponseStartLine],
    headers: httputil.HTTPHeaders,
) -> Optional[Awaitable[None]]:
    # TODO: either make context an official part of the
    # HTTPConnection interface or figure out some other way to do this.
    self.connection.context._apply_xheaders(headers)  # type: ignore
    return self.delegate.headers_received(start_line, headers)

def parse_body_arguments(
    content_type: str,
    body: bytes,
    arguments: Dict[str, List[bytes]],
    files: Dict[str, List[HTTPFile]],
    headers: Optional[HTTPHeaders] = None,
) -> None:
    """Parses a form request body.

    Supports ``application/x-www-form-urlencoded`` and
    ``multipart/form-data``.  The ``content_type`` parameter should be
    a string and ``body`` should be a byte string.  The ``arguments``
    and ``files`` parameters are dictionaries that will be updated
    with the parsed contents.
    """
    if content_type.startswith("application/x-www-form-urlencoded"):
        if headers and "Content-Encoding" in headers:
            raise HTTPInputError(
                "Unsupported Content-Encoding: %s" % headers["Content-Encoding"]
            )
        try:
            # real charset decoding will happen in RequestHandler.decode_argument()
            uri_arguments = parse_qs_bytes(body, keep_blank_values=True)
        except Exception as e:
            raise HTTPInputError("Invalid x-www-form-urlencoded body: %s" % e) from e
        for name, values in uri_arguments.items():
            if values:
                arguments.setdefault(name, []).extend(values)
    elif content_type.startswith("multipart/form-data"):
        if headers and "Content-Encoding" in headers:
            raise HTTPInputError(
                "Unsupported Content-Encoding: %s" % headers["Content-Encoding"]
            )
        try:
            fields = content_type.split(";")
            for field in fields:
                k, sep, v = field.strip().partition("=")
                if k == "boundary" and v:
                    parse_multipart_form_data(utf8(v), body, arguments, files)
                    break
            else:
                raise HTTPInputError("multipart boundary not found")
        except Exception as e:
            raise HTTPInputError("Invalid multipart/form-data: %s" % e) from e

def format_timestamp(
    ts: Union[int, float, tuple, time.struct_time, datetime.datetime],
) -> str:
    """Formats a timestamp in the format used by HTTP.

    The argument may be a numeric timestamp as returned by `time.time`,
    a time tuple as returned by `time.gmtime`, or a `datetime.datetime`
    object. Naive `datetime.datetime` objects are assumed to represent
    UTC; aware objects are converted to UTC before formatting.

    >>> format_timestamp(1359312200)
    'Sun, 27 Jan 2013 18:43:20 GMT'
    """
    if isinstance(ts, (int, float)):
        time_num = ts
    elif isinstance(ts, (tuple, time.struct_time)):
        time_num = calendar.timegm(ts)
    elif isinstance(ts, datetime.datetime):
        time_num = calendar.timegm(ts.utctimetuple())
    else:
        raise TypeError("unknown timestamp type: %r" % ts)
    return email.utils.formatdate(time_num, usegmt=True)

def encode_username_password(
    username: Union[str, bytes], password: Union[str, bytes]
) -> bytes:
    """Encodes a username/password pair in the format used by HTTP auth.

    The return value is a byte string in the form ``username:password``.

    .. versionadded:: 5.1
    """
    if isinstance(username, unicode_type):
        username = unicodedata.normalize("NFC", username)
    if isinstance(password, unicode_type):
        password = unicodedata.normalize("NFC", password)
    return utf8(username) + b":" + utf8(password)

def split_host_and_port(netloc: str) -> Tuple[str, Optional[int]]:
    """Returns ``(host, port)`` tuple from ``netloc``.

    Returned ``port`` will be ``None`` if not present.

    .. versionadded:: 4.1
    """
    match = _netloc_re.match(netloc)
    if match:
        host = match.group(1)
        port = int(match.group(2))  # type: Optional[int]
    else:
        host = netloc
        port = None
    return (host, port)

def qs_to_qsl(qs: Dict[str, List[AnyStr]]) -> Iterable[Tuple[str, AnyStr]]:
    """Generator converting a result of ``parse_qs`` back to name-value pairs.

    .. versionadded:: 5.0
    """
    for k, vs in qs.items():
        for v in vs:
            yield (k, v)

def parse_cookie(cookie: str) -> Dict[str, str]:
    """Parse a ``Cookie`` HTTP header into a dict of name/value pairs.

    This function attempts to mimic browser cookie parsing behavior;
    it specifically does not follow any of the cookie-related RFCs
    (because browsers don't either).

    The algorithm used is identical to that used by Django version 1.9.10.

    .. versionadded:: 4.4.2
    """
    cookiedict = {}
    for chunk in cookie.split(";"):
        if "=" in chunk:
            key, val = chunk.split("=", 1)
        else:
            # Assume an empty name per
            # https://bugzilla.mozilla.org/show_bug.cgi?id=169091
            key, val = "", chunk
        key, val = key.strip(), val.strip()
        if key or val:
            # unquote using Python's algorithm.
            cookiedict[key] = _unquote_cookie(val)
    return cookiedict

def add(self, name: str, value: str, *, _chars_are_bytes: bool = True) -> None:
    """Adds a new value for the given key."""
    if not _ABNF.field_name.fullmatch(name):
        raise HTTPInputError("Invalid header name %r" % name)
    if _chars_are_bytes:
        if not _ABNF.field_value.fullmatch(to_unicode(value)):
            # TODO: the fact we still support bytes here (contrary to type annotations)
            # and still test for it should probably be changed.
            raise HTTPInputError("Invalid header value %r" % value)
    else:
        if _FORBIDDEN_HEADER_CHARS_RE.search(value):
            raise HTTPInputError("Invalid header value %r" % value)
    norm_name = _normalize_header(name)
    self._last_key = norm_name
    if norm_name in self:
        self._dict[norm_name] = (
            native_str(self[norm_name]) + "," + native_str(value)
        )
        self._as_list[norm_name].append(value)
    else:
        self[norm_name] = value

def get_all(self) -> Iterable[Tuple[str, str]]:
    """Returns an iterable of all (name, value) pairs.

    If a header has multiple values, multiple pairs will be
    returned with the same name.
    """
    for name, values in self._as_list.items():
        for value in values:
            yield (name, value)

def request_time(self) -> float:
    """Returns the amount of time it took for this request to execute."""
    if self._finish_time is None:
        return time.time() - self._start_time
    else:
        return self._finish_time - self._start_time

def configure(
    cls, impl: "Union[None, str, Type[Configurable]]", **kwargs: Any
) -> None:
    from tornado.platform.asyncio import BaseAsyncIOLoop

    if isinstance(impl, str):
        impl = import_object(impl)
    if isinstance(impl, type) and not issubclass(impl, BaseAsyncIOLoop):
        raise RuntimeError("only AsyncIOLoop is allowed when asyncio is available")
    super().configure(impl, **kwargs)

def close_fd(self, fd: Union[int, _Selectable]) -> None:
    # """Utility method to close an ``fd``.

    # If ``fd`` is a file-like object, we close it directly; otherwise
    # we use `os.close`.

    # This method is provided for use by `IOLoop` subclasses (in
    # implementations of ``IOLoop.close(all_fds=True)`` and should
    # not generally be used by application code.

    # .. versionadded:: 4.0
    # """
    try:
        if isinstance(fd, int):
            os.close(fd)
        else:
            fd.close()
    except OSError:
        pass

def stop(self) -> None:
    """Stops the timer."""
    self._running = False
    if self._timeout is not None:
        self.io_loop.remove_timeout(self._timeout)
        self._timeout = None

def run() -> None:
    try:
        result = func()
        if result is not None:
            from tornado.gen import convert_yielded

            result = convert_yielded(result)
    except Exception:
        fut = Future()  # type: Future[Any]
        future_cell["future"] = fut
        future_set_exc_info(fut, sys.exc_info())
    else:
        if is_future(result):
            future_cell["future"] = result
        else:
            fut = Future()
            future_cell["future"] = fut
            fut.set_result(result)
    assert future_cell["future"] is not None
    self.add_future(future_cell["future"], lambda future: self.stop())

def append(self, data: Union[bytes, bytearray, memoryview]) -> None:
    """
    Append the given piece of data (should be a buffer-compatible object).
    """
    size = len(data)
    if size > self._large_buf_threshold:
        if not isinstance(data, memoryview):
            data = memoryview(data)
        self._buffers.append((True, data))
    elif size > 0:
        if self._buffers:
            is_memview, b = self._buffers[-1]
            new_buf = is_memview or len(b) >= self._large_buf_threshold
        else:
            new_buf = True
        if new_buf:
            self._buffers.append((False, bytearray(data)))
        else:
            b += data  # type: ignore

    self._size += size

def peek(self, size: int) -> memoryview:
    """
    Get a view over at most ``size`` bytes (possibly fewer) at the
    current buffer position.
    """
    assert size > 0
    try:
        is_memview, b = self._buffers[0]
    except IndexError:
        return memoryview(b"")

    pos = self._first_pos
    if is_memview:
        return typing.cast(memoryview, b[pos : pos + size])
    else:
        return memoryview(b)[pos : pos + size]

def close(
    self,
    exc_info: Union[
        None,
        bool,
        BaseException,
        Tuple[
            "Optional[Type[BaseException]]",
            Optional[BaseException],
            Optional[TracebackType],
        ],
    ] = False,
) -> None:
    """Close this stream.

    If ``exc_info`` is true, set the ``error`` attribute to the current
    exception from `sys.exc_info` (or if ``exc_info`` is a tuple,
    use that instead of `sys.exc_info`).
    """
    if not self.closed():
        if exc_info:
            if isinstance(exc_info, tuple):
                self.error = exc_info[1]
            elif isinstance(exc_info, BaseException):
                self.error = exc_info
            else:
                exc_info = sys.exc_info()
                if any(exc_info):
                    self.error = exc_info[1]
        if self._read_until_close:
            self._read_until_close = False
            self._finish_read(self._read_buffer_size)
        elif self._read_future is not None:
            # resolve reads that are pending and ready to complete
            try:
                pos = self._find_read_pos()
            except UnsatisfiableReadError:
                pass
            else:
                if pos is not None:
                    self._read_from_buffer(pos)
        if self._state is not None:
            self.io_loop.remove_handler(self.fileno())
            self._state = None
        self.close_fd()
        self._closed = True
    self._signal_closed()

def read_from_fd(self, buf: Union[bytearray, memoryview]) -> Optional[int]:
    try:
        return self.socket.recv_into(buf, len(buf))
    except BlockingIOError:
        return None
    finally:
        del buf

def set_nodelay(self, value: bool) -> None:
    if self.socket is not None and self.socket.family in (
        socket.AF_INET,
        socket.AF_INET6,
    ):
        try:
            self.socket.setsockopt(
                socket.IPPROTO_TCP, socket.TCP_NODELAY, 1 if value else 0
            )
        except OSError as e:
            # Sometimes setsockopt will fail if the socket is closed
            # at the wrong time.  This can happen with HTTPServer
            # resetting the value to ``False`` between requests.
            if e.errno != errno.EINVAL and not self._is_connreset(e):
                raise

def read_from_fd(self, buf: Union[bytearray, memoryview]) -> Optional[int]:
    try:
        if self._ssl_accepting:
            # If the handshake hasn't finished yet, there can't be anything
            # to read (attempting to read may or may not raise an exception
            # depending on the SSL version)
            return None
        # clip buffer size at 1GB since SSL sockets only support upto 2GB
        # this change in behaviour is transparent, since the function is
        # already expected to (possibly) read less than the provided buffer
        if len(buf) >> 30:
            buf = memoryview(buf)[: 1 << 30]
        try:
            return self.socket.recv_into(buf, len(buf))
        except ssl.SSLError as e:
            # SSLError is a subclass of socket.error, so this except
            # block must come first.
            if e.args[0] == ssl.SSL_ERROR_WANT_READ:
                return None
            else:
                raise
        except BlockingIOError:
            return None
    finally:
        del buf

def read_from_fd(self, buf: Union[bytearray, memoryview]) -> Optional[int]:
    try:
        return self._fio.readinto(buf)  # type: ignore
    except OSError as e:
        if errno_from_exception(e) == errno.EBADF:
            # If the writing half of a pipe is closed, select will
            # report it as readable but reads will fail with EBADF.
            self.close(exc_info=e)
            return None
        else:
            raise
    finally:
        del buf

def get_closest(cls, *locale_codes: str) -> "Locale":
    """Returns the closest match for the given locale code."""
    for code in locale_codes:
        if not code:
            continue
        code = code.replace("-", "_")
        parts = code.split("_")
        if len(parts) > 2:
            continue
        elif len(parts) == 2:
            code = parts[0].lower() + "_" + parts[1].upper()
        if code in _supported_locales:
            return cls.get(code)
        if parts[0].lower() in _supported_locales:
            return cls.get(parts[0].lower())
    return cls.get(_default_locale)

def get(cls, code: str) -> "Locale":
    """Returns the Locale for the given locale code.

    If it is not supported, we raise an exception.
    """
    if code not in cls._cache:
        assert code in _supported_locales
        translations = _translations.get(code, None)
        if translations is None:
            locale = CSVLocale(code, {})  # type: Locale
        elif _use_gettext:
            locale = GettextLocale(code, translations)
        else:
            locale = CSVLocale(code, translations)
        cls._cache[code] = locale
    return cls._cache[code]

def format_day(
    self, date: datetime.datetime, gmt_offset: int = 0, dow: bool = True
) -> bool:
    """Formats the given date as a day of week.

    Example: "Monday, January 22". You can remove the day of week with
    ``dow=False``.
    """
    local_date = date - datetime.timedelta(minutes=gmt_offset)
    _ = self.translate
    if dow:
        return _("%(weekday)s, %(month_name)s %(day)s") % {
            "month_name": self._months[local_date.month - 1],
            "weekday": self._weekdays[local_date.weekday()],
            "day": str(local_date.day),
        }
    else:
        return _("%(month_name)s %(day)s") % {
            "month_name": self._months[local_date.month - 1],
            "day": str(local_date.day),
        }

def list(self, parts: Any) -> str:
    """Returns a comma-separated list for the given list of parts.

    The format is, e.g., "A, B and C", "A and B" or just "A" for lists
    of size 1.
    """
    _ = self.translate
    if len(parts) == 0:
        return ""
    if len(parts) == 1:
        return parts[0]
    comma = " \u0648 " if self.code.startswith("fa") else ", "
    return _("%(commas)s and %(last)s") % {
        "commas": comma.join(parts[:-1]),
        "last": parts[len(parts) - 1],
    }

def friendly_number(self, value: int) -> str:
    """Returns a comma-separated number for the given integer."""
    if self.code not in ("en", "en_US"):
        return str(value)
    s = str(value)
    parts = []
    while s:
        parts.append(s[-3:])
        s = s[:-3]
    return ",".join(reversed(parts))

def translate(
    self,
    message: str,
    plural_message: Optional[str] = None,
    count: Optional[int] = None,
) -> str:
    if plural_message is not None:
        assert count is not None
        if count != 1:
            message = plural_message
            message_dict = self.translations.get("plural", {})
        else:
            message_dict = self.translations.get("singular", {})
    else:
        message_dict = self.translations.get("unknown", {})
    return message_dict.get(message, message)

def pgettext(
    self,
    context: str,
    message: str,
    plural_message: Optional[str] = None,
    count: Optional[int] = None,
) -> str:
    if self.translations:
        gen_log.warning("pgettext is not supported by CSVLocale")
    return self.translate(message, plural_message, count)

def translate(
    self,
    message: str,
    plural_message: Optional[str] = None,
    count: Optional[int] = None,
) -> str:
    if plural_message is not None:
        assert count is not None
        return self.ngettext(message, plural_message, count)
    else:
        return self.gettext(message)

def notify(self, n: int = 1) -> None:
    """Wake ``n`` waiters."""
    waiters = []  # Waiters we plan to run right now.
    while n and self._waiters:
        waiter = self._waiters.popleft()
        if not waiter.done():  # Might have timed out.
            n -= 1
            waiters.append(waiter)

    for waiter in waiters:
        future_set_result_unless_cancelled(waiter, True)

def set(self) -> None:
    """Set the internal flag to ``True``. All waiters are awakened.

    Calling `.wait` once the flag is set will not block.
    """
    if not self._value:
        self._value = True

        for fut in self._waiters:
            if not fut.done():
                fut.set_result(None)

def acquire(
    self, timeout: Optional[Union[float, datetime.timedelta]] = None
) -> Awaitable[_ReleasingContextManager]:
    """Decrement the counter. Returns an awaitable.

    Block if the counter is zero and wait for a `.release`. The awaitable
    raises `.TimeoutError` after the deadline.
    """
    waiter = Future()  # type: Future[_ReleasingContextManager]
    if self._value > 0:
        self._value -= 1
        waiter.set_result(_ReleasingContextManager(self))
    else:
        self._waiters.append(waiter)
        if timeout:

            def on_timeout() -> None:
                if not waiter.done():
                    waiter.set_exception(gen.TimeoutError())
                self._garbage_collect()

            io_loop = ioloop.IOLoop.current()
            timeout_handle = io_loop.add_timeout(timeout, on_timeout)
            waiter.add_done_callback(
                lambda _: io_loop.remove_timeout(timeout_handle)
            )
    return waiter

def release(self) -> None:
    """Increment the counter and wake one waiter."""
    if self._value >= self._initial_value:
        raise ValueError("Semaphore released too many times")
    super().release()

def release(self) -> None:
    """Unlock.

    The first coroutine in line waiting for `acquire` gets the lock.

    If not locked, raise a `RuntimeError`.
    """
    try:
        self._block.release()
    except ValueError:
        raise RuntimeError("release unlocked lock")

def is_valid_ip(ip: str) -> bool:
    """Returns ``True`` if the given string is a well-formed IP address.

    Supports IPv4 and IPv6.
    """
    if not ip or "\x00" in ip:
        # getaddrinfo resolves empty strings to localhost, and truncates
        # on zero bytes.
        return False
    try:
        res = socket.getaddrinfo(
            ip, 0, socket.AF_UNSPEC, socket.SOCK_STREAM, 0, socket.AI_NUMERICHOST
        )
        return bool(res)
    except socket.gaierror as e:
        if e.args[0] == socket.EAI_NONAME:
            return False
        raise
    except UnicodeError:
        # `socket.getaddrinfo` will raise a UnicodeError from the
        # `idna` decoder if the input is longer than 63 characters,
        # even for socket.AI_NUMERICHOST.  See
        # https://bugs.python.org/issue32958 for discussion
        return False
    return True

def initialize(
    self,
    executor: Optional[concurrent.futures.Executor] = None,
    close_executor: bool = True,
) -> None:
    if executor is not None:
        self.executor = executor
        self.close_executor = close_executor
    else:
        self.executor = dummy_executor
        self.close_executor = False

def resolve(
    self, host: str, port: int, family: socket.AddressFamily = socket.AF_UNSPEC
) -> Awaitable[List[Tuple[int, Any]]]:
    if (host, port, family) in self.mapping:
        host, port = self.mapping[(host, port, family)]
    elif (host, port) in self.mapping:
        host, port = self.mapping[(host, port)]
    elif host in self.mapping:
        host = self.mapping[host]
    return self.resolver.resolve(host, port, family)

def as_dict(self) -> Dict[str, Any]:
    """The names and values of all options.

    .. versionadded:: 3.1
    """
    return {opt.name: opt.value() for name, opt in self._options.items()}

def parse(self, value: str) -> Any:
    _parse = {
        datetime.datetime: self._parse_datetime,
        datetime.timedelta: self._parse_timedelta,
        bool: self._parse_bool,
        basestring_type: self._parse_string,
    }.get(
        self.type, self.type
    )  # type: Callable[[str], Any]
    if self.multiple:
        self._value = []
        for part in value.split(","):
            if issubclass(self.type, numbers.Integral):
                # allow ranges of the form X:Y (inclusive at both ends)
                lo_str, _, hi_str = part.partition(":")
                lo = _parse(lo_str)
                hi = _parse(hi_str) if hi_str else lo
                self._value.extend(range(lo, hi + 1))
            else:
                self._value.append(_parse(part))
    else:
        self._value = _parse(value)
    if self.callback is not None:
        self.callback(self._value)
    return self.value()

def set(self, value: Any) -> None:
    if self.multiple:
        if not isinstance(value, list):
            raise Error(
                "Option %r is required to be a list of %s"
                % (self.name, self.type.__name__)
            )
        for item in value:
            if item is not None and not isinstance(item, self.type):
                raise Error(
                    "Option %r is required to be a list of %s"
                    % (self.name, self.type.__name__)
                )
    else:
        if value is not None and not isinstance(value, self.type):
            raise Error(
                "Option %r is required to be a %s (%s given)"
                % (self.name, self.type.__name__, type(value))
            )
    self._value = value
    if self.callback is not None:
        self.callback(self._value)

def cpu_count() -> int:
    """Returns the number of processors on this machine."""
    if multiprocessing is None:
        return 1
    try:
        return multiprocessing.cpu_count()
    except NotImplementedError:
        pass
    try:
        return os.sysconf("SC_NPROCESSORS_CONF")  # type: ignore
    except (AttributeError, ValueError):
        pass
    gen_log.error("Could not detect number of processors; assuming 1")
    return 1

def callback(ret: int) -> None:
    if ret != 0 and raise_error:
        # Unfortunately we don't have the original args any more.
        future_set_exception_unless_cancelled(
            future, CalledProcessError(ret, "unknown")
        )
    else:
        future_set_result_unless_cancelled(future, ret)

def full(self) -> bool:
    if self.maxsize == 0:
        return False
    else:
        return self.qsize() >= self.maxsize

def get_nowait(self) -> _T:
    """Remove and return an item from the queue without blocking.

    Return an item if one is immediately available, else raise
    `QueueEmpty`.
    """
    self._consume_expired()
    if self._putters:
        assert self.full(), "queue not full, why are putters waiting?"
        item, putter = self._putters.popleft()
        self.__put_internal(item)
        future_set_result_unless_cancelled(putter, None)
        return self._get()
    elif self.qsize():
        return self._get()
    else:
        raise QueueEmpty

def add_rules(self, rules: _RuleList) -> None:
    """Appends new rules to the router.

    :arg rules: a list of Rule instances (or tuples of arguments, which are
        passed to Rule constructor).
    """
    for rule in rules:
        if isinstance(rule, (tuple, list)):
            assert len(rule) in (2, 3, 4)
            if isinstance(rule[0], basestring_type):
                rule = Rule(PathMatches(rule[0]), *rule[1:])
            else:
                rule = Rule(*rule)

        self.rules.append(self.process_rule(rule))

def find_handler(
    self, request: httputil.HTTPServerRequest, **kwargs: Any
) -> Optional[httputil.HTTPMessageDelegate]:
    for rule in self.rules:
        target_params = rule.matcher.match(request)
        if target_params is not None:
            if rule.target_kwargs:
                target_params["target_kwargs"] = rule.target_kwargs

            delegate = self.get_target_delegate(
                rule.target, request, **target_params
            )

            if delegate is not None:
                return delegate

    return None

def get_target_delegate(
    self, target: Any, request: httputil.HTTPServerRequest, **target_params: Any
) -> Optional[httputil.HTTPMessageDelegate]:
    """Returns an instance of `~.httputil.HTTPMessageDelegate` for a
    Rule's target. This method is called by `~.find_handler` and can be
    extended to provide additional target types.

    :arg target: a Rule's target.
    :arg httputil.HTTPServerRequest request: current request.
    :arg target_params: additional parameters that can be useful
        for `~.httputil.HTTPMessageDelegate` creation.
    """
    if isinstance(target, Router):
        return target.find_handler(request, **target_params)

    elif isinstance(target, httputil.HTTPServerConnectionDelegate):
        assert request.connection is not None
        return target.start_request(request.server_connection, request.connection)

    elif callable(target):
        assert request.connection is not None
        return _CallableAdapter(
            partial(target, **target_params), request.connection
        )

    return None

def process_rule(self, rule: "Rule") -> "Rule":
    rule = super().process_rule(rule)

    if rule.name:
        if rule.name in self.named_rules:
            app_log.warning(
                "Multiple handlers named %s; replacing previous value", rule.name
            )
        self.named_rules[rule.name] = rule

    return rule

def reverse_url(self, name: str, *args: Any) -> Optional[str]:
    if name in self.named_rules:
        return self.named_rules[name].matcher.reverse(*args)

    for rule in self.rules:
        if isinstance(rule.target, ReversibleRouter):
            reversed_url = rule.target.reverse_url(name, *args)
            if reversed_url is not None:
                return reversed_url

    return None

def match(self, request: httputil.HTTPServerRequest) -> Optional[Dict[str, Any]]:
    if self.host_pattern.match(request.host_name):
        return {}

    return None

def match(self, request: httputil.HTTPServerRequest) -> Optional[Dict[str, Any]]:
    # Look for default host if not behind load balancer (for debugging)
    if "X-Real-Ip" not in request.headers:
        if self.host_pattern.match(self.application.default_host):
            return {}
    return None

def match(self, request: httputil.HTTPServerRequest) -> Optional[Dict[str, Any]]:
    match = self.regex.match(request.path)
    if match is None:
        return None
    if not self.regex.groups:
        return {}

    path_args = []  # type: List[bytes]
    path_kwargs = {}  # type: Dict[str, bytes]

    # Pass matched groups to the handler.  Since
    # match.groups() includes both named and
    # unnamed groups, we want to use either groups
    # or groupdict but not both.
    if self.regex.groupindex:
        path_kwargs = {
            str(k): _unquote_or_none(v) for (k, v) in match.groupdict().items()
        }
    else:
        path_args = [_unquote_or_none(s) for s in match.groups()]

    return dict(path_args=path_args, path_kwargs=path_kwargs)

def reverse(self, *args: Any) -> Optional[str]:
    if self._path is None:
        raise ValueError("Cannot reverse url regex " + self.regex.pattern)
    assert len(args) == self._group_count, (
        "required number of arguments " "not found"
    )
    if not len(args):
        return self._path
    converted_args = []
    for a in args:
        if not isinstance(a, (unicode_type, bytes)):
            a = str(a)
        converted_args.append(url_escape(utf8(a), plus=False))
    return self._path % tuple(converted_args)

def close(self) -> None:
    super().close()
    if self.own_resolver:
        self.resolver.close()
    self.tcp_client.close()

def fetch_impl(
    self, request: HTTPRequest, callback: Callable[[HTTPResponse], None]
) -> None:
    key = object()
    self.queue.append((key, request, callback))
    assert request.connect_timeout is not None
    assert request.request_timeout is not None
    timeout_handle = None
    if len(self.active) >= self.max_clients:
        timeout = (
            min(request.connect_timeout, request.request_timeout)
            or request.connect_timeout
            or request.request_timeout
        )  # min but skip zero
        if timeout:
            timeout_handle = self.io_loop.add_timeout(
                self.io_loop.time() + timeout,
                functools.partial(self._on_timeout, key, "in request queue"),
            )
    self.waiting[key] = (request, callback, timeout_handle)
    self._process_queue()
    if self.queue:
        gen_log.debug(
            "max_clients limit reached, request queued. "
            "%d active, %d queued requests." % (len(self.active), len(self.queue))
        )

def on_connection_close(self) -> None:
    if self.final_callback is not None:
        message = "Connection closed"
        if self.stream.error:
            raise self.stream.error
        try:
            raise HTTPStreamClosedError(message)
        except HTTPStreamClosedError:
            self._handle_exception(*sys.exc_info())

def data_received(self, chunk: bytes) -> Optional[Awaitable[None]]:
    if self._should_follow_redirect():
        # We're going to follow a redirect so just discard the body.
        return None
    if self.request.streaming_callback is not None:
        return self.request.streaming_callback(chunk)
    else:
        self.chunks.append(chunk)
        return None

def start(
    self,
    timeout: float = _INITIAL_CONNECT_TIMEOUT,
    connect_timeout: Optional[Union[float, datetime.timedelta]] = None,
) -> "Future[Tuple[socket.AddressFamily, Any, IOStream]]":
    self.try_connect(iter(self.primary_addrs))
    self.set_timeout(timeout)
    if connect_timeout is not None:
        self.set_connect_timeout(connect_timeout)
    return self.future

def try_connect(self, addrs: Iterator[Tuple[socket.AddressFamily, Tuple]]) -> None:
    try:
        af, addr = next(addrs)
    except StopIteration:
        # We've reached the end of our queue, but the other queue
        # might still be working.  Send a final error on the future
        # only when both queues are finished.
        if self.remaining == 0 and not self.future.done():
            self.future.set_exception(
                self.last_error or IOError("connection failed")
            )
        return
    stream, future = self.connect(af, addr)
    self.streams.add(stream)
    future_add_done_callback(
        future, functools.partial(self.on_connect_done, addrs, af, addr)
    )

def clear_timeouts(self) -> None:
    if self.timeout is not None:
        self.io_loop.remove_timeout(self.timeout)
    if self.connect_timeout is not None:
        self.io_loop.remove_timeout(self.connect_timeout)

def filter_whitespace(mode: str, text: str) -> str:
    """Transform whitespace in ``text`` according to ``mode``.

    Available modes are:

    * ``all``: Return all whitespace unmodified.
    * ``single``: Collapse consecutive whitespace with a single whitespace
      character, preserving newlines.
    * ``oneline``: Collapse all runs of whitespace into a single space
      character, removing all newlines in the process.

    .. versionadded:: 4.3
    """
    if mode == "all":
        return text
    elif mode == "single":
        text = re.sub(r"([\t ]+)", " ", text)
        text = re.sub(r"(\s*\n\s*)", "\n", text)
        return text
    elif mode == "oneline":
        return re.sub(r"(\s+)", " ", text)
    else:
        raise Exception("invalid whitespace mode %s" % mode)

def resolve_path(self, name: str, parent_path: Optional[str] = None) -> str:
    if (
        parent_path
        and not parent_path.startswith("<")
        and not parent_path.startswith("/")
        and not name.startswith("/")
    ):
        file_dir = posixpath.dirname(parent_path)
        name = posixpath.normpath(posixpath.join(file_dir, name))
    return name

def find_named_blocks(
    self, loader: Optional[BaseLoader], named_blocks: Dict[str, "_NamedBlock"]
) -> None:
    for child in self.each_child():
        child.find_named_blocks(loader, named_blocks)

def find_named_blocks(
    self, loader: Optional[BaseLoader], named_blocks: Dict[str, _NamedBlock]
) -> None:
    assert loader is not None
    included = loader.load(self.name, self.template_name)
    included.file.find_named_blocks(loader, named_blocks)

def generate(self, writer: "_CodeWriter") -> None:
    value = self.value

    # Compress whitespace if requested, with a crude heuristic to avoid
    # altering preformatted whitespace.
    if "<pre>" not in value:
        value = filter_whitespace(self.whitespace, value)

    if value:
        writer.write_line("_tt_append(%r)" % escape.utf8(value), self.line)

def include(self, template: Template, line: int) -> "ContextManager":
    self.include_stack.append((self.current_template, line))
    self.current_template = template

    class IncludeTemplate:
        def __enter__(_) -> "_CodeWriter":
            return self

        def __exit__(_, *args: Any) -> None:
            self.current_template = self.include_stack.pop()[0]

    return IncludeTemplate()

def write_line(
    self, line: str, line_number: int, indent: Optional[int] = None
) -> None:
    if indent is None:
        indent = self._indent
    line_comment = "  # %s:%d" % (self.current_template.name, line_number)
    if self.include_stack:
        ancestors = [
            "%s:%d" % (tmpl.name, lineno) for (tmpl, lineno) in self.include_stack
        ]
        line_comment += " (via %s)" % ", ".join(reversed(ancestors))
    print("    " * indent + line + line_comment, file=self.file)

def find(self, needle: str, start: int = 0, end: Optional[int] = None) -> int:
    assert start >= 0, start
    pos = self.pos
    start += pos
    if end is None:
        index = self.text.find(needle, start)
    else:
        end += pos
        assert end >= start
        index = self.text.find(needle, start, end)
    if index != -1:
        index -= pos
    return index

def exec_in(
    code: Any, glob: Dict[str, Any], loc: Optional[Optional[Mapping[str, Any]]] = None
) -> None:
    if isinstance(code, str):
        # exec(string) inherits the caller's future imports; compile
        # the string first to prevent that.
        code = compile(code, "<string>", "exec", dont_inherit=True)
    exec(code, glob, loc)

def raise_exc_info(
    exc_info: Tuple[Optional[type], Optional[BaseException], Optional["TracebackType"]],
) -> typing.NoReturn:
    try:
        if exc_info[1] is not None:
            raise exc_info[1].with_traceback(exc_info[2])
        else:
            raise TypeError("raise_exc_info called with no exception")
    finally:
        # Clear the traceback reference from our stack frame to
        # minimize circular references that slow down GC.
        exc_info = (None, None, None)

def get_old_value(
    self, args: Sequence[Any], kwargs: Dict[str, Any], default: Any = None
) -> Any:
    """Returns the old value of the named argument without replacing it.

    Returns ``default`` if the argument is not present.
    """
    if self.arg_pos is not None and len(args) > self.arg_pos:
        return args[self.arg_pos]
    else:
        return kwargs.get(self.name, default)

def removeslash(
    method: Callable[..., Optional[Awaitable[None]]],
) -> Callable[..., Optional[Awaitable[None]]]:
    """Use this decorator to remove trailing slashes from the request path.

    For example, a request to ``/foo/`` would redirect to ``/foo`` with this
    decorator. Your request handler mapping should use a regular expression
    like ``r'/foo/*'`` in conjunction with using the decorator.
    """

    @functools.wraps(method)
    def wrapper(  # type: ignore
        self: RequestHandler, *args, **kwargs
    ) -> Optional[Awaitable[None]]:
        if self.request.path.endswith("/"):
            if self.request.method in ("GET", "HEAD"):
                uri = self.request.path.rstrip("/")
                if uri:  # don't try to redirect '/' to ''
                    if self.request.query:
                        uri += "?" + self.request.query
                    self.redirect(uri, permanent=True)
                    return None
            else:
                raise HTTPError(404)
        return method(self, *args, **kwargs)

    return wrapper

def addslash(
    method: Callable[..., Optional[Awaitable[None]]],
) -> Callable[..., Optional[Awaitable[None]]]:
    """Use this decorator to add a missing trailing slash to the request path.

    For example, a request to ``/foo`` would redirect to ``/foo/`` with this
    decorator. Your request handler mapping should use a regular expression
    like ``r'/foo/?'`` in conjunction with using the decorator.
    """

    @functools.wraps(method)
    def wrapper(  # type: ignore
        self: RequestHandler, *args, **kwargs
    ) -> Optional[Awaitable[None]]:
        if not self.request.path.endswith("/"):
            if self.request.method in ("GET", "HEAD"):
                uri = self.request.path + "/"
                if self.request.query:
                    uri += "?" + self.request.query
                self.redirect(uri, permanent=True)
                return None
            raise HTTPError(404)
        return method(self, *args, **kwargs)

    return wrapper

def decode_signed_value(
    secret: _CookieSecretTypes,
    name: str,
    value: Union[None, str, bytes],
    max_age_days: float = 31,
    clock: Optional[Callable[[], float]] = None,
    min_version: Optional[int] = None,
) -> Optional[bytes]:
    if clock is None:
        clock = time.time
    if min_version is None:
        min_version = DEFAULT_SIGNED_VALUE_MIN_VERSION
    if min_version > 2:
        raise ValueError("Unsupported min_version %d" % min_version)
    if not value:
        return None

    value = utf8(value)
    version = _get_version(value)

    if version < min_version:
        return None
    if version == 1:
        assert not isinstance(secret, dict)
        return _decode_signed_value_v1(secret, name, value, max_age_days, clock)
    elif version == 2:
        return _decode_signed_value_v2(secret, name, value, max_age_days, clock)
    else:
        return None

def get_signature_key_version(value: Union[str, bytes]) -> Optional[int]:
    value = utf8(value)
    version = _get_version(value)
    if version < 2:
        return None
    try:
        key_version, _, _, _, _ = _decode_fields_v2(value)
    except ValueError:
        return None

    return key_version

def decode_argument(self, value: bytes, name: Optional[str] = None) -> str:
    """Decodes an argument from the request.

    The argument has been percent-decoded and is now a byte string.
    By default, this method decodes the argument as utf-8 and returns
    a unicode string, but this may be overridden in subclasses.

    This method is used as a filter for both `get_argument()` and for
    values extracted from the url and passed to `get()`/`post()`/etc.

    The name of the argument is provided if known, but may be None
    (e.g. for unnamed groups in the url regex).
    """
    try:
        return _unicode(value)
    except UnicodeDecodeError:
        raise HTTPError(
            400, "Invalid unicode in {}: {!r}".format(name or "url", value[:40])
        )

def render_linked_js(self, js_files: Iterable[str]) -> str:
    """Default method used to render the final js links for the
    rendered webpage.

    Override this method in a sub-classed controller to change the output.
    """
    paths = []
    unique_paths = set()  # type: Set[str]

    for path in js_files:
        if not is_absolute(path):
            path = self.static_url(path)
        if path not in unique_paths:
            paths.append(path)
            unique_paths.add(path)

    return "".join(
        '<script src="'
        + escape.xhtml_escape(p)
        + '" type="text/javascript"></script>'
        for p in paths
    )

def render_linked_css(self, css_files: Iterable[str]) -> str:
    """Default method used to render the final css links for the
    rendered webpage.

    Override this method in a sub-classed controller to change the output.
    """
    paths = []
    unique_paths = set()  # type: Set[str]

    for path in css_files:
        if not is_absolute(path):
            path = self.static_url(path)
        if path not in unique_paths:
            paths.append(path)
            unique_paths.add(path)

    return "".join(
        '<link href="' + escape.xhtml_escape(p) + '" '
        'type="text/css" rel="stylesheet"/>'
        for p in paths
    )

def create_template_loader(self, template_path: str) -> template.BaseLoader:
    """Returns a new template loader for the given path.

    May be overridden by subclasses.  By default returns a
    directory-based loader on the given path, using the
    ``autoescape`` and ``template_whitespace`` application
    settings.  If a ``template_loader`` application setting is
    supplied, uses that instead.
    """
    settings = self.application.settings
    if "template_loader" in settings:
        return settings["template_loader"]
    kwargs = {}
    if "autoescape" in settings:
        # autoescape=None means "no escaping", so we have to be sure
        # to only pass this kwarg if the user asked for it.
        kwargs["autoescape"] = settings["autoescape"]
    if "template_whitespace" in settings:
        kwargs["whitespace"] = settings["template_whitespace"]
    return template.Loader(template_path, **kwargs)

def get_browser_locale(self, default: str = "en_US") -> tornado.locale.Locale:
    """Determines the user's locale from ``Accept-Language`` header.

    See http://www.w3.org/Protocols/rfc2616/rfc2616-sec14.html#sec14.4
    """
    if "Accept-Language" in self.request.headers:
        languages = self.request.headers["Accept-Language"].split(",")
        locales = []
        for language in languages:
            parts = language.strip().split(";")
            if len(parts) > 1 and parts[1].strip().startswith("q="):
                try:
                    score = float(parts[1].strip()[2:])
                    if score < 0:
                        raise ValueError()
                except (ValueError, TypeError):
                    score = 0.0
            else:
                score = 1.0
            if score > 0:
                locales.append((parts[0], score))
        if locales:
            locales.sort(key=lambda pair: pair[1], reverse=True)
            codes = [loc[0] for loc in locales]
            return locale.get(*codes)
    return locale.get(default)

def require_setting(self, name: str, feature: str = "this feature") -> None:
    """Raises an exception if the given app setting is not defined."""
    if not self.application.settings.get(name):
        raise Exception(
            "You must define the '%s' setting in your "
            "application to use %s" % (name, feature)
        )

def compute_etag(self) -> Optional[str]:
    """Computes the etag header to be used for this request.

    By default uses a hash of the content written so far.

    May be overridden to provide custom etag implementations,
    or may return None to disable tornado's default etag support.
    """
    hasher = hashlib.sha1()
    for part in self._write_buffer:
        hasher.update(part)
    return '"%s"' % hasher.hexdigest()

def wrapper(  # type: ignore
    self: RequestHandler, *args, **kwargs
) -> Optional[Awaitable[None]]:
    if self.request.path.endswith("/"):
        if self.request.method in ("GET", "HEAD"):
            uri = self.request.path.rstrip("/")
            if uri:  # don't try to redirect '/' to ''
                if self.request.query:
                    uri += "?" + self.request.query
                self.redirect(uri, permanent=True)
                return None
        else:
            raise HTTPError(404)
    return method(self, *args, **kwargs)

def wrapper(  # type: ignore
    self: RequestHandler, *args, **kwargs
) -> Optional[Awaitable[None]]:
    if not self.request.path.endswith("/"):
        if self.request.method in ("GET", "HEAD"):
            uri = self.request.path + "/"
            if self.request.query:
                uri += "?" + self.request.query
            self.redirect(uri, permanent=True)
            return None
        raise HTTPError(404)
    return method(self, *args, **kwargs)

def process_rule(self, rule: Rule) -> Rule:
    rule = super().process_rule(rule)

    if isinstance(rule.target, (list, tuple)):
        rule.target = _ApplicationRouter(
            self.application, rule.target  # type: ignore
        )

    return rule

def get_target_delegate(
    self, target: Any, request: httputil.HTTPServerRequest, **target_params: Any
) -> Optional[httputil.HTTPMessageDelegate]:
    if isclass(target) and issubclass(target, RequestHandler):
        return self.application.get_handler_delegate(
            request, target, **target_params
        )

    return super().get_target_delegate(target, request, **target_params)

def find_handler(
    self, request: httputil.HTTPServerRequest, **kwargs: Any
) -> "_HandlerDelegate":
    route = self.default_router.find_handler(request)
    if route is not None:
        return cast("_HandlerDelegate", route)

    if self.settings.get("default_handler_class"):
        return self.get_handler_delegate(
            request,
            self.settings["default_handler_class"],
            self.settings.get("default_handler_args", {}),
        )

    return self.get_handler_delegate(request, ErrorHandler, {"status_code": 404})

def headers_received(
    self,
    start_line: Union[httputil.RequestStartLine, httputil.ResponseStartLine],
    headers: httputil.HTTPHeaders,
) -> Optional[Awaitable[None]]:
    if self.stream_request_body:
        self.request._body_future = Future()
        return self.execute()
    return None

def data_received(self, data: bytes) -> Optional[Awaitable[None]]:
    if self.stream_request_body:
        return self.handler.data_received(data)
    else:
        self.chunks.append(data)
        return None

def finish(self) -> None:
    if self.stream_request_body:
        future_set_result_unless_cancelled(self.request._body_future, None)
    else:
        # Note that the body gets parsed in RequestHandler._execute so it can be in
        # the right exception handler scope.
        self.request.body = b"".join(self.chunks)
        self.execute()

def on_connection_close(self) -> None:
    if self.stream_request_body:
        self.handler.on_connection_close()
    else:
        self.chunks = None  # type: ignore

def log_message(self) -> Optional[str]:
    """
    A backwards compatible way of accessing log_message.
    """
    if self._log_message and not self.args:
        return self._log_message.replace("%", "%%")
    return self._log_message

def should_return_304(self) -> bool:
    """Returns True if the headers indicate that we should return 304.

    .. versionadded:: 3.1
    """
    # If client sent If-None-Match, use it, ignore If-Modified-Since
    if self.request.headers.get("If-None-Match"):
        return self.check_etag_header()

    # Check the If-Modified-Since, and don't send the result if the
    # content has not been modified
    ims_value = self.request.headers.get("If-Modified-Since")
    if ims_value is not None:
        try:
            if_since = email.utils.parsedate_to_datetime(ims_value)
        except Exception:
            return False
        if if_since.tzinfo is None:
            if_since = if_since.replace(tzinfo=datetime.timezone.utc)
        assert self.modified is not None
        if if_since >= self.modified:
            return True

    return False

def get_content_version(cls, abspath: str) -> str:
    """Returns a version string for the resource at the given path.

    This class method may be overridden by subclasses.  The
    default implementation is a SHA-512 hash of the file's contents.

    .. versionadded:: 3.1
    """
    data = cls.get_content(abspath)
    hasher = hashlib.sha512()
    if isinstance(data, bytes):
        hasher.update(data)
    else:
        for chunk in data:
            hasher.update(chunk)
    return hasher.hexdigest()

def transform_first_chunk(
    self,
    status_code: int,
    headers: httputil.HTTPHeaders,
    chunk: bytes,
    finishing: bool,
) -> Tuple[int, httputil.HTTPHeaders, bytes]:
    # TODO: can/should this type be inherited from the superclass?
    if "Vary" in headers:
        headers["Vary"] += ", Accept-Encoding"
    else:
        headers["Vary"] = "Accept-Encoding"
    if self._gzipping:
        ctype = _unicode(headers.get("Content-Type", "")).split(";")[0]
        self._gzipping = (
            self._compressible_type(ctype)
            and (not finishing or len(chunk) >= self.MIN_LENGTH)
            and ("Content-Encoding" not in headers)
        )
    if self._gzipping:
        headers["Content-Encoding"] = "gzip"
        self._gzip_value = BytesIO()
        self._gzip_file = gzip.GzipFile(
            mode="w", fileobj=self._gzip_value, compresslevel=self.GZIP_LEVEL
        )
        chunk = self.transform_chunk(chunk, finishing)
        if "Content-Length" in headers:
            # The original content length is no longer correct.
            # If this is the last (and only) chunk, we can set the new
            # content-length; otherwise we remove it and fall back to
            # chunked encoding.
            if finishing:
                headers["Content-Length"] = str(len(chunk))
            else:
                del headers["Content-Length"]
    return status_code, headers, chunk

def transform_chunk(self, chunk: bytes, finishing: bool) -> bytes:
    if self._gzipping:
        self._gzip_file.write(chunk)
        if finishing:
            self._gzip_file.close()
        else:
            self._gzip_file.flush()
        chunk = self._gzip_value.getvalue()
        self._gzip_value.truncate(0)
        self._gzip_value.seek(0)
    return chunk

def render(self, path: str, **kwargs: Any) -> bytes:
    def set_resources(**kwargs) -> str:  # type: ignore
        if path not in self._resource_dict:
            self._resource_list.append(kwargs)
            self._resource_dict[path] = kwargs
        else:
            if self._resource_dict[path] != kwargs:
                raise ValueError(
                    "set_resources called with different "
                    "resources for the same template"
                )
        return ""

    return self.render_string(path, set_resources=set_resources, **kwargs)

def javascript_files(self) -> Iterable[str]:
    result = []
    for f in self._get_resources("javascript_files"):
        if isinstance(f, (unicode_type, bytes)):
            result.append(f)
        else:
            result.extend(f)
    return result

def css_files(self) -> Iterable[str]:
    result = []
    for f in self._get_resources("css_files"):
        if isinstance(f, (unicode_type, bytes)):
            result.append(f)
        else:
            result.extend(f)
    return result

def render(*args, **kwargs) -> str:  # type: ignore
    if not hasattr(self, "_active_modules"):
        self._active_modules = {}  # type: Dict[str, UIModule]
    if name not in self._active_modules:
        self._active_modules[name] = module(self)
    rendered = self._active_modules[name].render(*args, **kwargs)
    return _unicode(rendered)

def set_resources(**kwargs) -> str:  # type: ignore
    if path not in self._resource_dict:
        self._resource_list.append(kwargs)
        self._resource_dict[path] = kwargs
    else:
        if self._resource_dict[path] != kwargs:
            raise ValueError(
                "set_resources called with different "
                "resources for the same template"
            )
    return ""

def on_connection_close(self) -> None:
    if self.ws_connection:
        self.ws_connection.on_connection_close()
        self.ws_connection = None
    if not self._on_close_called:
        self._on_close_called = True
        self.on_close()
        self._break_cycles()

def get_websocket_protocol(self) -> Optional["WebSocketProtocol"]:
    websocket_version = self.request.headers.get("Sec-WebSocket-Version")
    if websocket_version in ("7", "8", "13"):
        params = _WebSocketParams(
            ping_interval=self.ping_interval,
            ping_timeout=self.ping_timeout,
            max_message_size=self.max_message_size,
            compression_options=self.get_compression_options(),
        )
        return WebSocketProtocol13(self, False, params)
    return None

def decompress(self, data: bytes) -> bytes:
    decompressor = self._decompressor or self._create_decompressor()
    result = decompressor.decompress(
        data + b"\x00\x00\xff\xff", self._max_message_size
    )
    if decompressor.unconsumed_tail:
        raise _DecompressTooLargeError()
    return result

def write_message(
    self, message: Union[str, bytes, Dict[str, Any]], binary: bool = False
) -> "Future[None]":
    """Sends the given message to the client of this Web Socket."""
    if binary:
        opcode = 0x2
    else:
        opcode = 0x1
    if isinstance(message, dict):
        message = tornado.escape.json_encode(message)
    message = tornado.escape.utf8(message)
    assert isinstance(message, bytes)
    self._message_bytes_out += len(message)
    flags = 0
    if self._compressor:
        message = self._compressor.compress(message)
        flags |= self.RSV1
    # For historical reasons, write methods in Tornado operate in a semi-synchronous
    # mode in which awaiting the Future they return is optional (But errors can
    # still be raised). This requires us to go through an awkward dance here
    # to transform the errors that may be returned while presenting the same
    # semi-synchronous interface.
    try:
        fut = self._write_frame(True, opcode, message, flags=flags)
    except StreamClosedError:
        raise WebSocketClosedError()

    async def wrapper() -> None:
        try:
            await fut
        except StreamClosedError:
            raise WebSocketClosedError()

    return asyncio.ensure_future(wrapper())

def close(self, code: Optional[int] = None, reason: Optional[str] = None) -> None:
    """Closes the WebSocket connection."""
    if not self.server_terminated:
        if not self.stream.closed():
            if code is None and reason is not None:
                code = 1000  # "normal closure" status code
            if code is None:
                close_data = b""
            else:
                close_data = struct.pack(">H", code)
            if reason is not None:
                close_data += utf8(reason)
            try:
                self._write_frame(True, 0x8, close_data)
            except StreamClosedError:
                self._abort()
        self.server_terminated = True
    if self.client_terminated:
        if self._waiting is not None:
            self.stream.io_loop.remove_timeout(self._waiting)
            self._waiting = None
        self.stream.close()
    elif self._waiting is None:
        # Give the client a few seconds to complete a clean shutdown,
        # otherwise just close the connection.
        self._waiting = self.stream.io_loop.add_timeout(
            self.stream.io_loop.time() + 5, self._abort
        )
    if self._ping_coroutine:
        self._ping_coroutine.cancel()
        self._ping_coroutine = None

def ping_interval(self) -> float:
    interval = self.params.ping_interval
    if interval is not None:
        return interval
    return 0

def start_pinging(self) -> None:
    """Start sending periodic pings to keep the connection alive"""
    if (
        # prevent multiple ping coroutines being run in parallel
        not self._ping_coroutine
        # only run the ping coroutine if a ping interval is configured
        and self.ping_interval > 0
    ):
        self._ping_coroutine = asyncio.create_task(self.periodic_ping())

def on_connection_close(self) -> None:
    if not self.connect_future.done():
        self.connect_future.set_exception(StreamClosedError())
    self._on_message(None)
    self.tcp_client.close()
    super().on_connection_close()

def write_message(
    self, message: Union[str, bytes, Dict[str, Any]], binary: bool = False
) -> "Future[None]":
    """Sends a message to the WebSocket server.

    If the stream is closed, raises `WebSocketClosedError`.
    Returns a `.Future` which can be used for flow control.

    .. versionchanged:: 5.0
       Exception raised on a closed stream changed from `.StreamClosedError`
       to `WebSocketClosedError`.
    """
    if self.protocol is None:
        raise WebSocketClosedError("Client connection has been closed")
    return self.protocol.write_message(message, binary=binary)

def next_chunk() -> Optional[bytes]:
    try:
        return next(app_response_iter)
    except StopIteration:
        # StopIteration is special and is not allowed to pass through
        # coroutines normally.
        return None

def add_handler(
    self, fd: Union[int, _Selectable], handler: Callable[..., None], events: int
) -> None:
    fd, fileobj = self.split_fd(fd)
    if fd in self.handlers:
        raise ValueError("fd %s added twice" % fd)
    self.handlers[fd] = (fileobj, handler)
    if events & IOLoop.READ:
        self.selector_loop.add_reader(fd, self._handle_events, fd, IOLoop.READ)
        self.readers.add(fd)
    if events & IOLoop.WRITE:
        self.selector_loop.add_writer(fd, self._handle_events, fd, IOLoop.WRITE)
        self.writers.add(fd)

def update_handler(self, fd: Union[int, _Selectable], events: int) -> None:
    fd, fileobj = self.split_fd(fd)
    if events & IOLoop.READ:
        if fd not in self.readers:
            self.selector_loop.add_reader(fd, self._handle_events, fd, IOLoop.READ)
            self.readers.add(fd)
    else:
        if fd in self.readers:
            self.selector_loop.remove_reader(fd)
            self.readers.remove(fd)
    if events & IOLoop.WRITE:
        if fd not in self.writers:
            self.selector_loop.add_writer(fd, self._handle_events, fd, IOLoop.WRITE)
            self.writers.add(fd)
    else:
        if fd in self.writers:
            self.selector_loop.remove_writer(fd)
            self.writers.remove(fd)

def remove_handler(self, fd: Union[int, _Selectable]) -> None:
    fd, fileobj = self.split_fd(fd)
    if fd not in self.handlers:
        return
    if fd in self.readers:
        self.selector_loop.remove_reader(fd)
        self.readers.remove(fd)
    if fd in self.writers:
        self.selector_loop.remove_writer(fd)
        self.writers.remove(fd)
    del self.handlers[fd]

def call_at(
    self, when: float, callback: Callable, *args: Any, **kwargs: Any
) -> object:
    # asyncio.call_at supports *args but not **kwargs, so bind them here.
    # We do not synchronize self.time and asyncio_loop.time, so
    # convert from absolute to relative.
    return self.asyncio_loop.call_later(
        max(0, when - self.time()),
        self._run_callback,
        functools.partial(callback, *args, **kwargs),
    )

def add_callback(self, callback: Callable, *args: Any, **kwargs: Any) -> None:
    try:
        if asyncio.get_running_loop() is self.asyncio_loop:
            call_soon = self.asyncio_loop.call_soon
        else:
            call_soon = self.asyncio_loop.call_soon_threadsafe
    except RuntimeError:
        call_soon = self.asyncio_loop.call_soon_threadsafe

    try:
        call_soon(self._run_callback, functools.partial(callback, *args, **kwargs))
    except RuntimeError:
        # "Event loop is closed". Swallow the exception for
        # consistency with PollIOLoop (and logical consistency
        # with the fact that we can't guarantee that an
        # add_callback that completes without error will
        # eventually execute).
        pass
    except AttributeError:
        # ProactorEventLoop may raise this instead of RuntimeError
        # if call_soon_threadsafe races with a call to close().
        # Swallow it too for consistency.
        pass

def add_callback_from_signal(
    self, callback: Callable, *args: Any, **kwargs: Any
) -> None:
    warnings.warn("add_callback_from_signal is deprecated", DeprecationWarning)
    try:
        self.asyncio_loop.call_soon_threadsafe(
            self._run_callback, functools.partial(callback, *args, **kwargs)
        )
    except RuntimeError:
        pass

def initialize(self, **kwargs: Any) -> None:  # type: ignore
    self.is_current = False
    loop = None
    if "asyncio_loop" not in kwargs:
        kwargs["asyncio_loop"] = loop = asyncio.new_event_loop()
    try:
        super().initialize(**kwargs)
    except Exception:
        # If initialize() does not succeed (taking ownership of the loop),
        # we have to close it.
        if loop is not None:
            loop.close()
        raise

def add_reader(
    self, fd: _FileDescriptorLike, callback: Callable[..., None], *args: Any
) -> None:
    self._readers[fd] = functools.partial(callback, *args)
    self._wake_selector()

def add_writer(
    self, fd: _FileDescriptorLike, callback: Callable[..., None], *args: Any
) -> None:
    self._writers[fd] = functools.partial(callback, *args)
    self._wake_selector()

def remove_reader(self, fd: _FileDescriptorLike) -> bool:
    try:
        del self._readers[fd]
    except KeyError:
        return False
    self._wake_selector()
    return True

def remove_writer(self, fd: _FileDescriptorLike) -> bool:
    try:
        del self._writers[fd]
    except KeyError:
        return False
    self._wake_selector()
    return True

def get_event_loop(self) -> asyncio.AbstractEventLoop:
    try:
        return super().get_event_loop()
    except RuntimeError:
        # "There is no current event loop in thread %r"
        loop = self.new_event_loop()
        self.set_event_loop(loop)
        return loop

def resolve(
    self, host: str, port: int, family: int = 0
) -> "Generator[Any, Any, List[Tuple[int, Any]]]":
    if is_valid_ip(host):
        addresses = [host]
    else:
        # gethostbyname doesn't take callback as a kwarg
        fut = Future()  # type: Future[Tuple[Any, Any]]
        self.channel.gethostbyname(
            host, family, lambda result, error: fut.set_result((result, error))
        )
        result, error = yield fut
        if error:
            raise OSError(
                "C-Ares returned error %s: %s while resolving %s"
                % (error, pycares.errno.strerror(error), host)
            )
        addresses = result.addresses
    addrinfo = []
    for address in addresses:
        if "." in address:
            address_family = socket.AF_INET
        elif ":" in address:
            address_family = socket.AF_INET6
        else:
            address_family = socket.AF_UNSPEC
        if family != socket.AF_UNSPEC and family != address_family:
            raise OSError(
                "Requested socket family %d but got %d" % (family, address_family)
            )
        addrinfo.append((typing.cast(int, address_family), (address, port)))
    return addrinfo

def errback(failure: failure.Failure) -> None:
    try:
        failure.raiseException()
        # Should never happen, but just in case
        raise Exception("errback called without error")
    except:
        future_set_exc_info(f, sys.exc_info())

def doc2rst(doc, arglist=True, raw=False):
    """
    arglist  : bool, whether to create argument lists
    raw  : bool, ignores arglist and indents by 2 spaces
    """
    doc = doc.replace('`', '``')
    if raw:
        doc = doc.replace('\n ', '\n   ')
    else:
        doc = dedent(doc)
        if arglist:
            doc = '\n'.join(i if not i or i[0] == ' ' else '* ' + i + '  '
                            for i in doc.split('\n'))
    return doc

def alive_progress(self):
    from alive_progress import alive_bar

    class wrapper:
        def __init__(self, iterable):
            self.iterable = iterable

        def __iter__(self):
            iterable = self.iterable
            with alive_bar(len(iterable)) as bar:
                for i in iterable:
                    yield i
                    bar()

    return self.run(wrapper)

def count(start=0, step=1):
    i = start
    while True:
        new_start = yield i
        if new_start is None:
            i += step
        else:
            i = new_start

def autonext(func):
    @wraps(func)
    def inner(*args, **kwargs):
        res = func(*args, **kwargs)
        next(res)
        return res
    return inner

def grep(pattern, target):
    while True:
        line = (yield)
        if pattern in line:
            target.send(line)

def tarange(*args, **kwargs):
    """
    A shortcut for `tqdm.asyncio.tqdm(range(*args), **kwargs)`.
    """
    return tqdm_asyncio(range(*args), **kwargs)

def as_completed(cls, fs, *, loop=None, timeout=None, total=None, **tqdm_kwargs):
    """
    Wrapper for `asyncio.as_completed`.
    """
    if total is None:
        total = len(fs)
    kwargs = {}
    if version_info[:2] < (3, 10):
        kwargs['loop'] = loop
    yield from cls(asyncio.as_completed(fs, timeout=timeout, **kwargs),
                   total=total, **tqdm_kwargs)

def trange(*args, **kwargs):
    """
    A shortcut for `tqdm.auto.tqdm(range(*args), **kwargs)`.
    """
    return tqdm(range(*args), **kwargs)

def cast(val, typ):
    log.debug((val, typ))
    if " or " in typ:
        for t in typ.split(" or "):
            try:
                return cast(val, t)
            except TqdmTypeError:
                pass
        raise TqdmTypeError(f"{val} : {typ}")

    # sys.stderr.write('\ndebug | `val:type`: `' + val + ':' + typ + '`.\n')
    if typ == 'bool':
        if (val == 'True') or (val == ''):
            return True
        if val == 'False':
            return False
        raise TqdmTypeError(val + ' : ' + typ)
    if typ == 'chr':
        if len(val) == 1:
            return val.encode()
        if re.match(r"^\\\w+$", val):
            return eval(f'"{val}"').encode()
        raise TqdmTypeError(f"{val} : {typ}")
    if typ == 'str':
        return val
    if typ == 'int':
        try:
            return int(val)
        except ValueError as exc:
            raise TqdmTypeError(f"{val} : {typ}") from exc
    if typ == 'float':
        try:
            return float(val)
        except ValueError as exc:
            raise TqdmTypeError(f"{val} : {typ}") from exc
    raise TqdmTypeError(f"{val} : {typ}")

def close(self):
    if self.disable:
        return

    self.disable = True

    with self.get_lock():
        self._instances.remove(self)

    # Restore toolbars
    self.mpl.rcParams['toolbar'] = self.toolbar
    # Return to non-interactive mode
    if not self.wasion:
        self.plt.ioff()
    if self.leave:
        self.display()
    else:
        self.plt.close(self.fig)

def bar2callback(bar, pop=None, delta=(lambda logs: 1)):
    def callback(_, logs=None):
        n = delta(logs)
        if logs:
            if pop:
                logs = copy(logs)
                [logs.pop(i, 0) for i in pop]
            bar.set_postfix(logs, refresh=False)
        bar.update(n)

    return callback

def on_train_begin(self, *_, **__):
    params = self.params.get
    auto_total = params('epochs', params('nb_epoch', None))
    if auto_total is not None and auto_total != self.epoch_bar.total:
        self.epoch_bar.reset(total=auto_total)

def on_epoch_begin(self, epoch, *_, **__):
    if self.epoch_bar.n < epoch:
        ebar = self.epoch_bar
        ebar.n = ebar.last_print_n = ebar.initial = epoch
    if self.verbose:
        params = self.params.get
        total = params('samples', params(
            'nb_sample', params('steps', None))) or self.batches
        if self.verbose == 2:
            if hasattr(self, 'batch_bar'):
                self.batch_bar.close()
            self.batch_bar = self.tqdm_class(
                total=total, unit='batch', leave=True,
                unit_scale=1 / (params('batch_size', 1) or 1))
            self.on_batch_end = self.bar2callback(
                self.batch_bar, pop=['batch', 'size'],
                delta=lambda logs: logs.get('size', 1))
        elif self.verbose == 1:
            self.batch_bar.unit_scale = 1 / (params('batch_size', 1) or 1)
            self.batch_bar.reset(total=total)
        else:
            raise KeyError('Unknown verbosity')

def display(self):
    """Displays in the current cell in Notebooks."""
    container = getattr(self.epoch_bar, 'container', None)
    if container is None:
        return
    from .notebook import display
    display(container)
    batch_bar = getattr(self, 'batch_bar', None)
    if batch_bar is not None:
        display(batch_bar.container)

def callback(_, logs=None):
    n = delta(logs)
    if logs:
        if pop:
            logs = copy(logs)
            [logs.pop(i, 0) for i in pop]
        bar.set_postfix(logs, refresh=False)
    bar.update(n)

def update(self, n=1):
    try:
        return super().update(n=n)
    # NB: except ... [ as ...] breaks IPython async KeyboardInterrupt
    except:  # NOQA
        # cannot catch KeyboardInterrupt when using manual tqdm
        # as the interrupt will most likely happen on another statement
        self.disp(bar_style='danger')
        raise

def close(self):
    if self.disable:
        return
    super().close()
    # Try to detect if there was an error or KeyboardInterrupt
    # in manual mode: if n < total, things probably got wrong
    if self.total and self.n < self.total:
        self.disp(bar_style='danger', check_delay=False)
    else:
        if self.leave:
            self.disp(bar_style='success', check_delay=False)
        else:
            self.disp(close=True, check_delay=False)

def render(self, task):
    """Calculate common unit for completed and total."""
    completed = int(task.completed)
    total = int(task.total)
    if self.unit_scale:
        unit, suffix = filesize.pick_unit_and_suffix(
            total,
            ["", "K", "M", "G", "T", "P", "E", "Z", "Y"],
            self.unit_divisor,
        )
    else:
        unit, suffix = filesize.pick_unit_and_suffix(total, [""], 1)
    precision = 0 if unit == 1 else 1
    return Text(
        f"{completed/unit:,.{precision}f}/{total/unit:,.{precision}f} {suffix}",
        style="progress.download")

def render(self, task):
    """Show data transfer speed."""
    speed = task.speed
    if speed is None:
        return Text(f"? {self.unit}/s", style="progress.data.speed")
    if self.unit_scale:
        unit, suffix = filesize.pick_unit_and_suffix(
            speed,
            ["", "K", "M", "G", "T", "P", "E", "Z", "Y"],
            self.unit_divisor,
        )
    else:
        unit, suffix = filesize.pick_unit_and_suffix(speed, [""], 1)
    precision = 0 if unit == 1 else 1
    return Text(f"{speed/unit:,.{precision}f} {suffix}{self.unit}/s",
                style="progress.data.speed")

def close(self):
    if self.disable:
        return
    self.display()  # print 100%, vis #1306
    super().close()
    self._prog.__exit__(None, None, None)

def TRLock(*args, **kwargs):
    """threading RLock"""
    try:
        from threading import RLock
        return RLock(*args, **kwargs)
    except (ImportError, OSError):  # pragma: no cover
        pass

def create_mp_lock(cls):
    if not hasattr(cls, 'mp_lock'):
        try:
            from multiprocessing import RLock
            cls.mp_lock = RLock()
        except (ImportError, OSError):  # pragma: no cover
            cls.mp_lock = None

def colour(self, value):
    if not value:
        self._colour = None
        return
    try:
        if value.upper() in self.COLOURS:
            self._colour = self.COLOURS[value.upper()]
        elif value[0] == '#' and len(value) == 7:
            self._colour = self.COLOUR_RGB % tuple(
                int(i, 16) for i in (value[1:3], value[3:5], value[5:7]))
        else:
            raise KeyError
    except (KeyError, AttributeError):
        warn("Unknown colour (%s); valid choices: [hex (#00ff00), %s]" % (
             value, ", ".join(self.COLOURS)),
             TqdmWarning, stacklevel=2)
        self._colour = None

def get_lock(cls):
    """Get the global lock. Construct it if it does not exist."""
    if not hasattr(cls, '_lock'):
        cls._lock = TqdmDefaultWriteLock()
    return cls._lock

def close(self):
    """Cleanup and (if leave=False) close the progressbar."""
    if self.disable:
        return

    # Prevent multiple closures
    self.disable = True

    # decrement instance pos and remove from internal set
    pos = abs(self.pos)
    self._decr_instances(self)

    if self.last_print_t < self.start_t + self.delay:
        # haven't ever displayed; nothing to clear
        return

    # GUI mode
    if getattr(self, 'sp', None) is None:
        return

    # annoyingly, _supports_unicode isn't good enough
    def fp_write(s):
        self.fp.write(str(s))

    try:
        fp_write('')
    except ValueError as e:
        if 'closed' in str(e):
            return
        raise  # pragma: no cover

    leave = pos == 0 if self.leave is None else self.leave

    with self._lock:
        if leave:
            # stats for overall rate (no weighted average)
            self._ema_dt = lambda: None
            self.display(pos=0)
            fp_write('\n')
        else:
            # clear previous display
            if self.display(msg='', pos=pos) and not pos:
                fp_write('\r')

def clear(self, nolock=False):
    """Clear current bar display."""
    if self.disable:
        return

    if not nolock:
        self._lock.acquire()
    pos = abs(self.pos)
    if pos < (self.nrows or 20):
        self.moveto(pos)
        self.sp('')
        self.fp.write('\r')  # place cursor back at the beginning of line
        self.moveto(-pos)
    if not nolock:
        self._lock.release()

def set_description_str(self, desc=None, refresh=True):
    """Set/modify description without ': ' appended."""
    self.desc = desc or ''
    if refresh:
        self.refresh()

def format_dict(self):
    """Public API for read-only member access."""
    if self.disable and not hasattr(self, 'unit'):
        return defaultdict(lambda: None, {
            'n': self.n, 'total': self.total, 'elapsed': 0, 'unit': 'it'})
    if self.dynamic_ncols:
        self.ncols, self.nrows = self.dynamic_ncols(self.fp)
    return {
        'n': self.n, 'total': self.total,
        'elapsed': self._time() - self.start_t if hasattr(self, 'start_t') else 0,
        'ncols': self.ncols, 'nrows': self.nrows, 'prefix': self.desc,
        'ascii': self.ascii, 'unit': self.unit, 'unit_scale': self.unit_scale,
        'rate': self._ema_dn() / self._ema_dt() if self._ema_dt() else None,
        'bar_format': self.bar_format, 'postfix': self.postfix,
        'unit_divisor': self.unit_divisor, 'initial': self.initial,
        'colour': self.colour}

def close(self):
    if self.disable:
        return

    self.disable = True

    with self.get_lock():
        self._instances.remove(self)

    def _close():
        self._tk_window.after('idle', self._tk_window.destroy)
        if not self._tk_dispatching:
            self._tk_window.update()

    self._tk_window.protocol("WM_DELETE_WINDOW", _close)

    # if leave is set but we are self-dispatching, the left window is
    # totally unresponsive unless the user manually dispatches
    if not self.leave:
        _close()
    elif not self._tk_dispatching:
        if self._warn_leave:
            warn("leave flag ignored if not in tkinter mainloop",
                 TqdmWarning, stacklevel=2)
        _close()

def display(self, *_, **__):
    self._tk_n_var.set(self.n)
    d = self.format_dict
    # remove {bar}
    d['bar_format'] = (d['bar_format'] or "{l_bar}<bar/>{r_bar}").replace(
        "{bar}", "<bar/>")
    msg = self.format_meter(**d)
    if '<bar/>' in msg:
        msg = "".join(re.split(r'\|?<bar/>\|?', msg, maxsplit=1))
    self._tk_text_var.set(msg)
    if not self._tk_dispatching:
        self._tk_window.update()

def set_description_str(self, desc=None, refresh=True):
    self.desc = desc
    if not self.disable:
        self._tk_window.wm_title(desc)
        if refresh and not self._tk_dispatching:
            self._tk_window.update()

def reset(self, total=None):
    """
    Resets to 0 iterations for repeated use.

    Parameters
    ----------
    total  : int or float, optional. Total to use for the new bar.
    """
    if hasattr(self, '_tk_pbar'):
        if total is None:
            self._tk_pbar.configure(maximum=100, mode="indeterminate")
        else:
            self._tk_pbar.configure(maximum=total, mode="determinate")
    super().reset(total=total)

def disp_len(data):
    """
    Returns the real on-screen length of a string which may contain
    ANSI control codes and wide chars.
    """
    return _text_width(RE_ANSI.sub('', data))

def wrap(func):
    params = signature(func).parameters
    # ignore unknown env vars
    overrides = {k: v for k, v in env_overrides.items() if k in params}
    # infer overrides' `type`s
    for k in overrides:
        param = params[k]
        if param.annotation is not param.empty:  # typehints
            for typ in getattr(param.annotation, '__args__', (param.annotation,)):
                try:
                    overrides[k] = typ(overrides[k])
                except Exception:
                    pass
                else:
                    break
        elif param.default is not None:  # type of default value
            overrides[k] = type(param.default)(overrides[k])
        else:
            try:  # `types` fallback
                overrides[k] = types[k](overrides[k])
            except KeyError:  # keep unconverted (`str`)
                pass
    return part(func, **overrides)

def wrapper_getattr(self, name):
    """Actual `self.getattr` rather than self._wrapped.getattr"""
    try:
        return object.__getattr__(self, name)
    except AttributeError:  # py2
        return getattr(self, name)

def write(self, s):
    """
    Encode `s` and pass to the wrapped object's `.write()` method.
    """
    return self._wrapped.write(s.encode(self.wrapper_getattr('encoding')))

def disable_on_exception(tqdm_instance, func):
    """
    Quietly set `tqdm_instance.miniters=inf` if `func` raises `errno=5`.
    """
    tqdm_instance = proxy(tqdm_instance)

    def inner(*args, **kwargs):
        try:
            return func(*args, **kwargs)
        except OSError as e:
            if e.errno != 5:
                raise
            try:
                tqdm_instance.miniters = float('inf')
            except ReferenceError:
                pass
        except ValueError as e:
            if 'closed' not in str(e):
                raise
            try:
                tqdm_instance.miniters = float('inf')
            except ReferenceError:
                pass
    return inner

def inner(*args, **kwargs):
    try:
        return func(*args, **kwargs)
    except OSError as e:
        if e.errno != 5:
            raise
        try:
            tqdm_instance.miniters = float('inf')
        except ReferenceError:
            pass
    except ValueError as e:
        if 'closed' not in str(e):
            raise
        try:
            tqdm_instance.miniters = float('inf')
        except ReferenceError:
            pass

def exit(self):
    self.was_killed.set()
    if self is not current_thread():
        self.join()
    return self.report()

def get_instances(self):
    # returns a copy of started `tqdm_cls` instances
    return [i for i in self.tqdm_cls._instances.copy()
            # Avoid race by checking that the instance started
            if hasattr(i, 'start_t')]

def message_id(self):
    if hasattr(self, '_message_id'):
        return self._message_id
    try:
