def submit(self, func, *args, **kwargs):
    """`func(*args, **kwargs)` may replace currently waiting task."""
    futures = self.futures
    if len(futures) == futures.maxlen:
        running = futures.popleft()
        if not running.done():
            if len(futures):  # clear waiting
                waiting = futures.pop()
                waiting.cancel()
            futures.appendleft(running)  # re-insert running
    try:
        waiting = self.pool.submit(func, *args, **kwargs)
    except Exception as e:
        tqdm_auto.write(str(e))
    else:
        futures.append(waiting)
        return waiting

def tenumerate(iterable, start=0, total=None, tqdm_class=tqdm_auto, **tqdm_kwargs):
    """
    Equivalent of `numpy.ndenumerate` or builtin `enumerate`.

    Parameters
    ----------
    tqdm_class  : [default: tqdm.auto.tqdm].
    """
    try:
        import numpy as np
    except ImportError:
        pass
    else:
        if isinstance(iterable, np.ndarray):
            return tqdm_class(np.ndenumerate(iterable), total=total or iterable.size,
                              **tqdm_kwargs)
    return enumerate(tqdm_class(iterable, total=total, **tqdm_kwargs), start)

def write(self, x, nolock=False):
    nl = b"\n" if isinstance(x, bytes) else "\n"
    pre, sep, post = x.rpartition(nl)
    if sep:
        blank = type(nl)()
        tqdm.write(blank.join(self._buf + [pre, sep]),
                   end=blank, file=self._wrapped, nolock=nolock)
        self._buf = [post]
    else:
        self._buf.append(x)

def win_OpenSCManager():
    res = advapi32.OpenSCManagerW(None, None, SC_MANAGER_ALL_ACCESS)
    if not res:
        raise Exception('Opening service manager failed - '
                        'are you running this as administrator?')
    return res

def win_uninstall_service(service_name):
    manager = win_OpenSCManager()
    try:
        h = advapi32.OpenServiceW(manager, service_name, DELETE)
        if not h:
            raise OSError('Could not find service %s: %s' % (
                service_name, ctypes.FormatError()))

        try:
            if not advapi32.DeleteService(h):
                raise OSError('Deletion failed: %s' % ctypes.FormatError())
        finally:
            advapi32.CloseServiceHandle(h)
    finally:
        advapi32.CloseServiceHandle(manager)

