def wrapped(a, b):
    if _NaN in (a, b):
        return op(_NaN, None)
    if not isinstance(a, (compat_basestring, compat_numeric_types)):
        a, b = b, a
    # strings are === if ==
    # why 'a' is not 'a': https://stackoverflow.com/a/1504848
    if isinstance(a, (compat_basestring, compat_numeric_types)):
        return a == b if op(0, 0) else a != b
    return op(a, b)

