def yield_terms(s):
    skip = False
    for i, term in enumerate(s[:-1]):
        if skip:
            skip = False
            continue
        if not (dm_chars & set(term)):
            yield term
            continue
        for dm_op in dm_ops:
            bodmas = list(self._separate(term, dm_op, skip_delims=skip_delim))
            if len(bodmas) > 1 and not bodmas[-1].strip():
                bodmas[-1] = (op if op == '-' else '') + s[i + 1]
                yield dm_op.join(bodmas)
                skip = True
                break
        else:
            if term:
                yield term

    if not skip and s[-1]:
        yield s[-1]

def splits(limit=limit):
    i = 0
    for j, jj in where():
        if j == jj == 0:
            continue
        if j is None and i >= len(obj):
            break
        yield obj[i:j]
        if jj is None or limit == 1:
            break
        limit -= 1
        i = jj

def recvall(self, cnt):
    data = b''
    while len(data) < cnt:
        cur = self.recv(cnt - len(data))
        if not cur:
            raise EOFError('{0} bytes missing'.format(cnt - len(data)))
        data += cur
    return data

