    return re.sub(
        r'&([^&;]+;)', lambda m: _htmlentity_transform(m.group(1)), s)

def process_communicate_or_kill(p, *args, **kwargs):
    try:
        return p.communicate(*args, **kwargs)
    except BaseException:  # Including KeyboardInterrupt
        p.kill()
        p.wait()
        raise

def get_subprocess_encoding():
    if sys.platform == 'win32' and sys.getwindowsversion()[0] >= 5:
        # For subprocess calls, encode with locale encoding
        # Refer to http://stackoverflow.com/a/9951851/35070
        encoding = preferredencoding()
    else:
        encoding = sys.getfilesystemencoding()
    if encoding is None:
        encoding = 'utf-8'
    return encoding

def encodeArgument(s):
    if not isinstance(s, compat_str):
        # Legacy code that uses byte strings
        # Uncomment the following line after fixing all post processors
        # assert False, 'Internal error: %r should be of type %r, is %r' % (s, compat_str, type(s))
        s = s.decode('ascii')
    return encodeFilename(s, True)

def formatSeconds(secs):
    if secs > 3600:
        return '%d:%02d:%02d' % (secs // 3600, (secs % 3600) // 60, secs % 60)
    elif secs > 60:
        return '%d:%02d' % (secs // 60, secs % 60)
    else:
        return '%d' % secs

def bug_reports_message(before=';'):
    if ytdl_is_updateable():
        update_cmd = 'type  youtube-dl -U  to update'
    else:
        update_cmd = 'see  https://github.com/ytdl-org/youtube-dl/#user-content-installation  on how to update'

    msg = (
        'please report this issue on https://github.com/ytdl-org/youtube-dl/issues ,'
        ' using the appropriate issue template.'
        ' Make sure you are using the latest version; %s.'
        ' Be sure to call youtube-dl with the --verbose option and include the complete output.'
    ) % update_cmd

    before = (before or '').rstrip()
    if not before or before.endswith(('.', '!', '?')):
        msg = msg[0].title() + msg[1:]

    return (before + ' ' if before else '') + msg

def handle_youtubedl_headers(headers):
    filtered_headers = headers

    if 'Youtubedl-no-compression' in filtered_headers:
        filtered_headers = filter_dict(filtered_headers, cndn=lambda k, _: k.lower() != 'accept-encoding')
        del filtered_headers['Youtubedl-no-compression']

    return filtered_headers

def parse_iso8601(date_str, delimiter='T', timezone=None):
    """ Return a UNIX timestamp from the given date """

    if date_str is None:
        return None

    date_str = re.sub(r'\.[0-9]+', '', date_str)

    if timezone is None:
        timezone, date_str = extract_timezone(date_str)

    with compat_contextlib_suppress(ValueError):
        date_format = '%Y-%m-%d{0}%H:%M:%S'.format(delimiter)
        dt = datetime.datetime.strptime(date_str, date_format) - timezone
        return calendar.timegm(dt.timetuple())

def unified_strdate(date_str, day_first=True):
    """Return a string with the date in the format YYYYMMDD"""

    if date_str is None:
        return None
    upload_date = None
    # Replace commas
    date_str = date_str.replace(',', ' ')
    # Remove AM/PM + timezone
    date_str = re.sub(r'(?i)\s*(?:AM|PM)(?:\s+[A-Z]+)?', '', date_str)
    _, date_str = extract_timezone(date_str)

    for expression in date_formats(day_first):
        with compat_contextlib_suppress(ValueError):
            upload_date = datetime.datetime.strptime(date_str, expression).strftime('%Y%m%d')
    if upload_date is None:
        timetuple = email.utils.parsedate_tz(date_str)
        if timetuple:
            with compat_contextlib_suppress(ValueError):
                upload_date = datetime.datetime(*timetuple[:6]).strftime('%Y%m%d')
    if upload_date is not None:
        return compat_str(upload_date)

def unified_timestamp(date_str, day_first=True):
    if date_str is None:
        return None

    date_str = re.sub(r'\s+', ' ', re.sub(
        r'(?i)[,|]|(mon|tues?|wed(nes)?|thu(rs)?|fri|sat(ur)?)(day)?', '', date_str))

    pm_delta = 12 if re.search(r'(?i)PM', date_str) else 0
    timezone, date_str = extract_timezone(date_str)

    # Remove AM/PM + timezone
    date_str = re.sub(r'(?i)\s*(?:AM|PM)(?:\s+[A-Z]+)?', '', date_str)

    # Remove unrecognized timezones from ISO 8601 alike timestamps
    m = re.search(r'\d{1,2}:\d{1,2}(?:\.\d+)?(?P<tz>\s*[A-Z]+)$', date_str)
    if m:
        date_str = date_str[:-len(m.group('tz'))]

    # Python only supports microseconds, so remove nanoseconds
    m = re.search(r'^([0-9]{4,}-[0-9]{1,2}-[0-9]{1,2}T[0-9]{1,2}:[0-9]{1,2}:[0-9]{1,2}\.[0-9]{6})[0-9]+$', date_str)
    if m:
        date_str = m.group(1)

    for expression in date_formats(day_first):
        with compat_contextlib_suppress(ValueError):
            dt = datetime.datetime.strptime(date_str, expression) - timezone + datetime.timedelta(hours=pm_delta)
            return calendar.timegm(dt.timetuple())
    timetuple = email.utils.parsedate_tz(date_str)
    if timetuple:
        return calendar.timegm(timetuple) + pm_delta * 3600 - compat_datetime_timedelta_total_seconds(timezone)

def determine_ext(url, default_ext='unknown_video'):
    if url is None or '.' not in url:
        return default_ext
    guess = url.partition('?')[0].rpartition('.')[2]
    if re.match(r'^[A-Za-z0-9]+$', guess):
        return guess
    # Try extract ext from URLs like http://example.com/foo/bar.mp4/?download
    elif guess.rstrip('/') in KNOWN_EXTENSIONS:
        return guess.rstrip('/')
    else:
        return default_ext

def date_from_str(date_str):
    """
    Return a datetime object from a string in the format YYYYMMDD or
    (now|today)[+-][0-9](day|week|month|year)(s)?"""
    today = datetime.date.today()
    if date_str in ('now', 'today'):
        return today
    if date_str == 'yesterday':
        return today - datetime.timedelta(days=1)
    match = re.match(r'(now|today)(?P<sign>[+-])(?P<time>\d+)(?P<unit>day|week|month|year)(s)?', date_str)
    if match is not None:
        sign = match.group('sign')
        time = int(match.group('time'))
        if sign == '-':
            time = -time
        unit = match.group('unit')
        # A bad approximation?
        if unit == 'month':
            unit = 'day'
            time *= 30
        elif unit == 'year':
            unit = 'day'
            time *= 365
        unit += 's'
        delta = datetime.timedelta(**{unit: time})
        return today + delta
    return datetime.datetime.strptime(date_str, '%Y%m%d').date()

def hyphenate_date(date_str):
    """
    Convert a date in 'YYYYMMDD' format to 'YYYY-MM-DD' format"""
    match = re.match(r'^(\d\d\d\d)(\d\d)(\d\d)$', date_str)
    if match is not None:
        return '-'.join(match.groups())
    else:
        return date_str

def write_string(s, out=None, encoding=None):
    if out is None:
        out = sys.stderr
    assert isinstance(s, compat_str)

    if sys.platform == 'win32' and encoding is None and hasattr(out, 'fileno'):
        if _windows_write_string(s, out):
            return

    if ('b' in getattr(out, 'mode', '')
            or sys.version_info[0] < 3):  # Python 2 lies about mode of sys.stderr
        byt = s.encode(encoding or preferredencoding(), 'ignore')
        out.write(byt)
    elif hasattr(out, 'buffer'):
        enc = encoding or getattr(out, 'encoding', None) or preferredencoding()
        byt = s.encode(enc, 'ignore')
        out.buffer.write(byt)
    else:
        out.write(s)
    out.flush()

def bytes_to_intlist(bs):
    if not bs:
        return []
    if isinstance(bs[0], int):  # Python 3
        return list(bs)
    else:
        return [ord(c) for c in bs]

def shell_quote(args):
    quoted_args = []
    encoding = get_filesystem_encoding()
    for a in args:
        # We may get a filename encoded with 'encodeFilename'
        a = _decode_compat_str(a, encoding)
        quoted_args.append(compat_shlex_quote(a))
    return ' '.join(quoted_args)

def format_bytes(bytes):
    if bytes is None:
        return 'N/A'
    if type(bytes) is str:
        bytes = float(bytes)
    if bytes == 0.0:
        exponent = 0
    else:
        exponent = int(math.log(bytes, 1024.0))
    suffix = ['B', 'KiB', 'MiB', 'GiB', 'TiB', 'PiB', 'EiB', 'ZiB', 'YiB'][exponent]
    converted = float(bytes) / float(1024 ** exponent)
    return '%.2f%s' % (converted, suffix)

def lookup_unit_table(unit_table, s):
    units_re = '|'.join(re.escape(u) for u in unit_table)
    m = re.match(
        r'(?P<num>[0-9]+(?:[,.][0-9]*)?)\s*(?P<unit>%s)\b' % units_re, s)
    if not m:
        return None
    num_str = m.group('num').replace(',', '.')
    mult = unit_table[m.group('unit')]
    return int(float(num_str) * mult)

def parse_count(s):
    if s is None:
        return None

    s = s.strip()

    if re.match(r'^[\d,.]+$', s):
        return str_to_int(s)

    _UNIT_TABLE = {
        'k': 1000,
        'K': 1000,
        'm': 1000 ** 2,
        'M': 1000 ** 2,
        'kk': 1000 ** 2,
        'KK': 1000 ** 2,
    }

    return lookup_unit_table(_UNIT_TABLE, s)

def parse_resolution(s):
    if s is None:
        return {}

    mobj = re.search(r'\b(?P<w>\d+)\s*[xX]\s*(?P<h>\d+)\b', s)
    if mobj:
        return {
            'width': int(mobj.group('w')),
            'height': int(mobj.group('h')),
        }

    mobj = re.search(r'\b(\d+)[pPiI]\b', s)
    if mobj:
        return {'height': int(mobj.group(1))}

    mobj = re.search(r'\b([48])[kK]\b', s)
    if mobj:
        return {'height': int(mobj.group(1)) * 540}

    return {}

def parse_bitrate(s):
    s = txt_or_none(s)
    if not s:
        return None
    mobj = re.search(r'\b(\d+)\s*kbps', s)
    if mobj:
        return int(mobj.group(1))

def month_by_name(name, lang='en'):
    """ Return the number of a month by (locale-independently) English name """

    month_names = MONTH_NAMES.get(lang, MONTH_NAMES['en'])

    try:
        return month_names.index(name) + 1
    except ValueError:
        return None

def month_by_abbreviation(abbrev):
    """ Return the number of a month by (locale-independently) English
        abbreviations """

    try:
        return [s[:3] for s in ENGLISH_MONTH_NAMES].index(abbrev) + 1
    except ValueError:
        return None

def setproctitle(title):
    assert isinstance(title, compat_str)

    # ctypes in Jython is not complete
    # http://bugs.jython.org/issue2148
    if sys.platform.startswith('java'):
        return

    try:
        libc = ctypes.cdll.LoadLibrary('libc.so.6')
    except OSError:
        return
    except TypeError:
        # LoadLibrary in Windows Python 2.7.13 only expects
        # a bytestring, but since unicode_literals turns
        # every string into a unicode string, it fails.
        return
    title_bytes = title.encode('utf-8')
    buf = ctypes.create_string_buffer(len(title_bytes))
    buf.value = title_bytes
    try:
        libc.prctl(15, buf, 0, 0, 0)
    except AttributeError:
        return  # Strange libc, just skip this

def remove_quotes(s):
    if s is None or len(s) < 2:
        return s
    for quote in ('"', "'", ):
        if s[0] == quote and s[-1] == quote:
            return s[1:-1]
    return s

def int_or_none(v, scale=1, default=None, get_attr=None, invscale=1, base=None):
    if get_attr:
        if v is not None:
            v = getattr(v, get_attr, None)
    if v in (None, ''):
        return default
    try:
        # like int, raise if base is specified and v is not a string
        return (int(v) if base is None else int(v, base=base)) * invscale // scale
    except (ValueError, TypeError, OverflowError):
        return default

def str_to_int(int_str):
    """ A more relaxed version of int_or_none """
    if isinstance(int_str, compat_integer_types):
        return int_str
    elif isinstance(int_str, compat_str):
        int_str = re.sub(r'[,\.\+]', '', int_str)
        return int_or_none(int_str)

def float_or_none(v, scale=1, invscale=1, default=None):
    if v is None:
        return default
    try:
        return float(v) * invscale / scale
    except (ValueError, TypeError):
        return default

def url_or_none(url):
    if not url or not isinstance(url, compat_str):
        return None
    url = url.strip()
    return url if re.match(r'^(?:(?:https?|rt(?:m(?:pt?[es]?|fp)|sp[su]?)|mms|ftps?):)?//', url) else None

def parse_duration(s):
    if not isinstance(s, compat_basestring):
        return None

    s = s.strip()

    days, hours, mins, secs, ms = [None] * 5
    m = re.match(r'(?:(?:(?:(?P<days>[0-9]+):)?(?P<hours>[0-9]+):)?(?P<mins>[0-9]+):)?(?P<secs>[0-9]+)(?P<ms>\.[0-9]+)?Z?$', s)
    if m:
        days, hours, mins, secs, ms = m.groups()
    else:
        m = re.match(
            r'''(?ix)(?:P?
                (?:
                    [0-9]+\s*y(?:ears?)?\s*
                )?
                (?:
                    [0-9]+\s*m(?:onths?)?\s*
                )?
                (?:
                    [0-9]+\s*w(?:eeks?)?\s*
                )?
                (?:
                    (?P<days>[0-9]+)\s*d(?:ays?)?\s*
                )?
                T)?
                (?:
                    (?P<hours>[0-9]+)\s*h(?:ours?)?\s*
                )?
                (?:
                    (?P<mins>[0-9]+)\s*m(?:in(?:ute)?s?)?\s*
                )?
                (?:
                    (?P<secs>[0-9]+)(?P<ms>\.[0-9]+)?\s*s(?:ec(?:ond)?s?)?\s*
                )?Z?$''', s)
        if m:
            days, hours, mins, secs, ms = m.groups()
        else:
            m = re.match(r'(?i)(?:(?P<hours>[0-9.]+)\s*(?:hours?)|(?P<mins>[0-9.]+)\s*(?:mins?\.?|minutes?)\s*)Z?$', s)
            if m:
                hours, mins = m.groups()
            else:
                return None

    duration = 0
    if secs:
        duration += float(secs)
    if mins:
        duration += float(mins) * 60
    if hours:
        duration += float(hours) * 60 * 60
    if days:
        duration += float(days) * 24 * 60 * 60
    if ms:
        duration += float(ms)
    return duration

def detect_exe_version(output, version_re=None, unrecognized='present'):
    assert isinstance(output, compat_str)
    if version_re is None:
        version_re = r'version\s+([-0-9._a-zA-Z]+)'
    m = re.search(version_re, output)
    if m:
        return m.group(1)
    else:
        return unrecognized

def uppercase_escape(s):
    unicode_escape = codecs.getdecoder('unicode_escape')
    return re.sub(
        r'\\U[0-9a-fA-F]{8}',
        lambda m: unicode_escape(m.group(0))[0],
        s)

def lowercase_escape(s):
    unicode_escape = codecs.getdecoder('unicode_escape')
    return re.sub(
        r'\\u[0-9a-fA-F]{4}',
        lambda m: unicode_escape(m.group(0))[0],
        s)

def read_batch_urls(batch_fd):
    def fixup(url):
        url = _decode_compat_str(url, 'utf-8', 'replace')
        BOM_UTF8 = '\xef\xbb\xbf'
        if url.startswith(BOM_UTF8):
            url = url[len(BOM_UTF8):]
        url = url.strip()
        if url.startswith(('#', ';', ']')):
            return False
        return url

    with contextlib.closing(batch_fd) as fd:
        return [url for url in map(fixup, fd) if url]

def try_call(*funcs, **kwargs):

    # parameter defaults
    expected_type = kwargs.get('expected_type')
    fargs = kwargs.get('args', [])
    fkwargs = kwargs.get('kwargs', {})

    for f in funcs:
        try:
            val = f(*fargs, **fkwargs)
        except (AttributeError, KeyError, TypeError, IndexError, ZeroDivisionError):
            pass
        else:
            if expected_type is None or isinstance(val, expected_type):
                return val

def try_get(src, getter, expected_type=None):
    if not isinstance(getter, (list, tuple)):
        getter = [getter]
    for get in getter:
        try:
            v = get(src)
        except (AttributeError, KeyError, TypeError, IndexError):
            pass
        else:
            if expected_type is None or isinstance(v, expected_type):
                return v

def merge_dicts(*dicts, **kwargs):
    """
        Merge the `dict`s in `dicts` using the first valid value for each key.
        Normally valid: not None and not an empty string

        Keyword-only args:
        unblank:    allow empty string if False (default True)
        rev:        merge dicts in reverse order (default False)

        merge_dicts(dct1, dct2, ..., unblank=False, rev=True)
        matches {**dct1, **dct2, ...}

        However, merge_dicts(dct1, dct2, ..., rev=True) may often be better.
    """

    unblank = kwargs.get('unblank', True)
    rev = kwargs.get('rev', False)

    if unblank:
        def can_merge_str(k, v, to_dict):
            return (isinstance(v, compat_str) and v
                    and isinstance(to_dict[k], compat_str)
                    and not to_dict[k])
    else:
        can_merge_str = lambda k, v, to_dict: False

    merged = {}
    for a_dict in reversed(dicts) if rev else dicts:
        for k, v in a_dict.items():
            if v is None:
                continue
            if (k not in merged) or can_merge_str(k, v, merged):
                merged[k] = v
    return merged

def parse_age_limit(s):
    if not isinstance(s, bool):
        age = int_or_none(s)
        if age is not None:
            return age if 0 <= age <= 21 else None
    if not isinstance(s, compat_basestring):
        return None
    m = re.match(r'^(?P<age>\d{1,2})\+?$', s)
    if m:
        return int(m.group('age'))
    if s in US_RATINGS:
        return US_RATINGS[s]
    m = re.match(r'^TV[_-]?(%s)$' % '|'.join(k[3:] for k in TV_PARENTAL_GUIDELINES), s)
    if m:
        return TV_PARENTAL_GUIDELINES['TV-' + m.group(1)]
    return None

def qualities(quality_ids):
    """ Get a numeric quality value out of a list of possible values """
    def q(qid):
        try:
            return quality_ids.index(qid)
        except ValueError:
            return -1
    return q

def limit_length(s, length):
    """ Add ellipses to overly long strings """
    if s is None:
        return None
    ELLIPSES = '...'
    if len(s) > length:
        return s[:length - len(ELLIPSES)] + ELLIPSES
    return s

def is_outdated_version(version, limit, assume_new=True):
    if not version:
        return not assume_new
    try:
        return version_tuple(version) < version_tuple(limit)
    except ValueError:
        return not assume_new

def ytdl_is_updateable():
    """ Returns if youtube-dl can be updated with -U """
    from zipimport import zipimporter

    return isinstance(globals().get('__loader__'), zipimporter) or hasattr(sys, 'frozen')

def parse_codecs(codecs_str):
    # http://tools.ietf.org/html/rfc6381
    if not codecs_str:
        return {}
    split_codecs = list(filter(None, map(
        lambda str: str.strip(), codecs_str.strip().strip(',').split(','))))
    vcodec, acodec = None, None
    for full_codec in split_codecs:
        codec = full_codec.split('.')[0]
        if codec in ('avc1', 'avc2', 'avc3', 'avc4', 'vp9', 'vp8', 'hev1', 'hev2', 'h263', 'h264', 'mp4v', 'hvc1', 'av01', 'theora'):
            if not vcodec:
                vcodec = full_codec
        elif codec in ('mp4a', 'opus', 'vorbis', 'mp3', 'aac', 'ac-3', 'ec-3', 'eac3', 'dtsc', 'dtse', 'dtsh', 'dtsl'):
            if not acodec:
                acodec = full_codec
        else:
            write_string('WARNING: Unknown codec %s\n' % full_codec, sys.stderr)
    if not vcodec and not acodec:
        if len(split_codecs) == 2:
            return {
                'vcodec': split_codecs[0],
                'acodec': split_codecs[1],
            }
    else:
        return {
            'vcodec': vcodec or 'none',
            'acodec': acodec or 'none',
        }
    return {}

def urlhandle_detect_ext(url_handle):
    getheader = url_handle.headers.get

    cd = getheader('Content-Disposition')
    if cd:
        m = re.match(r'attachment;\s*filename="(?P<filename>[^"]+)"', cd)
        if m:
            e = determine_ext(m.group('filename'), default_ext=None)
            if e:
                return e

    return mimetype2ext(getheader('Content-Type'))

def is_html(first_bytes):
    """ Detect whether a file contains HTML by examining its first bytes. """

    BOMS = [
        (b'\xef\xbb\xbf', 'utf-8'),
        (b'\x00\x00\xfe\xff', 'utf-32-be'),
        (b'\xff\xfe\x00\x00', 'utf-32-le'),
        (b'\xff\xfe', 'utf-16-le'),
        (b'\xfe\xff', 'utf-16-be'),
    ]
    for bom, enc in BOMS:
        if first_bytes.startswith(bom):
            s = first_bytes[len(bom):].decode(enc, 'replace')
            break
    else:
        s = first_bytes.decode('utf-8', 'replace')

    return re.match(r'^\s*<', s)

def render_table(header_row, data):
    """ Render a list of rows, each as a list of values """
    table = [header_row] + data
    max_lens = [max(len(compat_str(v)) for v in col) for col in zip(*table)]
    format_str = ' '.join('%-' + compat_str(ml + 1) + 's' for ml in max_lens[:-1]) + '%s'
    return '\n'.join(format_str % tuple(row) for row in table)

def match_str(filter_str, dct):
    """ Filter a dictionary with a simple string syntax. Returns True (=passes filter) or false """

    return all(
        _match_one(filter_part, dct) for filter_part in filter_str.split('&'))

def match_filter_func(filter_str):
    def _match_func(info_dict):
        if match_str(filter_str, info_dict):
            return None
        else:
            video_title = info_dict.get('title', info_dict.get('id', 'video'))
            return '%s does not pass filter %s, skipping ..' % (video_title, filter_str)
    return _match_func

def parse_dfxp_time_expr(time_expr):
    if not time_expr:
        return

    mobj = re.match(r'^(?P<time_offset>\d+(?:\.\d+)?)s?$', time_expr)
    if mobj:
        return float(mobj.group('time_offset'))

    mobj = re.match(r'^(\d+):(\d\d):(\d\d(?:(?:\.|:)\d+)?)$', time_expr)
    if mobj:
        return 3600 * int(mobj.group(1)) + 60 * int(mobj.group(2)) + float(mobj.group(3).replace(':', '.'))

def cli_option(params, command_option, param):
    param = params.get(param)
    if param:
        param = compat_str(param)
    return [command_option, param] if param is not None else []

def cli_bool_option(params, command_option, param, true_value='true', false_value='false', separator=None):
    param = params.get(param)
    if param is None:
        return []
    assert isinstance(param, bool)
    if separator:
        return [command_option + separator + (true_value if param else false_value)]
    return [command_option, true_value if param else false_value]

def cli_configuration_args(params, param, default=[]):
    ex_args = params.get(param)
    if ex_args is None:
        return default
    assert isinstance(ex_args, list)
    return ex_args

def pkcs1pad(data, length):
    """
    Padding input data with PKCS#1 scheme

    @param {int[]} data        input data
    @param {int}   length      target length
    @returns {int[]}           padded data
    """
    if len(data) > length - 11:
        raise ValueError('Input data too long for PKCS#1 padding')

    pseudo_random = [random.randint(0, 254) for _ in range(length - len(data) - 3)]
    return [0, 2] + pseudo_random + [0] + data

def encode_base_n(num, n, table=None):
    FULL_TABLE = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'
    if not table:
        table = FULL_TABLE[:n]

    if n > len(table):
        raise ValueError('base %d exceeds table length %d' % (n, len(table)))

    if num == 0:
        return table[0]

    ret = ''
    while num:
        ret = table[num % n] + ret
        num = num // n
    return ret

def caesar(s, alphabet, shift):
    if shift == 0:
        return s
    l = len(alphabet)
    return ''.join(
        alphabet[(alphabet.index(c) + shift) % l] if c in alphabet else c
        for c in s)

def parse_m3u8_attributes(attrib):
    info = {}
    for (key, val) in re.findall(r'(?P<key>[A-Z0-9-]+)=(?P<val>"[^"]+"|[^",]+)(?:,|$)', attrib):
        if val.startswith('"'):
            val = val[1:-1]
        info[key] = val
    return info

def random_birthday(year_field, month_field, day_field):
    start_date = datetime.date(1950, 1, 1)
    end_date = datetime.date(1995, 12, 31)
    offset = random.randint(0, (end_date - start_date).days)
    random_date = start_date + datetime.timedelta(offset)
    return {
        year_field: str(random_date.year),
        month_field: str(random_date.month),
        day_field: str(random_date.day),
    }

def join_nonempty(*values, **kwargs):

    # parameter defaults
    delim = kwargs.get('delim', '-')
    from_dict = kwargs.get('from_dict')

    if from_dict is not None:
        values = (traverse_obj(from_dict, variadic(v)) for v in values)
    return delim.join(map(compat_str, filter(None, values)))

def find_xpath_attr(node, xpath, key, val=None):
    """ Find the xpath xpath[@key=val] """
    assert re.match(r'^[a-zA-Z_-]+$', key)
    expr = xpath + ('[@%s]' % key if val is None else "[@%s='%s']" % (key, val))
    return node.find(expr)

def find_xpath_attr(node, xpath, key, val=None):
    for f in node.findall(compat_xpath(xpath)):
        if key not in f.attrib:
            continue
        if val is None or f.attrib.get(key) == val:
            return f
    return None

def replace_insane(char):
    if restricted and char in ACCENT_CHARS:
        return ACCENT_CHARS[char]
    if char == '?' or ord(char) < 32 or ord(char) == 127:
        return ''
    elif char == '"':
        return '' if restricted else '\''
    elif char == ':':
        return '_-' if restricted else ' -'
    elif char in '\\/|*<>':
        return '_'
    if restricted and (char in '!&\'()[]{}$;`^,#' or char.isspace()):
        return '_'
    if restricted and ord(char) > 127:
        return '' if unicodedata.category(char)[0] in 'CM' else '_'

    return char

def deflate_gz(data):
    try:
        # format:zlib,gzip + windowsize:32768
        return data and zlib.decompress(data, 32 + zlib.MAX_WBITS)
    except zlib.error:
        # raw zlib * windowsize:32768 (RFC 9110: "non-conformant")
        return zlib.decompress(data, -zlib.MAX_WBITS)

def gzip(data):

    from gzip import GzipFile

    def _gzip(data):
        with io.BytesIO(data) as data_buf:
            gz = GzipFile(fileobj=data_buf, mode='rb')
            return gz.read()

    try:
        return _gzip(data)
    except IOError as original_ioerror:
        # There may be junk at the end of the file
        # See http://stackoverflow.com/q/4928560/35070 for details
        for i in range(1, 1024):
            try:
                return _gzip(data[:-i])
            except IOError:
                continue
        else:
            raise original_ioerror

def get_cookie_header(self, url):
    """Generate a Cookie HTTP header for a given url"""
    cookie_req = sanitized_Request(url)
    self.add_cookie_header(cookie_req)
    return cookie_req.get_header('Cookie')

def get_cookies_for_url(self, url):
    """Generate a list of Cookie objects for a given url"""
    # Policy `_now` attribute must be set before calling `_cookies_for_request`
    # Ref: https://github.com/python/cpython/blob/3.7/Lib/http/cookiejar.py#L1360
    self._policy._now = self._now = int(time.time())
    return self._cookies_for_request(sanitized_Request(url))

def not_a_console(handle):
    if handle == INVALID_HANDLE_VALUE or handle is None:
        return True
    return ((GetFileType(handle) & ~FILE_TYPE_REMOTE) != FILE_TYPE_CHAR
            or GetConsoleMode(handle, ctypes.byref(ctypes.wintypes.DWORD())) == 0)

def next_nonbmp_pos(s):
    try:
        return next(i for i, c in enumerate(s) if ord(c) > 0xffff)
    except StopIteration:
        return len(s)

def getslice(self, start=0, end=None):
    res = []
    for pagenum in itertools.count(start // self._pagesize):
        firstid = pagenum * self._pagesize
        nextfirstid = pagenum * self._pagesize + self._pagesize
        if start >= nextfirstid:
            continue

        page_results = None
        if self._use_cache:
            page_results = self._cache.get(pagenum)
        if page_results is None:
            page_results = list(self._pagefunc(pagenum))
        if self._use_cache:
            self._cache[pagenum] = page_results

        startv = (
            start % self._pagesize
            if firstid <= start < nextfirstid
            else 0)

        endv = (
            ((end - 1) % self._pagesize) + 1
            if (end is not None and firstid <= end <= nextfirstid)
            else None)

        if startv != 0 or endv is not None:
            page_results = page_results[startv:endv]
        res.extend(page_results)

        # A little optimization - if current page is not "full", ie. does
        # not contain page_size videos then we can assume that this page
        # is the last one - there are no more ids on further pages -
        # i.e. no need to query again.
        if len(page_results) + startv < self._pagesize:
            break

        # If we got the whole page, but the next page is not interesting,
        # break out early as well
        if end == nextfirstid:
            break
    return res

def fixup(url):
    url = _decode_compat_str(url, 'utf-8', 'replace')
    BOM_UTF8 = '\xef\xbb\xbf'
    if url.startswith(BOM_UTF8):
        url = url[len(BOM_UTF8):]
    url = url.strip()
    if url.startswith(('#', ';', ']')):
        return False
    return url

def template_substitute(match):
    evaluated = js_to_json(match.group(1), vars, strict=strict)
    if evaluated[0] == '"':
        return json.loads(evaluated)
    return evaluated

def fix_kv(m):
    v = m.group(0)
    if v in ('true', 'false', 'null'):
        return v
    elif v in ('undefined', 'void 0'):
        return 'null'
    elif v.startswith('/*') or v.startswith('//') or v == ',':
        return ''

    if v[0] in STRING_QUOTES:
        v = re.sub(r'(?s)\${([^}]+)}', template_substitute, v[1:-1]) if v[0] == '`' else v[1:-1]
        escaped = re.sub(r'(?s)(")|\\(.)', process_escape, v)
        return '"{0}"'.format(escaped)

    inv = IDENTITY
    im = re.split(r'^!+', v)
    if len(im) > 1 and not im[-1].endswith(':'):
        if (len(v) - len(im[1])) % 2 == 1:
            inv = lambda x: 'true' if x == 0 else 'false'
        else:
            inv = lambda x: 'false' if x == 0 else 'true'
    if not any(x for x in im):
        return
    v = im[-1]

    for regex, base in INTEGER_TABLE:
        im = re.match(regex, v)
        if im:
            i = int(im.group(1), base)
            return ('"%s":' if v.endswith(':') else '%s') % inv(i)

    if v in vars:
        try:
            if not strict:
                json.loads(vars[v])
        except JSONDecodeError:
            return inv(json.dumps(vars[v]))
        else:
            return inv(vars[v])

    if not strict:
        v = try_call(inv, args=(v,), default=v)
        if v in ('true', 'false'):
            return v
        return '"{0}"'.format(v)

    raise ValueError('Unknown value: ' + v)

def q(qid):
    try:
        return quality_ids.index(qid)
    except ValueError:
        return -1

def parse_node(node):
    target = TTMLPElementParser()
    parser = xml.etree.ElementTree.XMLParser(target=target)
    parser.feed(xml.etree.ElementTree.tostring(node))
    return parser.close()

def long2short(cls, code):
    """Convert language code from ISO 639-2/T to ISO 639-1"""
    for short_name, long_name in cls._lang_map.items():
        if long_name == code:
            return short_name

def random_ipv4(cls, code_or_block):
    if len(code_or_block) == 2:
        block = cls._country_ip_map.get(code_or_block.upper())
        if not block:
            return None
    else:
        block = code_or_block
    addr, preflen = block.split('/')
    addr_min = compat_struct_unpack('!L', socket.inet_aton(addr))[0]
    addr_max = addr_min | (0xffffffff >> int(preflen))
    return compat_str(socket.inet_ntoa(
        compat_struct_pack('!L', random.randint(addr_min, addr_max))))

def from_iterable(iterables):
    # chain.from_iterable(['ABC', 'DEF']) --> A B C D E F
    for it in iterables:
        for item in it:
            yield item

def lazy_last(iterable):
    iterator = iter(iterable)
    prev = next(iterator, NO_DEFAULT)
    if prev is NO_DEFAULT:
        return

    for item in iterator:
        yield False, prev
        prev = item

    yield True, prev

def apply_path(start_obj, path, test_type):
    objs = (start_obj,)
    has_branched = False

    key = None
    for last, key in lazy_last(variadic(path, (str, bytes, dict, set))):
        if not casesense and isinstance(key, str):
            key = compat_casefold(key)

        if key in (any, all):
            has_branched = False
            filtered_objs = (obj for obj in objs if obj not in (None, {}))
            if key is any:
                objs = (next(filtered_objs, None),)
            else:
                objs = (list(filtered_objs),)
            continue

        if __debug__ and callable(key):
            # Verify function signature
            _try_bind_args(key, None, None)

        new_objs = []
        for obj in objs:
            branching, results = apply_key(key, obj, last)
            has_branched |= branching
            new_objs.append(results)

        objs = from_iterable(new_objs)

    if test_type and not isinstance(key, (dict, list, tuple)):
        objs = map(type_test, objs)

    return objs, has_branched, isinstance(key, dict)

def sanitize_extension(cls, extension, **kwargs):
    # ... /, *, prepend=False
    prepend = kwargs.get('prepend', False)

    if '/' in extension or '\\' in extension:
        raise cls(extension)

    if not prepend:
        last = extension.rpartition('.')[-1]
        if last == 'bin':
            extension = last = 'unknown_video'
        if not (cls.lenient or last.lower() in cls._ALLOWED_EXTENSIONS):
            raise cls(extension)

    return extension

def encodings(hdrs):
    # A header field that allows multiple values can have multiple instances [2].
    # [2]: https://datatracker.ietf.org/doc/html/rfc9110#name-fields
    for e in reversed(','.join(hdrs).split(',')):
        if e:
            yield e.strip()

def connect(self):
    self.sock = sockssocket()
    self.sock.setproxy(*proxy_args)
    if type(self.timeout) in (int, float):
        self.sock.settimeout(self.timeout)
    self.sock.connect((self.host, self.port))

    if isinstance(self, compat_http_client.HTTPSConnection):
        if hasattr(self, '_context'):  # Python > 2.6
            self.sock = self._context.wrap_socket(
                self.sock, server_hostname=self.host)
        else:
            self.sock = ssl.wrap_socket(self.sock)

def prepare_line(line):
    if line.startswith(self._HTTPONLY_PREFIX):
        line = line[len(self._HTTPONLY_PREFIX):]
    # comments and empty lines are fine
    if line.startswith('#') or not line.strip():
        return line
    cookie_list = line.split('\t')
    if len(cookie_list) != self._ENTRY_LEN:
        raise compat_cookiejar.LoadError('invalid length %d' % len(cookie_list))
    cookie = self._CookieFileEntry(*cookie_list)
    if cookie.expires_at and not cookie.expires_at.isdigit():
        raise compat_cookiejar.LoadError('invalid expires at %s' % cookie.expires_at)
    return line

def start(self, tag, attrib):
    if tag in (_x('ttml:br'), 'br'):
        self._out += '\n'
    else:
        unclosed_elements = []
        style = {}
        element_style_id = attrib.get('style')
        if default_style:
            style.update(default_style)
        if element_style_id:
            style.update(styles.get(element_style_id, {}))
        for prop in SUPPORTED_STYLING:
            prop_val = attrib.get(_x('tts:' + prop))
            if prop_val:
                style[prop] = prop_val
        if style:
            font = ''
            for k, v in sorted(style.items()):
                if self._applied_styles and self._applied_styles[-1].get(k) == v:
                    continue
                if k == 'color':
                    font += ' color="%s"' % v
                elif k == 'fontSize':
                    font += ' size="%s"' % v
                elif k == 'fontFamily':
                    font += ' face="%s"' % v
                elif k == 'fontWeight' and v == 'bold':
                    self._out += '<b>'
                    unclosed_elements.append('b')
                elif k == 'fontStyle' and v == 'italic':
                    self._out += '<i>'
                    unclosed_elements.append('i')
                elif k == 'textDecoration' and v == 'underline':
                    self._out += '<u>'
                    unclosed_elements.append('u')
            if font:
                self._out += '<font' + font + '>'
                unclosed_elements.append('font')
            applied_style = {}
            if self._applied_styles:
                applied_style.update(self._applied_styles[-1])
            applied_style.update(style)
            self._applied_styles.append(applied_style)
        self._unclosed_elements.append(unclosed_elements)

def end(self, tag):
    if tag not in (_x('ttml:br'), 'br'):
        unclosed_elements = self._unclosed_elements.pop()
        for element in reversed(unclosed_elements):
            self._out += '</%s>' % element
        if unclosed_elements and self._applied_styles:
            self._applied_styles.pop()

def apply_specials(element):
    if special is None:
        return element
    if special == '@':
        return element.attrib
    if special.startswith('@'):
        return try_call(element.attrib.get, args=(special[1:],))
    if special == 'text()':
        return element.text
    raise SyntaxError('apply_specials is missing case for {0!r}'.format(special))

def wrapper(self, *args, **kwargs):
    try:
        return func(self, *args, **kwargs)
    except _UnsafeExtensionError as error:
        self.report_error(
            '{0} found; to avoid damaging your system, this value is disallowed.'
            ' If you believe this is an error{1}'.format(
                error_to_compat_str(error), bug_reports_message(',')))

def add_info_extractor(self, ie):
