def find_field(global_re, name, content_re=None, value_re='[^"]+', fatal=False):
    metadata_re = ''
    if content_re:
        metadata_re = r'|video_metadata\.content_' + content_re
    return self._search_regex(
        r'(?:_cnglobal\.currentVideo\.%s%s)\s*=\s*"(%s)";' % (global_re, metadata_re, value_re),
        webpage, name, fatal=fatal)

def extract_format(page, version):
    json_str = self._html_search_regex(
        r'player_data=(\\?["\'])(?P<player_data>.+?)\1', page,
        '%s player_json' % version, fatal=False, group='player_data')
    if not json_str:
        return
    player_data = self._parse_json(
        json_str, '%s player_data' % version, fatal=False)
    if not player_data:
        return
    video = player_data.get('video')
    if not video or 'file' not in video:
        self.report_warning('Unable to extract %s version information' % version)
        return
    if video['file'].startswith('uggc'):
        video['file'] = codecs.decode(video['file'], 'rot_13')
        if video['file'].endswith('adc.mp4'):
            video['file'] = video['file'].replace('adc.mp4', '.mp4')
    elif not video['file'].startswith('http'):
        video['file'] = decrypt_file(video['file'])
    f = {
        'url': video['file'],
    }
    m = re.search(
        r'<a[^>]+data-quality="(?P<format_id>[^"]+)"[^>]+href="[^"]+"[^>]+class="[^"]*quality-btn-active[^"]*">(?P<height>[0-9]+)p',
        page)
    if m:
        f.update({
            'format_id': m.group('format_id'),
            'height': int(m.group('height')),
        })
    info_dict['formats'].append(f)
    if not info_dict['duration']:
        info_dict['duration'] = parse_duration(video.get('duration'))

def initialize(self):
    """Initializes an instance (authentication, etc)."""
    self._initialize_geo_bypass({
        'countries': self._GEO_COUNTRIES,
        'ip_blocks': self._GEO_IP_BLOCKS,
    })
    if not self._ready:
        self._real_initialize()
        self._ready = True

def extract(self, url):
    """Extracts URL information and returns it in list of dicts."""
    try:
        for _ in range(2):
            try:
                self.initialize()
                ie_result = self._real_extract(url)
                if self._x_forwarded_for_ip:
                    ie_result['__x_forwarded_for_ip'] = self._x_forwarded_for_ip
                return ie_result
            except GeoRestrictedError as e:
                if self.__maybe_fake_ip_and_retry(e.countries):
                    continue
                raise
    except ExtractorError:
        raise
    except compat_http_client.IncompleteRead as e:
        raise ExtractorError('A network error has occurred.', cause=e, expected=True)
    except (KeyError, StopIteration) as e:
        raise ExtractorError('An extractor error has occurred.', cause=e)

def report_warning(self, msg, *args, **kwargs):
    if len(args) > 0:
        video_id = args[0]
        args = args[1:]
    else:
        video_id = kwargs.pop('video_id', None)
    idstr = '' if video_id is None else '%s: ' % video_id
    self._downloader.report_warning(
        self.__ie_msg(idstr, msg), *args, **kwargs)

def get_param(self, name, *args, **kwargs):
    default, args = (args[0], args[1:]) if len(args) > 0 else (kwargs.pop('default', None), args)
    if self._downloader:
        return self._downloader.params.get(name, default, *args, **kwargs)
    return default

def raise_no_formats(self, msg, expected=False, video_id=None):
    if expected and (
            self.get_param('ignore_no_formats_error') or self.get_param('wait_for_video')):
        self.report_warning(msg, video_id)
    elif isinstance(msg, ExtractorError):
        raise msg
    else:
        raise ExtractorError(msg, expected=expected, video_id=video_id)

