def parse_graphql_video(video):
    formats = []
    q = qualities(['sd', 'hd'])
    for (suffix, format_id) in [('', 'sd'), ('_quality_hd', 'hd')]:
        playable_url = video.get('playable_url' + suffix)
        if not playable_url:
            continue
        formats.append({
            'format_id': format_id,
            'quality': q(format_id),
            'url': playable_url,
        })
    extract_dash_manifest(video, formats)
    process_formats(formats)
    v_id = video.get('videoId') or video.get('id') or video_id
    info = {
        'id': v_id,
        'formats': formats,
        'thumbnail': try_get(video, lambda x: x['thumbnailImage']['uri']),
        'uploader_id': try_get(video, lambda x: x['owner']['id']),
        'timestamp': int_or_none(video.get('publish_time')),
        'duration': float_or_none(video.get('playable_duration_in_ms'), 1000),
    }
    description = try_get(video, lambda x: x['savable_description']['text'])
    title = video.get('name')
    if title:
        info.update({
            'title': title,
            'description': description,
        })
    else:
        info['title'] = description or 'Facebook video #%s' % v_id
    entries.append(info)

def sign(manifest_url, manifest_id):
    for host in ('hdfauthftv-a.akamaihd.net', 'hdfauth.francetv.fr'):
        signed_url = url_or_none(self._download_webpage(
            'https://%s/esi/TA' % host, video_id,
            'Downloading signed %s manifest URL' % manifest_id,
            fatal=False, query={
                'url': manifest_url,
            }))
        if signed_url:
            return signed_url
    return manifest_url

def getlicensetoken(license):
    modlicense = license.replace('$', '').replace('0', '1')
    center = int(len(modlicense) / 2)
    fronthalf = int(modlicense[:center + 1])
    backhalf = int(modlicense[center:])

    modlicense = compat_str(4 * abs(fronthalf - backhalf))

    def parts():
        for o in range(0, center + 1):
            for i in range(1, 5):
                yield compat_str((int(license[o + i]) + int(modlicense[o])) % 10)

    return ''.join(parts())

def getrealurl(video_url, license_code):
    if not video_url.startswith('function/0/'):
        return video_url  # not obfuscated

    url_path, _, url_query = video_url.partition('?')
    urlparts = url_path.split('/')[2:]
    license = getlicensetoken(license_code)
    newmagic = urlparts[5][:32]

    def spells(x, o):
        l = (o + sum(int(n) for n in license[o:])) % 32
        for i in range(0, len(x)):
            yield {l: x[o], o: x[l]}.get(i, x[i])

    for o in range(len(newmagic) - 1, -1, -1):
        newmagic = ''.join(spells(newmagic, o))

    urlparts[5] = newmagic + urlparts[5][32:]
    return '/'.join(urlparts) + '?' + url_query

