def aa_decode(aa_code):
    symbol_table = (
        ('7', '(() + (o^_^o))'),
        ('6', '((o^_^o) +(o^_^o))'),
        ('5', '(() + ())'),
        ('2', '((o^_^o) - ())'),
        ('4', '()'),
        ('3', '(o^_^o)'),
        ('1', '()'),
        ('0', '(c^_^o)'),
        ('+', ''),
    )
    delim = '()[]+'

    def chr_from_code(c):
        for val, pat in symbol_table:
            c = c.replace(pat, val)
        if c.startswith(('u', 'U')):
            base = 16
            c = c[1:]
        else:
            base = 10
        c = int_or_none(c, base=base)
        return '' if c is None else compat_chr(c)

    return ''.join(
        chr_from_code(aa_char)
        for aa_char in aa_code.split(delim))

def chr_from_code(c):
    for val, pat in symbol_table:
        c = c.replace(pat, val)
    if c.startswith(('u', 'U')):
        base = 16
        c = c[1:]
    else:
        base = 10
    c = int_or_none(c, base=base)
    return '' if c is None else compat_chr(c)

