def gen_sig_code(idxs):
    def _genslice(start, end, step):
        starts = '' if start == 0 else str(start)
        ends = (':%d' % (end + step)) if end + step >= 0 else ':'
        steps = '' if step == 1 else (':%d' % step)
        return 's[{0}{1}{2}]'.format(starts, ends, steps)

    step = None
    # Quelch pyflakes warnings - start will be set when step is set
    start = '(Never used)'
    for i, prev in zip(idxs[1:], idxs[:-1]):
        if step is not None:
            if i - prev == step:
                continue
            yield _genslice(start, prev, step)
            step = None
            continue
        if i - prev in [-1, 1]:
            step = i - prev
            start = prev
            continue
        else:
            yield 's[%d]' % prev
    if step is None:
        yield 's[%d]' % i
    else:
        yield _genslice(start, i, step)

def inner(*args, **kwargs):
    if cache_id not in self._player_cache:
        try:
            self._player_cache[cache_id] = func(*args, **kwargs)
        except ExtractorError as e:
            self._player_cache[cache_id] = e
        except Exception as e:
            self._player_cache[cache_id] = ExtractorError(traceback.format_exc(), cause=e)

    ret = self._player_cache[cache_id]
    if isinstance(ret, Exception):
        raise ret
    return ret

def extract_nsig(s):
    try:
        ret = func([s], kwargs={'_ytdl_do_not_return': s})
    except JSInterpreter.Exception:
        raise
    except Exception as e:
        raise JSInterpreter.Exception(traceback.format_exc(), cause=e)

    if ret.startswith('enhanced_except_') or ret.endswith(s):
        raise JSInterpreter.Exception('Signature function returned an exception')
    return ret

def is_agegated(playability):
    # playability: dict
    if not playability:
        return False

    if playability.get('desktopLegacyAgeGateReason'):
        return True

    reasons = traverse_obj(playability, (('status', 'reason'),))
    AGE_GATE_REASONS = (
        'confirm your age', 'age-restricted', 'inappropriate',  # reason
        'age_verification_required', 'age_check_required',  # status
    )
    return any(expected in reason for expected in AGE_GATE_REASONS for reason in reasons)

def build_fragments(f):
    return LazyList({
        'url': update_url_query(f['url'], {
            'range': '{0}-{1}'.format(range_start, min(range_start + CHUNK_SIZE - 1, f['filesize'])),
        })
    } for range_start in range(0, f['filesize'], CHUNK_SIZE))

def process_manifest_format(f, proto, client_name, itag, all_formats=False):
    key = (proto, f.get('language'))
    if not all_formats and key in itags[itag]:
        return False
    itags[itag].add(key)

    if itag:
        f['format_id'] = (
            '{0}-{1}'.format(itag, proto)
            if all_formats or any(p != proto for p, _ in itags[itag])
            else itag)

    if f.get('source_preference') is None:
        f['source_preference'] = -1

    # Deprioritize since its pre-merged m3u8 formats may have lower quality audio streams
    if client_name == 'web_safari' and proto == 'hls' and not is_live:
        f['source_preference'] -= 1

    if itag in ('616', '235'):
        f['format_note'] = join_nonempty(f.get('format_note'), 'Premium', delim=' ')
        f['source_preference'] += 100

    f['quality'] = q(traverse_obj(f, (
        'format_id', T(lambda s: itag_qualities[s.split('-')[0]])), default=-1))
    if try_call(lambda: f['fps'] <= 1):
        del f['fps']

    if proto == 'hls' and f.get('has_drm'):
        f['has_drm'] = 'maybe'
        f['source_preference'] -= 5
    return True

def process_language(container, base_url, lang_code, query):
    lang_subs = []
    for fmt in self._SUBTITLE_FORMATS:
        query.update({
            'fmt': fmt,
            # xosf=1 causes undesirable text position data for vtt, json3 & srv* subtitles
            # See: https://github.com/yt-dlp/yt-dlp/issues/13654
            'xosf': []
        })
        lang_subs.append({
            'ext': fmt,
            'url': update_url_query(base_url, query),
        })
    container[lang_code] = lang_subs

def entries():
    for item in (data.get('items') or []):
        entry = self._extract_item(item, False)
        if entry:
            yield entry

def try_utime(self, path, atime, mtime, errnote='Cannot update utime of file'):
    try:
        os.utime(encodeFilename(path), (atime, mtime))
    except Exception:
        self._downloader.report_warning(errnote)

def check_version(self):
    if not self.available:
        raise FFmpegPostProcessorError('ffmpeg or avconv not found. Please install one.')

    required_version = '10-0' if self.basename == 'avconv' else '1.0'
    if is_outdated_version(
            self._versions[self.basename], required_version):
        warning = 'Your copy of %s is outdated, update %s to version %s or newer if you encounter any errors.' % (
            self.basename, self.basename, required_version)
        if self._downloader:
            self._downloader.report_warning(warning)

def run_ffmpeg(self, path, out_path, codec, more_opts):
    if codec is None:
        acodec_opts = []
    else:
        acodec_opts = ['-acodec', codec]
    opts = ['-vn'] + acodec_opts + more_opts
    try:
        FFmpegPostProcessor.run_ffmpeg(self, path, out_path, opts)
    except FFmpegPostProcessorError as err:
        raise AudioConversionError(err.msg)

def get_ffmpeg_version(path):
    ver = get_exe_version(path, args=['-version'])
    if ver:
        regexs = [
            r'(?:\d+:)?([0-9.]+)-[0-9]+ubuntu[0-9.]+$',  # Ubuntu, see [1]
            r'n([0-9.]+)$',  # Arch Linux
            # 1. http://www.ducea.com/2006/06/17/ubuntu-package-version-naming-explanation/
        ]
        for regex in regexs:
            mobj = re.match(regex, ver)
            if mobj:
                ver = mobj.group(1)
    return ver

def add(meta_list, info_list=None):
    if not info_list:
        info_list = meta_list
    if not isinstance(meta_list, (list, tuple)):
        meta_list = (meta_list,)
    if not isinstance(info_list, (list, tuple)):
        info_list = (info_list,)
    for info_f in info_list:
        if info.get(info_f) is not None:
            for meta_f in meta_list:
                metadata[meta_f] = info[info_f]
            break

def run(self, info):
    """ Set extended attributes on downloaded file (if xattr support is found). """

    # Write the metadata to the file's xattrs
    self._downloader.to_screen('[metadata] Writing metadata to file\'s xattrs')

    filename = info['filepath']

    try:
        xattr_mapping = {
            'user.xdg.referrer.url': 'webpage_url',
            # 'user.xdg.comment':            'description',
            'user.dublincore.title': 'title',
            'user.dublincore.date': 'upload_date',
            'user.dublincore.description': 'description',
            'user.dublincore.contributor': 'uploader',
            'user.dublincore.format': 'format',
        }

        num_written = 0
        for xattrname, infoname in xattr_mapping.items():

            value = info.get(infoname)

            if value:
                if infoname == 'upload_date':
                    value = hyphenate_date(value)

                byte_value = value.encode('utf-8')
                write_xattr(filename, xattrname, byte_value)
                num_written += 1

        return [], info

    except XAttrUnavailableError as e:
        self._downloader.report_error(str(e))
        return [], info

    except XAttrMetadataError as e:
        if e.reason == 'NO_SPACE':
            self._downloader.report_warning(
                'There\'s no disk space left, disk quota exceeded or filesystem xattr limit exceeded. '
                + (('Some ' if num_written else '') + 'extended attributes are not written.').capitalize())
        elif e.reason == 'VALUE_TOO_LONG':
            self._downloader.report_warning(
                'Unable to write extended attributes due to too long values.')
        else:
            msg = 'This filesystem doesn\'t support extended attributes. '
            if compat_os_name == 'nt':
                msg += 'You need to use NTFS.'
            else:
                msg += '(You may have to enable them in your /etc/fstab)'
            self._downloader.report_error(msg)
        return [], info
